// src/components/AdminLayout.js
import React from "react";
import { NavLink, useNavigate, Outlet } from "react-router-dom";
import "./AdminLayout.css";

export default function AdminLayout() {
  const navigate = useNavigate();

  const menus = [
  { label: "Dashboard", icon: "üìä", path: "/admin" },                // ‚úÖ ‡πÉ‡∏ä‡πâ /admin
  { label: "‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤", icon: "üì¶", path: "/admin/products" },
  { label: "‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏Ñ‡∏á‡∏Ñ‡∏•‡∏±‡∏á", icon: "üè¨", path: "/admin/product-management" },
  { label: "‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠", icon: "üßæ", path: "/admin/orders" },
  { label: "‡∏™‡∏£‡∏∏‡∏õ‡∏¢‡∏≠‡∏î‡∏Ç‡∏≤‡∏¢", icon: "üí∞", path: "/admin/reports" },
];
  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    navigate("/login?role=admin");
  };

  return (
    <div className="admin-layout">
      {/* Top Navbar */}
      <header className="admin-navbar">
        <div className="logo">üåø ADMIN PANEL</div>
        
        <nav className="admin-menu">
          {menus.map((m, i) => (
            <NavLink
              key={i}
              to={m.path}
              className={({ isActive }) =>
                `admin-link ${isActive ? "active" : ""}`
              }
              end={m.path === "/admin"} // ‚úÖ Dashboard active ‡πÅ‡∏Ñ‡πà‡∏ï‡∏≠‡∏ô‡∏≠‡∏¢‡∏π‡πà /admin
            >
              <span className="menu-icon">{m.icon}</span> {m.label}
            </NavLink>
          ))}
        </nav>

        <button className="logout-btn" onClick={logout}>
          üö™ ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö
        </button>
      </header>

      {/* Main Content */}
      <main className="admin-content">
        <Outlet />
      </main>
    </div>
  );
}
