// src/pages/ProfileModal.js
import React, { useEffect, useMemo, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./ProfileModal.css";

export default function ProfileModal({ initialTab = "profile", onClose }) {
  // ข้อมูลผู้ใช้ (ตอนนี้อ่านจาก localStorage ไปก่อน)
  const user0 = useMemo(() => {
    const fullname = localStorage.getItem("fullname") || "ผู้ใช้งาน";
    const email = localStorage.getItem("email") || "";
    const phone = localStorage.getItem("phone") || "";
    return {
      fullname,
      email,
      phone,
      avatar: process.env.PUBLIC_URL + "/profile.jpg",
    };
  }, []);

  const [user, setUser] = useState(user0);
  const [tab, setTab] = useState(initialTab); // profile | orders | addresses | settings

  // ปิด modal: คลิกพื้นหลัง / ปุ่มปิด / กด ESC
  const closeModal = () => {
    if (typeof onClose === "function") onClose();
  };

  useEffect(() => {
    function onKey(e) {
      if (e.key === "Escape") closeModal();
    }
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, []);

  // (เผื่อในอนาคต) โหลดจาก API จริง
  useEffect(() => {
    let ignore = false;
    (async () => {
      try {
        // ตัวอย่าง: const r = await axios.get("/api/me");
        // if (!ignore) setUser((u) => ({ ...u, ...r.data }));
      } catch {}
    })();
    return () => { ignore = true; };
  }, []);

  function logout() {
    try {
      localStorage.removeItem("token");
      localStorage.removeItem("role");
      delete axios.defaults.headers.common["Authorization"];
    } finally {
      // ปิด modal แล้วให้ผู้ใช้ไปจัดการหน้า login เอง (หรือจะ window.location='/login' ก็ได้)
      closeModal();
      window.location.href = "/login";
    }
  }

  return (
    <div className="pm-backdrop" onClick={closeModal} role="dialog" aria-modal="true">
      <div className="pm-card" onClick={(e) => e.stopPropagation()}>
        {/* header */}
        <button className="pm-close" onClick={closeModal} aria-label="ปิด">×</button>

        <div className="pm-head">
          <img
            className="pm-avatar"
            src={user.avatar}
            alt="avatar"
            onError={(e) => (e.currentTarget.src = process.env.PUBLIC_URL + "/logo.png")}
          />
          <div className="pm-head-info">
            <div className="pm-name">{user.fullname}</div>
            {(user.email || user.phone) && (
              <div className="pm-sub">{user.email || user.phone}</div>
            )}
          </div>
        </div>

        {/* tabs */}
        <div className="pm-tabs" role="tablist">
          <button className={`pm-tab ${tab === "profile" ? "active" : ""}`} onClick={() => setTab("profile")} role="tab">
            โปรไฟล์
          </button>
          <button className={`pm-tab ${tab === "orders" ? "active" : ""}`} onClick={() => setTab("orders")} role="tab">
            คำสั่งซื้อของฉัน
          </button>
          <button className={`pm-tab ${tab === "addresses" ? "active" : ""}`} onClick={() => setTab("addresses")} role="tab">
            ที่อยู่ของฉัน
          </button>
          <button className={`pm-tab ${tab === "settings" ? "active" : ""}`} onClick={() => setTab("settings")} role="tab">
            การตั้งค่า
          </button>
        </div>

        {/* body */}
        <div className="pm-body">
          {tab === "profile" && (
            <div className="pm-section">
              <div className="pm-grid">
                <label>ชื่อ-นามสกุล</label>
                <input defaultValue={user.fullname} disabled />
                <label>อีเมล</label>
                <input defaultValue={user.email} disabled />
                <label>เบอร์โทร</label>
                <input defaultValue={user.phone} disabled />
              </div>
              <div className="pm-actions">
                <button className="pm-btn" onClick={() => alert("โหมดแก้ไขจะเปิดในรอบถัดไป")}>
                  แก้ไขโปรไฟล์
                </button>
                <button className="pm-btn pm-outline" onClick={() => alert("เปลี่ยนรหัสผ่าน (ภายหลัง)")}>
                  เปลี่ยนรหัสผ่าน
                </button>
              </div>
            </div>
          )}

          {tab === "orders" && (
            <div className="pm-section">
              <div className="pm-note">รายการคำสั่งซื้อจะแสดงที่นี่</div>
              <div className="pm-list-skel">
                <div className="pm-skel" />
                <div className="pm-skel" />
                <div className="pm-skel" />
              </div>
              {/* <Link to="/account/orders" className="pm-link">ดูคำสั่งซื้อทั้งหมด →</Link> */}
            </div>
          )}

          {tab === "addresses" && (
            <div className="pm-section">
              <div className="pm-note">จัดการที่อยู่จัดส่ง</div>
              <Link to="/account/addresses" className="pm-btn" onClick={closeModal}>
                เปิดหน้าที่อยู่ของฉัน
              </Link>
            </div>
          )}

          {tab === "settings" && (
            <div className="pm-section">
              <div className="pm-note">ตั้งค่าทั่วไปของบัญชี</div>
              <div className="pm-actions">
                <button className="pm-btn pm-danger" onClick={logout}>
                  ออกจากระบบ
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
