// src/admin/AdminUnits.js
// ✅ จัดการ "หน่วยสินค้า (product_units)" — ดึงจากฐานข้อมูลเท่านั้น
// - ใช้ตาราง: product_units (code, unit_name, description, category_id, is_published)
// - ผูกหมวดหมู่ที่อ้างอิง: product_categories (category_id, category_name)
// - API ที่ใช้ (ต้องมีฝั่ง backend):
//    GET    /api/units                         → รายการหน่วย (จากตาราง product_units)
//    POST   /api/units                         → สร้างหน่วยใหม่
//    PUT    /api/units/:code                   → แก้ไขหน่วย (อิง code เป็นไอดี)
//    DELETE /api/units/:code                   → ลบหน่วย
//    PATCH  /api/admin/units/:code/publish     → toggle is_published
//    GET    /api/categories?published=1        → ดึงหมวดหมู่สินค้า (อ้างอิง category_id)
// - ไม่มีค่า default ในฝั่ง client: ถ้าโหลดไม่ได้จะแจ้ง Error (ไม่แสดงรายการหลอกตา)

import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductManagement.css';
import { api, path } from '../lib/api';

const asStr = (v) => (v === null || v === undefined) ? '' : String(v).trim();
const truthy = (v) => v === true || String(v).toLowerCase() === 'true' || v === 1 || v === '1';

export default function AdminUnits() {
  const [units, setUnits] = useState([]);
  const [categories, setCategories] = useState([]); // [{category_id, category_name}]
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [filter, setFilter] = useState('all'); // all | published | hidden
  const [error, setError] = useState('');

  /* --------- ฟอร์มสร้างใหม่ (จำเป็นตาม DB) --------- */
  const [form, setForm] = useState({
    code: '',
    unit_name: '',
    description: '',
    category_id: '',
    is_published: true,
  });

  const handleErr = (err, msg = 'เกิดข้อผิดพลาด') => {
    console.error(msg, err?.response?.data || err?.message || err);
    setError(err?.response?.data?.error || err?.message || msg);
    alert(err?.response?.data?.error || err?.message || msg);
  };

  /* --------- โหลดข้อมูลจากฐานข้อมูลเท่านั้น --------- */
  const fetchCategories = async () => {
    const { data } = await api.get(path('/categories'), { params: { published: 1 } });
    // normalize เฉพาะฟิลด์ที่ใช้ในฟอร์ม
    const list = Array.isArray(data) ? data : (data?.items || []);
    const norm = list.map((c) => ({
      category_id: asStr(c.category_id ?? c.id ?? c.code ?? ''),
      category_name: asStr(c.category_name ?? c.name ?? ''),
    })).filter((c) => c.category_id && c.category_name);
    setCategories(norm);
  };

  const fetchUnits = async () => {
    const { data } = await api.get(path('/units'));
    const list = Array.isArray(data) ? data : (data?.items || []);
    const norm = list.map((u) => ({
      code: asStr(u.code ?? u.unit_code ?? u.id ?? ''),
      unit_name: asStr(u.unit_name ?? u.label ?? u.name ?? ''),
      description: asStr(u.description ?? ''),
      category_id: asStr(u.category_id ?? ''),
      is_published: typeof u.is_published === 'boolean'
        ? u.is_published
        : truthy(u.is_published ?? u.published ?? u.status === 'published'),
      updated_at: u.updated_at,
      created_at: u.created_at,
    })).filter((u) => u.code && u.unit_name); // บังคับฟิลด์ที่จำเป็น
    setUnits(norm);
  };

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        setError('');
        await Promise.all([fetchCategories(), fetchUnits()]);
      } catch (err) {
        handleErr(err, 'โหลดข้อมูลจากฐานข้อมูลไม่สำเร็จ');
      } finally {
        setLoading(false);
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  /* --------- ฟิลเตอร์แสดงผล --------- */
  const filteredUnits = useMemo(() => {
    if (!Array.isArray(units)) return [];
    if (filter === 'all') return units;
    return units.filter((u) => filter === 'published' ? u.is_published : !u.is_published);
  }, [units, filter]);

  /* --------- สร้างหน่วยใหม่ --------- */
  const validateForm = (payload) => {
    if (!asStr(payload.code)) return 'กรอกรหัสหน่วย (code)';
    if (!/^[a-z0-9_]+$/i.test(payload.code)) return 'code ต้องเป็นตัวอักษร/ตัวเลข/ขีดล่าง';
    if (!asStr(payload.unit_name)) return 'กรอกชื่อหน่วย (unit_name)';
    if (!asStr(payload.category_id)) return 'เลือกหมวดหมู่ (category_id)';
    return '';
  };

  const createUnit = async () => {
    const payload = {
      code: asStr(form.code).toLowerCase(),
      unit_name: asStr(form.unit_name),
      description: asStr(form.description),
      category_id: asStr(form.category_id),
      is_published: !!form.is_published,
    };
    const msg = validateForm(payload);
    if (msg) return alert(msg);

    if (units.some((u) => u.code === payload.code)) {
      return alert(`code "${payload.code}" มีอยู่แล้ว`);
    }

    try {
      setSaving(true);
      await api.post(path('/units'), payload);
      await fetchUnits();
      setForm({ code: '', unit_name: '', description: '', category_id: '', is_published: true });
    } catch (err) {
      handleErr(err, 'บันทึกหน่วยสินค้าไม่สำเร็จ');
    } finally {
      setSaving(false);
    }
  };

  /* --------- แก้ไขหน่วย (inline prompt) --------- */
  const editUnit = async (u) => {
    const nextName = window.prompt('ชื่อหน่วยใหม่ (unit_name)', u.unit_name);
    if (!nextName) return;
    const next = {
      code: u.code, // ใช้รหัสเดิมเป็นไอดี
      unit_name: asStr(nextName),
      description: asStr(u.description),
      category_id: asStr(u.category_id),
      is_published: !!u.is_published,
    };
    const msg = validateForm(next);
    if (msg) return alert(msg);

    try {
      setSaving(true);
      await api.put(path(`/units/${u.code}`), next);
      await fetchUnits();
    } catch (err) {
      handleErr(err, 'แก้ไขหน่วยไม่สำเร็จ');
    } finally {
      setSaving(false);
    }
  };

  /* --------- ลบ --------- */
  const deleteUnit = async (u) => {
    if (!window.confirm(`ลบหน่วย "${u.unit_name}" ?`)) return;
    try {
      setSaving(true);
      await api.delete(path(`/units/${u.code}`));
      await fetchUnits();
    } catch (err) {
      handleErr(err, 'ลบหน่วยไม่สำเร็จ');
    } finally {
      setSaving(false);
    }
  };

  /* --------- toggle แสดงผล --------- */
  const togglePublish = async (u) => {
    try {
      setSaving(true);
      await api.patch(path(`/admin/units/${u.code}/publish`), { is_published: !u.is_published });
      await fetchUnits();
    } catch (err) {
      handleErr(err, 'อัปเดตสถานะแสดงผลไม่สำเร็จ');
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <div style={{ padding: 12 }}>กำลังโหลดข้อมูลจากฐานข้อมูล…</div>;

  return (
    <div className="pm-page">
      {/* ปุ่มกลับ */}
      <div style={{display:'flex', gap:8, margin:'12px 0 20px', flexWrap:'wrap'}}>
        <Link to="/admin/products" className="btn btn-ghost">← กลับไปจัดการสินค้า</Link>
      </div>

      <h2>จัดการหน่วยสินค้า (จากฐานข้อมูล)</h2>

      {/* แจ้ง Error ถ้ามี */}
      {!!error && (
        <div className="pm-alert pm-alert-error" style={{ marginTop: 8 }}>
          {error}
        </div>
      )}

      {/* ฟอร์มเพิ่มใหม่ — บังคับกรอกตามคอลัมน์สำคัญใน DB */}
      <div className="pm-actions mt-8" style={{ flexWrap:'wrap', rowGap: 8 }}>
        <input
          placeholder="code (เช่น: piece, pot) [a-z0-9_]"
          value={form.code}
          onChange={(e)=>setForm((p)=>({ ...p, code: e.target.value }))}
          style={{ maxWidth:220 }}
        />
        <input
          placeholder="unit_name (เช่น: ชิ้น, กระถาง)"
          value={form.unit_name}
          onChange={(e)=>setForm((p)=>({ ...p, unit_name: e.target.value }))}
          style={{ minWidth:220 }}
        />
        <select
          value={form.category_id}
          onChange={(e)=>setForm((p)=>({ ...p, category_id: e.target.value }))}
          style={{ minWidth:220 }}
        >
          <option value="">— เลือกหมวดหมู่ (category_id) —</option>
          {categories.map(c => (
            <option key={c.category_id} value={c.category_id}>
              {c.category_id} — {c.category_name}
            </option>
          ))}
        </select>
        <input
          placeholder="description (ถ้ามี)"
          value={form.description}
          onChange={(e)=>setForm((p)=>({ ...p, description: e.target.value }))}
          style={{ minWidth:260 }}
        />
        <label style={{ display:'inline-flex', alignItems:'center', gap:6 }}>
          <input
            type="checkbox"
            checked={!!form.is_published}
            onChange={(e)=>setForm((p)=>({ ...p, is_published: e.target.checked }))}
          />
          แสดงผล (is_published)
        </label>
        <button
          type="button"
          className="btn btn-primary"
          disabled={saving}
          onClick={createUnit}
        >
          {saving ? 'กำลังบันทึก…' : 'เพิ่มหน่วยสินค้า'}
        </button>
      </div>

      {/* ฟิลเตอร์แสดงผล */}
      <div className="pm-toolbar">
        <div className="seg">
          <button type="button" className={`seg-btn ${filter==='all' ? 'active': ''}`} onClick={()=>setFilter('all')}>ทั้งหมด</button>
          <button type="button" className={`seg-btn ${filter==='published' ? 'active': ''}`} onClick={()=>setFilter('published')}>เฉพาะที่แสดง</button>
          <button type="button" className={`seg-btn ${filter==='hidden' ? 'active': ''}`} onClick={()=>setFilter('hidden')}>เฉพาะที่ซ่อน</button>
        </div>
      </div>

      {/* ตาราง */}
      <div className="pm-table-wrap mt-8">
        <table className="pm-table pm-fixed">
          <thead>
            <tr>
              <th style={{width:160}}>code</th>
              <th style={{width:200}}>unit_name</th>
              <th>description</th>
              <th style={{width:220}}>category_id</th>
              <th style={{textAlign:'center', width:140}}>แสดงผล</th>
              <th style={{textAlign:'center', width:220}}>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {filteredUnits.map((u) => (
              <tr key={u.code}>
                <td>{u.code}</td>
                <td>
                  <span className={`pill ${u.is_published ? 'on' : 'off'}`} style={{ marginRight: 6 }}>
                    {u.is_published ? 'กำลังแสดง' : 'ถูกซ่อน'}
                  </span>
                  <span className="name-with-badges">{u.unit_name}</span>
                </td>
                <td style={{ color: u.description ? '#222' : '#999' }}>
                  {u.description || '—'}
                </td>
                <td>{u.category_id || <span style={{ color:'#c00' }}>NULL (ควรแก้)</span>}</td>
                <td className="cell-actions" style={{ textAlign:'center' }}>
                  <button
                    className={`btn ${u.is_published ? 'btn-warn' : 'btn-primary'}`}
                    onClick={() => togglePublish(u)}
                    title={u.is_published ? 'ซ่อน' : 'แสดง'}
                  >
                    {u.is_published ? 'ไม่แสดง' : 'แสดง'}
                  </button>
                </td>
                <td className="cell-actions">
                  <button className="btn btn-ghost" onClick={()=>editUnit(u)}>แก้ไข</button>
                  <button className="btn btn-danger" onClick={()=>deleteUnit(u)}>ลบ</button>
                </td>
              </tr>
            ))}
            {filteredUnits.length === 0 && (
              <tr><td colSpan={6} style={{ color:'#777' }}>— ไม่มีข้อมูล —</td></tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
