// src/components/HeaderClassic.js
import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { getCount, CART_EVENT } from "../lib/cart"; // ‚¨ÖÔ∏è ‡πÉ‡∏ä‡πâ‡∏à‡∏≤‡∏Å‡∏™‡∏°‡∏≠‡∏á‡∏Å‡∏•‡∏≤‡∏á

export default function HeaderClassic() {
  const nav = useNavigate();
  const loc = useLocation();

  const fullname = localStorage.getItem("fullname") || "‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô";
  const avatar = process.env.PUBLIC_URL + "/profile.jpg";

  const [cartCount, setCartCount] = useState(getCount());
  useEffect(() => {
    const onChanged = () => setCartCount(getCount());
    window.addEventListener(CART_EVENT, onChanged);
    return () => window.removeEventListener(CART_EVENT, onChanged);
  }, []);

  const isHome = loc.pathname === "/";

  function logout() {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    nav("/login", { replace: true });
  }

  return (
    <header style={{
      display:"flex", alignItems:"center", justifyContent:"space-between",
      padding:"10px 16px", borderBottom:"1px solid #e5e7eb",
      background:"#fff", position:"sticky", top:0, zIndex:50
    }}>
      <Link to="/" style={{ fontWeight:700, fontSize:20, color:"#16a34a", textDecoration:"none" }}>
        PRACH MAEJO
      </Link>

      <div style={{ display:"flex", alignItems:"center", gap:16 }}>
        <Link to="/" title="‡∏´‡∏ô‡πâ‡∏≤‡πÅ‡∏£‡∏Å" aria-label="‡∏´‡∏ô‡πâ‡∏≤‡πÅ‡∏£‡∏Å">üè†</Link>

        {/* ‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤ + badge */}
        <div style={{ position:"relative" }}>
          <Link to="/cart" title="‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤" aria-label="‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤">üõí</Link>
          {cartCount > 0 && (
            <span style={{
              position:"absolute", top:-6, right:-10,
              background:"#ef4444", color:"#fff", borderRadius:999,
              fontSize:12, minWidth:18, height:18, display:"grid",
              placeItems:"center", padding:"0 4px"
            }}>
              {cartCount}
            </span>
          )}
        </div>

        <div style={{ display:"flex", alignItems:"center", gap:8 }}>
          <img
            src={avatar}
            alt="avatar"
            onError={(e)=> e.currentTarget.src = process.env.PUBLIC_URL + "/logo.png"}
            style={{ width:32, height:32, borderRadius:999, objectFit:"cover" }}
          />
          <span>{isHome ? `‡∏™‡∏ß‡∏±‡∏™‡∏î‡∏µ ${fullname}` : fullname}</span>
        </div>

        <button onClick={logout}
          style={{ border:"none", background:"transparent", cursor:"pointer" }}
          title="‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö" aria-label="‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö">
          üö™
        </button>
      </div>
    </header>
  );
}
