/* === ProductManagement.js (Part 1/4) START === */
// frontend/src/admin/ProductManagement.js
import React, { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductManagement.css';
import { api, path } from '../lib/api';
import { useLookups } from '../lib/lookups';

/* ---------- Helpers ---------- */
const asStr = (v) => (v === null || v === undefined) ? '' : String(v);
const pickStatusName = (x) => x?.status_name ?? x?.StatusName ?? x?.name ?? '';
const toInt = (v) => {
  const n = parseInt(String(v ?? '').trim(), 10);
  return Number.isFinite(n) ? n : null;
};
const toNum = (v) => {
  if (v === null || v === undefined) return null;
  const n = Number(String(v).replace(/[,\sà¸¿]/g, ''));
  return Number.isFinite(n) ? n : null;
};

/* ---------- Tiny Toast (in-file, no external dep) ---------- */
function useToasts() {
  const [toasts, setToasts] = useState([]);
  const push = (msg, type = 'info') => {
    const id = Math.random().toString(36).slice(2);
    setToasts((xs) => [...xs, { id, msg, type }]);
    setTimeout(() => setToasts((xs) => xs.filter((t) => t.id !== id)), 3000);
  };
  const remove = (id) => setToasts((xs) => xs.filter((t) => t.id !== id));
  return { toasts, push, remove };
}

/* ---------- Uploader (generic) ---------- */
async function uploadImage(file) {
  if (!file) return null;
  const fd = new FormData();
  fd.append('file', file);
  const res = await api.post(path('/upload'), fd, {
    headers: { 'Content-Type': 'multipart/form-data' },
  });
  return res?.data?.url || res?.data?.imageUrl || res?.data?.path || res?.data?.location || null;
}

/* ---------- ProductManagement ---------- */
export default function ProductManagement() {
  /* ===== states (original layout kept) ===== */
  const [products, setProducts] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showArchived, setShowArchived] = useState(false);

  // filters
  const [query, setQuery] = useState('');
  const [catFilter, setCatFilter] = useState('');
  const [subcatFilter, setSubcatFilter] = useState('');
  const [stockFilter, setStockFilter] = useState('all');
  const [pageSize, setPageSize] = useState(20);
  const [groupByCategory, setGroupByCategory] = useState(false);

  /* lookups */
  const { data: lookups, reload: reloadLookups, loading: lkLoading } = useLookups({ published: true });

  /* form state (keep original names) */
  const [form, setForm] = useState({
    product_name: '', description: '', price: '',
    category_id: null, subcategory_id: null,
    product_unit_id: null, size_value: '', size_unit_id: null,
    origin: '', product_status_id: ''
  });
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editProductId, setEditProductId] = useState(null);

  // â˜… VARIANTS QUICK MODE â€” state
  const [quickVar, setQuickVar] = useState({
    enabled: false,
    // option names + values (à¸ªà¸¹à¸‡à¸ªà¸¸à¸” 3)
    opt1Name: 'à¸ªà¸µ', opt1Value: '',
    opt2Name: 'à¸‚à¸™à¸²à¸”', opt2Value: '',
    opt3Name: '', opt3Value: '',
    // extra
    sku: '', price: '', stock: '', is_active: true
  });

  /* refs */
  const nameRef = useRef(null);

  /* toasts */
  const { toasts, push, remove } = useToasts();

  /* ---------- API Helpers ---------- */
  const handleApiError = (err, fallback = 'à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”') => {
    console.error(fallback, err?.response?.data || err?.message || err);
    const msg = err?.response?.data?.message || err?.message || fallback;
    push(`âŒ ${msg}`, 'danger');
  };

  /* ---------- Load Products ---------- */
  const fetchProducts = useCallback(async () => {
    try {
      const { data } = await api.get(path('/admin/products'), {
        params: { include_archived: showArchived ? 1 : 0 },
      });
      let items = Array.isArray(data?.items) ? data.items : (Array.isArray(data) ? data : []);
      items = items.map((p) => {
        const stock = Number(p.stock_qty ?? p.stock ?? 0);
        return {
          ...p,
          product_status_name: stock <= 0 ? 'à¸ªà¸´à¸™à¸„à¹‰à¸²à¸«à¸¡à¸”' : stock <= 5 ? 'à¸ªà¸•à¹‡à¸­à¸à¹ƒà¸à¸¥à¹‰à¸«à¸¡à¸”' : 'à¸žà¸£à¹‰à¸­à¸¡à¸ˆà¸³à¸«à¸™à¹ˆà¸²à¸¢',
        };
      });
      setProducts(items);
    } catch (err) {
      handleApiError(err, 'âŒ à¹‚à¸«à¸¥à¸”à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  }, [showArchived]);

  /* ---------- Load Statuses ---------- */
  const fetchStatuses = useCallback(async () => {
    try {
      let res = await api.get(path('/product-status'));
      if (!Array.isArray(res.data)) { try { res = await api.get(path('/product-statuses')); } catch {} }
      const normalized = (res.data || []).map((x) => ({
        id: asStr(x?.product_status_id ?? x?.id),
        name: pickStatusName(x),
      }));
      setStatuses(normalized);
    } catch {
      setStatuses([]);
    }
  }, []);

  /* ---------- Toggle publish/archive ---------- */
  const togglePublish = useCallback(async (productId, current) => {
    try {
      await api.patch(path(`/admin/products/${productId}/publish`), { is_published: !current });
      push(!current ? 'âœ… à¹€à¸›à¸´à¸”à¹à¸ªà¸”à¸‡à¸ªà¸´à¸™à¸„à¹‰à¸²à¹à¸¥à¹‰à¸§' : 'âœ… à¸‹à¹ˆà¸­à¸™à¸ªà¸´à¸™à¸„à¹‰à¸²à¹à¸¥à¹‰à¸§', 'ok');
      await fetchProducts();
    } catch (err) {
      handleApiError(err, 'âŒ à¸­à¸±à¸›à¹€à¸”à¸•à¸ªà¸–à¸²à¸™à¸°à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  }, [fetchProducts]);

  const archiveProduct = useCallback(async (id) => {
    if (!window.confirm('à¸•à¹‰à¸­à¸‡à¸à¸²à¸£à¹€à¸à¹‡à¸šà¸ªà¸´à¸™à¸„à¹‰à¸²à¸™à¸µà¹‰à¹„à¸§à¹‰à¹ƒà¸™à¸„à¸¥à¸±à¸‡à¹ƒà¸Šà¹ˆà¹„à¸«à¸¡?')) return;
    try {
      await api.delete(path(`/admin/products/${id}`));
      push('ðŸ—ƒï¸ à¸¢à¹‰à¸²à¸¢à¹„à¸›à¸–à¸±à¸‡à¹€à¸à¹‡à¸šà¹à¸¥à¹‰à¸§', 'warn');
      await fetchProducts();
    } catch (err) {
      handleApiError(err, 'âŒ à¹€à¸à¹‡à¸šà¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  }, [fetchProducts]);

  const unarchiveProduct = useCallback(async (id) => {
    try {
      await api.patch(path(`/admin/products/${id}/unarchive`));
      push('â™»ï¸ à¸à¸¹à¹‰à¸„à¸·à¸™à¸ªà¸´à¸™à¸„à¹‰à¸²à¹à¸¥à¹‰à¸§', 'ok');
      await fetchProducts();
    } catch (err) {
      handleApiError(err, 'âŒ à¸à¸¹à¹‰à¸„à¸·à¸™à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  }, [fetchProducts]);

  /* ---------- Init ---------- */
  useEffect(() => {
    (async () => {
      setLoading(true);
      await Promise.all([fetchProducts(), fetchStatuses(), reloadLookups()]);
      setLoading(false);
    })();
  }, [fetchProducts, fetchStatuses, reloadLookups, showArchived]);

  /* ---------- Form Events ---------- */
  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  };

  const onFiles = (e) => setSelectedFiles(Array.from(e.target.files || []));
  useEffect(() => {
    if (!selectedFiles.length) { setPreviews([]); return; }
    const urls = selectedFiles.map((f) => ({ name: f.name, url: URL.createObjectURL(f) }));
    setPreviews(urls);
    return () => urls.forEach((u) => URL.revokeObjectURL(u.url));
  }, [selectedFiles]);

  const clearForm = () => {
    setForm({
      product_name: '', description: '', price: '',
      category_id: null, subcategory_id: null,
      product_unit_id: null, size_value: '', size_unit_id: null,
      origin: '', product_status_id: ''
    });
    setSelectedFiles([]); setPreviews([]);
    setIsEditing(false); setEditProductId(null);

    // â˜… reset quick variant
    setQuickVar({
      enabled: false,
      opt1Name: 'à¸ªà¸µ', opt1Value: '',
      opt2Name: 'à¸‚à¸™à¸²à¸”', opt2Value: '',
      opt3Name: '', opt3Value: '',
      sku: '', price: '', stock: '', is_active: true
    });
  };
/* === ProductManagement.js (Part 1/4) END === */
/* === ProductManagement.js (Part 2/4) START === */
  /* ---------- Submit ---------- */
  const onSubmit = async (e) => {
    e.preventDefault();
    const price = parseInt(form.price);
    if (isNaN(price) || price < 0) { push('âš ï¸ à¸£à¸²à¸„à¸²à¸•à¹‰à¸­à¸‡à¹€à¸›à¹‡à¸™à¸ˆà¸³à¸™à¸§à¸™à¹€à¸•à¹‡à¸¡', 'warn'); return; }
    try {
      let uploaded = [];
      if (selectedFiles.length > 0)
        uploaded = (await Promise.all(selectedFiles.map(uploadImage))).filter(Boolean);

      const body = {
        product_name: form.product_name.trim(),
        description: form.description,
        price,
        category_id: form.category_id,
        subcategory_id: form.subcategory_id,
        product_unit_id: form.product_unit_id,
        size_unit_id: form.size_unit_id,
        size_value: form.size_value,
        origin: form.origin,
        product_status_id: form.product_status_id,
      };

      let id = editProductId;
      if (isEditing && id) {
        await api.put(path(`/admin/products/${id}`), body);
        push('âœ… à¸­à¸±à¸›à¹€à¸”à¸•à¸ªà¸´à¸™à¸„à¹‰à¸²à¸ªà¸³à¹€à¸£à¹‡à¸ˆ', 'ok');
      } else {
        const res = await api.post(path('/admin/products'), body);
        id = res?.data?.product_id ?? res?.data?.id;
        push('ðŸŽ‰ à¹€à¸žà¸´à¹ˆà¸¡à¸ªà¸´à¸™à¸„à¹‰à¸²à¹€à¸£à¸µà¸¢à¸šà¸£à¹‰à¸­à¸¢', 'ok');
      }

      if (uploaded.length) {
        for (let i = 0; i < uploaded.length; i++) {
          try {
            await api.post(path(`/admin/products/${id}/images`), {
              images: [{ url: uploaded[i], position: i + 1, is_primary: i === 0 }],
            });
          } catch {}
        }
      }

      await fetchProducts();
      await reloadLookups();
      clearForm();
    } catch (err) {
      handleApiError(err, 'âŒ à¸šà¸±à¸™à¸—à¸¶à¸à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  };

  /* ---------- Quick Mode (original UI/flow kept) ---------- */
  const handleQuickAdd = async () => {
    try {
      if (!quickVar.enabled) return;
      if (!form.product_name?.trim()) {
        nameRef.current?.focus();
        push('à¸à¸£à¸¸à¸“à¸²à¸à¸£à¸­à¸à¸Šà¸·à¹ˆà¸­à¸ªà¸´à¸™à¸„à¹‰à¸²', 'warn');
        return;
      }
      // à¸ªà¸£à¹‰à¸²à¸‡à¸ªà¸´à¸™à¸„à¹‰à¸² + à¸•à¸±à¸§à¹€à¸¥à¸·à¸­à¸à¹à¸£à¸à¸žà¸£à¹‰à¸­à¸¡à¸à¸±à¸™
      const body = {
        product_name: form.product_name.trim(),
        description: form.description,
        price: parseInt(form.price) || null,
        category_id: form.category_id,
        subcategory_id: form.subcategory_id,
        product_unit_id: form.product_unit_id,
        size_unit_id: form.size_unit_id,
        size_value: form.size_value,
        origin: form.origin,
        product_status_id: form.product_status_id,
      };
      const pr = await api.post(path('/admin/products'), body);
      const newId = pr?.data?.product_id ?? pr?.data?.id;

      // â¬‡ï¸ à¹€à¸•à¸£à¸µà¸¢à¸¡ options
      const opts = [];
      const pushOpt = (n, value) => {
        const v = (value || '').trim();
        if (n && v) opts.push({ name: n, value: v });
      };
      pushOpt(quickVar.opt1Name, quickVar.opt1Value);
      pushOpt(quickVar.opt2Name, quickVar.opt2Value);
      pushOpt(quickVar.opt3Name, quickVar.opt3Value);

      // à¹€à¸£à¸µà¸¢à¸ generate (à¸à¸±à¹ˆà¸‡à¸„à¸¸à¸“à¸¡à¸µ endpoint)
      await api.post(path(`/admin/products/${newId}/variants/generate`), {
        options: opts, price: quickVar.price || null, sku: quickVar.sku || null,
      });

      // à¸£à¸¹à¸›à¸«à¸¥à¸±à¸à¸‚à¸­à¸‡à¸ªà¸´à¸™à¸„à¹‰à¸²
      if (selectedFiles.length > 0) {
        const uploaded = (await Promise.all(selectedFiles.map(uploadImage))).filter(Boolean);
        for (let i = 0; i < uploaded.length; i++) {
          try {
            await api.post(path(`/admin/products/${newId}/images`), {
              images: [{ url: uploaded[i], position: i + 1, is_primary: i === 0 }],
            });
          } catch {}
        }
      }

      push('ðŸŽ‰ à¹€à¸žà¸´à¹ˆà¸¡à¸ªà¸´à¸™à¸„à¹‰à¸² + à¸•à¸±à¸§à¹€à¸¥à¸·à¸­à¸à¸ªà¸³à¹€à¸£à¹‡à¸ˆ', 'ok');
      await fetchProducts();
      clearForm();
    } catch (err) {
      handleApiError(err, 'âŒ à¹€à¸žà¸´à¹ˆà¸¡à¸ªà¸´à¸™à¸„à¹‰à¸²à¹à¸šà¸šà¸”à¹ˆà¸§à¸™à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  };

  /* ---------- Render ---------- */
  const filtered = useMemo(() => {
    let xs = products;
    if (query.trim()) {
      const q = query.trim().toLowerCase();
      xs = xs.filter((p) =>
        [p.product_name, p.description, p.origin].some((s) => String(s || '').toLowerCase().includes(q))
      );
    }
    if (catFilter) xs = xs.filter((p) => String(p.category_id || '') === String(catFilter));
    if (subcatFilter) xs = xs.filter((p) => String(p.subcategory_id || '') === String(subcatFilter));
    if (stockFilter === 'in') xs = xs.filter((p) => Number(p.stock_qty ?? p.stock ?? 0) > 0);
    if (stockFilter === 'low') xs = xs.filter((p) => Number(p.stock_qty ?? p.stock ?? 0) > 0 && Number(p.stock_qty ?? p.stock ?? 0) <= 5);
    if (stockFilter === 'out') xs = xs.filter((p) => Number(p.stock_qty ?? p.stock ?? 0) <= 0);
    return xs;
  }, [products, query, catFilter, subcatFilter, stockFilter]);

  const grouped = useMemo(() => {
    if (!groupByCategory) return [filtered];
    const map = new Map();
    filtered.forEach((p) => {
      const k = p.category_name || 'à¹„à¸¡à¹ˆà¸—à¸£à¸²à¸šà¸«à¸¡à¸§à¸”';
      if (!map.has(k)) map.set(k, []);
      map.get(k).push(p);
    });
    return Array.from(map.values());
  }, [filtered, groupByCategory]);
/* === ProductManagement.js (Part 2/4) END === */
/* === ProductManagement.js (Part 3/4) START === */
  if (loading || lkLoading)
    return <div style={{ padding: 16 }}>â³ à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥â€¦</div>;

  return (
    <div className="pm-page">
      {/* Toast Host */}
      <div className="pm-toasts">
        {toasts.map((t) => (
          <div key={t.id} className={`toast ${t.type}`}>
            <div>{t.msg}</div>
            <button className="toast-x" onClick={() => remove(t.id)}>Ã—</button>
          </div>
        ))}
      </div>

      {/* Header */}
      <div className="pm-header">
        <div className="pm-title">
          <span className="emoji">ðŸª´</span>
          <h1>à¸ˆà¸±à¸”à¸à¸²à¸£à¸ªà¸´à¸™à¸„à¹‰à¸²</h1>
          <span className="subtitle">à¹€à¸žà¸´à¹ˆà¸¡/à¹à¸à¹‰à¹„à¸‚à¸ªà¸´à¸™à¸„à¹‰à¸² à¹à¸¥à¸°à¸„à¸§à¸šà¸„à¸¸à¸¡à¸à¸²à¸£à¹à¸ªà¸”à¸‡à¸œà¸¥</span>
        </div>
        <div className="pm-links">
          <Link to="/admin/categories" className="btn btn-ghost">ðŸ“‚ à¸ˆà¸±à¸”à¸à¸²à¸£à¸›à¸£à¸°à¹€à¸ à¸—</Link>
          <Link to="/admin/subcategories" className="btn btn-ghost">ðŸ—‚ï¸ à¸ˆà¸±à¸”à¸à¸²à¸£à¸«à¸¡à¸§à¸”à¸¢à¹ˆà¸­à¸¢</Link>
          <Link to="/admin/units" className="btn btn-ghost">ðŸ“ à¸«à¸™à¹ˆà¸§à¸¢à¸ªà¸´à¸™à¸„à¹‰à¸²</Link>
          <Link to="/admin/brands" className="btn btn-ghost">ðŸ·ï¸ à¸«à¸™à¹ˆà¸§à¸¢à¸¢à¸µà¹ˆà¸«à¹‰à¸­</Link>
        </div>
      </div>

      {/* Search + Filters */}
      <div className="pm-searchbar">
        <div className="pm-filters">
          <div className="pm-toggle">
            <button className={`chip ${!showArchived ? 'active' : ''}`} onClick={() => setShowArchived(false)}>à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”</button>
            <button className={`chip ${showArchived ? 'active' : ''}`} onClick={() => setShowArchived(true)}>à¹à¸ªà¸”à¸‡à¸ªà¸´à¸™à¸„à¹‰à¸²à¸—à¸µà¹ˆà¹€à¸à¹‡à¸šà¹à¸¥à¹‰à¸§</button>
          </div>
          <select value={subcatFilter ?? ''} onChange={(e) => setSubcatFilter(e.target.value)}>
            <option value="">à¸—à¸¸à¸à¸«à¸¡à¸§à¸”à¸¢à¹ˆà¸­à¸¢</option>
            {(lookups?.subcategories || []).map((s) => (
              <option key={s.subcategory_id || s.id} value={s.subcategory_id || s.id}>{s.subcategory_name || s.name}</option>
            ))}
          </select>
          <label className="switch">
            <input type="checkbox" checked={groupByCategory} onChange={(e) => setGroupByCategory(e.target.checked)} />
            à¸ˆà¸±à¸”à¸à¸¥à¸¸à¹ˆà¸¡à¸•à¸²à¸¡à¸›à¸£à¸°à¹€à¸ à¸—
          </label>
          <select value={pageSize} onChange={(e) => setPageSize(parseInt(e.target.value) || 20)}>
            {[10, 20, 50, 100].map((n) => <option key={n} value={n}>{n}</option>)}
          </select>
          <span>à¸£à¸²à¸¢à¸à¸²à¸£/à¸«à¸™à¹‰à¸²</span>
        </div>
        <div className="pm-search">
          <input value={query} onChange={(e) => setQuery(e.target.value)} placeholder="à¸„à¹‰à¸™à¸«à¸²à¸Šà¸·à¹ˆà¸­/à¸£à¸²à¸¢à¸¥à¸°à¹€à¸­à¸µà¸¢à¸”/à¹à¸«à¸¥à¹ˆà¸‡à¸—à¸µà¹ˆà¸¡à¸²..." />
        </div>
      </div>

      {/* Add Product (keep original form) */}
      <div className="pm-panel">
        <h2 className="section-title">{isEditing ? 'à¹à¸à¹‰à¹„à¸‚à¸ªà¸´à¸™à¸„à¹‰à¸²' : 'à¹€à¸žà¸´à¹ˆà¸¡à¸ªà¸´à¸™à¸„à¹‰à¸²à¹ƒà¸«à¸¡à¹ˆ'}</h2>
        <form onSubmit={onSubmit} className="pm-form">
          <div className="frm">
            <label>à¸Šà¸·à¹ˆà¸­à¸ªà¸´à¸™à¸„à¹‰à¸²</label>
            <input name="product_name" value={form.product_name} onChange={onChange} placeholder="à¹€à¸Šà¹ˆà¸™ à¸¢à¸²à¸‡à¸­à¸´à¸™à¹‚à¸” / Monstera" ref={nameRef} />
          </div>
          <div className="frm">
            <label>à¸£à¸²à¸„à¸² (à¸ˆà¸³à¸™à¸§à¸™à¹€à¸•à¹‡à¸¡)</label>
            <input type="number" min="0" step="1" name="price" value={form.price} onChange={onChange} placeholder="à¹€à¸Šà¹ˆà¸™ 150" />
          </div>
          <div className="frm">
            <label>à¸‚à¸™à¸²à¸”à¸ªà¸´à¸™à¸„à¹‰à¸² (à¸•à¸±à¸§à¹€à¸¥à¸‚)</label>
            <input name="size_value" value={form.size_value} onChange={onChange} placeholder="à¹€à¸Šà¹ˆà¸™ 50" />
          </div>
          <div className="frm">
            <label>à¹à¸«à¸¥à¹ˆà¸‡à¸—à¸µà¹ˆà¸¡à¸²</label>
            <input name="origin" value={form.origin} onChange={onChange} placeholder="à¹€à¸Šà¹ˆà¸™ à¸ˆ.à¹€à¸Šà¸µà¸¢à¸‡à¹ƒà¸«à¸¡à¹ˆ" />
          </div>
          <div className="frm">
            <label>à¸›à¸£à¸°à¹€à¸ à¸—à¸ªà¸´à¸™à¸„à¹‰à¸²</label>
