// [FRONTEND] src/admin/ProductManagement.js
// จัดการสินค้า + CRUD ประเภท/หมวดย่อย + อัปโหลดรูปหลายไฟล์ + archive/unarchive + ลิงก์ไป Variants
// ปรับปรุงตาม requirement:
// 1) ราคาขาย = จำนวนเต็มไม่ติดลบ (กันที่ UI + ตรวจซ้ำก่อนส่ง)
// 2) หน่วยสินค้าเป็น select (+ ปุ่มเพิ่ม/แก้ไข/ลบ)
// 3) ขนาดสินค้าแยกเป็นตัวเลข + หน่วย และจัดให้อยู่บรรทัดเดียวกับหน่วยสินค้า
// 4) อัปโหลดรูปได้หลายรูป (รูปแรกเป็นหลัก; ที่เหลือพยายามบันทึกลง product_images)

import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductManagement.css';
import { api, path } from '../lib/api';

/* ------------------------- Helpers ------------------------- */
const asStr = (v) => (v === null || v === undefined) ? '' : String(v);
const pickStatusName = (x) => x?.status_name ?? x?.StatusName ?? x?.name ?? '';

/* ====== รายการเริ่มต้น (ทำฝั่ง FE ก่อน — ภายหลังค่อยย้ายไป DB ได้) ====== */
const DEFAULT_PRODUCT_UNITS = [
  { code: 'piece', label: 'ชิ้น' },
  { code: 'pot',   label: 'กระถาง' },
  { code: 'bag',   label: 'ถุง' },
  { code: 'pack',  label: 'แพ็ค' },
  { code: 'kg',    label: 'กิโลกรัม' },
  { code: 'g',     label: 'กรัม' },
  { code: 'ltr',   label: 'ลิตร' },
  { code: 'ml',    label: 'มิลลิลิตร' },
];

const DEFAULT_SIZE_UNITS = [
  { code: 'mm',   label: 'มม.' },
  { code: 'cm',   label: 'ซม.' },
  { code: 'm',    label: 'ม.' },
  { code: 'inch', label: 'นิ้ว' },
  { code: 'kg',   label: 'กก.' },
  { code: 'ltr',  label: 'ลิตร' },
];

/* ========================= Component ========================= */
export default function ProductManagement() {
  /* ---------- state หลัก ---------- */
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showArchived, setShowArchived] = useState(false);

  // รายการหน่วยที่แก้ไขได้ (บนหน้า)
  const [productUnits, setProductUnits] = useState(DEFAULT_PRODUCT_UNITS);
  const [sizeUnits, setSizeUnits] = useState(DEFAULT_SIZE_UNITS);

  /* ---------- state ฟอร์มสินค้า ---------- */
  const [form, setForm] = useState({
    product_name: '',
    description: '',
    selling_price: '',          // เก็บเป็นสตริงในฟอร์ม แล้วค่อยแปลง/ตรวจตอน submit
    category_id: '',
    subcategory_id: '',
    image_url: '',              // รูปหลัก (กรณีไม่อัปโหลดไฟล์)
    unit: '',                   // หน่วยสินค้า (select)
    size_value: '',             // ขนาด (ตัวเลข)
    size_unit: '',              // หน่วยของขนาด (select)
    origin: '',
    product_status_id: ''
  });

  // หลายไฟล์
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editProductId, setEditProductId] = useState(null);

  /* ---------- state ฟอร์มประเภท/หมวดย่อย ---------- */
  const [newCat, setNewCat] = useState({ category_id: '', category_name: '' });
  const [newSub, setNewSub] = useState({ subcategory_id: '', subcategory_name: '', category_id: '' });

  /* ------------------------- API loaders ------------------------- */
  const handleApiError = (err, fallback = 'เกิดข้อผิดพลาด') => {
    console.error(fallback, err?.response?.data || err?.message || err);
    alert(err?.response?.data?.error || err?.message || fallback);
  };

  const fetchProducts = async () => {
    try {
      const { data } = await api.get(path('/admin/products'), {
        params: { include_archived: showArchived ? 1 : 0 }
      });
      const items = Array.isArray(data?.items) ? data.items : data;
      setProducts(items || []);
    } catch (err) {
      handleApiError(err, '❌ โหลดสินค้าไม่สำเร็จ');
    }
  };

  const fetchCategories = async () => {
    try {
      const { data } = await api.get(path('/categories'));
      setCategories(data || []);
    } catch (err) {
      handleApiError(err, '❌ โหลดประเภทไม่สำเร็จ');
    }
  };

  const fetchSubcategories = async () => {
    try {
      const { data } = await api.get(path('/subcategories'));
      setSubcategories(data || []);
    } catch (err) {
      handleApiError(err, '❌ โหลดหมวดหมู่ไม่สำเร็จ');
    }
  };

  const fetchStatuses = async () => {
    try {
      let res = await api.get(path('/product-status'));
      if (!Array.isArray(res.data)) {
        try { res = await api.get(path('/product-statuses')); } catch {}
      }
      const normalized = (res.data || []).map((x) => ({
        id: asStr(x?.product_status_id ?? x?.ProductStatusID ?? x?.id),
        name: pickStatusName(x)
      }));
      setStatuses(normalized);
    } catch (err) {
      console.warn('⚠ ไม่มี endpoint product-status | product-statuses');
      setStatuses([]);
    }
  };

  /* ============================================================
     NOTE: CRUD ประเภท/หมวดย่อย
  ============================================================ */
  async function createCategory(payload) {
    try { await api.post(path('/categories'), payload); await fetchCategories(); }
    catch (err) { handleApiError(err, '❌ เพิ่มประเภทไม่สำเร็จ'); }
  }
  async function updateCategory(category_id, payload) {
    try { await api.put(path(`/categories/${category_id}`), payload); await fetchCategories(); }
    catch (err) { handleApiError(err, '❌ แก้ไขประเภทไม่สำเร็จ'); }
  }
  async function deleteCategory(category_id) {
    try { await api.delete(path(`/categories/${category_id}`)); await fetchCategories(); await fetchSubcategories(); }
    catch (err) { handleApiError(err, '❌ ลบประเภทไม่สำเร็จ'); }
  }
  async function createSubcategory(payload) {
    try { await api.post(path('/subcategories'), payload); await fetchSubcategories(); }
    catch (err) { handleApiError(err, '❌ เพิ่มหมวดหมู่ย่อยไม่สำเร็จ'); }
  }
  async function updateSubcategory(subcategory_id, payload) {
    try { await api.put(path(`/subcategories/${subcategory_id}`), payload); await fetchSubcategories(); }
    catch (err) { handleApiError(err, '❌ แก้ไขหมวดหมู่ย่อยไม่สำเร็จ'); }
  }
  async function deleteSubcategory(subcategory_id) {
    try { await api.delete(path(`/subcategories/${subcategory_id}`)); await fetchSubcategories(); }
    catch (err) { handleApiError(err, '❌ ลบหมวดหมู่ย่อยไม่สำเร็จ'); }
  }

  /* ============================================================
     NOTE: อัปโหลดรูป
  ============================================================ */
  async function uploadImage(file) {
    if (!file) return null;

    const sendWith = async (field) => {
      const fd = new FormData();
      fd.append(field, file);
      const res = await api.post(path('/upload'), fd, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      return res?.data?.imageUrl || res?.data?.url || res?.data?.path || res?.data?.location || null;
    };

    try { return await sendWith('file'); }
    catch (e1) {
      const msg = e1?.response?.data?.error || '';
      if (/no file uploaded/i.test(msg) || e1?.response?.status === 400) {
        try { return await sendWith('image'); }
        catch (e2) {
          const msg2 = e2?.response?.data?.error || '';
          if (/no file uploaded/i.test(msg2) || e2?.response?.status === 400) {
            return await sendWith('photo');
          }
          throw e2;
        }
      }
      throw e1;
    }
  }

  useEffect(() => {
    (async () => {
      setLoading(true);
      await Promise.all([fetchProducts(), fetchCategories(), fetchSubcategories(), fetchStatuses()]);
      setLoading(false);
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [showArchived]);

  /* ------------------------- handlers: form สินค้า ------------------------- */
  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({
      ...p,
      [name]: value,
      ...(name === 'category_id' ? { subcategory_id: '' } : {})
    }));
  };

  const onFiles = (e) => {
    const files = Array.from(e.target.files || []);
    setSelectedFiles(files);
  };

  // === ปุ่มจัดการรายการหน่วย (ทำแบบ prompt ง่าย ๆ) ===
  function addUnit(list, setList, title) {
    const name = (window.prompt(`เพิ่ม${title} (เช่น "ชิ้น", "ซม.")`) || '').trim();
    if (!name) return;
    if (list.some(x => x.label === name)) return alert(`มี "${name}" อยู่แล้ว`);
    const code = name.toLowerCase().replace(/\s+/g, '_');
    setList([...list, { code, label: name }]);
  }
  function editUnit(list, setList, title) {
    if (list.length === 0) return;
    const target = (window.prompt(`จะแก้ไข${title}ตัวไหน?\nพิมพ์ชื่อเดิมให้ตรง`) || '').trim();
    if (!target) return;
    const idx = list.findIndex(x => x.label === target);
    if (idx < 0) return alert(`ไม่พบ "${target}"`);
    const newName = (window.prompt(`แก้ไข "${target}" เป็น…`) || '').trim();
    if (!newName || newName === target) return;
    if (list.some((x,i)=> i!==idx && x.label === newName)) return alert(`มี "${newName}" อยู่แล้ว`);
    const next = [...list];
    next[idx] = { code: newName.toLowerCase().replace(/\s+/g,'_'), label: newName };
    setList(next);
    setForm(p => ({
      ...p,
      unit: (title==='หน่วยสินค้า' && p.unit===target) ? newName : p.unit,
      size_unit: (title==='หน่วยขนาด' && p.size_unit===target) ? newName : p.size_unit
    }));
  }
  function removeUnit(list, setList, title) {
    if (list.length === 0) return;
    const target = (window.prompt(`ลบ${title}ตัวไหน?\nพิมพ์ชื่อให้ตรง`) || '').trim();
    if (!target) return;
    const idx = list.findIndex(x => x.label === target);
    if (idx < 0) return alert(`ไม่พบ "${target}"`);
    if (!window.confirm(`ลบ "${target}" ?`)) return;
    const next = list.slice(0, idx).concat(list.slice(idx+1));
    setList(next);
    setForm(p => ({
      ...p,
      unit: (title==='หน่วยสินค้า' && p.unit===target) ? '' : p.unit,
      size_unit: (title==='หน่วยขนาด' && p.size_unit===target) ? '' : p.size_unit
    }));
  }

  const onEdit = (p) => {
    // แยก size เก่า (เช่น "50 ซม.") → value + unit
    let size_value = '', size_unit = '';
    if (p.size && typeof p.size === 'string') {
      const m = p.size.trim().match(/^(\d+(?:\.\d+)?)\s*(.+)$/);
      if (m) { size_value = m[1]; size_unit = m[2]; }
      else { size_value = p.size; }
    }
    setForm({
      product_name:        p.product_name ?? '',
      description:         p.description ?? '',
      selling_price:       asStr(p.selling_price ?? ''),
      category_id:         asStr(p.category_id ?? ''),
      subcategory_id:      asStr(p.subcategory_id ?? ''),
      image_url:           p.image_url ?? '',
      unit:                p.unit ?? '',
      size_value,
      size_unit,
      origin:              p.origin ?? '',
      product_status_id:   asStr(p.product_status_id ?? p.ProductStatusID ?? '')
    });
    setIsEditing(true);
    setEditProductId(p.product_id);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const clearForm = () => {
    setForm({
      product_name: '',
      description: '',
      selling_price: '',
      category_id: '',
      subcategory_id: '',
      image_url: '',
      unit: '',
      size_value: '',
      size_unit: '',
      origin: '',
      product_status_id: ''
    });
    setSelectedFiles([]);
    setIsEditing(false);
    setEditProductId(null);
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    // ✅ ตรวจราคาขาย: ต้องเป็นจำนวนเต็มไม่ติดลบ
    const trimmedPrice = String(form.selling_price ?? '').trim();
    const priceInt = Number.parseInt(trimmedPrice, 10);
    if (!Number.isInteger(priceInt) || priceInt < 0 || String(priceInt) !== trimmedPrice.replace(/^0+(?=\d)/, '')) {
      return alert('ราคาขายต้องเป็น "จำนวนเต็มไม่ติดลบ" เท่านั้น');
    }

    // รวมค่า size (ถ้ามี) ให้เป็นข้อความเดียว (สอดคล้อง DB ปัจจุบันที่เก็บเป็น text)
    let sizeText = '';
    if (form.size_value) {
      sizeText = form.size_unit ? `${form.size_value} ${form.size_unit}` : String(form.size_value);
    }

    // 1) อัปโหลดรูปก่อน (ถ้ามี)
    let uploadedUrls = [];
    if (selectedFiles.length > 0) {
      try {
        uploadedUrls = (await Promise.all(selectedFiles.map(f => uploadImage(f)))).filter(Boolean);
      } catch (err) {
        return handleApiError(err, '❌ อัปโหลดรูปไม่สำเร็จ');
      }
    }

    // ใช้รูปแรกเป็น image_url หลัก ถ้าไม่มี ให้ใช้ค่าที่ผู้ใช้กรอก URL
    const primaryImageUrl = uploadedUrls[0] || (form.image_url || '').trim();

    // 2) ร่าง body ให้ตรงคอลัมน์ที่ backend รับแน่ ๆ
    const body = {
      product_name: form.product_name,
      description: form.description,
      image_url: primaryImageUrl || '',
      selling_price: priceInt,                  // ส่งเป็นจำนวนเต็ม
      category_id: form.category_id || null,
      subcategory_id: form.subcategory_id || null,
      product_status_id: form.product_status_id || null,
      ...(form.unit ? { unit: form.unit } : {}),
      ...(sizeText ? { size: sizeText } : {}),
      ...(form.origin ? { origin: form.origin } : {})
    };

    try {
      let createdId = editProductId;

      if (isEditing && editProductId) {
        await api.put(path(`/admin/products/${editProductId}`), body);
      } else {
        const res = await api.post(path('/admin/products'), body);
        // พยายามดึง product_id จากคำตอบ (รองรับหลายรูป)
        createdId =
          res?.data?.product_id ??
          res?.data?.id ??
          res?.data?.ProductID ??
          createdId;
      }

      // 3) ถ้ามีรูปมากกว่า 1 รูป และมี product_id → ยิงเข้า product_images (ถ้า endpoint มี)
      if (createdId && uploadedUrls.length > 1) {
        const more = uploadedUrls.slice(1);
        for (const url of more) {
          try {
            try {
              await api.post(path('/product-images'), { product_id: createdId, image_url: url });
            } catch {
              await api.post(path('/admin/product-images'), { product_id: createdId, image_url: url });
            }
          } catch (imgErr) {
            console.warn('⚠ บันทึกรูปเสริมไม่สำเร็จ', imgErr?.response?.data || imgErr?.message || imgErr);
          }
        }
      }

      await fetchProducts();
      clearForm();
    } catch (err) {
      handleApiError(err, '❌ บันทึกสินค้าไม่สำเร็จ');
    }
  };

  /* ------------------------- archive / unarchive ------------------------- */
  const archiveProduct = async (id) => {
    if (!window.confirm('ย้ายสินค้านี้ไปถังเก็บใช่ไหม?')) return;
    try { await api.delete(path(`/admin/products/${id}`)); fetchProducts(); }
    catch (err) { handleApiError(err, '❌ เก็บสินค้าไม่สำเร็จ'); }
  };

  const unarchiveProduct = async (id) => {
    try { await api.patch(path(`/admin/products/${id}/unarchive`)); fetchProducts(); }
    catch (err) { handleApiError(err, '❌ กู้คืนสินค้าไม่สำเร็จ'); }
  };

  /* ------------------------- options ของ select ------------------------- */
  const statusOptions = useMemo(() => ([
    <option key="" value="">— เลือกสถานะ —</option>,
    ...statuses.map(s => <option key={s.id} value={asStr(s.id)}>{s.name || '(ไม่มีชื่อ)'}</option>)
  // eslint-disable-next-line react-hooks/exhaustive-deps
  ]), [JSON.stringify(statuses)]);

  const categoryOptions = useMemo(() => ([
    <option key="" value="">เลือกประเภท</option>,
    ...categories.map(c => (
      <option key={c.category_id} value={asStr(c.category_id)}>{c.category_name}</option>
    ))
  // eslint-disable-next-line react-hooks/exhaustive-deps
  ]), [JSON.stringify(categories)]);

  const filteredSubcategories = useMemo(() => {
    if (!form.category_id) return [];
    const cid = asStr(form.category_id);
    return (subcategories || []).filter(s => asStr(s.category_id) === cid);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [form.category_id, JSON.stringify(subcategories)]);

  const subcategoryOptions = useMemo(() => ([
    <option key="" value="">{form.category_id ? 'เลือกหมวดหมู่' : 'เลือกประเภทก่อน'}</option>,
    ...filteredSubcategories.map(s => (
      <option key={s.subcategory_id} value={asStr(s.subcategory_id)}>{s.subcategory_name}</option>
    ))
  ]), [filteredSubcategories, form.category_id]);

  /* ------------------------- UI ------------------------- */
  if (loading) return <div style={{ padding: 12 }}>กำลังโหลดข้อมูล…</div>;

  return (
    <div className="pm-page">
      <div className="pm-panel">
        <h2>{isEditing ? 'แก้ไขสินค้า' : 'เพิ่มสินค้าใหม่'}</h2>

        {/* ฟอร์ม: ใช้กริดจาก pm-form */}
        <form onSubmit={onSubmit} className="pm-form">
          <input name="product_name" placeholder="ชื่อสินค้า" value={form.product_name} onChange={onChange} />

          {/* ราคาขาย: จำนวนเต็ม ≥ 0 */}
          <input
            name="selling_price"
            type="number"
            inputMode="numeric"
            min="0"
            step="1"
            placeholder="ราคาขาย (จำนวนเต็มเท่านั้น)"
            value={form.selling_price}
            onChange={onChange}
          />

          {/* กลุ่ม: ขนาด(ตัวเลข) + หน่วยขนาด + หน่วยสินค้า (บรรทัดเดียว + ปุ่มจัดการ) */}
          <div className="pm-input-group pm-row-3 col-span-2">
            <input
              name="size_value"
              type="number"
              inputMode="decimal"
              min="0"
              step="1"
              placeholder="ขนาดสินค้า (ตัวเลข)"
              value={form.size_value}
              onChange={onChange}
              title="เช่น 50"
            />

            <div className="pm-input-with-actions">
              <select
                name="size_unit"
                value={form.size_unit}
                onChange={onChange}
                title="หน่วยของขนาด"
              >
                <option value="">— เลือกหน่วยขนาด —</option>
                {sizeUnits.map(u => <option key={u.code} value={u.label}>{u.label}</option>)}
              </select>
              <div className="pm-mini-actions">
                <button type="button" className="mini" title="เพิ่มหน่วยขนาด"
                  onClick={()=>addUnit(sizeUnits, setSizeUnits, 'หน่วยขนาด')}>＋</button>
                <button type="button" className="mini" title="แก้ไขหน่วยขนาด"
                  onClick={()=>editUnit(sizeUnits, setSizeUnits, 'หน่วยขนาด')}>✎</button>
                <button type="button" className="mini danger" title="ลบหน่วยขนาด"
                  onClick={()=>removeUnit(sizeUnits, setSizeUnits, 'หน่วยขนาด')}>🗑</button>
              </div>
            </div>

            <div className="pm-input-with-actions">
              <select
                name="unit"
                value={form.unit}
                onChange={onChange}
                title="หน่วยสินค้า"
              >
                <option value="">— เลือกหน่วยสินค้า —</option>
                {productUnits.map(u => <option key={u.code} value={u.label}>{u.label}</option>)}
              </select>
              <div className="pm-mini-actions">
                <button type="button" className="mini" title="เพิ่มหน่วยสินค้า"
                  onClick={()=>addUnit(productUnits, setProductUnits, 'หน่วยสินค้า')}>＋</button>
                <button type="button" className="mini" title="แก้ไขหน่วยสินค้า"
                  onClick={()=>editUnit(productUnits, setProductUnits, 'หน่วยสินค้า')}>✎</button>
                <button type="button" className="mini danger" title="ลบหน่วยสินค้า"
                  onClick={()=>removeUnit(productUnits, setProductUnits, 'หน่วยสินค้า')}>🗑</button>
              </div>
            </div>
          </div>

          <input name="origin" placeholder="แหล่งที่มา" value={form.origin} onChange={onChange} />

          <select name="product_status_id" value={form.product_status_id} onChange={onChange}>
            {statusOptions}
          </select>

          <select name="category_id" value={form.category_id} onChange={onChange}>
            {categoryOptions}
          </select>

          <select
            name="subcategory_id"
            value={form.subcategory_id}
            onChange={onChange}
            disabled={!form.category_id}
          >
            {subcategoryOptions}
          </select>

          <textarea className="col-span-2" name="description" placeholder="คำอธิบายสินค้า" value={form.description} onChange={onChange} />

          {/* URL รูปหลัก (เผื่อไม่อัปโหลด) */}
          <input className="col-span-2" name="image_url" placeholder="URL รูป (หากไม่อัปโหลด)" value={form.image_url} onChange={onChange} />

          {/* เลือกไฟล์หลายไฟล์ */}
          <input
            className="col-span-2"
            type="file"
            accept="image/*"
            multiple
            onChange={onFiles}
          />

          <div className="pm-actions col-span-2">
            <button type="submit" className="btn btn-primary">{isEditing ? 'อัปเดต' : 'บันทึก'}</button>
            {isEditing && <button type="button" className="btn btn-ghost" onClick={clearForm}>ยกเลิกแก้ไข</button>}
          </div>
        </form>
      </div>

      {/* สวิตช์โชว์ของเก็บแล้ว */}
      <div className="pm-toolbar">
        <label className="pm-check">
          <input
            type="checkbox"
            checked={showArchived}
            onChange={e => setShowArchived(e.target.checked)}
          />
          <span>แสดงสินค้าที่เก็บแล้ว</span>
        </label>
      </div>

      {/* ตารางสินค้า */}
      <h2>รายการสินค้า</h2>
      <div className="pm-table-wrap">
        <table className="pm-table">
          <thead>
            <tr>
              <th>สินค้า</th>
              <th>ราคา</th>
              <th>คงเหลือ</th>
              <th>หมวดหมู่</th>
              <th>สถานะ</th>
              <th>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {(products || []).map(p => (
              <tr key={p.product_id}>
                <td>
                  {p.product_name}
                  {p.is_archived && <span className="badge-archived">เก็บแล้ว</span>}
                </td>
                <td>{Number(p.selling_price ?? 0).toLocaleString()}</td>
                <td>{p.stock ?? p.stock_quantity ?? 0}</td>
                <td>{`${p.category_name || '-'} / ${p.subcategory_name || '-'}`}</td>
                <td>{p.product_status_name ?? pickStatusName(p)}</td>
                <td className="cell-actions">
                  <button className="btn btn-ghost" onClick={() => onEdit(p)}>แก้ไข</button>
                  {!p.is_archived ? (
                    <button className="btn btn-warn" onClick={() => archiveProduct(p.product_id)}>ย้ายไปถังเก็บ</button>
                  ) : (
                    <button className="btn btn-primary" onClick={() => unarchiveProduct(p.product_id)}>กู้คืน</button>
                  )}
                  <Link to={`/admin/products/${p.product_id}/variants`} className="btn btn-ghost">
                    ตัวเลือก/ขนาด
                  </Link>
                </td>
              </tr>
            ))}
            {(!products || products.length === 0) && (
              <tr><td colSpan={6} style={{ color:'#777' }}>— ไม่มีข้อมูล —</td></tr>
            )}
          </tbody>
        </table>
      </div>

      {/* จัดการประเภท */}
      <div className="mt-16">
        <h3>จัดการประเภท</h3>
        <div className="pm-actions mt-8" style={{ flexWrap:'wrap' }}>
          <input
            placeholder="category_id (เว้นได้ให้ระบบ gen)"
            value={newCat.category_id}
            onChange={(e)=>setNewCat({ ...newCat, category_id: e.target.value })}
            style={{ maxWidth:220 }}
          />
          <input
            placeholder="category_name"
            value={newCat.category_name}
            onChange={(e)=>setNewCat({ ...newCat, category_name: e.target.value })}
            style={{ minWidth:220 }}
          />
          <button
            type="button"
            className="btn btn-primary"
            onClick={async ()=>{
              if (!newCat.category_name.trim()) return alert('ใส่ category_name ก่อน');
              await createCategory({
                category_id:  newCat.category_id?.trim() || undefined,
                category_name: newCat.category_name.trim()
              });
              setNewCat({ category_id:'', category_name:'' });
            }}
          >
            เพิ่มประเภท
          </button>
        </div>

        <div className="pm-table-wrap mt-8">
          <table className="pm-table">
            <tbody>
              {(categories || []).map(c => (
                <tr key={c.category_id}>
                  <td style={{ width:160 }}>{c.category_id}</td>
                  <td>{c.category_name}</td>
                  <td className="cell-actions" style={{ width:220 }}>
                    <button
                      className="btn btn-ghost"
                      onClick={async ()=>{
                        const name = window.prompt('ชื่อประเภทใหม่', c.category_name);
                        if (name === null) return;
                        const trimmed = name.trim();
                        if (!trimmed || trimmed === c.category_name) return;
                        await updateCategory(c.category_id, { category_name: trimmed });
                      }}
                    >
                      แก้ไข
                    </button>
                    <button
                      className="btn btn-danger"
                      onClick={async ()=>{
                        if (window.confirm(`ลบประเภท "${c.category_name}" ?`)) {
                          await deleteCategory(c.category_id);
                        }
                      }}
                    >
                      ลบ
                    </button>
                  </td>
                </tr>
              ))}
              {(!categories || categories.length === 0) && (
                <tr><td colSpan={3} style={{ color:'#777' }}>— ไม่มีประเภท —</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* จัดการหมวดหมู่ย่อย */}
      <div className="mt-16">
        <h3>จัดการหมวดหมู่ย่อย</h3>
        <div className="pm-actions mt-8" style={{ flexWrap:'wrap' }}>
          <input
            placeholder="subcategory_id (เว้นได้ให้ระบบ gen)"
            value={newSub.subcategory_id}
            onChange={(e)=>setNewSub({ ...newSub, subcategory_id: e.target.value })}
            style={{ maxWidth:240 }}
          />
          <input
            placeholder="subcategory_name"
            value={newSub.subcategory_name}
            onChange={(e)=>setNewSub({ ...newSub, subcategory_name: e.target.value })}
            style={{ minWidth:240 }}
          />
          <select
            value={newSub.category_id}
            onChange={(e)=>setNewSub({ ...newSub, category_id: e.target.value })}
            style={{ minWidth:200 }}
          >
            <option value="">เลือกประเภทหลัก</option>
            {(categories || []).map(c => (
              <option key={c.category_id} value={asStr(c.category_id)}>{c.category_name}</option>
            ))}
          </select>
          <button
            type="button"
            className="btn btn-primary"
            onClick={async ()=>{
              if (!newSub.subcategory_name.trim() || !newSub.category_id) {
                return alert('ใส่ชื่อหมวดย่อย + เลือกประเภทหลัก');
              }
              await createSubcategory({
                subcategory_id:   newSub.subcategory_id?.trim() || undefined,
                subcategory_name: newSub.subcategory_name.trim(),
                category_id:      newSub.category_id.trim()
              });
              setNewSub({ subcategory_id:'', subcategory_name:'', category_id:'' });
            }}
          >
            เพิ่มหมวดหมู่ย่อย
          </button>
        </div>

        <div className="pm-table-wrap mt-8">
          <table className="pm-table">
            <tbody>
              {(subcategories || []).map(s => (
                <tr key={s.subcategory_id}>
                  <td style={{ width:160 }}>{s.subcategory_id}</td>
                  <td>{s.subcategory_name}</td>
                  <td style={{ width:160 }}>{s.category_id}</td>
                  <td className="cell-actions" style={{ width:240 }}>
                    <button
                      className="btn btn-ghost"
                      onClick={async ()=>{
                        const name = window.prompt('ชื่อหมวดหมู่ย่อยใหม่', s.subcategory_name);
                        if (!name) return;
                        const newCat = window.prompt('ย้ายไปประเภทไหน? (พิมพ์ category_id เช่น ro1 | เว้นว่าง=ไม่ย้าย)', s.category_id);
                        const payload = { subcategory_name: name.trim() };
                        if (newCat !== null && newCat.trim() !== '' && newCat !== s.category_id) {
                          payload.category_id = newCat.trim();
                        }
                        await updateSubcategory(s.subcategory_id, payload);
                      }}
                    >
                      แก้ไข
                    </button>
                    <button
                      className="btn btn-danger"
                      onClick={async ()=>{
                        if (window.confirm(`ลบหมวดหมู่ย่อย "${s.subcategory_name}" ?`)) {
                          await deleteSubcategory(s.subcategory_id);
                        }
                      }}
                    >
                      ลบ
                    </button>
                  </td>
                </tr>
              ))}
              {(!subcategories || subcategories.length === 0) && (
                <tr><td colSpan={4} style={{ color:'#777' }}>— ไม่มีหมวดย่อย —</td></tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
