// src/index.js
import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import axios from 'axios';

/* ===================== Axios Global Config =====================
   1) ตั้งฐาน URL ของ API:
      - ใช้ค่า .env ถ้ามี: REACT_APP_API_BASE_URL
      - ไม่งั้น fallback เป็น http://localhost:3001
   2) แนบ Bearer token อัตโนมัติ:
      - ถ้ามี token เก็บไว้ใน localStorage จากตอนล็อกอิน
   3) (ตัวเลือก) เปิดส่งคุกกี้/CORS ด้วย withCredentials หากใช้ session cookie
================================================================= */
axios.defaults.baseURL =
  process.env.REACT_APP_API_BASE_URL || 'http://localhost:3001';

  axios.interceptors.request.use(cfg => {
  const t = localStorage.getItem('token'); // ได้หลัง login
  if (t) cfg.headers.Authorization = `Bearer ${t}`;
  return cfg;
});

// ✅ แนบ Authorization: Bearer <token> อัตโนมัติถ้าเคยล็อกอินไว้แล้ว
const token = localStorage.getItem('token');
if (token) {
  axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
}

// ถ้าอนาคตใช้ session cookie / SameSite=None;Secure ให้เปิดบรรทัดนี้ได้
// axios.defaults.withCredentials = true;

/* ========================= React Mount =========================
   หมายเหตุ StrictMode:
   - ใน dev, React.StrictMode จะ mount/unmount ซ้ำ ทำให้ useEffect(fetch)
     บางตัวถูกเรียก 2 ครั้ง (พฤติกรรมเฉพาะ dev)
   - ถ้ารบกวนการทดสอบ สามารถเอา StrictMode ออกชั่วคราวได้ (เฉพาะช่วง dev)
================================================================= */
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// รายงาน performance (ไม่จำเป็นต้องแก้)
reportWebVitals();
