              <th align="left">‡∏Ñ‡∏≠‡∏°‡πÇ‡∏ö</th>
              <th align="right">‡∏£‡∏≤‡∏Ñ‡∏≤</th>
              <th align="right">‡∏™‡∏ï‡πá‡∏≠‡∏Å</th>
              <th>‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£</th>
            </tr>
          </thead>
          <tbody>
            {variants.map((v, i) => {
              const [price, setPrice] = useState(v.final_price ?? v.price ?? '');
              const [stock, setStock] = useState(v.stock ?? v.stock_qty ?? 0);
              const [sku, setSku] = useState(v.sku || '');
              const [active, setActive] = useState(v.is_active ?? true);

              function Row() {
                return (
                  <tr key={v.variant_id} style={{ borderTop:'1px solid #eee' }}>
                    <td>{i + 1}</td>
                    <td>
                      <input value={sku} onChange={e=>setSku(e.target.value)} style={{width:180}} />
                    </td>
                    <td>{comboText(v)}</td>
                    <td align="right">
                      <input type="number" value={price} onChange={e=>setPrice(e.target.value)} style={{width:120, textAlign:'right'}} />
                    </td>
                    <td align="right">
                      <input type="number" value={stock} onChange={e=>setStock(e.target.value)} style={{width:100, textAlign:'right'}} />
                    </td>
                    <td>
                      <label style={{marginRight:8}}>
                        <input type="checkbox" checked={!!active} onChange={e=>setActive(e.target.checked)} /> ‡πÅ‡∏™‡∏î‡∏á
                      </label>
                      <button onClick={()=>updateVariant(v.variant_id, { price, stock_qty: stock, sku, is_active: active })} disabled={busy}>‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å</button>
                      <button onClick={()=>deleteVariant(v.variant_id)} disabled={busy} style={{marginLeft:8}}>‡∏•‡∏ö</button>
                    </td>
                  </tr>
                );
              }
              return <Row key={v.variant_id} />;
            })}
          </tbody>
        </table>
      </section>
    </div>
  );
}
