// frontend/src/admin/Dashboard.js
import React, { useEffect, useMemo, useState } from 'react';
import { api } from '../lib/api';
import {
  Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement,
  LineElement, ArcElement, Tooltip, Legend, Title
} from 'chart.js';
import { Bar, Line, Pie } from 'react-chartjs-2';
import './Dashboard.css';

ChartJS.register(
  CategoryScale, LinearScale, BarElement, PointElement,
  LineElement, ArcElement, Tooltip, Legend, Title
);

function StatCard({ title, value, suffix }) {
  return (
    <div className="p-4 rounded-2xl shadow bg-white">
      <div className="text-sm text-gray-500">{title}</div>
      <div className="text-2xl font-semibold mt-1">
        {value === undefined || value === null ? '-' : value}
        {suffix ? <span className="text-base font-normal ml-1">{suffix}</span> : null}
      </div>
    </div>
  );
}

function DataTable({ columns, rows, emptyText = '‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•' }) {
  return (
    <div className="overflow-auto rounded-xl border border-gray-200">
      <table className="min-w-full text-sm">
        <thead className="bg-gray-50">
          <tr>
            {columns.map((c) => (
              <th key={c.key} className="text-left px-3 py-2 font-medium text-gray-700">{c.header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rows.length === 0 ? (
            <tr><td className="px-3 py-3 text-gray-500" colSpan={columns.length}>{emptyText}</td></tr>
          ) : rows.map((r, idx) => (
            <tr key={idx} className="border-t">
              {columns.map((c) => (
                <td key={c.key} className="px-3 py-2">{c.render ? c.render(r[c.key], r) : (r[c.key] ?? '')}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default function Dashboard() {
  const [summary, setSummary] = useState({});
  const [salesByMonth, setSalesByMonth] = useState([]);
  const [ordersByStatus, setOrdersByStatus] = useState([]);
  const [customersByProvince, setCustomersByProvince] = useState([]);
  const [topCategoriesPurchased, setTopCategoriesPurchased] = useState([]);
  const [productCountByCategory, setProductCountByCategory] = useState([]);
  const [recentOrders, setRecentOrders] = useState([]);
  const [recentProducts, setRecentProducts] = useState([]);
  const [recentAddresses, setRecentAddresses] = useState([]);

  // ‡∏ä‡πà‡∏ß‡∏á‡πÄ‡∏ß‡∏•‡∏≤ optional (‡πÉ‡∏™‡πà ‡∏´‡∏£‡∏∑‡∏≠‡∏õ‡∏•‡πà‡∏≠‡∏¢‡∏ß‡πà‡∏≤‡∏á‡πÑ‡∏î‡πâ)
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');

  const q = useMemo(() => {
    const params = new URLSearchParams();
    if (from) params.set('from', from);
    if (to) params.set('to', to);
    const s = params.toString();
    return s ? `?${s}` : '';
  }, [from, to]);

  useEffect(() => {
    // ‡πÇ‡∏´‡∏•‡∏î‡∏ó‡∏∏‡∏Å‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏Ñ‡∏π‡πà‡∏Å‡∏±‡∏ô
    api.get(`/dashboard/summary${q}`).then(r => setSummary(r.data));
    api.get(`/dashboard/sales-by-month${q}`).then(r => setSalesByMonth(r.data));
    api.get(`/dashboard/orders-by-status${q}`).then(r => setOrdersByStatus(r.data));
    api.get(`/dashboard/customers-by-province`).then(r => setCustomersByProvince(r.data));
    api.get(`/dashboard/top-categories-by-purchased${q}`).then(r => setTopCategoriesPurchased(r.data));
    api.get(`/dashboard/product-count-by-category`).then(r => setProductCountByCategory(r.data));
    api.get(`/dashboard/recent-orders`).then(r => setRecentOrders(r.data));
    api.get(`/dashboard/recent-products`).then(r => setRecentProducts(r.data));
    api.get(`/dashboard/recent-addresses`).then(r => setRecentAddresses(r.data));
  }, [q]);

  // ---------- Chart Data ----------
  const salesByMonthData = useMemo(() => ({
    labels: salesByMonth.map(x => x.month),
    datasets: [{
      label: '‡∏¢‡∏≠‡∏î‡∏Ç‡∏≤‡∏¢‡∏£‡∏ß‡∏° (‡∏ö‡∏≤‡∏ó)',
      data: salesByMonth.map(x => Number(x.total || 0)),
    }]
  }), [salesByMonth]);

  const ordersByStatusData = useMemo(() => ({
    labels: ordersByStatus.map(x => x.status_name),
    datasets: [{
      label: '‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠',
      data: ordersByStatus.map(x => Number(x.count || 0)),
    }]
  }), [ordersByStatus]);

  const customersByProvinceData = useMemo(() => {
    const top = customersByProvince.slice(0, 10); // ‡πÇ‡∏ä‡∏ß‡πå Top 10
    return {
      labels: top.map(x => x.province || '‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏'),
      datasets: [{ label: '‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà', data: top.map(x => Number(x.count || 0)) }]
    };
  }, [customersByProvince]);

  const topCategoriesPurchasedData = useMemo(() => ({
    labels: topCategoriesPurchased.map(x => x.category_name),
    datasets: [{ label: '‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏ä‡∏¥‡πâ‡∏ô‡∏ó‡∏µ‡πà‡∏Ç‡∏≤‡∏¢', data: topCategoriesPurchased.map(x => Number(x.qty || 0)) }]
  }), [topCategoriesPurchased]);

  const productCountByCategoryData = useMemo(() => ({
    labels: productCountByCategory.map(x => x.category_name),
    datasets: [{ label: '‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÉ‡∏ô‡∏£‡∏∞‡∏ö‡∏ö', data: productCountByCategory.map(x => Number(x.products || 0)) }]
  }), [productCountByCategory]);

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-end justify-between flex-wrap gap-3">
        <h1 className="text-2xl font-bold">üìä Dashboard ‡∏†‡∏≤‡∏û‡∏£‡∏ß‡∏°</h1>
        <div className="flex items-center gap-2">
          <div className="text-sm text-gray-600">‡∏ä‡πà‡∏ß‡∏á‡πÄ‡∏ß‡∏•‡∏≤ (‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏¢‡∏≠‡∏î‡∏Ç‡∏≤‡∏¢/‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠/Top Category)</div>
          <input type="date" value={from} onChange={e=>setFrom(e.target.value)} className="border rounded px-2 py-1"/>
          <span>‚Äî</span>
          <input type="date" value={to} onChange={e=>setTo(e.target.value)} className="border rounded px-2 py-1"/>
          <button onClick={()=>{setFrom('');setTo('');}} className="px-3 py-1 rounded bg-gray-100 hover:bg-gray-200">‡∏•‡πâ‡∏≤‡∏á</button>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
        <StatCard title="‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤" value={summary.products}/>
        <StatCard title="‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô" value={summary.users}/>
        <StatCard title="‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠" value={summary.orders}/>
        <StatCard title="‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡∏à‡∏±‡∏î‡∏™‡πà‡∏á" value={summary.addresses}/>
        <StatCard title="‡∏¢‡∏≠‡∏î‡∏Ç‡∏≤‡∏¢‡∏£‡∏ß‡∏°" value={summary.sales ? summary.sales.toLocaleString('th-TH') : '-'} suffix="‡∏ö‡∏≤‡∏ó"/>
      </div>

      {/* Charts Row 1 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="p-4 rounded-2xl shadow bg-white">
          <div className="font-semibold mb-2">‡∏¢‡∏≠‡∏î‡∏Ç‡∏≤‡∏¢‡∏£‡∏≤‡∏¢‡πÄ‡∏î‡∏∑‡∏≠‡∏ô</div>
          <Bar data={salesByMonthData} />
        </div>
        <div className="p-4 rounded-2xl shadow bg-white">
          <div className="font-semibold mb-2">‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠‡πÅ‡∏¢‡∏Å‡∏ï‡∏≤‡∏°‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞</div>
          <Pie data={ordersByStatusData} />
        </div>
      </div>

      {/* Charts Row 2 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="p-4 rounded-2xl shadow bg-white">
          <div className="font-semibold mb-2">‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤‡∏≠‡∏¢‡∏π‡πà‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î‡πÑ‡∏´‡∏ô‡πÄ‡∏¢‡∏≠‡∏∞‡∏ó‡∏µ‡πà‡∏™‡∏∏‡∏î (Top 10)</div>
          <Bar data={customersByProvinceData} />
        </div>
        <div className="p-4 rounded-2xl shadow bg-white">
          <div className="font-semibold mb-2">‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤‡∏ã‡∏∑‡πâ‡∏≠ ‚Äú‡∏´‡∏°‡∏ß‡∏î‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‚Äù ‡πÑ‡∏´‡∏ô‡πÄ‡∏¢‡∏≠‡∏∞‡∏™‡∏∏‡∏î</div>
          <Bar data={topCategoriesPurchasedData} />
        </div>
      </div>

      {/* Charts Row 3 (Admin view) */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="p-4 rounded-2xl shadow bg-white">
          <div className="font-semibold mb-2">‡∏ù‡∏±‡πà‡∏á‡πÅ‡∏≠‡∏î‡∏°‡∏¥‡∏ô: ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÅ‡∏¢‡∏Å‡∏ï‡∏≤‡∏°‡∏´‡∏°‡∏ß‡∏î</div>
          <Bar data={productCountByCategoryData} />
        </div>
        <div className="p-4 rounded-2xl shadow bg-white">
          <div className="font-semibold mb-2">‡πÅ‡∏ô‡∏ß‡πÇ‡∏ô‡πâ‡∏°‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠ (‡πÄ‡∏™‡πâ‡∏ô)</div>
          <Line data={{
            labels: salesByMonth.map(x=>x.month),
            datasets: [{ label: '‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡πÄ‡∏î‡∏∑‡∏≠‡∏ô‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠', data: salesByMonth.map(()=>1) }]
          }}/>
        </div>
      </div>

      {/* Recent Tables */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="p-4 rounded-2xl shadow bg-white">
          <div className="font-semibold mb-2">‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î</div>
          <DataTable
            columns={[
              { key: 'order_id', header: 'Order' },
              { key: 'email', header: '‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤' },
              { key: 'total_amount', header: '‡∏¢‡∏≠‡∏î (‡∏ø)', render: v => Number(v||0).toLocaleString('th-TH') },
              { key: 'status_name', header: '‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞' },
              { key: 'order_date', header: '‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà', render: v => v ? new Date(v).toLocaleString('th-TH') : '' },
            ]}
            rows={recentOrders}
          />
        </div>
        <div className="p-4 rounded-2xl shadow bg-white">
          <div className="font-semibold mb-2">‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î</div>
          <DataTable
            columns={[
              { key: 'product_id', header: 'ID' },
              { key: 'product_name', header: '‡∏ä‡∏∑‡πà‡∏≠‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤' },
              { key: 'category_name', header: '‡∏´‡∏°‡∏ß‡∏î' },
              { key: 'created_at', header: '‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏°‡∏∑‡πà‡∏≠', render: v => v ? new Date(v).toLocaleString('th-TH') : '' },
            ]}
            rows={recentProducts}
          />
        </div>
        <div className="p-4 rounded-2xl shadow bg-white">
          <div className="font-semibold mb-2">‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î</div>
          <DataTable
            columns={[
              { key: 'address_id', header: 'ID' },
              { key: 'email', header: '‡∏Ç‡∏≠‡∏á‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ' },
              { key: 'recipient_name', header: '‡∏ú‡∏π‡πâ‡∏£‡∏±‡∏ö' },
              { key: 'province', header: '‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î' },
              { key: 'created_at', header: '‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏°‡∏∑‡πà‡∏≠', render: v => v ? new Date(v).toLocaleString('th-TH') : '' },
            ]}
            rows={recentAddresses}
          />
        </div>
      </div>
    </div>
  );
}
