// frontend/src/account/Addresses.js
// Customer Addresses (FE only) — ใช้เรียก /api/addresses ทาง backend ผ่าน api helper

import React, { useEffect, useState } from "react";
import { api } from "../lib/api"; // ✅ ใช้ helper ฝั่ง frontend
import "./Addresses.css"; // ถ้าไม่มีไฟล์นี้ สามารถลบทิ้งบรรทัดนี้ได้

const emptyForm = {
  label: "",
  recipient_name: "",
  phone: "",
  line1: "",
  line2: "",
  subdistrict: "",
  district: "",
  province: "",
  postal_code: "",
  country: "TH",
  is_default: false,
};

export default function Addresses() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState(emptyForm);
  const [saving, setSaving] = useState(false);
  const [editingId, setEditingId] = useState(null);

  const load = async () => {
    try {
      setLoading(true);
      const { data } = await api.get("/api/addresses");
      setItems(Array.isArray(data) ? data : []);
    } catch (e) {
      console.error("load addresses error:", e);
      setItems([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); }, []);

  const onChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((f) => ({ ...f, [name]: type === "checkbox" ? checked : value }));
  };

  const onEdit = (addr) => {
    setEditingId(addr.address_id);
    setForm({
      label: addr.label || "",
      recipient_name: addr.recipient_name || "",
      phone: addr.phone || "",
      line1: addr.line1 || "",
      line2: addr.line2 || "",
      subdistrict: addr.subdistrict || "",
      district: addr.district || "",
      province: addr.province || "",
      postal_code: addr.postal_code || "",
      country: addr.country || "TH",
      is_default: !!addr.is_default,
    });
  };

  const resetForm = () => { setEditingId(null); setForm(emptyForm); };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      setSaving(true);
      if (!form.line1) {
        alert("กรุณากรอกที่อยู่ (บรรทัด 1)");
        return;
      }
      if (editingId) {
        await api.put(`/api/addresses/${editingId}`, form);
      } else {
        await api.post("/api/addresses", form);
      }
      resetForm();
      await load();
    } catch (err) {
      console.error("save address error:", err);
      alert("บันทึกที่อยู่ไม่สำเร็จ");
    } finally {
      setSaving(false);
    }
  };

  const setDefault = async (id) => {
    try {
      await api.patch(`/api/addresses/${id}/default`);
      await load();
    } catch (e) {
      console.error("set default error:", e);
      alert("ตั้งค่าเริ่มต้นไม่สำเร็จ");
    }
  };

  const onDelete = async (id) => {
    if (!window.confirm("ลบที่อยู่นี้?")) return;
    try {
      await api.delete(`/api/addresses/${id}`);
      await load();
    } catch (e) {
      console.error("delete address error:", e);
      alert("ลบไม่สำเร็จ");
    }
  };

  return (
    <div className="addresses-page" style={{ maxWidth: 720, margin: "0 auto", padding: 16 }}>
      <h2 style={{ marginBottom: 8 }}>ที่อยู่จัดส่งของฉัน</h2>

      {/* List */}
      {loading ? (
        <p>กำลังโหลด...</p>
      ) : items.length === 0 ? (
        <p>ยังไม่มีรายการที่อยู่</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0, margin: "8px 0 24px" }}>
          {items.map((a) => (
            <li key={a.address_id} style={{ border: "1px solid #e5e7eb", borderRadius: 12, padding: 12, marginBottom: 10 }}>
              <div style={{ display: "flex", justifyContent: "space-between", gap: 12, alignItems: "center" }}>
                <div>
                  <div style={{ fontWeight: 600 }}>
                    {a.recipient_name || "-"} {a.is_default ? <span style={{ fontSize: 12, marginLeft: 6, padding: "2px 6px", border: "1px solid #10b981", borderRadius: 8 }}>ค่าเริ่มต้น</span> : null}
                  </div>
                  <div>{a.phone || "-"}</div>
                  <div style={{ color: "#374151" }}>
                    {[a.line1, a.line2, a.subdistrict, a.district, a.province, a.postal_code].filter(Boolean).join(" ") || "-"}
                  </div>
                </div>
                <div style={{ display: "flex", gap: 8 }}>
                  {!a.is_default && (
                    <button onClick={() => setDefault(a.address_id)} style={{ padding: "6px 10px" }}>
                      ตั้งค่าเริ่มต้น
                    </button>
                  )}
                  <button onClick={() => onEdit(a)} style={{ padding: "6px 10px" }}>แก้ไข</button>
                  <button onClick={() => onDelete(a.address_id)} style={{ padding: "6px 10px", color: "#b91c1c" }}>ลบ</button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      )}

      {/* Form */}
      <form onSubmit={onSubmit} style={{ border: "1px solid #e5e7eb", borderRadius: 12, padding: 16 }}>
        <h3 style={{ marginTop: 0 }}>{editingId ? "แก้ไขที่อยู่" : "เพิ่มที่อยู่ใหม่"}</h3>

        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 }}>
          <div>
            <label>ป้ายกำกับ</label>
            <input name="label" value={form.label} onChange={onChange} placeholder="เช่น บ้าน / ที่ทำงาน" />
          </div>
          <div>
            <label>ชื่อผู้รับ</label>
            <input name="recipient_name" value={form.recipient_name} onChange={onChange} />
          </div>
          <div>
            <label>เบอร์โทร</label>
            <input name="phone" value={form.phone} onChange={onChange} />
          </div>
          <div style={{ gridColumn: "1 / -1" }}>
            <label>ที่อยู่บรรทัด 1</label>
            <input name="line1" value={form.line1} onChange={onChange} required />
          </div>
          <div style={{ gridColumn: "1 / -1" }}>
            <label>ที่อยู่บรรทัด 2 (ถ้ามี)</label>
            <input name="line2" value={form.line2} onChange={onChange} />
          </div>
          <div>
            <label>ตำบล/แขวง</label>
            <input name="subdistrict" value={form.subdistrict} onChange={onChange} />
          </div>
          <div>
            <label>อำเภอ/เขต</label>
            <input name="district" value={form.district} onChange={onChange} />
          </div>
          <div>
            <label>จังหวัด</label>
            <input name="province" value={form.province} onChange={onChange} />
          </div>
          <div>
            <label>รหัสไปรษณีย์</label>
            <input name="postal_code" value={form.postal_code} onChange={onChange} />
          </div>
          <div>
            <label>ประเทศ</label>
            <input name="country" value={form.country} onChange={onChange} />
          </div>
          <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
            <input id="is_default" type="checkbox" name="is_default" checked={form.is_default} onChange={onChange} />
            <label htmlFor="is_default">ตั้งเป็นค่าเริ่มต้น</label>
          </div>
        </div>

        <div style={{ marginTop: 12, display: "flex", gap: 8 }}>
          <button type="submit" disabled={saving} style={{ padding: "8px 14px" }}>
            {saving ? "กำลังบันทึก..." : (editingId ? "บันทึกการแก้ไข" : "เพิ่มที่อยู่")}
          </button>
          {editingId && (
            <button type="button" onClick={resetForm} style={{ padding: "8px 14px" }}>
              ยกเลิก
            </button>
          )}
        </div>
      </form>
    </div>
  );
}
