          if (!name) {
            name =
              u.fullname ||
              u.full_name ||
              [u.first_name, u.last_name].filter(Boolean).join(' ') ||
              u.username ||
              u.email ||
              username;
          }
        } catch {
          // ignore fallback error
        }
      }

