// src/components/AdminLayout.js
import React from "react";
import { Outlet, useNavigate } from "react-router-dom";
import axios from "axios";
import "./AdminLayout.css";

export default function AdminLayout() {
  const navigate = useNavigate();

  // ‡πÄ‡∏Å‡πá‡∏ö‡πÄ‡∏°‡∏ô‡∏π‡πÑ‡∏ß‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô/‡∏≠‡πâ‡∏≤‡∏á‡∏≠‡∏¥‡∏á‡∏†‡∏≤‡∏¢‡∏´‡∏•‡∏±‡∏á‡πÑ‡∏î‡πâ (‡πÑ‡∏°‡πà‡∏à‡∏≥‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡πâ‡∏≠‡∏á‡∏•‡∏ö)
  const menus = [
    { label: "Dashboard",     icon: "üìä", path: "/admin" },
    { label: "‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤",   icon: "üì¶", path: "/admin/products" },
    { label: "‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏Ñ‡∏á‡∏Ñ‡∏•‡∏±‡∏á",   icon: "üè¨", path: "/admin/product-management" },
    { label: "‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠", icon: "üßæ", path: "/admin/orders" },
    { label: "‡∏™‡∏£‡∏∏‡∏õ‡∏¢‡∏≠‡∏î‡∏Ç‡∏≤‡∏¢",    icon: "üí∞", path: "/admin/reports" },
  ];

  // ‚úÖ ‡∏ï‡∏±‡πâ‡∏á‡∏ò‡∏á‡∏Ñ‡∏ß‡∏ö‡∏Ñ‡∏∏‡∏°‡∏Å‡∏≤‡∏£‡πÅ‡∏™‡∏î‡∏á‡πÄ‡∏°‡∏ô‡∏π‡∏ö‡∏ô‡∏ö‡∏≤‡∏£‡πå (‡∏ï‡∏≠‡∏ô‡∏ô‡∏µ‡πâ‡πÑ‡∏°‡πà‡πÇ‡∏ä‡∏ß‡πå)
  const SHOW_MENU = false;

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    delete axios.defaults.headers.common["Authorization"];
    navigate("/login?role=admin", { replace: true });
  };

  return (
    <div className="admin-layout">
      {/* Top Navbar */}
      <header className="admin-navbar simple">
        <div className="logo">üåø ADMIN PANEL</div>

        {/* ‚ùå ‡πÑ‡∏°‡πà‡πÅ‡∏™‡∏î‡∏á‡πÄ‡∏°‡∏ô‡∏π‡∏ö‡∏ô‡∏ö‡∏≤‡∏£‡πå‡∏ï‡∏≠‡∏ô‡∏ô‡∏µ‡πâ */}
        {SHOW_MENU && (
          <nav className="admin-menu">
            {menus.map((m, i) => (
              <a key={i} href={m.path} className="admin-link">
                <span className="menu-icon">{m.icon}</span> {m.label}
              </a>
            ))}
          </nav>
        )}

        <div className="grow" />
        <button className="logout-btn" onClick={logout}>üö™ ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö</button>
      </header>

      {/* Main Content */}
      <main className="admin-content">
        <Outlet />
      </main>
    </div>
  );
}
