// frontend/src/admin/ProductManagement.js
// ‚úÖ ‡∏´‡∏ô‡πâ‡∏≤‡∏ó‡∏µ‡πà: ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ + ‡∏ü‡∏≠‡∏£‡πå‡∏°‡πÄ‡∏û‡∏¥‡πà‡∏°/‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç + ‡∏î‡∏£‡∏≠‡∏õ‡∏î‡∏≤‡∏ß‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó/‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà/‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞ + ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó/‡∏´‡∏°‡∏ß‡∏î‡∏¢‡πà‡∏≠‡∏¢ (CRUD)
// ‚úÖ Endpoint ‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ (‡∏ú‡πà‡∏≤‡∏ô axios.defaults.baseURL):
//    - Products (‡πÅ‡∏≠‡∏î‡∏°‡∏¥‡∏ô):   GET/POST/PUT/DELETE/UNARCHIVE  /api/admin/products
//    - Categories:          GET/POST/PUT/DELETE            /api/categories
//    - Subcategories:       GET/POST/PUT/DELETE            /api/subcategories
//    - Product Statuses:    GET                            /api/product-status
//    - Upload:              POST                           /api/upload
//
// üÜï ‡πÄ‡∏ß‡∏≠‡∏£‡πå‡∏ä‡∏±‡∏ô‡∏ô‡∏µ‡πâ‡πÄ‡∏û‡∏¥‡πà‡∏° ‚Äú‡∏õ‡∏∏‡πà‡∏°‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å/‡∏Ç‡∏ô‡∏≤‡∏î (Variants)‚Äù ‡∏•‡∏¥‡∏á‡∏Å‡πå‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ /admin/products/:productId/variants
// üÜï ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö "‡πÄ‡∏Å‡πá‡∏ö‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ (archive)" + "‡∏Å‡∏π‡πâ‡∏Ñ‡∏∑‡∏ô (unarchive)"

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // ‚úÖ ‡πÉ‡∏ä‡πâ Link ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å/‡∏Ç‡∏ô‡∏≤‡∏î

function ProductManagement() {
  /* ---------- STATE ‡∏´‡∏•‡∏±‡∏Å‡∏Ç‡∏≠‡∏á‡∏´‡∏ô‡πâ‡∏≤ ---------- */
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editProductId, setEditProductId] = useState(null);

  // üÜï ‡πÅ‡∏™‡∏î‡∏á/‡∏ã‡πà‡∏≠‡∏ô‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ó‡∏µ‡πà‡∏ñ‡∏π‡∏Å‡πÄ‡∏Å‡πá‡∏ö‡πÑ‡∏ß‡πâ
  const [showArchived, setShowArchived] = useState(false);

  /* ---------- STATE ‡∏ü‡∏≠‡∏£‡πå‡∏°‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó/‡∏´‡∏°‡∏ß‡∏î‡∏¢‡πà‡∏≠‡∏¢ ---------- */
  const [newCat, setNewCat] = useState({ category_id: '', category_name: '' });
  const [newSub, setNewSub] = useState({ subcategory_id: '', subcategory_name: '', category_id: '' });

  /* ---------- STATE ‡∏ü‡∏≠‡∏£‡πå‡∏°‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ ---------- */
  const [form, setForm] = useState({
    product_name: '',
    description: '',
    selling_price: '',
    cost_price: '',
    stock_quantity: '',
    category_id: '',
    subcategory_id: '',
    image_url: '',
    unit: '',
    size: '',
    origin: '',
    product_status_id: ''
  });

  /* ---------- ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡πÅ‡∏£‡∏Å + ‡πÄ‡∏°‡∏∑‡πà‡∏≠ toggle showArchived ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô ---------- */
  useEffect(() => {
    fetchProducts();
    fetchCategories();
    fetchSubcategories();
    fetchStatuses();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [showArchived]);

  /* ---------- Helper ‡∏£‡∏ß‡∏°‡∏®‡∏π‡∏ô‡∏¢‡πå error ---------- */
  function handleApiError(err, fallback = '‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î') {
    console.error(fallback, err.response?.data || err.message);
    alert(err?.response?.data?.error || fallback);
  }

  /* ---------- LOADERS (‡πÄ‡∏£‡∏µ‡∏¢‡∏Å API) ---------- */
  async function fetchProducts() {
    try {
      const res = await axios.get('/api/admin/products', {
        params: { include_archived: showArchived ? 1 : 0 }
      });
      const items = Array.isArray(res.data?.items) ? res.data.items : res.data;
      setProducts(items || []);
    } catch (err) {
      handleApiError(err, '‚ùå ‡πÇ‡∏´‡∏•‡∏î‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:');
    }
  }

  async function fetchCategories() {
    try {
      const res = await axios.get('/api/categories');
      setCategories(res.data || []);
    } catch (err) {
      handleApiError(err, '‚ùå ‡πÇ‡∏´‡∏•‡∏î‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:');
    }
  }

  async function fetchSubcategories() {
    try {
      const res = await axios.get('/api/subcategories');
      setSubcategories(res.data || []);
    } catch (err) {
      handleApiError(err, '‚ùå ‡πÇ‡∏´‡∏•‡∏î‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:');
    }
  }

  async function fetchStatuses() {
    try {
      const res = await axios.get('/api/product-status');
      const normalized = (res.data || []).map(x => ({
        id:   x.ProductStatusID ?? x.product_status_id ?? x.id,
        name: x.StatusName      ?? x.status_name      ?? x.name
      }));
      setStatuses(normalized);
    } catch (err) {
      handleApiError(err, '‚ùå ‡πÇ‡∏´‡∏•‡∏î‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:');
    }
  }

  /* ---------- FORM HANDLERS ‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ ---------- */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({
      ...prev,
      [name]: value,
      ...(name === 'category_id' ? { subcategory_id: '' } : {})
    }));
  };

  const handleImageChange = (e) => setSelectedFile(e.target.files[0]);

  const handleEdit = (product) => {
    setForm({
      product_name: product.product_name,
      description: product.description || '',
      selling_price: product.selling_price,
      cost_price: product.cost_price,
      stock_quantity: product.stock_quantity || '',
      category_id: product.category_id,
      subcategory_id: product.subcategory_id,
      image_url: product.image_url || '',
      unit: product.unit || '',
      size: product.size || '',
      origin: product.origin || '',
      product_status_id: product.product_status_id || product.ProductStatusID || ''
    });
    setIsEditing(true);
    setEditProductId(product.product_id);
  };

  const clearForm = () => {
    setForm({
      product_name: '',
      description: '',
      selling_price: '',
      cost_price: '',
      stock_quantity: '',
      category_id: '',
      subcategory_id: '',
      image_url: '',
      unit: '',
      size: '',
      origin: '',
      product_status_id: ''
    });
    setSelectedFile(null);
    setIsEditing(false);
    setEditProductId(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let imageUrl = form.image_url;
    if (selectedFile) {
      try {
        const fd = new FormData();
        fd.append('image', selectedFile);
        const uploadRes = await axios.post('/api/upload', fd);
        imageUrl = uploadRes.data.imageUrl;
      } catch (err) {
        return handleApiError(err, '‚ùå ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏†‡∏≤‡∏û‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:');
      }
    }

    const dataToSend = {
      ...form,
      image_url: imageUrl,
      selling_price:  +form.selling_price || 0,
      cost_price:     +form.cost_price || 0,
      stock_quantity: +form.stock_quantity || 0
    };

    try {
      if (isEditing) {
        await axios.put(`/api/admin/products/${editProductId}`, dataToSend);
      } else {
        await axios.post('/api/admin/products', dataToSend);
      }
      await fetchProducts();
      clearForm();
    } catch (err) {
      handleApiError(err, '‚ùå ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:');
    }
  };

  /* ---------- Archive / Unarchive ---------- */
  async function archiveProduct(id) {
    if (!window.confirm('‡∏¢‡πâ‡∏≤‡∏¢‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ô‡∏µ‡πâ‡πÑ‡∏õ‡∏ñ‡∏±‡∏á‡πÄ‡∏Å‡πá‡∏ö‡πÉ‡∏ä‡πà‡πÑ‡∏´‡∏°?')) return;
    try {
      await axios.delete(`/api/admin/products/${id}`);
      fetchProducts();
    } catch (err) {
      handleApiError(err, '‚ùå ‡πÄ‡∏Å‡πá‡∏ö‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:');
    }
  }

  async function unarchiveProduct(id) {
    try {
      await axios.patch(`/api/admin/products/${id}/unarchive`);
      fetchProducts();
    } catch (err) {
      handleApiError(err, '‚ùå ‡∏Å‡∏π‡πâ‡∏Ñ‡∏∑‡∏ô‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:');
    }
  }

  /* ---------- OPTIONS ‡∏Ç‡∏≠‡∏á‡∏î‡∏£‡∏≠‡∏õ‡∏î‡∏≤‡∏ß ---------- */
  const statusOptions = [
    React.createElement('option', { key: '', value: '' }, '‚Äî ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞ ‚Äî'),
    ...statuses.map(s =>
      React.createElement('option', { key: s.id, value: s.id }, s.name || '(‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ä‡∏∑‡πà‡∏≠)')
    )
  ];

  const categoryOptions = [
    React.createElement('option', { key: '', value: '' }, '‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó'),
    ...categories.map(c =>
      React.createElement('option', { key: c.category_id, value: c.category_id }, c.category_name)
    )
  ];

  const filteredSubcategories = form.category_id
    ? subcategories.filter(s => s.category_id === form.category_id)
    : [];

  const subcategoryOptions = [
    React.createElement('option', { key: '', value: '' }, form.category_id ? '‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà' : '‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏Å‡πà‡∏≠‡∏ô'),
    ...filteredSubcategories.map(s =>
      React.createElement('option', { key: s.subcategory_id, value: s.subcategory_id }, s.subcategory_name)
    )
  ];

  /* ---------- CRUD: Categories/Subcategories ---------- */
  async function createCategory(payload) {
    try {
      const res = await axios.post('/api/categories', payload);
      await Promise.all([fetchCategories(), fetchProducts()]);
      return res.data;
    } catch (err) {
      handleApiError(err, '‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    }
  }
  async function updateCategory(id, payload) {
    try {
      const res = await axios.put(`/api/categories/${id}`, payload);
      await Promise.all([fetchCategories(), fetchProducts()]);
      return res.data;
    } catch (err) {
      handleApiError(err, '‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    }
  }
  async function deleteCategory(id) {
    try {
      await axios.delete(`/api/categories/${id}`);
      await Promise.all([fetchCategories(), fetchProducts()]);
    } catch (err) {
      handleApiError(err, '‡∏•‡∏ö‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    }
  }

  async function createSubcategory(payload) {
    try {
      const res = await axios.post('/api/subcategories', payload);
      await Promise.all([fetchSubcategories(), fetchProducts()]);
      return res.data;
    } catch (err) {
      handleApiError(err, '‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà‡∏¢‡πà‡∏≠‡∏¢‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    }
  }
  async function updateSubcategory(id, payload) {
    try {
      const res = await axios.put(`/api/subcategories/${id}`, payload);
      await Promise.all([fetchSubcategories(), fetchProducts()]);
      return res.data;
    } catch (err) {
      handleApiError(err, '‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà‡∏¢‡πà‡∏≠‡∏¢‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    }
  }
  async function deleteSubcategory(id) {
    try {
      await axios.delete(`/api/subcategories/${id}`);
      await Promise.all([fetchSubcategories(), fetchProducts()]);
    } catch (err) {
      handleApiError(err, '‡∏•‡∏ö‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà‡∏¢‡πà‡∏≠‡∏¢‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    }
  }

  /* ---------- UI: Toggle ‡πÇ‡∏ä‡∏ß‡πå‡∏Ç‡∏≠‡∏á‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡πá‡∏ö‡πÅ‡∏•‡πâ‡∏ß ---------- */
  const toolbar = React.createElement('div', {
    style: { display: 'flex', gap: 12, alignItems: 'center', margin: '12px 0' }
  }, [
    React.createElement('label', { key: 'sw', style: { display: 'flex', gap: 8, alignItems: 'center' } }, [
      React.createElement('input', {
        key: 'ck', type: 'checkbox', checked: showArchived,
        onChange: e => setShowArchived(e.target.checked)
      }),
      '‡πÅ‡∏™‡∏î‡∏á‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡πá‡∏ö‡πÅ‡∏•‡πâ‡∏ß'
    ])
  ]);

