// src/account/Addresses.js
// หน้าที่: จัดการที่อยู่ผู้ใช้ (CRUD + ตั้งค่า default)
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Addresses() {
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [form, setForm] = useState({
    label: '',
    recipient_name: '',
    phone: '',
    line1: '',
    line2: '',
    subdistrict: '',
    district: '',
    province: '',
    postal_code: '',
    country: 'TH',
    is_default: false,
  });

  async function fetchList() {
    setLoading(true);
    try {
      const res = await axios.get('/api/addresses'); // default จะอยู่บนสุดจาก backend แล้ว
      setRows(res.data || []);
    } catch (e) {
      alert(e?.response?.data?.error || 'ดึงรายการไม่สำเร็จ');
    } finally {
      setLoading(false);
    }
  }
  useEffect(() => { fetchList(); }, []);

  function resetForm() {
    setEditingId(null);
    setForm({
      label: '',
      recipient_name: '',
      phone: '',
      line1: '',
      line2: '',
      subdistrict: '',
      district: '',
      province: '',
      postal_code: '',
      country: 'TH',
      is_default: false,
    });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      if (!form.line1) return alert('กรอกที่อยู่บรรทัด 1');
      if (editingId) {
        await axios.put(`/api/addresses/${editingId}`, form);
      } else {
        await axios.post('/api/addresses', form);
      }
      resetForm();
      fetchList();
    } catch (e) {
      alert(e?.response?.data?.error || 'บันทึกไม่สำเร็จ');
    }
  }

  function handleEdit(a) {
    setEditingId(a.address_id);
    setForm({
      label: a.label || '',
      recipient_name: a.recipient_name || '',
      phone: a.phone || '',
      line1: a.line1 || '',
      line2: a.line2 || '',
      subdistrict: a.subdistrict || '',
      district: a.district || '',
      province: a.province || '',
      postal_code: a.postal_code || '',
      country: a.country || 'TH',
      is_default: !!a.is_default,
    });
  }

  async function handleDelete(a) {
    if (!window.confirm('ลบที่อยู่นี้ใช่ไหม?')) return;
    try {
      await axios.delete(`/api/addresses/${a.address_id}`);
      fetchList();
    } catch (e) {
      alert(e?.response?.data?.error || 'ลบไม่สำเร็จ');
    }
  }

  async function setDefault(a) {
    try {
      await axios.patch(`/api/addresses/${a.address_id}/default`);
      fetchList();
    } catch (e) {
      alert(e?.response?.data?.error || 'ตั้งค่าเริ่มต้นไม่สำเร็จ');
    }
  }

  return (
    <div style={{ maxWidth: 880, margin: '0 auto', padding: 16 }}>
      <h2>ที่อยู่ของฉัน</h2>

      {/* ฟอร์มเพิ่ม/แก้ไข */}
      <form onSubmit={handleSubmit} style={{ display: 'grid', gap: 8, marginBottom: 24 }}>
        <div style={{ display: 'grid', gap: 8, gridTemplateColumns: '1fr 1fr' }}>
          <input placeholder="ชื่อเรียก (บ้าน/คอนโด)" value={form.label}
                 onChange={e => setForm(f => ({ ...f, label: e.target.value }))} />
          <input placeholder="ชื่อผู้รับ *" value={form.recipient_name}
                 onChange={e => setForm(f => ({ ...f, recipient_name: e.target.value }))} />
        </div>
        <div style={{ display: 'grid', gap: 8, gridTemplateColumns: '1fr 1fr' }}>
          <input placeholder="เบอร์โทร" value={form.phone}
                 onChange={e => setForm(f => ({ ...f, phone: e.target.value }))} />
          <input placeholder="ประเทศ" value={form.country}
                 onChange={e => setForm(f => ({ ...f, country: e.target.value }))} />
        </div>
        <input placeholder="ที่อยู่ (บรรทัด 1) *" value={form.line1} required
               onChange={e => setForm(f => ({ ...f, line1: e.target.value }))} />
        <input placeholder="ที่อยู่ (บรรทัด 2)" value={form.line2}
               onChange={e => setForm(f => ({ ...f, line2: e.target.value }))} />
        <div style={{ display: 'grid', gap: 8, gridTemplateColumns: '1fr 1fr' }}>
          <input placeholder="แขวง/ตำบล" value={form.subdistrict}
                 onChange={e => setForm(f => ({ ...f, subdistrict: e.target.value }))} />
          <input placeholder="เขต/อำเภอ" value={form.district}
                 onChange={e => setForm(f => ({ ...f, district: e.target.value }))} />
        </div>
        <div style={{ display: 'grid', gap: 8, gridTemplateColumns: '1fr 1fr' }}>
          <input placeholder="จังหวัด" value={form.province}
                 onChange={e => setForm(f => ({ ...f, province: e.target.value }))} />
          <input placeholder="รหัสไปรษณีย์" value={form.postal_code}
                 onChange={e => setForm(f => ({ ...f, postal_code: e.target.value }))} />
        </div>

        <label style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
          <input type="checkbox" checked={form.is_default}
                 onChange={e => setForm(f => ({ ...f, is_default: e.target.checked }))} />
          ตั้งเป็นค่าเริ่มต้น
        </label>

        <div style={{ display: 'flex', gap: 8 }}>
          <button type="submit">{editingId ? 'อัปเดตที่อยู่' : 'เพิ่มที่อยู่'}</button>
          {editingId && <button type="button" onClick={resetForm}>ยกเลิกแก้ไข</button>}
        </div>
      </form>

      {/* ตารางรายการ */}
      {loading ? <p>กำลังโหลด...</p> : rows.length === 0 ? <p>ยังไม่มีที่อยู่</p> : (
        <table width="100%" border="1" cellPadding="8" style={{ borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>ค่าเริ่มต้น</th>
              <th>ชื่อ/โทร</th>
              <th>ที่อยู่</th>
              <th>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {rows.map(a => (
              <tr key={a.address_id}>
                <td style={{ textAlign: 'center' }}>{a.is_default ? '✅' : ''}</td>
                <td>
                  <div><b>{a.recipient_name || '-'}</b></div>
                  <div>{a.phone || '-'}</div>
                  <div style={{ fontSize: 12, color: '#666' }}>{a.label || ''}</div>
                </td>
                <td>
                  <div>{a.line1}{a.line2 ? `, ${a.line2}` : ''}</div>
                  <div>{[a.subdistrict, a.district, a.province].filter(Boolean).join(', ')}</div>
                  <div>{a.postal_code} {a.country}</div>
                </td>
                <td>
                  <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                    {!a.is_default && <button onClick={() => setDefault(a)}>ตั้งเป็นค่าเริ่มต้น</button>}
                    <button onClick={() => handleEdit(a)}>แก้ไข</button>
                    <button onClick={() => handleDelete(a)}>ลบ</button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
