/* === ProductManagement.js (Part 1/4) START === */
import React, {
  useState,
  useEffect,
  useCallback,
  useRef,
  useMemo,
} from "react";
import { Link } from "react-router-dom";
import "./ProductManagement.css";
import { api, path } from "../lib/api";
import { useLookups } from "../lib/lookups";

/* =========================
   Helper Functions
========================= */
const asStr = (v) =>
  v === null || v === undefined ? "" : String(v);
const pickStatusName = (x) =>
  x?.status_name ?? x?.StatusName ?? x?.name ?? "";
const toInt = (v) => {
  const n = parseInt(String(v ?? "").trim(), 10);
  return Number.isFinite(n) ? n : null;
};
const toNum = (v) => {
  if (v === null || v === undefined) return null;
  const n = Number(String(v).replace(/[,\s฿]/g, ""));
  return Number.isFinite(n) ? n : null;
};

/* =========================
   Tiny Toast (in-file)
========================= */
function useToasts() {
  const [toasts, setToasts] = useState([]);

  const addToast = useCallback((text, type = "info") => {
    const id = Math.random().toString(36).slice(2);
    setToasts((prev) => [...prev, { id, text, type }]);
    setTimeout(() => {
      setToasts((prev) => prev.filter((t) => t.id !== id));
    }, 2800);
  }, []);

  const Toasts = useMemo(
    () => (
      <div className="toast-container">
        {toasts.map((t) => (
          <div key={t.id} className={`toast ${t.type}`}>
            {t.text}
          </div>
        ))}
      </div>
    ),
    [toasts]
  );

  return { addToast, Toasts };
}

/* =========================
   ProductManagement Component
========================= */
export default function ProductManagement() {
  const { addToast, Toasts } = useToasts();

  // ─── State ─────────────────────────────
  const [products, setProducts] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [variants, setVariants] = useState([]);
  const [options, setOptions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");
  const [addingProduct, setAddingProduct] = useState(false);

  const { units, categories } = useLookups();

  /* =========================
     Fetch Products / Status
  ========================= */
  const fetchStatuses = useCallback(async () => {
    try {
      const res = await api.get(path("statuses"));
      setStatuses(res || []);
    } catch (err) {
      console.error(err);
      addToast("โหลดสถานะไม่สำเร็จ", "error");
    }
  }, [addToast]);

  const fetchProducts = useCallback(async () => {
    try {
      setLoading(true);
      const res = await api.get(path("admin/products"));
      setProducts(res || []);
    } catch (err) {
      console.error(err);
      addToast("โหลดสินค้าล้มเหลว", "error");
    } finally {
      setLoading(false);
    }
  }, [addToast]);

  useEffect(() => {
    fetchStatuses();
    fetchProducts();
  }, [fetchStatuses, fetchProducts]);

  /* =========================
     Select Product to Manage
  ========================= */
  const handleSelectProduct = useCallback((p) => {
    setSelectedProduct((prev) =>
      prev && prev.product_id === p.product_id ? null : p
    );
    setVariants([]);
    setOptions([]);
  }, []);

  /* =========================
     Search Filter
  ========================= */
  const filteredProducts = useMemo(() => {
    const s = search.trim().toLowerCase();
    if (!s) return products;
    return products.filter((p) =>
      (p.product_name || "").toLowerCase().includes(s)
    );
  }, [search, products]);

  /* =========================
     Add New Product
  ========================= */
  const [newProduct, setNewProduct] = useState({
    product_name: "",
    category_id: "",
    price: "",
    unit_id: "",
    description: "",
  });

  const handleAddProduct = async () => {
    try {
      if (!newProduct.product_name) {
        addToast("กรุณากรอกชื่อสินค้า", "warn");
        return;
      }
      setAddingProduct(true);
      const res = await api.post(path("admin/products"), newProduct);
      addToast("เพิ่มสินค้าใหม่แล้ว", "ok");
      setNewProduct({
        product_name: "",
        category_id: "",
        price: "",
        unit_id: "",
        description: "",
      });
      fetchProducts();
    } catch (err) {
      console.error(err);
      addToast("เพิ่มสินค้าไม่สำเร็จ", "error");
    } finally {
      setAddingProduct(false);
    }
  };
/* === ProductManagement.js (Part 1/4) END === */
/* === ProductManagement.js (Part 2/4) START === */

/* =========================
   Table of Products
========================= */
  const renderTable = () => (
    <div className="product-table-wrapper">
      <table className="product-table">
        <thead>
          <tr>
            <th>ชื่อสินค้า</th>
            <th>หมวดหมู่</th>
            <th>ราคา</th>
            <th>คงเหลือ</th>
            <th>สถานะ</th>
            <th>จัดการ</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map((p) => (
            <tr
              key={p.product_id}
              className={
                selectedProduct &&
                selectedProduct.product_id === p.product_id
                  ? "selected"
                  : ""
              }
            >
              <td>{p.product_name}</td>
              <td>{p.category_name || "-"}</td>
              <td>{p.price ? `${p.price}฿` : "-"}</td>
              <td>{p.stock ?? 0}</td>
              <td>{pickStatusName(p.status)}</td>
              <td>
                <button
                  className="btn-manage"
                  onClick={() => handleSelectProduct(p)}
                >
                  {selectedProduct &&
                  selectedProduct.product_id === p.product_id
                    ? "ซ่อน"
                    : "จัดการ"}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

/* =========================
   Render Selected Product Panel
========================= */
  const renderPanel = () => {
    if (!selectedProduct) return null;
    const p = selectedProduct;

    return (
      <div className="product-panel">
        <h3>รายละเอียดสินค้า: {p.product_name}</h3>
        <div className="product-detail">
          <div>
            <strong>หมวดหมู่:</strong> {p.category_name}
          </div>
          <div>
            <strong>ราคา:</strong> {p.price}฿
          </div>
          <div>
            <strong>สถานะ:</strong> {pickStatusName(p.status)}
          </div>
          <div>
            <strong>รายละเอียด:</strong>{" "}
            {p.description || "ไม่มีรายละเอียด"}
          </div>
        </div>

        {/* ────────────── Variant Manager ────────────── */}
        {renderVariantManager()}
        {/* ────────────── Action Buttons ────────────── */}
        {renderBottomActions()}
      </div>
    );
  };

  /* =========================
     Main Render
  ========================= */
  return (
    <div className="product-page">
      <h2>จัดการสินค้า (Full Mode)</h2>

      {/* Search & Add */}
      <div className="toolbar">
        <input
          type="text"
          placeholder="🔍 ค้นหาสินค้า..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button
          className="btn-add"
          onClick={handleAddProduct}
          disabled={addingProduct}
        >
          ➕ เพิ่มสินค้าใหม่
        </button>
      </div>

      {/* Add Product Form */}
      <div className="new-product-form">
        <input
          type="text"
          placeholder="ชื่อสินค้า"
          value={newProduct.product_name}
          onChange={(e) =>
            setNewProduct({ ...newProduct, product_name: e.target.value })
          }
        />
        <select
          value={newProduct.category_id}
          onChange={(e) =>
            setNewProduct({ ...newProduct, category_id: e.target.value })
          }
        >
          <option value="">เลือกหมวดหมู่</option>
          {categories.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name}
            </option>
          ))}
        </select>
        <input
          type="number"
          placeholder="ราคา (฿)"
          value={newProduct.price}
          onChange={(e) =>
            setNewProduct({ ...newProduct, price: e.target.value })
          }
        />
        <select
          value={newProduct.unit_id}
          onChange={(e) =>
            setNewProduct({ ...newProduct, unit_id: e.target.value })
          }
        >
          <option value="">เลือกหน่วย</option>
          {units.map((u) => (
            <option key={u.id} value={u.id}>
              {u.name}
            </option>
          ))}
        </select>
        <textarea
          placeholder="รายละเอียดสินค้า"
          value={newProduct.description}
          onChange={(e) =>
            setNewProduct({ ...newProduct, description: e.target.value })
          }
        ></textarea>
      </div>

      {/* Table */}
      {loading ? <p>กำลังโหลดสินค้า...</p> : renderTable()}

      {/* Panel */}
      {renderPanel()}

      {/* Toasts */}
      {Toasts}
    </div>
  );
}
/* === ProductManagement.js (Part 2/4) END === */
