// src/admin/VariantsManager.js
// ======================================================================
// VariantsManager ‚Äî ‡πÇ‡∏´‡∏•‡∏î SKU ‡πÄ‡∏î‡∏¥‡∏°‡∏Ç‡∏∂‡πâ‡∏ô‡∏°‡∏≤‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡πÑ‡∏î‡πâ + ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ñ‡∏≠‡∏°‡πÇ‡∏ö‡πÉ‡∏´‡∏°‡πà‡πÑ‡∏î‡πâ (‡πÉ‡∏´‡πâ‡πÄ‡∏´‡∏°‡∏∑‡∏≠‡∏ô panel ‡πÉ‡∏ô ProductManagement)
// - ‡∏´‡∏±‡∏ß‡∏Å‡∏£‡∏∞‡∏î‡∏≤‡∏ô: ‡∏£‡∏´‡∏±‡∏™‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ + ‡∏ä‡∏∑‡πà‡∏≠‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ (‡πÇ‡∏ä‡∏ß‡πå‡∏™‡∏±‡πâ‡∏ô ‡πÜ)
// - ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£ variants ‡πÄ‡∏î‡∏¥‡∏° (‡∏£‡∏π‡∏õ/‡∏£‡∏≤‡∏Ñ‡∏≤/SKU/‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å 1-3) ‡∏Ç‡∏≠‡∏á‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ô‡∏±‡πâ‡∏ô "‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î"
// - ‡∏≠‡∏ô‡∏∏‡∏ç‡∏≤‡∏ï‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏ó‡∏µ‡∏•‡∏∞‡πÅ‡∏ñ‡∏ß ‡πÅ‡∏•‡πâ‡∏ß "‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î" (batch) ‚Äî ‡∏û‡∏¢‡∏≤‡∏¢‡∏≤‡∏°‡∏´‡∏•‡∏≤‡∏¢ endpoint ‡πÉ‡∏´‡πâ‡πÄ‡∏Ç‡πâ‡∏≤‡∏Å‡∏±‡∏ö‡∏´‡∏•‡∏±‡∏á‡∏ö‡πâ‡∏≤‡∏ô
// - ‡∏ñ‡πâ‡∏≤‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ variants ‚Üí ‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡πÉ‡∏™‡πà‡∏Ñ‡πà‡∏≤ chips ‡πÄ‡∏û‡∏∑‡πà‡∏≠ generate ‡∏ä‡∏∏‡∏î‡∏Ñ‡∏≠‡∏°‡πÇ‡∏ö‡πÉ‡∏´‡∏°‡πà
// - ‡∏õ‡∏∏‡πà‡∏°‡∏Å‡∏•‡∏±‡∏ö‡πÑ‡∏õ‡∏ó‡∏µ‡πà /admin/products
// - ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö backend ‡∏´‡∏•‡∏≤‡∏¢‡πÅ‡∏ö‡∏ö (PUT /variants, POST /variants/save, POST /variants/generate, /api/variants/upsert-batch)
// ======================================================================

import React, { useEffect, useMemo, useState, useCallback } from "react";
import { useParams, Link, useLocation } from "react-router-dom";
import { api, path, mediaSrc } from "../lib/api";
import "./VariantsManager.css";

/* ---------- Helpers ---------- */
const authHeader = () => {
  const t = localStorage.getItem("token");
  return t ? { Authorization: `Bearer ${t}` } : {};
};

const pickStr = (...xs) => xs.find((v) => typeof v === "string" && v.trim())?.trim() || "";
const rand2 = () => String(Math.floor(Math.random() * 99) + 1).padStart(2, "0");

// ‡∏î‡∏∂‡∏á productId ‡∏à‡∏≤‡∏Å params ‡∏´‡∏£‡∏∑‡∏≠ path (‡∏Å‡∏±‡∏ô‡πÄ‡∏Ñ‡∏™ /admin/products/null/variants)
const useSafeProductId = () => {
  const params = useParams();
  const location = useLocation();

  // 1) ‡∏à‡∏≤‡∏Å params (‡πÄ‡∏ä‡πà‡∏ô { id } ‡∏´‡∏£‡∏∑‡∏≠ { productId })
  const idFromParams = params?.id ?? params?.productId ?? null;

  // 2) ‡∏à‡∏≤‡∏Å path regex: ‡πÄ‡∏≠‡∏≤‡πÄ‡∏•‡∏Ç‡∏´‡∏•‡∏±‡∏á‡∏Ñ‡∏≥‡∏ß‡πà‡∏≤ products/
  //    ‡πÄ‡∏ä‡πà‡∏ô /admin/products/42/variants ‚Üí 42
  const m = String(location?.pathname || "").match(/products\/(\d+)/i);
  const idFromPath = m?.[1] ?? null;

  // 3) ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏ï‡∏±‡∏ß‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏Ç‡∏à‡∏£‡∏¥‡∏á
  const n = Number(idFromParams ?? idFromPath);
  return Number.isFinite(n) && n > 0 ? n : null;
};

/* ---------- Upload ---------- */
const extractUrl = (data) => {
  if (!data) return null;
  if (typeof data === "string") return data;
  const direct =
    data.url || data.path || data.imageUrl || data.Location || data.location ||
    data.file?.url || data.file?.path || data.data?.url || data.data?.path || data.data?.imageUrl;
  if (direct) return direct;
  try {
    const flat = JSON.stringify(data);
    const m = flat.match(/"([^"]+\.(?:png|jpe?g|webp|gif))"/i);
    return m ? m[1] : null;
  } catch { return null; }
};

const tryUpload = async (file) => {
  const fd = new FormData();
  fd.append("file", file);
  const res = await api.post(path("/upload"), fd, {
    headers: { ...authHeader(), "Content-Type": "multipart/form-data" },
  });
  const url = extractUrl(res?.data);
  if (!url) throw new Error("UPLOAD_OK_BUT_NO_URL");
  return url;
};

/* ---------- Normalize product ---------- */
const normalizeProduct = (raw) => {
  if (!raw) return null;
  const d = raw.data || raw.item || raw.product || raw.result || raw;
  return d || null;
};

/* ---------- Variant helpers ---------- */
function normalizeVariant(v) {
  if (!v) return null;
  // ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö‡∏´‡∏•‡∏≤‡∏¢ schema ‡∏Ç‡∏≠‡∏á‡∏ü‡∏¥‡∏•‡∏î‡πå
  const out = {
    variant_id: v.variant_id ?? v.id ?? v.product_variant_id ?? null,
    sku: pickStr(v.sku, v.SKU, v.code),
    price: v.price ?? v.unit_price ?? v.sale_price ?? "",
    image: v.image_url ?? v.image ?? v.imageUrl ?? null,
    // value ‡∏Ç‡∏≠‡∏á‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å
    opt1: pickStr(v.option1_value, v.opt1, v.color, v.value1),
    opt2: pickStr(v.option2_value, v.opt2, v.size,  v.value2),
    opt3: pickStr(v.option3_value, v.opt3, v.material, v.value3),
    // ‡∏ä‡∏∑‡πà‡∏≠‡∏Ç‡∏≠‡∏á‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å
    opt1_name: pickStr(v.opt1_name, v.option1_name, v.name1, v.option1, "‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å 1"),
    opt2_name: pickStr(v.opt2_name, v.option2_name, v.name2, v.option2, "‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å 2"),
    opt3_name: pickStr(v.opt3_name, v.option3_name, v.name3, v.option3, ""),
    // ‡∏Å‡∏£‡∏ì‡∏µ‡∏°‡∏µ images ‡πÄ‡∏õ‡πá‡∏ô array
    images: Array.isArray(v.images) ? v.images : [],
  };
  // number ‚Üí string ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö <input type=number>
  if (typeof out.price === "number") out.price = String(out.price);
  // ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ images ‡πÉ‡∏´‡πâ‡πÉ‡∏ä‡πâ‡∏£‡∏π‡∏õ‡πÅ‡∏£‡∏Å‡πÄ‡∏õ‡πá‡∏ô image
  if (!out.image && out.images.length) {
    const im = out.images[0];
    out.image = im?.url || im?.image_url || im?.path || null;
  }
  return out;
}

function toRowFromVariant(v) {
  const n = normalizeVariant(v);
  if (!n) return null;
  return {
    __existing: true,
    variant_id: n.variant_id,
    opt1: n.opt1 || "",
    opt2: n.opt2 || "",
    opt3: n.opt3 || "",
    price: n.price ?? "",
    sku: n.sku || "",
    image: n.image || null,
    opt1_name: n.opt1_name,
    opt2_name: n.opt2_name,
    opt3_name: n.opt3_name,
  };
}

/* ====================================================================== */
export default function VariantsManager() {
  const productId = useSafeProductId();

  // product for header + SKU
  const [product, setProduct] = useState(null);

  // option names
  const [opt1Name, setOpt1Name] = useState("‡∏™‡∏µ");
  const [opt2Name, setOpt2Name] = useState("‡∏Ç‡∏ô‡∏≤‡∏î");
  const [opt3Name, setOpt3Name] = useState("");

  // option values (‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡πÇ‡∏´‡∏°‡∏î generate ‡πÉ‡∏´‡∏°‡πà)
  const [opt1Values, setOpt1Values] = useState([]);
  const [opt2Values, setOpt2Values] = useState([]);
  const [opt3Values, setOpt3Values] = useState([]);

  // rows = ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏Ñ‡∏≠‡∏°‡πÇ‡∏ö‡∏ó‡∏µ‡πà‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç (‡∏≠‡∏≤‡∏à‡πÄ‡∏õ‡πá‡∏ô‡∏Ç‡∏≠‡∏á‡πÄ‡∏î‡∏¥‡∏° ‡∏´‡∏£‡∏∑‡∏≠‡∏ó‡∏µ‡πà generate)
  const [rows, setRows] = useState([]);

  const [saving, setSaving] = useState(false);
  const [msg, setMsg] = useState("");

  /* ---------- Load product (‡∏ä‡∏∑‡πà‡∏≠/‡πÇ‡∏Ñ‡πâ‡∏î) ---------- */
  const loadProduct = useCallback(async () => {
    if (!productId) return;
    let p = null;
    try {
      const r = await api.get(path(`/admin/products/${productId}`));
      p = normalizeProduct(r?.data);
    } catch {}
    if (!p) {
      try {
        const r = await api.get(path(`/products/${productId}`));
        p = normalizeProduct(r?.data);
      } catch {}
    }
    if (!p) {
      try {
        const r = await api.get(path(`/admin/products`), { params: { include_archived: 1 } });
        const arr = Array.isArray(r?.data?.items) ? r.data.items : (Array.isArray(r?.data) ? r.data : []);
        p = (arr || []).find((x) => Number(x.product_id) === Number(productId)) || null;
      } catch {}
    }
    setProduct(p || null);
  }, [productId]);

  /* ---------- Load variants ‡πÄ‡∏î‡∏¥‡∏° (‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î‡∏Ç‡∏≠‡∏á‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ô‡∏±‡πâ‡∏ô) ---------- */
  const loadVariants = useCallback(async () => {
    if (!productId) return;
    let arr = [];

    // üîé ‡∏•‡∏≠‡∏á‡∏´‡∏•‡∏≤‡∏¢ endpoint (‡πÉ‡∏´‡πâ‡πÄ‡∏´‡∏°‡∏∑‡∏≠‡∏ô‡πÉ‡∏ô ProductManagement)
    const tryEndpoints = [
      path(`/admin/products/${productId}/variants`),
      path(`/products/${productId}/variants`),
      path(`/api/variants/by-product/${productId}`),
      path(`/api/variants?product_id=${productId}`)
    ];

    for (const url of tryEndpoints) {
      try {
        const r = await api.get(url, { headers: authHeader() });
        const rows = r?.data?.items || r?.data?.rows || r?.data || [];
        if (Array.isArray(rows) && rows.length) { arr = rows; break; }
      } catch { /* ignore and try next */ }
    }

    if (arr?.length) {
      const mapped = arr.map(toRowFromVariant).filter(Boolean);
      setRows(mapped);

      // ‡πÄ‡∏î‡∏≤‡∏ä‡∏∑‡πà‡∏≠ option ‡∏à‡∏≤‡∏Å‡πÅ‡∏ñ‡∏ß‡πÅ‡∏£‡∏Å ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ
      const first = mapped[0];
      if (first) {
        if (first.opt1_name) setOpt1Name(first.opt1_name);
        if (first.opt2_name) setOpt2Name(first.opt2_name);
        if (first.opt3_name) setOpt3Name(first.opt3_name);
      }
    } else {
      // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ variant ‡πÄ‡∏î‡∏¥‡∏° ‚Üí ‡πÄ‡∏Ñ‡∏•‡∏µ‡∏¢‡∏£‡πå rows ‡πÅ‡∏•‡∏∞‡∏£‡∏≠‡πÉ‡∏´‡πâ‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ñ‡∏≠‡∏°‡πÇ‡∏ö‡πÉ‡∏´‡∏°‡πà
      setRows([]);
    }
  }, [productId]);

  useEffect(() => { loadProduct(); loadVariants(); }, [loadProduct, loadVariants]);

  const productName = useMemo(
    () => pickStr(product?.product_name, product?.name, product?.title),
    [product]
  );
  const productCode = useMemo(
    () => pickStr(product?.product_code, product?.code, product ? `P${product?.product_id || ""}` : (productId ? `P${productId}` : "")),
    [product, productId]
  );

  /* ---------- Rebuild combos when values change (‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡πÇ‡∏´‡∏°‡∏î‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÉ‡∏´‡∏°‡πà) ---------- */
  useEffect(() => {
    // ‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏ï‡∏≠‡∏ô "‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡πÅ‡∏ñ‡∏ß‡πÄ‡∏î‡∏¥‡∏°" ‡πÄ‡∏ó‡πà‡∏≤‡∏ô‡∏±‡πâ‡∏ô‡∏ñ‡∏∂‡∏á‡∏à‡∏∞ build ‡∏à‡∏≤‡∏Å chips
    if (rows.length) return;
    const v1 = opt1Values.length ? opt1Values : [null];
    const v2 = opt2Values.length ? opt2Values : [null];
    const v3 = opt3Values.length ? opt3Values : [null];
    const combos = [];
    for (const a of v1) for (const b of v2) for (const c of v3) {
      combos.push({ opt1: a, opt2: b, opt3: c, price: "", sku: "", image: null });
    }
    setRows(combos);
  }, [opt1Values, opt2Values, opt3Values]); // eslint-disable-line react-hooks/exhaustive-deps

  /* ---------- chip helpers ---------- */
  const addTag = (list, setList, txt) => {
    const v = String(txt || "").trim();
    if (!v) return;
    if (!list.some((x) => x === v)) setList([...list, v]);
  };
  const removeTag = (list, setList, idx) => setList(list.filter((_, i) => i !== idx));

  /* ---------- per-row upload ---------- */
  const onUploadRowImage = async (file, idx) => {
    if (!file) return;

    const tempUrl = URL.createObjectURL(file);
    setRows((prev) => {
      const a = [...prev];
      a[idx] = { ...a[idx], image: tempUrl, __temp: true };
      return a;
    });

    try {
      const serverUrl = await tryUpload(file);
      setRows((prev) => {
        const a = [...prev];
        a[idx] = { ...a[idx], image: serverUrl, __temp: false };
        return a;
      });
    } catch (e) {
      setRows((prev) => {
        const a = [...prev];
        a[idx] = { ...a[idx], image: null, __temp: false };
        return a;
      });
      alert(e?.message || "Upload failed");
    } finally {
      setTimeout(() => URL.revokeObjectURL(tempUrl), 2500);
    }
  };

  /* ---------- Auto SKU (‡πÑ‡∏°‡πà‡πÅ‡∏õ‡∏•‡∏á‡∏†‡∏≤‡∏©‡∏≤) ---------- */
  const autoSku = () => {
    const base = productCode || (productId ? `P${productId}` : "P");
    setRows((prev) =>
      prev.map((r) => {
        const parts = [base, r.opt1, r.opt2, r.opt3].filter(Boolean);
        return { ...r, sku: parts.join("-") + "-" + rand2() };
      })
    );
  };

  /* ---------- Save batch (‡∏û‡∏¢‡∏≤‡∏¢‡∏≤‡∏°‡∏´‡∏•‡∏≤‡∏¢ endpoint ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÉ‡∏´‡πâ‡πÄ‡∏Ç‡πâ‡∏≤‡∏Å‡∏±‡∏ö‡∏´‡∏•‡∏±‡∏á‡∏ö‡πâ‡∏≤‡∏ô) ---------- */
  const saveAll = async () => {
    setSaving(true);
    try {
      // ‡∏Å‡∏£‡∏≠‡∏á‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡πÅ‡∏ñ‡∏ß‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 1 ‡∏ä‡πà‡∏≠‡∏á ‡∏´‡∏£‡∏∑‡∏≠‡πÄ‡∏õ‡πá‡∏ô‡πÅ‡∏ñ‡∏ß‡πÄ‡∏î‡∏¥‡∏°
      const effective = rows.filter((r) => r.opt1 || r.opt2 || r.opt3 || r.__existing);

      const payloadRows = effective.map((r) => ({
        variant_id: r.variant_id || null,
        details: [
          r.opt1 ? { name: opt1Name, value: r.opt1 } : null,
          r.opt2 ? { name: opt2Name, value: r.opt2 } : null,
          r.opt3 && (opt3Name || r.opt3) ? { name: opt3Name || "‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å 3", value: r.opt3 } : null,
        ].filter(Boolean),
        sku: r.sku || null,
        price: r.price !== "" ? Number(r.price) : null,
        image_url: r.image || null,
      }));

      if (!payloadRows.length) {
        alert("‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡πÅ‡∏ñ‡∏ß‡∏ó‡∏µ‡πà‡∏°‡∏µ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å");
        setSaving(false);
        return;
      }

      // 0) /api/variants/upsert-batch
      let done = false;
      try {
        await api.post(
          path(`/api/variants/upsert-batch`),
          { product_id: productId, rows: payloadRows },
          { headers: authHeader() }
        );
        done = true;
      } catch {}

      if (!done) {
        // 1) PUT /variants (‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡πÅ‡∏ö‡∏ö batch)
        try {
          await api.put(
            path(`/admin/products/${productId}/variants`),
            { rows: payloadRows },
            { headers: authHeader() }
          );
          done = true;
        } catch {}
      }

      if (!done) {
        // 2) POST /variants/save (upsert)
        try {
          await api.post(
            path(`/admin/products/${productId}/variants/save`),
            { rows: payloadRows },
            { headers: authHeader() }
          );
          done = true;
        } catch {}
      }

      if (!done) {
