// FRONTEND: src/pages/SignUp.js
import React, { useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css';

/** ‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î->‡∏≠‡∏≥‡πÄ‡∏†‡∏≠->‡∏ï‡∏≥‡∏ö‡∏• (‡πÄ‡∏û‡∏¥‡πà‡∏°/‡πÅ‡∏Å‡πâ‡πÑ‡∏î‡πâ‡∏ï‡∏≤‡∏°‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£) */
const TH = {
  '‡πÄ‡∏ä‡∏µ‡∏¢‡∏á‡πÉ‡∏´‡∏°‡πà': {
    '‡πÄ‡∏°‡∏∑‡∏≠‡∏á‡πÄ‡∏ä‡∏µ‡∏¢‡∏á‡πÉ‡∏´‡∏°‡πà': { '‡∏®‡∏£‡∏µ‡∏†‡∏π‡∏°‡∏¥': '50200', '‡∏û‡∏£‡∏∞‡∏™‡∏¥‡∏á‡∏´‡πå': '50200', '‡∏™‡∏∏‡πÄ‡∏ó‡∏û': '50200' },
    '‡∏™‡∏±‡∏ô‡∏ó‡∏£‡∏≤‡∏¢': { '‡∏™‡∏±‡∏ô‡∏ó‡∏£‡∏≤‡∏¢‡∏ô‡πâ‡∏≠‡∏¢': '50210', '‡∏™‡∏±‡∏ô‡∏ó‡∏£‡∏≤‡∏¢‡∏´‡∏•‡∏ß‡∏á': '50210' },
  },
  '‡∏Å‡∏£‡∏∏‡∏á‡πÄ‡∏ó‡∏û‡∏°‡∏´‡∏≤‡∏ô‡∏Ñ‡∏£': {
    '‡πÄ‡∏Ç‡∏ï‡∏õ‡∏ó‡∏∏‡∏°‡∏ß‡∏±‡∏ô': { '‡∏£‡∏≠‡∏á‡πÄ‡∏°‡∏∑‡∏≠‡∏á': '10330', '‡∏õ‡∏ó‡∏∏‡∏°‡∏ß‡∏±‡∏ô': '10330' },
    '‡πÄ‡∏Ç‡∏ï‡∏à‡∏ï‡∏∏‡∏à‡∏±‡∏Å‡∏£': { '‡∏•‡∏≤‡∏î‡∏¢‡∏≤‡∏ß': '10900', '‡∏à‡∏±‡∏ô‡∏ó‡∏£‡πÄ‡∏Å‡∏©‡∏°': '10900' },
  },
};

export default function SignUp() {
  const navigate = useNavigate();

  // ‡∏ü‡∏≠‡∏£‡πå‡∏°‡∏´‡∏•‡∏±‡∏Å (NOTE: backend ‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£ email ‡πÅ‡∏ó‡πâ ‡πÜ, username, password)
  const [form, setForm] = useState({
    identifier: '',         // ‚Üê ‡πÉ‡∏´‡πâ‡∏Å‡∏£‡∏≠‡∏Å "‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏à‡∏£‡∏¥‡∏á" (‡∏ñ‡πâ‡∏≤‡πÉ‡∏™‡πà‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÄ‡∏â‡∏¢ ‡πÜ ‡∏´‡∏•‡∏±‡∏á‡∏ö‡πâ‡∏≤‡∏ô‡∏à‡∏∞‡πÑ‡∏°‡πà‡∏£‡∏±‡∏ö)
    password: '',
    fullname: '',
    phone: '',
    houseNo: '',
    province: '',
    amphoe: '',
    tambon: '',
    gender: '',
  });
  const [showPw, setShowPw] = useState(false);
  const [error, setError]   = useState('');
  const [loading, setLoading] = useState(false);

  const provinces = useMemo(() => Object.keys(TH), []);
  const amphoes   = useMemo(() => (form.province ? Object.keys(TH[form.province] || {}) : []), [form.province]);
  const tambons   = useMemo(
    () => (form.province && form.amphoe ? Object.keys(TH[form.province]?.[form.amphoe] || {}) : []),
    [form.province, form.amphoe]
  );

  // ---------------- helpers ----------------
  const isEmail = (s) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(String(s).trim());

  // ‡πÅ‡∏¢‡∏Å‡∏ä‡∏∑‡πà‡∏≠-‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•‡πÅ‡∏ö‡∏ö‡∏á‡πà‡∏≤‡∏¢ ‡πÜ
  function splitName(fullname) {
    const parts = String(fullname || '').trim().split(/\s+/);
    if (parts.length === 0) return { first_name: '', last_name: '' };
    if (parts.length === 1) return { first_name: parts[0], last_name: '' };
    return { first_name: parts[0], last_name: parts.slice(1).join(' ') };
  }

  // ‡∏™‡∏£‡πâ‡∏≤‡∏á username ‡∏à‡∏≤‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏• (‡∏´‡∏ô‡πâ‡∏≤ @) ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡∏à‡∏∞ fallback ‡πÄ‡∏õ‡πá‡∏ô cust+timestamp
  function makeUsername(email) {
    const raw = String(email).split('@')[0] || '';
    const cleaned = raw.toLowerCase().replace(/[^a-z0-9_.-]/g, '');
    return cleaned || `cust${Date.now().toString().slice(-6)}`;
  }

  const onChange = (e) => {
    const { name, value } = e.target;
    if (name === 'province') return setForm(p => ({ ...p, province: value, amphoe: '', tambon: '' }));
    if (name === 'amphoe')   return setForm(p => ({ ...p, amphoe: value, tambon: '' }));
    setForm(p => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    const required = [
      'identifier','password','fullname','phone',
      'houseNo','province','amphoe','tambon','gender'
    ];
    if (required.some(k => !String(form[k]).trim())) {
      setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô');
      return;
    }
    if (!isEmail(form.identifier)) {
      setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á (‡∏ï‡πâ‡∏≠‡∏á‡πÄ‡∏õ‡πá‡∏ô‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏à‡∏£‡∏¥‡∏á)');
      return;
    }
    if (form.password.length < 8) {
      setError('‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡∏ï‡πâ‡∏≠‡∏á‡∏°‡∏µ‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£');
      return;
    }

    // ‡∏õ‡∏£‡∏∞‡∏Å‡∏≠‡∏ö payload ‡πÉ‡∏´‡πâ‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö backend (/auth/register)
    const { first_name, last_name } = splitName(form.fullname);
    const address = `${form.houseNo} ‡∏ï.${form.tambon} ‡∏≠.${form.amphoe} ‡∏à.${form.province}`; // ‡∏£‡∏ß‡∏°‡πÄ‡∏õ‡πá‡∏ô‡∏™‡∏ï‡∏£‡∏¥‡∏á‡πÄ‡∏î‡∏µ‡∏¢‡∏ß

    const payload = {
      first_name,
      last_name,
      email: form.identifier.trim(),
      username: makeUsername(form.identifier),
      password: form.password,
      confirm_password: form.password,     // backend ‡πÄ‡∏ä‡πá‡∏Ñ‡πÉ‡∏´‡πâ‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô
      phone_number: form.phone.trim(),
      address,
      // NOTE: backend ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ‡πÄ‡∏Å‡πá‡∏ö gender ‚Äî ‡∏ñ‡πâ‡∏≤‡∏à‡∏∞‡πÄ‡∏Å‡πá‡∏ö‡∏à‡∏£‡∏¥‡∏á ‡∏ï‡πâ‡∏≠‡∏á‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏Ñ‡∏≠‡∏•‡∏±‡∏°‡∏ô‡πå/‡πÅ‡∏Å‡πâ routes/auth ‡∏Å‡πà‡∏≠‡∏ô
    };

    try {
      setLoading(true);

      // baseURL ‡∏ñ‡∏π‡∏Å‡∏ï‡∏±‡πâ‡∏á‡πÑ‡∏ß‡πâ‡πÉ‡∏ô src/index.js ‡πÅ‡∏•‡πâ‡∏ß ‚Üí ‡πÉ‡∏ä‡πâ path ‡∏™‡∏±‡πâ‡∏ô ‡πÜ ‡πÑ‡∏î‡πâ
      // ‡πÄ‡∏£‡∏≤ mount ‡∏ó‡∏±‡πâ‡∏á '/api/auth' ‡πÅ‡∏•‡∏∞ '/auth' ‡∏ó‡∏µ‡πà server ‡πÅ‡∏•‡πâ‡∏ß ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏≠‡∏±‡∏ô‡πÉ‡∏î‡∏≠‡∏±‡∏ô‡∏´‡∏ô‡∏∂‡πà‡∏á‡∏Å‡πá‡πÑ‡∏î‡πâ
      const res = await axios.post('/auth/register', payload);

      // ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à ‚Üí backend ‡∏Ñ‡∏∑‡∏ô token + user
      const token =
        res.data?.token ||
        res.data?.accessToken ||
        res.data?.jwt;

      const role  = res.data?.user?.role || 'customer'; // ‡∏î‡∏µ‡∏ü‡∏≠‡∏•‡∏ï‡πå‡∏•‡∏π‡∏Å‡∏Ñ‡πâ‡∏≤
      if (token) {
        localStorage.setItem('token', token);
        localStorage.setItem('role', role);
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      }

      // ‡∏û‡∏≤‡∏Å‡∏•‡∏±‡∏ö‡∏´‡∏ô‡πâ‡∏≤‡πÅ‡∏£‡∏Å (‡∏´‡∏£‡∏∑‡∏≠‡∏à‡∏∞‡πÄ‡∏î‡πâ‡∏á‡πÑ‡∏õ /login ‡∏Å‡πá‡πÑ‡∏î‡πâ)
      navigate('/', { replace: true });
    } catch (err) {
      // ‡πÅ‡∏™‡∏î‡∏á error ‡∏ó‡∏µ‡πà‡∏≠‡πà‡∏≤‡∏ô‡∏á‡πà‡∏≤‡∏¢
      const msg =
        err?.response?.status === 409 ? (err.response.data?.error || '‡∏≠‡∏µ‡πÄ‡∏°‡∏•/‡∏ä‡∏∑‡πà‡∏≠‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ ‡∏ñ‡∏π‡∏Å‡πÉ‡∏ä‡πâ‡πÅ‡∏•‡πâ‡∏ß')
      : err?.response?.data?.error || err.message || '‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à';
      setError(msg);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="signup-page">
      {/* ‡πÄ‡∏Æ‡∏î + ‡∏ä‡πà‡∏≠‡∏á‡πÄ‡∏ß‡πâ‡∏≤‡πÇ‡∏•‡πÇ‡∏Å‡πâ */}
      <div className="signup-hero">
        <div className="notch">
          <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="PRACH MAEJO" />
        </div>
      </div>

      {/* Brand + ‡∏´‡∏±‡∏ß‡∏Ç‡πâ‡∏≠ */}
      <div className="brand-head">
        <div className="brand-line">
          <span className="brand">PRACH MAEJO</span>
          <span className="online">ONLINE</span>
        </div>
        <h2>‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏±‡∏ç‡∏ä‡∏µ / ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å</h2>
      </div>

      {/* ‡∏ü‡∏≠‡∏£‡πå‡∏° 2 ‡∏Ñ‡∏≠‡∏•‡∏±‡∏°‡∏ô‡πå */}
      <form className="signup-form" onSubmit={onSubmit} noValidate>
        {/* ‡πÅ‡∏ñ‡∏ß 1 */}
        <label className="field col">
          <span className="label">‡∏≠‡∏µ‡πÄ‡∏°‡∏•</span>
          <input
            type="email"
            name="identifier"
            placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô‡πÑ‡∏î‡πâ‡∏à‡∏£‡∏¥‡∏á"
            value={form.identifier}
            onChange={onChange}
          />
        </label>
        <label className="field col">
          <span className="label">‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô</span>
          <div className="pw-wrap">
            <input
              type={showPw ? 'text' : 'password'}
              name="password"
              placeholder="‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£"
              value={form.password}
              onChange={onChange}
            />
            <button type="button" className="pw-toggle" onClick={() => setShowPw(s => !s)}>
              {showPw ? 'üôà' : 'üëÅÔ∏è'}
            </button>
          </div>
        </label>

        {/* ‡πÅ‡∏ñ‡∏ß 2 */}
        <label className="field col">
          <span className="label">‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•</span>
          <input
            type="text" name="fullname" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•"
            value={form.fullname} onChange={onChange}
          />
        </label>
        <label className="field col">
          <span className="label">‡πÄ‡∏ö‡∏≠‡∏£‡πå‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠</span>
          <input
            type="tel" name="phone" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£‡∏ó‡∏µ‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠‡πÑ‡∏î‡πâ"
            value={form.phone} onChange={onChange}
          />
        </label>

        {/* ‡πÅ‡∏ñ‡∏ß 3 : ‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà (‡∏ã‡πâ‡∏≤‡∏¢) + ‡πÄ‡∏û‡∏® (‡∏Ç‡∏ß‡∏≤) */}
        <div className="col span-2-left">
          <span className="label">‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà</span>
          <div className="addr-grid">
            <input
              type="text" name="houseNo"
              placeholder="‡∏ö‡πâ‡∏≤‡∏ô‡πÄ‡∏•‡∏Ç‡∏ó‡∏µ‡πà / ‡∏´‡∏°‡∏π‡πà / ‡∏≠‡∏≤‡∏Ñ‡∏≤‡∏£ (‡∏ñ‡πâ‡∏≤‡∏°‡∏µ)"
              value={form.houseNo} onChange={onChange}
