// FRONTEND: src/pages/SignUp.js
import React, { useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css';

/** ตัวอย่างข้อมูลจังหวัด->อำเภอ->ตำบล (เพิ่ม/แก้ได้ตามต้องการ) */
const TH = {
  'เชียงใหม่': {
    'เมืองเชียงใหม่': { 'ศรีภูมิ': '50200', 'พระสิงห์': '50200', 'สุเทพ': '50200' },
    'สันทราย': { 'สันทรายน้อย': '50210', 'สันทรายหลวง': '50210' },
  },
  'กรุงเทพมหานคร': {
    'เขตปทุมวัน': { 'รองเมือง': '10330', 'ปทุมวัน': '10330' },
    'เขตจตุจักร': { 'ลาดยาว': '10900', 'จันทรเกษม': '10900' },
  },
};

export default function SignUp() {
  const navigate = useNavigate();

  // ฟอร์มหลัก (NOTE: backend ต้องการ email แท้ ๆ, username, password)
  const [form, setForm] = useState({
    identifier: '',         // ← ให้กรอก "อีเมลจริง" (ถ้าใส่เบอร์เฉย ๆ หลังบ้านจะไม่รับ)
    password: '',
    fullname: '',
    phone: '',
    houseNo: '',
    province: '',
    amphoe: '',
    tambon: '',
    gender: '',
  });
  const [showPw, setShowPw] = useState(false);
  const [error, setError]   = useState('');
  const [loading, setLoading] = useState(false);

  const provinces = useMemo(() => Object.keys(TH), []);
  const amphoes   = useMemo(() => (form.province ? Object.keys(TH[form.province] || {}) : []), [form.province]);
  const tambons   = useMemo(
    () => (form.province && form.amphoe ? Object.keys(TH[form.province]?.[form.amphoe] || {}) : []),
    [form.province, form.amphoe]
  );

  // ---------------- helpers ----------------
  const isEmail = (s) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(String(s).trim());

  // แยกชื่อ-นามสกุลแบบง่าย ๆ
  function splitName(fullname) {
    const parts = String(fullname || '').trim().split(/\s+/);
    if (parts.length === 0) return { first_name: '', last_name: '' };
    if (parts.length === 1) return { first_name: parts[0], last_name: '' };
    return { first_name: parts[0], last_name: parts.slice(1).join(' ') };
  }

  // สร้าง username จากอีเมล (หน้า @) ถ้าไม่ได้จะ fallback เป็น cust+timestamp
  function makeUsername(email) {
    const raw = String(email).split('@')[0] || '';
    const cleaned = raw.toLowerCase().replace(/[^a-z0-9_.-]/g, '');
    return cleaned || `cust${Date.now().toString().slice(-6)}`;
  }

  const onChange = (e) => {
    const { name, value } = e.target;
    if (name === 'province') return setForm(p => ({ ...p, province: value, amphoe: '', tambon: '' }));
    if (name === 'amphoe')   return setForm(p => ({ ...p, amphoe: value, tambon: '' }));
    setForm(p => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    const required = [
      'identifier','password','fullname','phone',
      'houseNo','province','amphoe','tambon','gender'
    ];
    if (required.some(k => !String(form[k]).trim())) {
      setError('กรุณากรอกข้อมูลให้ครบถ้วน');
      return;
    }
    if (!isEmail(form.identifier)) {
      setError('กรุณากรอกอีเมลให้ถูกต้อง (ต้องเป็นอีเมลจริง)');
      return;
    }
    if (form.password.length < 8) {
      setError('รหัสผ่านต้องมีอย่างน้อย 8 ตัวอักษร');
      return;
    }

    // ประกอบ payload ให้ตรงกับ backend (/auth/register)
    const { first_name, last_name } = splitName(form.fullname);
    const address = `${form.houseNo} ต.${form.tambon} อ.${form.amphoe} จ.${form.province}`; // รวมเป็นสตริงเดียว

    const payload = {
      first_name,
      last_name,
      email: form.identifier.trim(),
      username: makeUsername(form.identifier),
      password: form.password,
      confirm_password: form.password,     // backend เช็คให้ตรงกัน
      phone_number: form.phone.trim(),
      address,
      // NOTE: backend ยังไม่ได้เก็บ gender — ถ้าจะเก็บจริง ต้องเพิ่มคอลัมน์/แก้ routes/auth ก่อน
    };

    try {
      setLoading(true);

      // baseURL ถูกตั้งไว้ใน src/index.js แล้ว → ใช้ path สั้น ๆ ได้
      // เรา mount ทั้ง '/api/auth' และ '/auth' ที่ server แล้ว เลือกอันใดอันหนึ่งก็ได้
      const res = await axios.post('/auth/register', payload);

      // สมัครสำเร็จ → backend คืน token + user
      const token =
        res.data?.token ||
        res.data?.accessToken ||
        res.data?.jwt;

      const role  = res.data?.user?.role || 'customer'; // ดีฟอลต์ลูกค้า
      if (token) {
        localStorage.setItem('token', token);
        localStorage.setItem('role', role);
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      }

      // พากลับหน้าแรก (หรือจะเด้งไป /login ก็ได้)
      navigate('/', { replace: true });
    } catch (err) {
      // แสดง error ที่อ่านง่าย
      const msg =
        err?.response?.status === 409 ? (err.response.data?.error || 'อีเมล/ชื่อผู้ใช้ ถูกใช้แล้ว')
      : err?.response?.data?.error || err.message || 'สมัครสมาชิกไม่สำเร็จ';
      setError(msg);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="signup-page">
      {/* เฮด + ช่องเว้าโลโก้ */}
      <div className="signup-hero">
        <div className="notch">
          <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="PRACH MAEJO" />
        </div>
      </div>

      {/* Brand + หัวข้อ */}
      <div className="brand-head">
        <div className="brand-line">
          <span className="brand">PRACH MAEJO</span>
          <span className="online">ONLINE</span>
        </div>
        <h2>สร้างบัญชี / สมัครสมาชิก</h2>
      </div>

      {/* ฟอร์ม 2 คอลัมน์ */}
      <form className="signup-form" onSubmit={onSubmit} noValidate>
        {/* แถว 1 */}
        <label className="field col">
          <span className="label">อีเมล</span>
          <input
            type="email"
            name="identifier"
            placeholder="กรอกอีเมลที่ใช้งานได้จริง"
            value={form.identifier}
            onChange={onChange}
          />
        </label>
        <label className="field col">
          <span className="label">รหัสผ่าน</span>
          <div className="pw-wrap">
            <input
              type={showPw ? 'text' : 'password'}
              name="password"
              placeholder="อย่างน้อย 8 ตัวอักษร"
              value={form.password}
              onChange={onChange}
            />
            <button type="button" className="pw-toggle" onClick={() => setShowPw(s => !s)}>
              {showPw ? '🙈' : '👁️'}
            </button>
          </div>
        </label>

        {/* แถว 2 */}
        <label className="field col">
          <span className="label">ชื่อ - นามสกุล</span>
          <input
            type="text" name="fullname" placeholder="กรอกชื่อ - นามสกุล"
            value={form.fullname} onChange={onChange}
          />
        </label>
        <label className="field col">
          <span className="label">เบอร์ติดต่อ</span>
          <input
            type="tel" name="phone" placeholder="กรอกเบอร์โทรที่สามารถติดต่อได้"
            value={form.phone} onChange={onChange}
          />
        </label>

        {/* แถว 3 : ที่อยู่ (ซ้าย) + เพศ (ขวา) */}
        <div className="col span-2-left">
          <span className="label">ที่อยู่</span>
          <div className="addr-grid">
            <input
              type="text" name="houseNo"
              placeholder="บ้านเลขที่ / หมู่ / อาคาร (ถ้ามี)"
              value={form.houseNo} onChange={onChange}
            />
            <select name="province" value={form.province} onChange={onChange}>
              <option value="">เลือกจังหวัด</option>
              {provinces.map(pv => <option key={pv} value={pv}>{pv}</option>)}
            </select>
            <select name="amphoe" value={form.amphoe} onChange={onChange} disabled={!form.province}>
              <option value="">{form.province ? 'เลือกอำเภอ' : 'เลือกจังหวัดก่อน'}</option>
              {amphoes.map(am => <option key={am} value={am}>{am}</option>)}
            </select>
            <select name="tambon" value={form.tambon} onChange={onChange} disabled={!form.amphoe}>
              <option value="">{form.amphoe ? 'เลือกตำบล' : 'เลือกอำเภอก่อน'}</option>
              {tambons.map(tb => <option key={tb} value={tb}>{tb}</option>)}
            </select>
          </div>
        </div>

        {/* ขวา: เพศ (ยังไม่ได้ส่งเข้าหลังบ้าน) */}
        <div className="right-group col">
          <div className="field">
            <span className="label">เพศ</span>
            <div className="radios">
              <label><input type="radio" name="gender" value="ชาย"  checked={form.gender==='ชาย'}  onChange={onChange}/> ชาย</label>
              <label><input type="radio" name="gender" value="หญิง" checked={form.gender==='หญิง'} onChange={onChange}/> หญิง</label>
              <label><input type="radio" name="gender" value="อื่นๆ" checked={form.gender==='อื่นๆ'} onChange={onChange}/> อื่นๆ</label>
            </div>
          </div>
        </div>

        {error && <div className="form-error" role="alert">{error}</div>}

        <div className="actions span-2">
          <button className="btn-primary" type="submit" disabled={loading}>
            {loading ? 'กำลังสมัครสมาชิก…' : 'สมัครสมาชิก'}
          </button>
        </div>
      </form>
    </div>
  );
}
