// src/admin/InventoryPage.jsx
import React, { useEffect, useMemo, useState } from "react";
import {
  listInventory,
  receiveInventory,
  issueInventory,
  listMoves,
} from "../lib/api";
import VariantPicker from "../components/VariantPicker";
import { toast } from "react-hot-toast";

/* utils */
const fmt = (n) => new Intl.NumberFormat("th-TH").format(Number(n || 0));
const currency = (n) =>
  new Intl.NumberFormat("th-TH", { style: "currency", currency: "THB" }).format(
    Number(n || 0)
  );
const cls = (...xs) => xs.filter(Boolean).join(" ");
const nowLocalDatetime = () => {
  const d = new Date();
  d.setMinutes(d.getMinutes() - d.getTimezoneOffset());
  return d.toISOString().slice(0, 16);
};
const Help = ({ children }) => (
  <div className="text-xs text-gray-600 mt-1">{children}</div>
);

/* ---------- à¸ªà¹ˆà¸§à¸™à¸žà¸±à¸šà¸”à¸¹ JSON ---------- */
function JsonDetails({ data, title = "à¸£à¸²à¸¢à¸¥à¸°à¹€à¸­à¸µà¸¢à¸” (JSON)" }) {
  const [open, setOpen] = useState(false);
  if (!data) return null;
  return (
    <div className="mt-3 border rounded">
      <button
        type="button"
        onClick={() => setOpen((v) => !v)}
        className="w-full text-left px-3 py-2 bg-gray-50 border-b"
      >
        {open ? "à¸‹à¹ˆà¸­à¸™" : "à¸”à¸¹"} {title}
      </button>
      {open && (
        <pre className="p-3 text-xs overflow-auto">
          {JSON.stringify(data, null, 2)}
        </pre>
      )}
    </div>
  );
}

export default function InventoryPage() {
  const [tab, setTab] = useState("overview"); // overview | receive | issue | moves
  return (
    <div className="p-4 max-w-6xl mx-auto">
      <h1 className="text-2xl font-bold">Inventory Management</h1>

      <div className="mt-4 flex gap-2">
        {[
          ["overview", "à¸ à¸²à¸žà¸£à¸§à¸¡à¸„à¸‡à¸„à¸¥à¸±à¸‡"],
          ["receive", "à¸£à¸±à¸šà¹€à¸‚à¹‰à¸² (IN)"],
          ["issue", "à¸•à¸±à¸”à¸­à¸­à¸ (OUT) FIFO"],
          ["moves", "à¸›à¸£à¸°à¸§à¸±à¸•à¸´ (Moves)"],
        ].map(([k, label]) => (
          <button
            key={k}
            className={cls(
              "px-3 py-2 rounded border",
              tab === k ? "bg-black text-white border-black" : "bg-white"
            )}
            onClick={() => setTab(k)}
          >
            {label}
          </button>
        ))}
      </div>

      <div className="mt-6">
        {tab === "overview" && <InventoryOverview />}
        {tab === "receive" && <ReceiveForm />}
        {tab === "issue" && <IssueForm />}
        {tab === "moves" && <MovesPanel />}
      </div>
    </div>
  );
}

/* ---------- 1) à¸ à¸²à¸žà¸£à¸§à¸¡ ---------- */
function InventoryOverview() {
  const [q, setQ] = useState("");
  const [scope, setScope] = useState("variant");
  const [order, setOrder] = useState("low_stock");
  const [page, setPage] = useState(1);
  const [pageSize] = useState(20);
  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);

  const offset = (page - 1) * pageSize;
  const totalPages = Math.max(1, Math.ceil(total / pageSize));

  async function load() {
    setLoading(true);
    try {
      const data = await listInventory({
        search: q,
        scope,
        order,
        limit: pageSize,
        offset,
      });
      setItems(data.items || []);
      setTotal(Number(data.total || 0));
    } catch (e) {
      console.error(e);
      toast.error(e?.response?.data?.error || "à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ");
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { load(); /* eslint-disable-next-line */ }, [scope, order, page]);

  return (
    <div>
      <div className="flex flex-wrap items-end gap-2">
        <div className="min-w-[260px]">
          <label className="block text-sm">à¸„à¹‰à¸™à¸«à¸²</label>
          <input
            value={q}
            onChange={(e) => setQ(e.target.value)}
            placeholder="à¸Šà¸·à¹ˆà¸­à¸ªà¸´à¸™à¸„à¹‰à¸² / à¸„à¸³à¸­à¸˜à¸´à¸šà¸²à¸¢ / SKU"
            className="border rounded px-3 py-2 w-full"
          />
          <Help>à¸žà¸´à¸¡à¸žà¹Œà¸Šà¸·à¹ˆà¸­à¸ªà¸´à¸™à¸„à¹‰à¸² à¸„à¸³à¸­à¸˜à¸´à¸šà¸²à¸¢ à¸«à¸£à¸·à¸­ SKU à¹€à¸žà¸·à¹ˆà¸­à¸„à¹‰à¸™à¸«à¸²</Help>
        </div>
        <div>
          <label className="block text-sm">Scope</label>
          <select
            value={scope}
            onChange={(e) => { setScope(e.target.value); setPage(1); }}
            className="border rounded px-3 py-2"
          >
            <option value="variant">à¸£à¸²à¸¢à¸•à¸±à¸§à¹€à¸¥à¸·à¸­à¸ (variant)</option>
            <option value="product">à¸£à¸²à¸¢à¸ªà¸´à¸™à¸„à¹‰à¸² (product)</option>
          </select>
          <Help>à¹€à¸¥à¸·à¸­à¸à¸”à¸¹à¸ªà¸£à¸¸à¸›à¹€à¸›à¹‡à¸™à¸£à¸²à¸¢à¸•à¸±à¸§à¹€à¸¥à¸·à¸­à¸ à¸«à¸£à¸·à¸­à¸£à¸²à¸¢à¸ªà¸´à¸™à¸„à¹‰à¸²</Help>
        </div>
        <div>
          <label className="block text-sm">à¸ˆà¸±à¸”à¹€à¸£à¸µà¸¢à¸‡</label>
          <select
            value={order}
            onChange={(e) => setOrder(e.target.value)}
            className="border rounded px-3 py-2"
          >
            <option value="low_stock">à¹€à¸«à¸¥à¸·à¸­à¸™à¹‰à¸­à¸¢à¸à¹ˆà¸­à¸™</option>
            <option value="newest">à¹ƒà¸«à¸¡à¹ˆà¸¥à¹ˆà¸²à¸ªà¸¸à¸”</option>
            <option value="name_asc">à¸Šà¸·à¹ˆà¸­ Aâ†’Z</option>
            <option value="name_desc">à¸Šà¸·à¹ˆà¸­ Zâ†’A</option>
          </select>
          <Help>à¸§à¸´à¸˜à¸µà¹€à¸£à¸µà¸¢à¸‡à¸¥à¸³à¸”à¸±à¸šà¸£à¸²à¸¢à¸à¸²à¸£à¸—à¸µà¹ˆà¹à¸ªà¸”à¸‡</Help>
        </div>
        <button
          onClick={() => { setPage(1); load(); }}
          className="px-3 py-2 border rounded bg-black text-white"
        >
          à¸„à¹‰à¸™à¸«à¸²
        </button>
      </div>

      <div className="mt-4 overflow-auto border rounded">
        <table className="min-w-[720px] w-full">
          <thead className="bg-gray-100">
            <tr>
              <th className="text-left p-2">à¸ªà¸´à¸™à¸„à¹‰à¸²</th>
              {scope === "variant" && <th className="text-left p-2">SKU</th>}
              <th className="text-right p-2">à¸ªà¸•à¹Šà¸­à¸</th>
              {scope === "variant" && <th className="text-right p-2">à¸£à¸²à¸„à¸²</th>}
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td className="p-4" colSpan={scope === "variant" ? 4 : 2}>à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”...</td></tr>
            ) : items.length === 0 ? (
              <tr><td className="p-4" colSpan={scope === "variant" ? 4 : 2}>à¹„à¸¡à¹ˆà¸žà¸šà¸‚à¹‰à¸­à¸¡à¸¹à¸¥</td></tr>
            ) : items.map((it) => (
              <tr key={`${it.product_id}-${it.variant_id || "p"}`} className="border-t">
                <td className="p-2"><div className="font-medium">{it.product_name || `#${it.product_id}`}</div></td>
                {scope === "variant" && <td className="p-2">{it.sku || "-"}</td>}
                <td className="p-2 text-right">{fmt(it.stock)}</td>
                {scope === "variant" && <td className="p-2 text-right">{it.price ? currency(it.price) : "-"}</td>}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

/* ---------- 2) à¸£à¸±à¸šà¹€à¸‚à¹‰à¸² (IN) ---------- */
function ReceiveForm() {
  const [variantId, setVariantId] = useState("");
  const [selected, setSelected] = useState(null);
  const [qty, setQty] = useState("");
  const [unitCost, setUnitCost] = useState("");
  const [note, setNote] = useState("");
  const [receivedAt, setReceivedAt] = useState(nowLocalDatetime());
  const [resJson, setResJson] = useState(null);
  const [loading, setLoading] = useState(false);

  async function onSubmit(e) {
    e.preventDefault();
    if (!variantId || !qty || !unitCost) {
      toast.error("à¸à¸£à¸­à¸ Variant ID, à¸ˆà¸³à¸™à¸§à¸™ à¹à¸¥à¸°à¸•à¹‰à¸™à¸—à¸¸à¸™à¸•à¹ˆà¸­à¸«à¸™à¹ˆà¸§à¸¢ à¹ƒà¸«à¹‰à¸„à¸£à¸š");
      return;
    }
    setLoading(true);
    try {
      const data = await receiveInventory({
        variant_id: Number(variantId),
        qty: Number(qty),
        unit_cost: Number(unitCost),
        received_at: receivedAt || null,
        note: note || null,
      });
      setResJson(data);

      // ðŸŽ‰ Toast à¸Šà¸±à¸”à¹€à¸ˆà¸™
      toast.success(
        `à¸£à¸±à¸šà¹€à¸‚à¹‰à¸²à¹€à¸£à¸µà¸¢à¸šà¸£à¹‰à¸­à¸¢ â€¢ lot_id ${data?.lot?.lot_id} â€¢ qty ${fmt(qty)} â€¢ à¸¿${unitCost}/à¸«à¸™à¹ˆà¸§à¸¢`,
        { duration: 4000 }
      );

      // à¹€à¸„à¸¥à¸µà¸¢à¸£à¹Œà¸ˆà¸³à¸™à¸§à¸™/à¸«à¸¡à¸²à¸¢à¹€à¸«à¸•à¸¸ (à¸à¸±à¸™à¸„à¸¥à¸´à¸à¸‹à¹‰à¸³)
      setQty("");
      setNote("");
    } catch (e) {
      console.error(e);
      toast.error(e?.response?.data?.error || "à¸£à¸±à¸šà¹€à¸‚à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ");
    } finally {
      setLoading(false);
    }
  }

  return (
    <form onSubmit={onSubmit} className="grid gap-3 max-w-xl">
      <VariantPicker
        onChange={(it) => { setVariantId(it.variant_id); setSelected(it); }}
        autoFocus
      />
      <Help>à¸žà¸´à¸¡à¸žà¹Œà¸Šà¸·à¹ˆà¸­à¸ªà¸´à¸™à¸„à¹‰à¸²/à¸„à¸³à¸­à¸˜à¸´à¸šà¸²à¸¢ à¸«à¸£à¸·à¸­ SKU à¹à¸¥à¹‰à¸§à¹€à¸¥à¸·à¸­à¸ à¸£à¸°à¸šà¸šà¸ˆà¸°à¹ƒà¸ªà¹ˆ Variant ID à¹ƒà¸«à¹‰à¸­à¸±à¸•à¹‚à¸™à¸¡à¸±à¸•à¸´</Help>

      <div className="grid sm:grid-cols-2 gap-3">
        <div>
          <label className="block text-sm">Variant ID</label>
          <input
            value={variantId}
            onChange={(e) => setVariantId(e.target.value)}
            className="border rounded px-3 py-2 w-full"
            type="number"
            min="1"
          />
          <Help><b>Variant ID</b> = à¸£à¸«à¸±à¸§à¸¢à¹ˆà¸­à¸¢à¸‚à¸­à¸‡à¸•à¸±à¸§à¹€à¸¥à¸·à¸­à¸à¸ªà¸´à¸™à¸„à¹‰à¸² (à¸•à¸²à¸£à¸²à¸‡ product_variants)</Help>
          {selected && (
            <div className="text-xs text-gray-600 mt-1">
              {selected.product_name} â€¢ SKU: {selected.sku || "-"} â€¢ à¸„à¸‡à¸„à¸¥à¸±à¸‡: {fmt(selected.stock)}
            </div>
          )}
        </div>

        <div>
          <label className="block text-sm">à¸ˆà¸³à¸™à¸§à¸™ (qty)</label>
          <input
            value={qty}
            onChange={(e) => setQty(e.target.value)}
            className="border rounded px-3 py-2 w-full"
            type="number"
            min="1"
            placeholder="à¹€à¸Šà¹ˆà¸™ 10"
          />
          <Help>à¸ˆà¸³à¸™à¸§à¸™à¸«à¸™à¹ˆà¸§à¸¢à¸—à¸µà¹ˆà¸ˆà¸°à¸£à¸±à¸šà¹€à¸‚à¹‰à¸² (à¸ˆà¸³à¸™à¸§à¸™à¹€à¸•à¹‡à¸¡ &gt; 0)</Help>
        </div>

        <div>
          <label className="block text-sm">à¸•à¹‰à¸™à¸—à¸¸à¸™à¸•à¹ˆà¸­à¸«à¸™à¹ˆà¸§à¸¢ (unit_cost)</label>
          <input
            value={unitCost}
            onChange={(e) => setUnitCost(e.target.value)}
            className="border rounded px-3 py-2 w-full"
            type="number"
            step="0.01"
            min="0"
            placeholder="à¹€à¸Šà¹ˆà¸™ 12.50"
          />
          <Help>à¹ƒà¸Šà¹‰à¸„à¸³à¸™à¸§à¸“à¸¡à¸¹à¸¥à¸„à¹ˆà¸²à¸ªà¸•à¹Šà¸­à¸/COGS â€” à¸£à¸²à¸„à¸²à¸•à¹ˆà¸­ 1 à¸«à¸™à¹ˆà¸§à¸¢</Help>
        </div>

        <div>
          <label className="block text-sm">à¸§à¸±à¸™à¸—à¸µà¹ˆ/à¹€à¸§à¸¥à¸²à¸—à¸µà¹ˆà¸£à¸±à¸šà¹€à¸‚à¹‰à¸² (received_at)</label>
          <input
            type="datetime-local"
            value={receivedAt}
            onChange={(e) => setReceivedAt(e.target.value)}
            className="border rounded px-3 py-2 w-full"
          />
          <Help>à¹€à¸§à¸¥à¸²à¸£à¸±à¸šà¹€à¸‚à¹‰à¸²à¸ˆà¸£à¸´à¸‡ (à¹€à¸§à¹‰à¸™à¸§à¹ˆà¸²à¸‡à¹ƒà¸Šà¹‰à¹€à¸§à¸¥à¸²à¸›à¸±à¸ˆà¸ˆà¸¸à¸šà¸±à¸™)</Help>
        </div>

        <div className="sm:col-span-2">
          <label className="block text-sm">à¸«à¸¡à¸²à¸¢à¹€à¸«à¸•à¸¸</label>
          <input
            value={note}
            onChange={(e) => setNote(e.target.value)}
            className="border rounded px-3 py-2 w-full"
            placeholder="à¹€à¸Šà¹ˆà¸™ à¸£à¸±à¸šà¸ˆà¸²à¸à¸„à¸™à¸ªà¸§à¸™/à¸‹à¸±à¸žà¸žà¸¥à¸²à¸¢à¹€à¸­à¸­à¸£à¹Œ A"
          />
          <Help>à¸šà¸±à¸™à¸—à¸¶à¸à¹€à¸žà¸´à¹ˆà¸¡à¹€à¸•à¸´à¸¡ (à¹„à¸¡à¹ˆà¸šà¸±à¸‡à¸„à¸±à¸š)</Help>
        </div>
      </div>

      <button type="submit" disabled={loading}
        className="px-4 py-2 rounded bg-green-600 text-white disabled:opacity-50">
        {loading ? "à¸à¸³à¸¥à¸±à¸‡à¸šà¸±à¸™à¸—à¸¶à¸..." : "à¸šà¸±à¸™à¸—à¸¶à¸à¸à¸²à¸£à¸£à¸±à¸šà¹€à¸‚à¹‰à¸² (IN)"}
      </button>

      {/* ðŸ”½ à¸£à¸²à¸¢à¸¥à¸°à¹€à¸­à¸µà¸¢à¸”à¹à¸šà¸šà¸žà¸±à¸šà¹€à¸à¹‡à¸šà¹„à¸”à¹‰ */}
      <JsonDetails data={resJson} title="à¸£à¸²à¸¢à¸¥à¸°à¹€à¸­à¸µà¸¢à¸”à¸à¸²à¸£à¸£à¸±à¸šà¹€à¸‚à¹‰à¸² (JSON)" />
    </form>
  );
}

/* ---------- 3) à¸•à¸±à¸”à¸­à¸­à¸ (OUT) FIFO ---------- */
function IssueForm() {
  const [variantId, setVariantId] = useState("");
  const [selected, setSelected] = useState(null);
  const [qty, setQty] = useState("");
  const [note, setNote] = useState("");
  const [ref, setRef] = useState("");
  const [reason, setReason] = useState("SALE");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  async function onSubmit(e) {
    e.preventDefault();
    if (!variantId || !qty) {
      toast.error("à¸à¸£à¸­à¸ Variant ID à¹à¸¥à¸°à¸ˆà¸³à¸™à¸§à¸™à¸—à¸µà¹ˆà¸•à¹‰à¸­à¸‡à¸à¸²à¸£à¸•à¸±à¸”à¸­à¸­à¸");
      return;
    }
    setLoading(true);
    try {
      const data = await issueInventory({
        variant_id: Number(variantId),
        qty: Number(qty),
        note: note || null,
        ref_order_detail_id: ref ? Number(ref) : null,
        reason_code: reason,
      });
      setResult(data);

      // ðŸŽ‰ Toast à¸Šà¸±à¸”à¹€à¸ˆà¸™
      const lots = (data?.allocations || []).length;
      toast.success(
        `à¸•à¸±à¸”à¸­à¸­à¸à¸ªà¸³à¹€à¸£à¹‡à¸ˆ â€¢ ${fmt(data?.total_allocated)} à¸«à¸™à¹ˆà¸§à¸¢ â€¢ ${lots} lot â€¢ à¹€à¸«à¸•à¸¸à¸œà¸¥: ${reason}`,
        { duration: 4500 }
      );

      // à¹€à¸„à¸¥à¸µà¸¢à¸£à¹Œà¸ˆà¸³à¸™à¸§à¸™/à¸«à¸¡à¸²à¸¢à¹€à¸«à¸•à¸¸
      setQty("");
      setNote("");
      setRef("");
    } catch (e) {
      console.error(e);
      toast.error(e?.response?.data?.error || "à¸•à¸±à¸”à¸ªà¸•à¹Šà¸­à¸à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ");
    } finally {
      setLoading(false);
    }
  }

  const totalAllocated = useMemo(
    () => (result?.allocations || []).reduce((s, a) => s + Number(a.allocated_qty || 0), 0),
    [result]
  );

  return (
    <div className="grid gap-4 max-w-6xl">
      <form onSubmit={onSubmit} className="grid gap-3 max-w-xl">
        <VariantPicker
          onChange={(it) => { setVariantId(it.variant_id); setSelected(it); }}
        />
        <Help>à¹€à¸¥à¸·à¸­à¸à¸ªà¸´à¸™à¸„à¹‰à¸²à¸—à¸µà¹ˆà¸•à¹‰à¸­à¸‡à¸à¸²à¸£à¸•à¸±à¸”à¸­à¸­à¸ à¸£à¸°à¸šà¸šà¸ˆà¸°à¸à¸³à¸«à¸™à¸” Variant ID à¹ƒà¸«à¹‰à¸­à¸±à¸•à¹‚à¸™à¸¡à¸±à¸•à¸´</Help>

        <div className="grid sm:grid-cols-2 gap-3">
          <div>
            <label className="block text-sm">Variant ID</label>
            <input
