// FRONTEND: src/pages/Login.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

export default function Login() {
  const navigate = useNavigate();

  const [form, setForm]   = useState({ username: '', password: '', showPw: false });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // ✅ เลือก path ให้ถูก: baseURL ลงท้าย /api → ใช้ '/auth/login' และ '/me'
  const baseEndsWithApi = (axios.defaults.baseURL || '').replace(/\/+$/, '').endsWith('/api');
  const LOGIN_PATH = baseEndsWithApi ? '/auth/login' : '/api/auth/login';
  const ME_PATH    = baseEndsWithApi ? '/me'         : '/api/me';

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm(p => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    const username = form.username.trim();
    const password = form.password.trim();
    if (!username || !password) {
      setError('กรุณากรอกข้อมูลให้ครบถ้วน');
      return;
    }

    try {
      setLoading(true);

      const res = await axios.post(LOGIN_PATH, {
        username,
        email: username, // เผื่อผู้ใช้กรอกเป็นอีเมล
        password,
      });

      // ----- ✅ รองรับคีย์ token/role หลายแบบ -----
      const token =
        res.data?.token ||
        res.data?.accessToken ||
        res.data?.jwt ||
        res.data?.data?.token;

      const roleRaw =
        res.data?.role ||
        res.data?.user?.role ||
        res.data?.data?.user?.role ||
        'customer';

      if (!token) throw new Error('ไม่พบ token จากเซิร์ฟเวอร์');

      // แนบ header ให้รีเควสต์ถัดไป
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // ----- ✅ ดึงชื่อผู้ใช้ -----
      // พยายามอ่านจาก response ก่อน ถ้าไม่มีค่อย fallback ไปเรียก /me
      let name =
        res.data?.user?.fullname ||
        res.data?.user?.name ||
        res.data?.user?.display_name ||
        res.data?.profile?.fullname ||
        res.data?.profile?.name ||
        res.data?.data?.user?.fullname ||
        res.data?.data?.user?.name ||
        '';

      if (!name) {
        try {
          const me = await axios.get(ME_PATH, {
            headers: { Authorization: `Bearer ${token}` },
          });
          const u = me.data?.user || me.data || {};
          name =
            u.fullname || u.name || u.display_name || u.username || u.email || username;
        } catch {
          // ถ้าดึงไม่สำเร็จ ใช้ username แทนไปก่อน
          name = username;
        }
      }

      // เก็บลง localStorage
      const role = String(roleRaw).toLowerCase();
      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('fullname', name);

      // แจ้งทุกคอมโพเนนต์ให้รู้ว่า auth/profile เปลี่ยน (Header จะอัปเดตทันที)
      window.dispatchEvent(new Event('auth:changed'));
      window.dispatchEvent(new Event('profile:changed'));

      // ----- ✅ เด้งตามสิทธิ์ -----
      if (role.includes('admin')) {
        navigate('/admin/products', { replace: true }); // หน้าแอดมิน
      } else {
        navigate('/home-user', { replace: true });      // ลูกค้า → HomeUser
      }
    } catch (e) {
      const status = e?.response?.status;
      const msg =
        status === 401 ? 'อีเมล/ชื่อผู้ใช้ หรือรหัสผ่านไม่ถูกต้อง'
      : status === 403 ? 'บัญชีนี้ยังไม่มีสิทธิ์เข้าใช้งาน'
      : status === 404 ? 'ไม่พบปลายทาง /auth/login (เช็กว่า backend mount ที่ /api/auth และ baseURL ถูกต้อง)'
      : e?.code === 'ERR_NETWORK' ? 'เชื่อมต่อเซิร์ฟเวอร์ไม่ได้ (ตรวจสอบ backend พอร์ต 3001)'
      : e?.response?.data?.error || e?.message || 'เข้าสู่ระบบล้มเหลว';
      setError(msg);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="login-page">
      {/* ฝั่งโปรโมท */}
      <aside className="promo">
        <div className="promo-inner">
          <div className="promo-badge">GARDEN PRODUCT ONLINE</div>
          <h1>🌿 ยินดีต้อนรับเข้าสู่ หจก.ปราชญ์แม่โจ้ 🌿</h1>
          <p>มีพันธุ์ไม้มากกว่า 100 ชนิด ราคาเป็นมิตร พร้อมบริการจัดส่งรวดเร็ว</p>
        </div>
      </aside>

      {/* ฟอร์มล็อกอิน */}
      <main className="panel">
        <div className="login-card">
          <div className="brand">
            <Link to="/" className="brand-name">PRACH MAEJO</Link>
            <p className="brand-sub">กรุณาเข้าสู่ระบบ</p>
          </div>

          <form className="login-form" onSubmit={onSubmit} noValidate>
            <label className="field">
              <span className="label">อีเมล / ชื่อผู้ใช้</span>
              <div className="input-wrap">
                <input
                  type="text"
                  name="username"
                  placeholder="กรอกอีเมลหรือชื่อผู้ใช้"
                  value={form.username}
                  onChange={onChange}
                  required
                />
              </div>
            </label>

            <label className="field">
              <span className="label">รหัสผ่าน</span>
              <div className="input-wrap">
                <input
                  type={form.showPw ? 'text' : 'password'}
                  name="password"
                  placeholder="********"
                  value={form.password}
                  onChange={onChange}
                  required
                />
                <button
                  type="button"
                  className="pw-toggle"
                  onClick={() => setForm(p => ({ ...p, showPw: !p.showPw }))}>
                  {form.showPw ? '🙈' : '👁️'}
                </button>
              </div>
            </label>

            <div className="row-between">
              <span />
              <Link className="text-link" to="/reset-password">ลืมรหัสผ่าน?</Link>
            </div>

            {error && <div className="form-error" role="alert">{error}</div>}

            <button className="btn-primary" type="submit" disabled={loading}>
              {loading ? 'กำลังเข้าสู่ระบบ…' : 'เข้าสู่ระบบ'}
            </button>
          </form>
        </div>
      </main>
    </div>
  );
}
