// src/components/HeaderClassic.js
import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { getCount } from "../lib/cart";

export default function HeaderClassic() {
  const nav = useNavigate();
  const loc = useLocation();

  const fullname = localStorage.getItem("fullname") || "‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô";
  const avatar = process.env.PUBLIC_URL + "/profile.jpg";

  // Badge ‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤
  const [cartCount, setCartCount] = useState(getCount());
  useEffect(() => {
    const onChanged = () => setCartCount(getCount());
    window.addEventListener("cart:changed", onChanged);
    return () => window.removeEventListener("cart:changed", onChanged);
  }, []);

  // ‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏´‡∏ô‡πâ‡∏≤‡πÅ‡∏£‡∏Å ‡πÅ‡∏™‡∏î‡∏á‡∏Ñ‡∏≥‡∏ß‡πà‡∏≤ "‡∏™‡∏ß‡∏±‡∏™‡∏î‡∏µ"
  const isHome = loc.pathname === "/";

  function logout() {
    localStorage.clear();
    nav("/login", { replace: true });
  }

  return (
    <header
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "10px 16px",
        borderBottom: "1px solid #e5e7eb",
        background: "#fff",
        position: "sticky",
        top: 0,
        zIndex: 50,
      }}
    >
      {/* ‡πÇ‡∏•‡πÇ‡∏Å‡πâ */}
      <Link to="/" style={{ fontWeight: 700, fontSize: 20, color: "#16a34a", textDecoration: "none" }}>
        PRACH MAEJO
      </Link>

      {/* ‡∏õ‡∏∏‡πà‡∏°‡πÄ‡∏°‡∏ô‡∏π‡∏î‡πâ‡∏≤‡∏ô‡∏Ç‡∏ß‡∏≤ */}
      <div style={{ display: "flex", alignItems: "center", gap: 16 }}>
        {/* ‡∏õ‡∏∏‡πà‡∏°‡∏´‡∏ô‡πâ‡∏≤‡πÅ‡∏£‡∏Å */}
        <Link to="/" title="‡∏´‡∏ô‡πâ‡∏≤‡πÅ‡∏£‡∏Å">üè†</Link>

        {/* ‡∏õ‡∏∏‡πà‡∏°‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤ + badge */}
        <div style={{ position: "relative" }}>
          <Link to="/cart" title="‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤">üõí</Link>
          {cartCount > 0 && (
            <span
              style={{
                position: "absolute",
                top: -6,
                right: -10,
                background: "#ef4444",
                color: "#fff",
                borderRadius: 999,
                fontSize: 12,
                minWidth: 18,
                height: 18,
                display: "grid",
                placeItems: "center",
                padding: "0 4px",
              }}
            >
              {cartCount}
            </span>
          )}
        </div>

        {/* ‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ */}
        <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
          <img
            src={avatar}
            alt="avatar"
            onError={(e) => (e.currentTarget.src = process.env.PUBLIC_URL + "/logo.png")}
            style={{ width: 32, height: 32, borderRadius: "999px", objectFit: "cover" }}
          />
          <span>{isHome ? `‡∏™‡∏ß‡∏±‡∏™‡∏î‡∏µ ${fullname}` : fullname}</span>
        </div>

        {/* ‡∏õ‡∏∏‡πà‡∏°‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö */}
        <button onClick={logout} style={{ border: "none", background: "transparent", cursor: "pointer" }}>
          üö™
        </button>
      </div>
    </header>
  );
}
