import React, { useEffect, useState, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './HomeUser.css';

export default function HomeUser() {
  const nav = useNavigate();

  // ========= ผู้ใช้ (ลองอ่านชื่อจาก localStorage ถ้ามี) =========
  const user = useMemo(() => {
    const fullname = localStorage.getItem('fullname') || 'ผู้ใช้งาน';
    return {
      fullname,
      avatar: process.env.PUBLIC_URL + '/profile.jpg',
    };
  }, []);

  // ========= ตัวช่วยคำนวณ path ให้ถูก ไม่เกิด /api/api =========
  const apiPath = (p) =>
    (axios.defaults.baseURL || '').replace(/\/+$/, '').endsWith('/api')
      ? p
      : '/api' + p;

  // ========= หมวดหมู่ & สินค้าขายดี =========
  const [categories, setCategories] = useState([]); // {id, name, slug, image}
  const [bestSellers, setBestSellers] = useState([]); // {id, name, price, img}
  const [loading, setLoading] = useState({ cat: true, best: true });
  const [errMsg, setErrMsg] = useState('');

  // ------ Fallback เมื่อ API ยังไม่พร้อม ------
  const fallbackCategories = [
    { id: 'tools',  slug: 'tools',  name: 'อุปกรณ์ปลูก', image: process.env.PUBLIC_URL + '/opakorn.jpg' },
    { id: 'plants', slug: 'plants', name: 'ต้นไม้',       image: process.env.PUBLIC_URL + '/tonmai.jpg' },
  ];
  const fallbackBest = [
    { id: 1, name: 'ต้นกล้าลำใยคริสตัล', price: 250, img: process.env.PUBLIC_URL + '/lamyai.jpg' },
    { id: 2, name: 'ต้นพญานาคราช',       price: 150, img: process.env.PUBLIC_URL + '/payanaka1.jpg' },
    { id: 3, name: 'กระถางมินิมอล',      price: 300, img: process.env.PUBLIC_URL + '/katar.jpeg' },
    { id: 4, name: 'ดินสูตรทางร้าน',      price:  99, img: process.env.PUBLIC_URL + '/din.jpg' },
  ];

  // ------ โหลดหมวดหมู่จากฐานข้อมูล ------
  useEffect(() => {
    (async () => {
      try {
        setLoading((s) => ({ ...s, cat: true }));
        const res = await axios.get(apiPath('/categories?status=active'));
        // ปรับชื่อตามสคีมแบ็กเอนด์ของคุณได้เลย
        const list = (Array.isArray(res.data?.data) ? res.data.data : res.data) || [];
        const norm = list.map((c) => ({
          id: c.id || c._id || c.slug || c.code,
          slug: c.slug || c.code || String(c.id || c._id || '').toLowerCase(),
          name: c.name_th || c.name || c.title || 'ไม่ระบุชื่อ',
          image: c.image || c.cover || c.thumbnail || (
            (String(c.slug || '').includes('tool') ? process.env.PUBLIC_URL + '/opakorn.jpg'
              : process.env.PUBLIC_URL + '/tonmai.jpg')
          ),
        }));

        // อยากแสดงเฉพาะ 2 หมวดนี้ก่อน (ถ้ามี)
        const order = ['tools', 'plants'];
        const bySlug = (s) => norm.find((x) => x.slug === s);
        const picked =
          order.map(bySlug).filter(Boolean).length
            ? order.map(bySlug).filter(Boolean)
            : norm.slice(0, 2);

        setCategories(picked.length ? picked : fallbackCategories);
      } catch (err) {
        console.warn('load categories failed:', err?.response?.status, err?.message);
        setCategories(fallbackCategories);
      } finally {
        setLoading((s) => ({ ...s, cat: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // ------ โหลดสินค้าขายดีจากฐานข้อมูล (ลองหลายปลายทาง) ------
  useEffect(() => {
    (async () => {
      const normalizeProduct = (p) => ({
        id: p.id || p._id,
        name: p.name_th || p.name || p.title || 'สินค้า',
        price: Number(p.price || p.sale_price || p.amount || 0),
        img: p.image || p.cover || p.thumbnail || (Array.isArray(p.images) ? p.images[0] : '') || process.env.PUBLIC_URL + '/logo.png',
      });

      async function tryFetch(url) {
        try {
          const r = await axios.get(url);
          const arr = (Array.isArray(r.data?.data) ? r.data.data : r.data) || [];
          const mapped = arr.map(normalizeProduct).filter((x) => x.id && x.name);
          if (mapped.length) return mapped;
        } catch (_) {}
        return null;
      }

      try {
        setLoading((s) => ({ ...s, best: true }));
        const urls = [
          apiPath('/products/best-sellers?limit=8'),
          apiPath('/products?sort=popular&limit=8'),
          apiPath('/products?featured=1&limit=8'),
        ];
        let products = null;
        for (const u of urls) {
          products = await tryFetch(u);
          if (products) break;
        }
        setBestSellers(products?.slice(0, 8) || fallbackBest);
      } catch (err) {
        console.warn('load best-sellers failed:', err?.response?.status, err?.message);
        setBestSellers(fallbackBest);
        setErrMsg('แสดงข้อมูลจากตัวอย่างชั่วคราว');
      } finally {
        setLoading((s) => ({ ...s, best: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // ========= ตะกร้า (localStorage) =========
  const addToCart = (product) => {
    let cart = JSON.parse(localStorage.getItem('cart') || '[]');
    const idx = cart.findIndex((x) => x.id === product.id);
    if (idx > -1) cart[idx].quantity += 1;
    else cart.push({ ...product, quantity: 1 });
    localStorage.setItem('cart', JSON.stringify(cart));
    alert(`เพิ่ม ${product.name} ลงตะกร้าแล้ว`);
  };

  return (
    <div className="home-container">
      {/* Header */}
      <header className="header">
        <div className="logo">
          <span>PRACH MAEJO</span>
        </div>

        <div className="user-icons">
          <Link to="/profile" className="avatar-link">
            <img
              src={user.avatar}
              alt="Avatar"
              className="avatar-img"
              onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
            />
          </Link>
          <span className="hello">{user.fullname}</span>

          <Link to="/cart" title="ตะกร้าสินค้า">
            <span className="icon-cart">🛒</span>
          </Link>

          <button
            className="icon-logout"
            title="ออกจากระบบ"
            onClick={() => { localStorage.clear(); nav('/login', { replace:true }); }}>
            🚪
          </button>
        </div>
      </header>

      {/* Middle bar */}
      <div className="middle-bar">GARDEN PRODUCT ONLINE</div>

      {/* Search */}
      <div className="search-box">
        <input type="text" placeholder="ค้นหาสินค้า" />
        <button className="search-btn">🔍</button>
      </div>

      {/* Hero */}
      <section
        className="hero"
        style={{
          backgroundImage: `url(${process.env.PUBLIC_URL + '/p2.png'})`,
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
        }}
      >
        <div className="hero-text">
          <h1>“<span className="highlight">ร้านปราชญ์แม่โจ้</span>”</h1>
          <p>
            แหล่งผลิตและจัดจำหน่าย ต้นไม้ <br />
            และวัสดุปลูก มากกว่า 100 รายการ
          </p>
        </div>
      </section>

      {/* Categories (ดึงจากฐานข้อมูล; ถ้า API ล่ม → fallback) */}
      <section className="categories">
        {categories.map((cat) => (
          <Link
            key={cat.id}
            to={`/products?category=${encodeURIComponent(cat.slug || cat.id)}`}
            className="category-card"
            title={cat.name}
          >
            <img
              src={cat.image}
              alt={cat.name}
              onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
            />
            <p>{cat.name}</p>
          </Link>
        ))}
      </section>

      {/* Best Sellers */}
      <section className="best-sellers">
        <h2>🌟 สินค้าขายดี</h2>
        {errMsg && <div className="info-inline">{errMsg}</div>}
        <div className="product-grid">
          {(loading.best ? fallbackBest : bestSellers).map((item) => (
            <div className="product-card" key={item.id}>
              <img
                src={item.img}
                alt={item.name}
                onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
              />
              <h3>{item.name}</h3>
              <p>{Number(item.price).toLocaleString()} บาท</p>
              <button className="btn-add" onClick={() => addToCart(item)}>
                + เพิ่มลงตะกร้า
              </button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
