// src/pages/HomeUser.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './HomeUser.css';
import { addItem as addToCart } from '../lib/cart';

/* -------------------- ค่าพื้นฐานเผื่อ API ว่าง -------------------- */
const FALLBACK_CATEGORIES = [
  { id: 'plants', slug: 'plants', name: 'ต้นไม้',            image: process.env.PUBLIC_URL + '/tonmai.jpg' },
  { id: 'tools',  slug: 'tools',  name: 'อุปกรณ์เพาะปลูก', image: process.env.PUBLIC_URL + '/opakorn.jpg' },
];

export default function HomeUser() {
  // กัน baseURL ลงท้าย /api ซ้อนกัน
  const apiPath = (p) =>
    (axios.defaults.baseURL || '').replace(/\/+$/, '').endsWith('/api') ? p : '/api' + p;

  const [categories, setCategories]   = useState([]);
  const [bestSellers, setBestSellers] = useState([]); // 5 ชิ้น
  const [allProducts, setAllProducts] = useState([]); // สินค้าทั้งหมด (กริดต่อเนื่อง)
  const [loading, setLoading]         = useState({ cat: true, best: true, all: true });
  const [errMsg, setErrMsg]           = useState('');

  /* -------------------- ตัวแปลงข้อมูลจาก DB → UI -------------------- */
  const normalizeProduct = (p, idx) => ({
    id:    p.product_id ?? p.id ?? `p-${idx}`,
    name:  p.product_name ?? p.name ?? 'สินค้า',
    // ราคา: ใช้ฟิลด์ที่มีจริงในระบบคุณ ถ้าไม่มีให้เป็น 0 ไปก่อน
    price: Number(p.selling_price ?? p.price ?? p.unit_price ?? 0),
    img:
      p.image_url ||
      p.cover_url ||
      p.image ||
      (Array.isArray(p.images) ? p.images[0] : '') ||
      process.env.PUBLIC_URL + '/logo.png',
  });

  const normalizeCategory = (c, idx) => {
    const cid   = c.category_id ?? c.id ?? c.code ?? c.slug ?? `cat-${idx}`;
    const name  = c.category_name ?? c.name ?? 'หมวดหมู่';
    const slug  = c.slug ?? String(cid).toLowerCase();
    const image = c.image_url || c.image || (
      slug.includes('tool') ? process.env.PUBLIC_URL + '/opakorn.jpg'
                            : process.env.PUBLIC_URL + '/tonmai.jpg'
    );
    return { id: String(cid), slug, name, image };
  };

  const toArray = (data) => {
    if (Array.isArray(data)) return data;
    if (Array.isArray(data?.data)) return data.data;
    if (Array.isArray(data?.rows)) return data.rows;
    return [];
  };

  /* -------------------- โหลดหมวดหมู่ (ใช้แค่ 2 ปุ่มด้านบน) -------------------- */
  useEffect(() => {
    (async () => {
      try {
        setLoading(s => ({ ...s, cat: true }));
        const res  = await axios.get(apiPath('/categories'), { params: { status: 'active', _: Date.now() }});
        const list = toArray(res.data).map(normalizeCategory);
        // ถ้า DB มีมากกว่า 2 หมวด ให้เรียง “ต้นไม้ → อุปกรณ์” ไว้ก่อน
        const order = ['plants', 'tools'];
        const arranged =
          order.map(k => list.find(x => (x.slug || '').toLowerCase() === k)).filter(Boolean);
        setCategories(arranged.length ? arranged.slice(0, 2) : (list.slice(0, 2).length ? list.slice(0, 2) : FALLBACK_CATEGORIES));
      } catch {
        setCategories(FALLBACK_CATEGORIES);
      } finally {
        setLoading(s => ({ ...s, cat: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  /* -------------------- สินค้าขายดี (5 ชิ้น จาก DB จริง) -------------------- */
  useEffect(() => {
    (async () => {
      try {
        setLoading(s => ({ ...s, best: true }));
        // ใช้ endpoint เดียวให้ชัด ถ้าไม่มีให้ไปใช้ popular เป็นสำรอง
        let res = null;
        try {
          res = await axios.get(apiPath('/products/best-sellers'), { params: { limit: 5, _: Date.now() }});
        } catch {
          res = await axios.get(apiPath('/products'), { params: { sort: 'popular', limit: 5, _: Date.now() }});
        }
        const list = toArray(res.data).map(normalizeProduct).slice(0, 5);
        setBestSellers(list);
      } catch {
        setBestSellers([]); // ไม่ต้องโชว์ mock แล้ว
      } finally {
        setLoading(s => ({ ...s, best: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  /* -------------------- สินค้าทั้งหมด (ต่อเนื่องจาก DB จริง) -------------------- */
  useEffect(() => {
    (async () => {
      try {
        setLoading(s => ({ ...s, all: true }));
        const res  = await axios.get(apiPath('/products'), { params: { limit: 100, _: Date.now() }});
        const list = toArray(res.data).map(normalizeProduct);
        setAllProducts(list);
        if (!list.length) setErrMsg('ยังไม่มีสินค้าในระบบหรือ API ไม่ส่งข้อมูล');
      } catch {
        setAllProducts([]);
        setErrMsg('เชื่อมต่อ API ไม่สำเร็จ');
      } finally {
        setLoading(s => ({ ...s, all: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="home-container">
      {/* กล่องค้นหา (ยังไม่ผูก logic) */}
      <div className="search-box">
        <input type="text" placeholder="ค้นหาสินค้า" />
        <button className="search-btn">🔍</button>
      </div>

      {/* แบนเนอร์ */}
      <section
        className="hero"
        style={{
          backgroundImage: `url(${process.env.PUBLIC_URL + '/p2.png'})`,
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
        }}
      >
        <div className="hero-text">
          <h1>“<span className="highlight">ร้านปราชญ์แม่โจ้</span>”</h1>
          <p>แหล่งผลิตและจัดจำหน่าย ต้นไม้ และวัสดุปลูก มากกว่า 100 รายการ</p>
        </div>
      </section>

      {/* หมวดหลัก 2 ปุ่มจาก DB */}
      <section className="categories">
        {(loading.cat ? FALLBACK_CATEGORIES : categories).map((cat, idx) => (
          <Link
            key={cat.id || `cat-${idx}`}
            to={`/products?category=${encodeURIComponent(cat.slug || cat.id)}`}
            className="category-card"
            title={cat.name}
          >
            <img
              src={cat.image}
              alt={cat.name}
              onError={(e) => {
                e.currentTarget.src =
                  (cat.slug || '').toLowerCase().includes('tool')
                    ? process.env.PUBLIC_URL + '/opakorn.jpg'
                    : process.env.PUBLIC_URL + '/tonmai.jpg';
              }}
            />
            <p>{cat.name}</p>
          </Link>
        ))}
      </section>

      {/* ขายดี (5 ชิ้น) */}
      {bestSellers.length > 0 && (
        <section className="best-sellers">
          <h2>🌟 สินค้าขายดี</h2>
          <div className="product-grid">
            {(loading.best ? [] : bestSellers).map((item, idx) => (
              <div className="product-card" key={item.id ?? `best-${idx}`}>
                <img
                  src={item.img}
                  alt={item.name}
                  onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
                />
                <h3>{item.name}</h3>
                <p>{Number(item.price || 0).toLocaleString()} บาท</p>
                <button className="btn-add" onClick={() => addToCart(item)}>
                  + เพิ่มลงตะกร้า
                </button>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* สินค้าทั้งหมด (ต่อเนื่อง) */}
      <section className="all-products">
        <h2>🛒 สินค้าทั้งหมด</h2>
        {loading.all ? (
          <div className="info-inline">กำลังโหลดสินค้า…</div>
        ) : allProducts.length ? (
          <div className="product-grid">
            {allProducts.map((p, idx) => (
              <div className="product-card" key={p.id ?? `all-${idx}`}>
                <img
                  src={p.img}
                  alt={p.name}
                  onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
                />
                <h3>{p.name}</h3>
                <p>{Number(p.price || 0).toLocaleString()} บาท</p>
                <button className="btn-add" onClick={() => addToCart(p)}>
                  + เพิ่มลงตะกร้า
                </button>
              </div>
            ))}
          </div>
        ) : (
          <div className="info-inline">{errMsg || 'ไม่พบสินค้า'}</div>
        )}
      </section>
    </div>
  );
}
