// [FRONTEND] src/App.js
import React, { useEffect } from 'react';
import {
  BrowserRouter, Routes, Route, NavLink, Navigate, useNavigate
} from 'react-router-dom';
import axios from 'axios';

import ProductManagement from './admin/ProductManagement';
import OrderManagement   from './admin/OrderManagement';
import VariantsManager   from './admin/VariantsManager';

import Addresses         from './account/Addresses';

import SignUp            from './pages/SignUp';
import Login             from './pages/Login';
import ResetPassword     from './pages/ResetPassword';
import HomeUser          from './pages/HomeUser';

/* -------------------------------------------
   ❌ ไม่ตั้ง baseURL ใน App.js
   ✅ ตั้งที่ src/index.js ที่เดียวพอ
--------------------------------------------*/

/* ===== Helpers ===== */
const getToken = () => localStorage.getItem('token') || '';
const getRole  = () => (localStorage.getItem('role') || '').toLowerCase();

// ระบุ role ให้ครอบคลุมชื่อเรียกหลากหลาย
const isAdminRole = (r) => {
  const x = String(r || '').toLowerCase();
  return x.includes('admin'); // 'admin' | 'administrator' | 'superadmin'
};
const isCustomerRole = (r) => {
  const x = String(r || '').toLowerCase();
  return ['customer','user','member','client','buyer',''].includes(x) && !isAdminRole(x);
};

/* ===== แนบ token เริ่มต้น (กันรีเควสต์แรกไม่มี header) ===== */
const bootToken = getToken();
if (bootToken) {
  axios.defaults.headers.common['Authorization'] = `Bearer ${bootToken}`;
}

/* ===== ErrorBoundary กันหน้าขาวจาก runtime error ===== */
class ErrorBoundary extends React.Component {
  constructor(p){ super(p); this.state = { err: null }; }
  static getDerivedStateFromError(error){ return { err: error }; }
  componentDidCatch(error, info){ console.error('UI error:', error, info); }
  render(){
    if (this.state.err){
      return (
        <div style={{ padding:16 }}>
          <h2>เกิดข้อผิดพลาดในหน้า</h2>
          <pre style={{
            background:'#111', color:'#0f0', padding:12,
            borderRadius:8, overflow:'auto'
          }}>
{String(this.state.err?.message || this.state.err)}
          </pre>
        </div>
      );
    }
    return this.props.children;
  }
}

/* ===== การ์ดสิทธิ์ (JWT) ===== */
function RequireAuth({ children }) {
  if (!getToken()) return <Navigate to="/login" replace />;
  return children;
}

/* ===== การ์ดสิทธิ์เฉพาะ ADMIN ===== */
function RequireAdmin({ children }) {
  if (!getToken()) return <Navigate to="/login" replace />;
  if (!isAdminRole(getRole())) return <Navigate to="/home-user" replace />;
  return children;
}

/* ===== การ์ดสิทธิ์เฉพาะ “ลูกค้า” (ไม่ใช่แอดมิน) ===== */
function RequireCustomer({ children }) {
  if (!getToken()) return <Navigate to="/login" replace />;
  if (!isCustomerRole(getRole())) return <Navigate to="/admin/products" replace />;
  return children;
}

/* ===== Navbar ===== */
function Navbar() {
  const nav = useNavigate();
  const linkStyle = ({ isActive }) => ({
    textDecoration: 'none', padding: '6px 10px', borderRadius: 8,
    background: isActive ? '#f0f0f0' : 'transparent',
  });

  const hasToken = !!getToken();
  const role = getRole();

  function logout() {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    delete axios.defaults.headers.common['Authorization'];
    nav('/login', { replace: true });
  }

  return (
    <nav style={{ padding: 10, display: 'flex', gap: 16, borderBottom: '1px solid #eee' }}>
      <NavLink to="/" style={linkStyle} end>หน้าแรก</NavLink>

      {/* ✅ เมนูแอดมิน */}
      {isAdminRole(role) && (
        <>
          <NavLink to="/admin/products" style={linkStyle}>สินค้า</NavLink>
          <NavLink to="/admin/orders" style={linkStyle}>คำสั่งซื้อ</NavLink>
        </>
      )}

      {/* ✅ เมนูลูกค้า (แสดงเมื่อ login และไม่ใช่ admin) */}
      {hasToken && isCustomerRole(role) && (
        <>
          <NavLink to="/home-user" style={linkStyle}>หน้าแรก</NavLink>
          <NavLink to="/account/addresses" style={linkStyle}>ที่อยู่ของฉัน</NavLink>
        </>
      )}

      <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
        {!hasToken ? (
          <>
            <NavLink to="/login" style={linkStyle}>เข้าสู่ระบบ</NavLink>
            <NavLink to="/register" style={linkStyle}>สมัครสมาชิก</NavLink>
          </>
        ) : (
          <button onClick={logout} style={{ padding: '6px 10px', borderRadius: 8 }}>
            ออกจากระบบ
          </button>
        )}
      </div>
    </nav>
  );
}

/* ===== หน้า Home เบา ๆ (สำหรับผู้ใช้ทั่วไป/ก่อนล็อกอิน) ===== */
function HomeLite(){
  return (
    <div style={{ padding:16 }}>
      <h1>🌿 ยินดีต้อนรับ</h1>
      <p>หน้านี้เป็นหน้าแรกแบบเบา ๆ ก่อนล็อกอิน</p>
      <p>หลังล็อกอิน: ลูกค้า→ HomeUser, แอดมิน→ ระบบจัดการ</p>
    </div>
  );
}

/* ===== ห่อเพจด้วย Title ===== */
function Page({ title, children }) {
  return (
    <div style={{ padding: 16 }}>
      {title ? <h1 style={{ marginTop: 0 }}>{title}</h1> : null}
      {children}
    </div>
  );
}

/* ===== App ===== */
export default function App() {
  useEffect(() => {
    const t = getToken();
    if (t) axios.defaults.headers.common['Authorization'] = `Bearer ${t}`;
  }, []);

  return (
    <BrowserRouter>
      <ErrorBoundary>
        <Navbar />
        <Routes>
          {/* ผู้ใช้ทั่วไป */}
          <Route path="/" element={<HomeLite />} />

          {/* 🔒 ลูกค้าเท่านั้น */}
          <Route
            path="/home-user"
            element={
              <RequireCustomer>
                <HomeUser />
              </RequireCustomer>
            }
          />

          {/* 🔒 แอดมินเท่านั้น */}
          <Route
            path="/admin/products"
            element={
              <RequireAdmin>
                <Page title="🌿 ระบบจัดการสินค้า">
                  <ProductManagement />
                </Page>
              </RequireAdmin>
            }
          />
          <Route
            path="/admin/orders"
            element={
              <RequireAdmin>
                <Page title="📦 จัดการคำสั่งซื้อ">
                  <OrderManagement />
                </Page>
              </RequireAdmin>
            }
          />
          <Route
            path="/admin/products/:productId/variants"
            element={
              <RequireAdmin>
                <Page title="🧩 ตัวเลือก/ขนาดสินค้า">
                  <VariantsManager />
                </Page>
              </RequireAdmin>
            }
          />

          {/* 🔒 ต้องล็อกอินก่อน */}
          <Route
            path="/account/addresses"
            element={
              <RequireAuth>
                <Page title="🏠 ที่อยู่ของฉัน">
                  <Addresses />
                </Page>
              </RequireAuth>
            }
          />

          {/* Auth */}
          <Route path="/login"          element={<Page><Login /></Page>} />
          <Route path="/register"       element={<Page><SignUp /></Page>} />
          <Route path="/reset-password" element={<ResetPassword />} />

          {/* ไม่รู้จักเส้นทาง → กลับหน้าแรก */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </ErrorBoundary>
    </BrowserRouter>
  );
}
