// src/pages/Plants.js
import React from "react";
import { Link } from "react-router-dom";
import "./Plants.css";

export default function Plants() {
  // ✅ หมวดย่อยของ "ต้นไม้"
  const subcats = [
    {
      slug: "fruit",
      name: "ไม้ผล",
      desc: "เช่น ทุเรียน ลำไย มะม่วง ฯลฯ",
      img: process.env.PUBLIC_URL + "/lamyai.jpg",
    },
    {
      slug: "ornamental",
      name: "ไม้ดอก/ประดับ",
      desc: "ไม้สวยงาม แต่งบ้าน แต่งสวน",
      img: process.env.PUBLIC_URL + "/tonmai.jpg",
    },
    {
      slug: "herb",
      name: "ไม้สมุนไพร",
      desc: "พืชสมุนไพรเพื่อสุขภาพ",
      img: process.env.PUBLIC_URL + "/payanaka1.jpg",
    },
    {
      slug: "economic",
      name: "ไม้เศรษฐกิจ",
      desc: "ไม้ที่นิยมปลูกเชิงพาณิชย์ เช่น ยางพารา ปาล์ม ไม้สัก",
      img: process.env.PUBLIC_URL + "/p2.png",
    },
  ];

  const buildLink = (s) => `/products?category=plants&sub=${encodeURIComponent(s)}`;

  return (
    <div className="plants-page">
      {/* breadcrumb */}
      <nav className="breadcrumb">
        <Link to="/" className="crumb">หน้าแรก</Link>
        <span className="sep">›</span>
        <span className="crumb current">ต้นไม้</span>
      </nav>

      {/* hero */}
      <header className="plants-hero">
        <h1>เลือกหมวดย่อยของ <span className="hl">ต้นไม้</span></h1>
        <p className="sub">เลือกประเภทที่ต้องการ เพื่อดูรายการสินค้าเฉพาะหมวดนั้น ๆ</p>
      </header>

      {/* cards */}
      <section className="plants-categories">
        {subcats.map((c) => (
          <Link to={buildLink(c.slug)} className="plant-card" key={c.slug} aria-label={c.name}>
            <div className="thumb">
              <img
                src={c.img}
                alt={c.name}
                onError={(e) => (e.currentTarget.src = process.env.PUBLIC_URL + "/logo.png")}
              />
            </div>
            <div className="meta">
              <p className="p-name">{c.name}</p>
              <p className="p-desc">{c.desc}</p>
            </div>
          </Link>
        ))}
      </section>
    </div>
  );
}
