// FRONTEND: src/pages/SignUp.js
import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css';

export default function SignUp() {
  const nav = useNavigate();

  /* -----------------------------------------------------------
   * ‡∏ü‡∏≠‡∏£‡πå‡∏°: ‡πÄ‡∏Å‡πá‡∏ö‡πÄ‡∏õ‡πá‡∏ô "code" ‡πÑ‡∏°‡πà‡πÉ‡∏ä‡πà‡∏≠‡∏≠‡∏ö‡πÄ‡∏à‡πá‡∏Å‡∏ï‡πå‡∏ó‡∏±‡πâ‡∏á‡∏Å‡πâ‡∏≠‡∏ô
   *   - province_code, amphoe_code, tambon_code = ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏ï‡∏≤‡∏°‡∏£‡∏´‡∏±‡∏™
   *   - ‡πÅ‡∏Å‡πâ‡∏õ‡∏±‡∏ç‡∏´‡∏≤ Objects are not valid as a React child ‡∏ó‡∏±‡∏ô‡∏ó‡∏µ
   * --------------------------------------------------------- */
  const [form, setForm] = useState({
    email: '',
    password: '',
    confirm: '',
    full_name: '',
    phone: '',
    houseNo: '',
    province_code: '',
    amphoe_code: '',
    tambon_code: '',
    gender: '',
  });
  const [showPw, setShowPw] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // ===== ‡πÇ‡∏´‡∏•‡∏î‡∏ê‡∏≤‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà (‡∏≠‡∏¢‡∏π‡πà‡πÉ‡∏ô public/data/) =====
  const [db, setDb] = useState(null);
  const [addrLoadErr, setAddrLoadErr] = useState('');

  useEffect(() => {
    (async () => {
      try {
        const url = `${process.env.PUBLIC_URL}/data/thai-address.min.json`;
        const res = await fetch(url);
        const json = await res.json();
        setDb(json);
      } catch (e) {
        console.error(e);
        setAddrLoadErr('‡πÇ‡∏´‡∏•‡∏î‡∏ê‡∏≤‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
      }
    })();
  }, []);

  /* -----------------------------------------------------------
   * Normalizers: ‡∏ó‡∏≥‡πÉ‡∏´‡πâ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å‡πÑ‡∏ü‡∏•‡πå‡πÉ‡∏ä‡πâ‡∏£‡∏π‡∏õ‡πÄ‡∏î‡∏µ‡∏¢‡∏ß‡∏Å‡∏±‡∏ô‡πÄ‡∏™‡∏°‡∏≠
   * ‡∏Ñ‡∏∑‡∏ô‡∏Ñ‡πà‡∏≤‡πÄ‡∏õ‡πá‡∏ô [{ code, name_th, zip? }, ...]
   *  - ‡∏£‡∏±‡∏ö‡πÑ‡∏î‡πâ‡∏ó‡∏±‡πâ‡∏á array ‡∏Ç‡∏≠‡∏á objects, array ‡∏Ç‡∏≠‡∏á strings,
   *    ‡πÅ‡∏•‡∏∞ object map (‡∏à‡∏∞‡πÉ‡∏ä‡πâ values/enries)
   * --------------------------------------------------------- */
  const toArray = (v) => {
    if (!v) return [];
    if (Array.isArray(v)) return v;
    if (typeof v === 'object') return Object.values(v);
    return [];
  };

  const normList = (raw) =>
    toArray(raw).map((it) => {
      if (it && typeof it === 'object') {
        return {
          code: it.code ?? it.id ?? it.value ?? String(it.name_th ?? it.name ?? ''),
          name_th: it.name_th ?? it.name ?? String(it.label ?? it.code ?? ''),
          zip: it.zip ?? it.zipcode ?? it.postcode ?? it.postal_code ?? '',
        };
      }
      const s = String(it);
      return { code: s, name_th: s, zip: '' };
    });

  // ===== ‡∏™‡∏£‡πâ‡∏≤‡∏á options ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö select ‡∏ï‡∏≤‡∏°‡∏£‡∏´‡∏±‡∏™‡∏ó‡∏µ‡πà‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡πÑ‡∏ß‡πâ =====
  const provinces = useMemo(() => (db ? normList(db.provinces) : []), [db]);

  const amphoes = useMemo(() => {
    if (!db || !form.province_code) return [];
    return normList(db.districts?.[form.province_code]);
  }, [db, form.province_code]);

  const tambons = useMemo(() => {
    if (!db || !form.province_code || !form.amphoe_code) return [];
    return normList(db.subdistricts?.[form.province_code]?.[form.amphoe_code]);
  }, [db, form.province_code, form.amphoe_code]);

  // ZIP ‡∏≠‡∏±‡∏ï‡πÇ‡∏ô‡∏°‡∏±‡∏ï‡∏¥‡∏à‡∏≤‡∏Å‡∏ï‡∏≥‡∏ö‡∏•‡∏ó‡∏µ‡πà‡πÄ‡∏•‡∏∑‡∏≠‡∏Å
  const zipcode = useMemo(() => {
    const t = tambons.find((x) => x.code === form.tambon_code);
    return t?.zip || '';
  }, [tambons, form.tambon_code]);

  // ===== helpers =====
  const isEmail = (v) => /\S+@\S+\.\S+/.test(String(v || '').trim());
  const pwMismatch = form.password && form.confirm && form.password !== form.confirm;

  const splitName = (full) => {
    const p = String(full || '').trim().split(/\s+/);
    if (p.length <= 1) return { first_name: p[0] || '', last_name: '' };
    return { first_name: p[0], last_name: p.slice(1).join(' ') };
  };
  const makeUsername = (email) => {
    const raw = String(email).split('@')[0] || '';
    const cleaned = raw.toLowerCase().replace(/[^a-z0-9_.-]/g, '');
    return cleaned || `cust${Date.now().toString().slice(-6)}`;
  };

  // ‡∏î‡∏∂‡∏á‡∏ä‡∏∑‡πà‡∏≠‡πÑ‡∏ó‡∏¢‡∏à‡∏≤‡∏Å code (‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÉ‡∏ä‡πâ‡∏õ‡∏£‡∏∞‡∏Å‡∏≠‡∏ö address ‡πÄ‡∏õ‡πá‡∏ô‡∏†‡∏≤‡∏©‡∏≤‡πÑ‡∏ó‡∏¢)
  const findName = (list, code) => list.find((x) => x.code === code)?.name_th || '';

  // ===== handlers =====
  const onChange = (e) => {
    const { name, value } = e.target;
    // ‡∏£‡∏µ‡πÄ‡∏ã‡πá‡∏ï‡πÄ‡∏ä‡∏¥‡∏á‡∏•‡∏≥‡∏î‡∏±‡∏ö‡∏ä‡∏±‡πâ‡∏ô ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î/‡∏≠‡∏≥‡πÄ‡∏†‡∏≠
    if (name === 'province_code')
      return setForm((p) => ({ ...p, province_code: value, amphoe_code: '', tambon_code: '' }));
    if (name === 'amphoe_code')
      return setForm((p) => ({ ...p, amphoe_code: value, tambon_code: '' }));
    setForm((p) => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    if (!isEmail(form.email)) return setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á');
    if (form.password.length < 8) return setError('‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£');
    if (pwMismatch) return setError('‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô');

    const required = ['full_name', 'phone', 'houseNo', 'province_code', 'amphoe_code', 'tambon_code'];
    if (required.some((k) => !String(form[k]).trim())) return setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô');

    const { first_name, last_name } = splitName(form.full_name);

    // ‡πÅ‡∏õ‡∏•‡∏á‡∏£‡∏´‡∏±‡∏™ -> ‡∏ä‡∏∑‡πà‡∏≠‡πÑ‡∏ó‡∏¢ ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏™‡∏£‡πâ‡∏≤‡∏á address ‡∏≠‡πà‡∏≤‡∏ô‡∏á‡πà‡∏≤‡∏¢
    const provName = findName(provinces, form.province_code) || form.province_code;
    const amphName = findName(amphoes, form.amphoe_code) || form.amphoe_code;
    const tambName = findName(tambons, form.tambon_code) || form.tambon_code;
    const address = `${form.houseNo} ‡∏ï.${tambName} ‡∏≠.${amphName} ‡∏à.${provName} ${zipcode || ''}`.trim();

    const payload = {
      first_name,
      last_name,
      email: form.email.trim(),
      username: makeUsername(form.email),
      password: form.password,
      confirm_password: form.confirm,
      phone_number: form.phone.trim(),
      address,

      // ‡∏ñ‡πâ‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡πÄ‡∏Å‡πá‡∏ö‡πÄ‡∏õ‡πá‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏î‡πâ‡∏ß‡∏¢ (‡πÅ‡∏ô‡∏∞‡∏ô‡∏≥):
      province_code: form.province_code,
      amphoe_code: form.amphoe_code,
      tambon_code: form.tambon_code,
      zipcode,
      gender: form.gender,
    };

    try {
      setLoading(true);
      const res = await axios.post('/auth/register', payload);

      // auto-login ‡∏ñ‡πâ‡∏≤ backend ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏™‡πà‡∏á token ‡∏ï‡∏≠‡∏ô register
      let token = res?.data?.token || res?.data?.accessToken || res?.data?.jwt;
      if (!token) {
        const login = await axios.post('/auth/login', { email: form.email, password: form.password });
        token = login?.data?.token || login?.data?.accessToken || login?.data?.jwt;
      }
      if (token) {
        localStorage.setItem('token', token);
        localStorage.setItem('role', res?.data?.user?.role || 'customer');
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      }
      nav('/', { replace: true });
    } catch (err) {
      console.error(err);
      setError(err?.response?.data?.error || '‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="signup-page">
      <div className="signup-hero">
        <div className="notch">
          <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="PRACH MAEJO" />
        </div>
      </div>

      <div className="brand-head">
        <div className="brand-line">
          <span className="brand">PRACH MAEJO</span>
