// src/pages/CartPage.js
import React, { useState, useEffect, useMemo } from "react";
import "./CartPage.css";
import { Link, useNavigate } from "react-router-dom";

export default function CartPage() {
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();

  // ‡πÇ‡∏´‡∏•‡∏î‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡πÅ‡∏£‡∏Å‡∏à‡∏≤‡∏Å localStorage (‡πÉ‡∏ä‡πâ key 'cart' ‡πÄ‡∏´‡∏°‡∏∑‡∏≠‡∏ô HomeUser)
  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCartItems(storedCart);
  }, []);

  // ‡πÄ‡∏ã‡∏ü‡∏Å‡∏•‡∏±‡∏ö‡∏ó‡∏∏‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡∏ó‡∏µ‡πà‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô + ‡πÅ‡∏à‡πâ‡∏á‡πÉ‡∏´‡πâ HeaderClassic ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï badge
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cartItems));
    window.dispatchEvent(new Event("cart:changed"));
  }, [cartItems]);

  const increaseQty = (id) => {
    setCartItems((prev) =>
      prev.map((it) => (it.id === id ? { ...it, quantity: it.quantity + 1 } : it))
    );
  };

  const decreaseQty = (id) => {
    setCartItems((prev) =>
      prev.map((it) =>
        it.id === id && it.quantity > 1 ? { ...it, quantity: it.quantity - 1 } : it
      )
    );
  };

  const removeItem = (id) => {
    if (window.confirm("‡∏Ñ‡∏∏‡∏ì‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ô‡∏µ‡πâ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà?")) {
      setCartItems((prev) => prev.filter((it) => it.id !== id));
    }
  };

  const clearCart = () => {
    if (window.confirm("‡∏•‡πâ‡∏≤‡∏á‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î‡πÉ‡∏ä‡πà‡πÑ‡∏´‡∏°?")) {
      setCartItems([]);
    }
  };

  const total = useMemo(
    () => cartItems.reduce((sum, it) => sum + it.price * it.quantity, 0),
    [cartItems]
  );

  const goCheckout = () => {
    // ‡πÄ‡∏Å‡πá‡∏ö‡∏¢‡∏≠‡∏î‡∏£‡∏ß‡∏°‡πÑ‡∏ß‡πâ‡πÉ‡∏ä‡πâ‡∏´‡∏ô‡πâ‡∏≤ checkout (mock ‡∏Ç‡∏±‡πâ‡∏ô‡∏´‡∏ô‡πâ‡∏≤)
    localStorage.setItem("checkoutTotal", JSON.stringify(total));
    navigate("/checkout");
  };

  return (
    <div className="cart-page">
      <h2>üõí ‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ß‡πà‡∏≤‡∏á‡πÄ‡∏õ‡∏•‡πà‡∏≤</p>
          <Link to="/plants" className="btn-primary">‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏ã‡∏∑‡πâ‡∏≠‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</Link>
        </div>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</th>
                <th>‡∏£‡∏≤‡∏Ñ‡∏≤</th>
                <th>‡∏à‡∏≥‡∏ô‡∏ß‡∏ô</th>
                <th>‡∏£‡∏ß‡∏°</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item) => {
                const imgSrc =
                  (item.img?.startsWith("/") ? process.env.PUBLIC_URL : "") + item.img;
                return (
                  <tr key={item.id}>
                    <td className="cart-product">
                      <img src={imgSrc} alt={item.name} />
                      <div className="p-info">
                        <div className="p-name">{item.name}</div>
                        {item.variant && <div className="p-variant">{item.variant}</div>}
                      </div>
                    </td>
                    <td>{item.price} ‡∏ö‡∏≤‡∏ó</td>
                    <td>
                      <div className="cart-actions">
                        <button onClick={() => decreaseQty(item.id)} aria-label="decrease">-</button>
                        <span>{item.quantity}</span>
                        <button onClick={() => increaseQty(item.id)} aria-label="increase">+</button>
                      </div>
                    </td>
                    <td className="cart-total">{item.price * item.quantity} ‡∏ö‡∏≤‡∏ó</td>
                    <td>
                      <button className="remove-btn" onClick={() => removeItem(item.id)} aria-label="remove">
                        üóë
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          {/* ‡∏™‡∏£‡∏∏‡∏õ‡∏¢‡∏≠‡∏î + ‡∏õ‡∏∏‡πà‡∏° */}
          <div className="cart-footer">
            <div className="cart-left">
              <Link to="/plants" className="btn-outline">‚Üê ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏ã‡∏∑‡πâ‡∏≠‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ï‡πà‡∏≠</Link>
              <button className="btn-ghost" onClick={clearCart}>‡∏•‡πâ‡∏≤‡∏á‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤</button>
            </div>
            <div className="cart-right">
              <div className="summary-row">
                <span>‡∏¢‡∏≠‡∏î‡∏£‡∏ß‡∏°‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</span><span>{total} ‡∏ö‡∏≤‡∏ó</span>
              </div>
              <div className="summary-row grand">
                <span>‡∏ä‡∏≥‡∏£‡∏∞‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</span><span>{total} ‡∏ö‡∏≤‡∏ó</span>
              </div>
              <button className="btn-primary" onClick={goCheckout}>
                ‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£‡∏ä‡∏≥‡∏£‡∏∞‡πÄ‡∏á‡∏¥‡∏ô ‚Üí
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
}
