// src/App.js
import React, { useEffect } from 'react';
import {
  BrowserRouter, Routes, Route, NavLink, Navigate, useNavigate
} from 'react-router-dom';
import axios from 'axios';

import ProductManagement from './admin/ProductManagement';
import OrderManagement   from './admin/OrderManagement';
import Login             from './auth/Login';
import Addresses         from './account/Addresses';
import Register          from './auth/Register';
import VariantsManager   from './admin/VariantsManager';

axios.defaults.baseURL = process.env.REACT_APP_API_BASEURL || 'http://localhost:3001';
const bootToken = localStorage.getItem('token');
if (bootToken) axios.defaults.headers.common['Authorization'] = `Bearer ${bootToken}`;

const ENABLE_AUTH_GUARD = false;
const getToken = () => localStorage.getItem('token');

function RequireAuth({ children }) {
  if (!ENABLE_AUTH_GUARD) return children;
  const token = getToken();
  if (!token) return <Navigate to="/login" replace />;
  return children;
}

function Navbar() {
  const nav = useNavigate();
  const linkStyle = ({ isActive }) => ({
    textDecoration: 'none', padding: '6px 10px', borderRadius: 8,
    background: isActive ? '#f0f0f0' : 'transparent',
  });
  const hasToken = !!getToken();
  function logout() {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    nav('/login', { replace: true });
  }
  return (
    <nav style={{ padding: 10, display: 'flex', gap: 16, borderBottom: '1px solid #eee' }}>
      <NavLink to="/" style={linkStyle} end>สินค้า</NavLink>
      <NavLink to="/admin/orders" style={linkStyle}>คำสั่งซื้อ</NavLink>
      <NavLink to="/account/addresses" style={linkStyle}>ที่อยู่ของฉัน</NavLink>
      <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
        {!hasToken ? (
          <>
            <NavLink to="/login" style={linkStyle}>เข้าสู่ระบบ</NavLink>
            <NavLink to="/register" style={linkStyle}>สมัครสมาชิก</NavLink>
          </>
        ) : (
          <button onClick={logout} style={{ padding: '6px 10px', borderRadius: 8 }}>
            ออกจากระบบ
          </button>
        )}
      </div>
    </nav>
  );
}

export default function App() {
  useEffect(() => {
    const t = getToken();
    if (t) axios.defaults.headers.common['Authorization'] = `Bearer ${t}`;
  }, []);

  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
        <Route
          path="/"
          element={
            <div className="App" style={{ padding: 16 }}>
              <h1>🌿 ระบบจัดการสินค้า</h1>
              <ProductManagement />
            </div>
          }
        />

        <Route
          path="/admin/orders"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>📦 จัดการคำสั่งซื้อ</h1>
                <OrderManagement />
              </div>
            </RequireAuth>
          }
        />

        <Route
          path="/account/addresses"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>🏠 ที่อยู่ของฉัน</h1>
                <Addresses />
              </div>
            </RequireAuth>
          }
        />

        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* ✅ เส้นทางไปหน้า “ตัวเลือก/ขนาด” */}
        <Route path="/admin/products/:productId/variants" element={<VariantsManager />} />

        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
}
