// [FRONTEND] src/App.js
import React, { useEffect } from 'react';
import {
  BrowserRouter, Routes, Route, NavLink, Navigate, useNavigate
} from 'react-router-dom';
import axios from 'axios';

import ProductManagement from './admin/ProductManagement';
import OrderManagement   from './admin/OrderManagement';
import Login             from './pages/Login';
import Addresses         from './account/Addresses';
import Register          from './auth/Register';
import VariantsManager   from './admin/VariantsManager';

/* ------------------------------------------------------------------
   ✅ Base URL (ปลอดภัยกับ config กลางใน src/index.js)
   - ถ้า index.js ตั้งไว้แล้ว จะไม่ override ซ้ำ
   - รองรับทั้ง REACT_APP_API_BASEURL และ REACT_APP_API_BASE
   ------------------------------------------------------------------ */
const ENV_BASE =
  process.env.REACT_APP_API_BASEURL ||
  process.env.REACT_APP_API_BASE ||
  'http://localhost:3001';
if (!axios.defaults.baseURL) {
  axios.defaults.baseURL = ENV_BASE.replace(/\/+$/, '');
}

/* ------------------------------------------------------------------
   ✅ แนบ token เริ่มต้น (กันรีเควสต์แรกไม่มี header)
   ------------------------------------------------------------------ */
const bootToken = localStorage.getItem('token');
if (bootToken) axios.defaults.headers.common['Authorization'] = `Bearer ${bootToken}`;

/* ------------------------------------------------------------------
   ✅ เปิดการ์ดสิทธิ์หน้า “ที่อยู่/คำสั่งซื้อ” (ฝั่ง backend ล็อกด้วย JWT)
   ------------------------------------------------------------------ */
const ENABLE_AUTH_GUARD = true; // <-- เปิดการ์ดแล้ว เพื่อกัน 401 จาก /api/addresses
const getToken = () => localStorage.getItem('token');

function RequireAuth({ children }) {
  if (!ENABLE_AUTH_GUARD) return children;
  const token = getToken();
  if (!token) return <Navigate to="/login" replace />;
  return children;
}

/* ------------------------------------------------------------------
   ✅ Navbar: ปรับลิงก์ “สินค้า” ไปที่ /admin/products (เป็น index ของระบบ)
   ------------------------------------------------------------------ */
function Navbar() {
  const nav = useNavigate();
  const linkStyle = ({ isActive }) => ({
    textDecoration: 'none', padding: '6px 10px', borderRadius: 8,
    background: isActive ? '#f0f0f0' : 'transparent',
  });
  const hasToken = !!getToken();
  function logout() {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    delete axios.defaults.headers.common['Authorization'];
    nav('/login', { replace: true });
  }
  return (
    <nav style={{ padding: 10, display: 'flex', gap: 16, borderBottom: '1px solid #eee' }}>
      {/* index ของระบบ = สินค้า */}
      <NavLink to="/admin/products" style={linkStyle} end>สินค้า</NavLink>
      <NavLink to="/admin/orders" style={linkStyle}>คำสั่งซื้อ</NavLink>
      <NavLink to="/account/addresses" style={linkStyle}>ที่อยู่ของฉัน</NavLink>
      <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
        {!hasToken ? (
          <>
            <NavLink to="/login" style={linkStyle}>เข้าสู่ระบบ</NavLink>
            <NavLink to="/register" style={linkStyle}>สมัครสมาชิก</NavLink>
          </>
        ) : (
          <button onClick={logout} style={{ padding: '6px 10px', borderRadius: 8 }}>
            ออกจากระบบ
          </button>
        )}
      </div>
    </nav>
  );
}

export default function App() {
  useEffect(() => {
    const t = getToken();
    if (t) axios.defaults.headers.common['Authorization'] = `Bearer ${t}`;
  }, []);

  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
        {/* ✅ หน้า index: ส่งไป “สินค้า” เสมอ */}
        <Route path="/" element={<Navigate to="/admin/products" replace />} />

        {/* ✅ หน้า “สินค้า” (อิงฐานข้อมูล/endpoint ที่เราทำไว้: /api/admin/products, /api/categories, /api/subcategories, /api/product-status) */}
        <Route
          path="/admin/products"
          element={
            <div className="App" style={{ padding: 16 }}>
              <h1>🌿 ระบบจัดการสินค้า</h1>
              <ProductManagement />
            </div>
          }
        />

        {/* ✅ จัดการคำสั่งซื้อ (ล็อกอินก่อน) — อิง /api/orders */}
        <Route
          path="/admin/orders"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>📦 จัดการคำสั่งซื้อ</h1>
                <OrderManagement />
              </div>
            </RequireAuth>
          }
        />

        {/* ✅ ที่อยู่ของฉัน (ล็อกอินก่อน) — อิง /api/addresses */}
        <Route
          path="/account/addresses"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>🏠 ที่อยู่ของฉัน</h1>
                <Addresses />
              </div>
            </RequireAuth>
          }
        />

        {/* ✅ Auth */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* ✅ ตัวเลือก/ขนาด (variants) — อิง /api/admin/products/:productId/variants */}
        <Route path="/admin/products/:productId/variants" element={<VariantsManager />} />

        {/* ไม่รู้จัก route → กลับ index */}
        <Route path="*" element={<Navigate to="/admin/products" replace />} />
      </Routes>
    </BrowserRouter>
  );
}
