  // ‡∏Å‡∏£‡∏≠‡∏á‡∏ù‡∏±‡πà‡∏á client (‡∏™‡∏≥‡∏£‡∏≠‡∏á)
  const clientFilter = useMemo(() => {
    return (mode, value) => {
      if (!value) return [];
      const val = String(value).toLowerCase();
      if (mode === 'keyword') {
        return allProducts.filter(p =>
          p.name.toLowerCase().includes(val) ||
          String(p.id).toLowerCase().includes(val)
