// frontend/src/admin/ProductManagement.js
import React, { useCallback, useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import "./ProductManagement.css";
import { api, path } from "../lib/api";
import { useLookups } from "../lib/lookups";

/* ========== Helpers ========== */
const asStr = (v) => (v === null || v === undefined) ? "" : String(v).trim();

/* Toast แบบเบาๆ */
function useToasts() {
  const [toasts, setToasts] = useState([]);
  const push = (msg, type = "info") => {
    const id = Math.random().toString(36).slice(2);
    setToasts((xs) => [...xs, { id, msg, type }]);
    setTimeout(() => setToasts((xs) => xs.filter((t) => t.id !== id)), 3000);
  };
  const remove = (id) => setToasts((xs) => xs.filter((t) => t.id !== id));
  return { toasts, push, remove };
}

/* อัปโหลดไฟล์ (ใช้ path เดิมฝั่งคุณ /upload หรือจะเปลี่ยนใน api) */
async function uploadImage(file) {
  if (!file) return null;
  const fd = new FormData();
  fd.append("file", file);
  const res = await api.post(path("/upload"), fd, {
    headers: { "Content-Type": "multipart/form-data" },
  });
  return (
    res?.data?.url ||
    res?.data?.imageUrl ||
    res?.data?.path ||
    res?.data?.location ||
    null
  );
}

/* ========== Component ========== */
export default function ProductManagement() {
  /* -------- States หลัก -------- */
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showArchived, setShowArchived] = useState(false);

  /* ฟิลเตอร์ (เผื่อของเดิมคุณมี UI ใช้งานอยู่แล้ว) */
  const [query, setQuery] = useState("");
  const [catFilter, setCatFilter] = useState("");
  const [subcatFilter, setSubcatFilter] = useState("");
  const [stockFilter, setStockFilter] = useState("all");

  /* lookups เดิม */
  const { data: lookups, reload: reloadLookups, loading: lkLoading } = useLookups({ published: true });

  /* --- ฟอร์มเพิ่มสินค้า (ฟอร์มนี้ “อยู่เหมือนเดิม”) --- */
  const [form, setForm] = useState({
    product_name: "",
    description: "",
    price: "",
    category_id: null,
    subcategory_id: null,
    product_unit_id: null,
    size_value: "",
    size_unit_id: null,
    origin: "",
    product_status_id: ""
  });
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editProductId, setEditProductId] = useState(null);

  /* --- Variants Panel (จะ “ไม่ขึ้น” จนกว่าจะกดปุ่ม ➕) --- */
  const [showVariantPanel, setShowVariantPanel] = useState(false);
  const [opt1Name, setOpt1Name] = useState("สี");
  const [opt2Name, setOpt2Name] = useState("ขนาด");
  const [opt3Name, setOpt3Name] = useState("");
  const [opt1Values, setOpt1Values] = useState([]);
  const [opt2Values, setOpt2Values] = useState([]);
  const [opt3Values, setOpt3Values] = useState([]);
  const [variantRows, setVariantRows] = useState([]);
  const [savingVariants, setSavingVariants] = useState(false);

  /* --- action bar ท้ายหน้า (ทำงานกับสินค้าที่กำลังเลือก / editProductId) --- */
  const [pageActionLoading, setPageActionLoading] = useState(false);

  /* refs */
  const nameRef = useRef(null);
  const formTopRef = useRef(null);

  /* toasts */
  const { toasts, push, remove } = useToasts();

  /* -------- API Helpers -------- */
  const handleApiError = (err, fallback = "เกิดข้อผิดพลาด") => {
    console.error(fallback, err?.response?.data || err?.message || err);
    const msg = err?.response?.data?.message || err?.message || fallback;
    push(`❌ ${msg}`, "danger");
  };

  /* -------- โหลดสินค้า -------- */
  const fetchProducts = useCallback(async () => {
    try {
      const { data } = await api.get(path("/admin/products"), {
        params: { include_archived: showArchived ? 1 : 0 },
      });
      const items = Array.isArray(data?.items)
        ? data.items
        : (Array.isArray(data) ? data : []);
      setProducts(items);
    } catch (err) {
      handleApiError(err, "❌ โหลดสินค้าไม่สำเร็จ");
    }
  }, [showArchived]);

  /* -------- toggle publish -------- */
  const togglePublish = useCallback(async (productId, current) => {
    try {
      await api.patch(path(`/admin/products/${productId}/publish`), {
        is_published: !current,
      });
      push(!current ? "✅ เปิดแสดงสินค้าแล้ว" : "✅ ซ่อนสินค้าแล้ว", "ok");
      await fetchProducts();
    } catch (err) {
      handleApiError(err, "❌ อัปเดตสถานะไม่สำเร็จ");
    }
  }, [fetchProducts]);

  /* -------- archive / unarchive -------- */
  const archiveProduct = useCallback(async (id) => {
    if (!window.confirm("ต้องการเก็บสินค้านี้ไว้ในคลังใช่ไหม?")) return;
    try {
      await api.delete(path(`/admin/products/${id}`));
      push("🗃️ ย้ายไปถังเก็บแล้ว", "warn");
      await fetchProducts();
    } catch (err) {
      handleApiError(err, "❌ เก็บสินค้าไม่สำเร็จ");
    }
  }, [fetchProducts]);

  const unarchiveProduct = useCallback(async (id) => {
    try {
      await api.patch(path(`/admin/products/${id}/unarchive`));
      push("♻️ กู้คืนสินค้าแล้ว", "ok");
      await fetchProducts();
    } catch (err) {
      handleApiError(err, "❌ กู้คืนสินค้าไม่สำเร็จ");
    }
  }, [fetchProducts]);

  /* -------- Init -------- */
  useEffect(() => {
    (async () => {
      setLoading(true);
      await Promise.all([fetchProducts(), reloadLookups()]);
      setLoading(false);
    })();
  }, [fetchProducts, reloadLookups, showArchived]);

  /* ========== ฟอร์มเพิ่มสินค้า (เหมือนเดิม) ========== */
  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  };

  const onFiles = (e) => setSelectedFiles(Array.from(e.target.files || []));
  useEffect(() => {
    if (!selectedFiles.length) {
      setPreviews([]);
      return;
    }
    const urls = selectedFiles.map((f) => ({
      name: f.name, url: URL.createObjectURL(f),
    }));
    setPreviews(urls);
    return () => urls.forEach((u) => URL.revokeObjectURL(u.url));
  }, [selectedFiles]);

  const clearForm = () => {
    setForm({
      product_name: "",
      description: "",
      price: "",
      category_id: null,
      subcategory_id: null,
      product_unit_id: null,
      size_value: "",
      size_unit_id: null,
      origin: "",
      product_status_id: ""
    });
    setSelectedFiles([]);
    setPreviews([]);
    setIsEditing(false);
    setEditProductId(null);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const price = parseInt(form.price);
    if (isNaN(price) || price < 0) {
      push("⚠️ ราคาต้องเป็นจำนวนเต็ม", "warn");
      return;
    }
    try {
      let uploaded = [];
      if (selectedFiles.length > 0) {
        uploaded = (await Promise.all(selectedFiles.map(uploadImage))).filter(Boolean);
      }

      const body = {
        product_name: asStr(form.product_name),
        description: asStr(form.description),
        price,
        category_id: form.category_id,
        subcategory_id: form.subcategory_id,
        product_unit_id: form.product_unit_id,
        size_unit_id: form.size_unit_id,
        size_value: form.size_value,
        origin: form.origin,
        product_status_id: form.product_status_id,
      };

      let id = editProductId;
      if (isEditing && id) {
        await api.put(path(`/admin/products/${id}`), body);
        push("✅ อัปเดตสินค้าสำเร็จ", "ok");
      } else {
        const res = await api.post(path("/admin/products"), body);
        id = res?.data?.product_id ?? res?.data?.id;
        push("🎉 เพิ่มสินค้าเรียบร้อย", "ok");
      }

      if (uploaded.length) {
        for (let i = 0; i < uploaded.length; i++) {
          try {
            await api.post(path(`/admin/products/${id}/images`), {
              images: [{ url: uploaded[i], position: i + 1, is_primary: i === 0 }],
            });
          } catch {}
        }
      }

      await fetchProducts();
      await reloadLookups();
      clearForm();
    } catch (err) {
      handleApiError(err, "❌ บันทึกสินค้าไม่สำเร็จ");
    }
  };

  /* ========== Variants (แสดงเมื่อกดปุ่ม ➕ เท่านั้น) ========== */
  const addTag = (list, setList, txt) => {
    const v = String(txt || "").trim();
    if (!v) return;
    if (!list.includes(v)) setList([...list, v]);
  };
  const removeTag = (list, setList, idx) => {
    const arr = list.filter((_, i) => i !== idx);
    setList(arr);
    push("ลบค่าเรียบร้อย", "warn");
  };
  const removeOption = (opt) => {
    if (opt === 1) { setOpt1Name(""); setOpt1Values([]); }
    if (opt === 2) { setOpt2Name(""); setOpt2Values([]); }
    if (opt === 3) { setOpt3Name(""); setOpt3Values([]); }
    push("ลบตัวเลือกนี้แล้ว", "warn");
  };
  const resetAllOptions = () => {
    setOpt1Name("สี"); setOpt2Name("ขนาด"); setOpt3Name("");
    setOpt1Values([]); setOpt2Values([]); setOpt3Values([]);
    setVariantRows([]);
    push("ล้างตัวเลือกทั้งหมดแล้ว", "warn");
  };

  /* สร้าง combination อัตโนมัติ */
  useEffect(() => {
    const v1 = opt1Values.length ? opt1Values : [null];
    const v2 = opt2Values.length ? opt2Values : [null];
    const v3 = opt3Values.length ? opt3Values : [null];
    const combos = [];
    for (const a of v1)
      for (const b of v2)
        for (const c of v3)
          combos.push({ opt1: a, opt2: b, opt3: c, price: "", sku: "", image: null, variant_id: null });
    setVariantRows(combos);
  }, [opt1Values, opt2Values, opt3Values]);

  /* อัปโหลดรูปต่อแถว */
  const onUploadRowImage = async (file, idx) => {
    if (!file) return;
    const temp = URL.createObjectURL(file);
    setVariantRows((prev) => {
      const a = [...prev];
      a[idx] = { ...a[idx], image: temp };
      return a;
    });
    try {
      const url = await uploadImage(file);
      setVariantRows((prev) => {
        const a = [...prev];
        a[idx] = { ...a[idx], image: url };
        return a;
      });
    } catch (err) {
      handleApiError(err, "อัปโหลดรูปไม่สำเร็จ");
    } finally {
      setTimeout(() => URL.revokeObjectURL(temp), 2000);
    }
  };

  /* ลบ Variant แถว */
  const handleDeleteVariant = async (row, idx) => {
    if (!row.variant_id) {
      setVariantRows((p) => p.filter((_, i) => i !== idx));
      push("🗑️ ลบตัวเลือกออกจากรายการแล้ว", "warn");
      return;
    }
    if (!window.confirm("ต้องการลบตัวเลือกนี้ใช่ไหม?")) return;
    try {
      await api.delete(path(`/api/variants/${row.variant_id}`));
      setVariantRows((p) => p.filter((_, i) => i !== idx));
      push("✅ ลบตัวเลือกเรียบร้อย", "ok");
    } catch (err) { handleApiError(err, "❌ ลบตัวเลือกไม่สำเร็จ"); }
  };

  /* SKU อัตโนมัติ */
  const autoSku = () => {
    setVariantRows((prev) =>
      prev.map((r, i) => {
        const parts = [
          asStr(form.product_name).substring(0, 3).toUpperCase() || "PRD",
          r.opt1, r.opt2, r.opt3,
        ].filter(Boolean);
        return { ...r, sku: parts.join("-") + "-" + String(i + 1).padStart(2, "0") };
      })
    );
    push("✨ เติม SKU สำเร็จ", "ok");
  };

  /* บันทึก Variant ทั้งหมด (POST ทีละแถว) */
  const saveAllVariants = async () => {
    if (!editProductId) { push("กรุณาเลือกสินค้าก่อน", "warn"); return; }
    setSavingVariants(true);
    try {
      const payloads = variantRows
        .filter((r) => r.opt1 || r.opt2 || r.opt3)
        .map((r) => ({
          product_id: editProductId,
          options: [
            r.opt1 ? { name: opt1Name, value: r.opt1 } : null,
            r.opt2 ? { name: opt2Name, value: r.opt2 } : null,
            r.opt3 && opt3Name ? { name: opt3Name, value: r.opt3 } : null,
          ].filter(Boolean),
          price: r.price ? Number(r.price) : null,
          sku: r.sku || null,
          image_url: r.image || null,
        }));
      for (const p of payloads) await api.post(path("/api/variants/upsert-single"), p);
      push("✅ บันทึกตัวเลือกสำเร็จ", "ok");
    } catch (err) { handleApiError(err, "❌ บันทึกตัวเลือกไม่สำเร็จ"); }
    finally { setSavingVariants(false); }
  };

  /* ========== ปุ่มสถานะท้ายหน้า ========== */
  const footerAction = async (type) => {
    if (!editProductId) { push("กรุณาเลือกสินค้าก่อน", "warn"); return; }
    setPageActionLoading(true);
    try {
      if (type === "publish") {
        await api.patch(path(`/admin/products/${editProductId}/publish`), { is_published: 1 });
        push("✅ เปิดแสดงสินค้าแล้ว", "ok");
      } else if (type === "unpublish") {
        await api.patch(path(`/admin/products/${editProductId}/publish`), { is_published: 0 });
        push("✅ ซ่อนสินค้าแล้ว", "ok");
      } else if (type === "archive") {
        await api.delete(path(`/admin/products/${editProductId}`));
        push("🗃️ เก็บสินค้าแล้ว", "warn");
      } else if (type === "unarchive") {
        await api.patch(path(`/admin/products/${editProductId}/unarchive`));
        push("♻️ คืนสินค้าจากคลังแล้ว", "ok");
      }
      await fetchProducts();
    } catch (err) {
      handleApiError(err, "❌ ทำรายการไม่สำเร็จ");
    } finally {
      setPageActionLoading(false);
    }
  };

  /* ========== Render ========== */
  if (loading || lkLoading) {
    return <div style={{ padding: 16 }}>⏳ กำลังโหลดข้อมูล…</div>;
  }

  return (
    <div className="pm-page">
      {/* Toasts */}
      <div className="pm-toasts">
        {toasts.map((t) => (
          <div key={t.id} className={`toast ${t.type}`}>
            <div>{t.msg}</div>
            <button className="toast-x" onClick={() => remove(t.id)}>×</button>
          </div>
        ))}
      </div>

      {/* Header */}
      <div className="pm-header">
        <div className="pm-title">
          <span className="emoji">🪴</span>
          <h1>เพิ่มและจัดการสินค้า</h1>
          <span className="subtitle">โหมดเต็ม (Full Mode) — ตัวเลือกในหน้าเดียว</span>
        </div>
        <div className="pm-links">
          <Link to="/admin/categories" className="btn btn-ghost">📂 ประเภท</Link>
          <Link to="/admin/subcategories" className="btn btn-ghost">🗂️ หมวดย่อย</Link>
          <Link to="/admin/units" className="btn btn-ghost">📏 หน่วยสินค้า</Link>
        </div>
      </div>

      {/* ฟอร์มเพิ่มสินค้า: “เหมือนเดิม” */}
      <div className="pm-panel">
        <h2 className="section-title">{isEditing ? "แก้ไขสินค้า" : "เพิ่มสินค้าใหม่"}</h2>
        <form onSubmit={onSubmit} className="pm-form" ref={formTopRef}>
          <div className="frm">
            <label>ชื่อสินค้า</label>
            <input
              name="product_name"
              value={form.product_name}
              onChange={onChange}
              placeholder="เช่น ยางอินโด / Monstera"
              ref={nameRef}
            />
          </div>

          <div className="frm">
            <label>ราคา</label>
            <input type="number" name="price" min="0" value={form.price} onChange={onChange} />
          </div>

          <div className="frm">
            <label>แหล่งที่มา</label>
            <input name="origin" value={form.origin} onChange={onChange} placeholder="เช่น เชียงใหม่" />
          </div>

          <div className="frm">
            <label>หมวดหมู่</label>
            <select name="category_id" value={form.category_id ?? ""} onChange={onChange}>
              <option value="">— เลือก —</option>
              {(lookups?.categories || []).map((c) => (
                <option key={c.category_id || c.id} value={c.category_id || c.id}>{c.category_name || c.name}</option>
              ))}
            </select>
          </div>

          <div className="frm">
            <label>หมวดย่อย</label>
            <select name="subcategory_id" value={form.subcategory_id ?? ""} onChange={onChange}>
              <option value="">— เลือก —</option>
              {(lookups?.subcategories || []).map((s) => (
                <option key={s.subcategory_id || s.id} value={s.subcategory_id || s.id}>{s.subcategory_name || s.name}</option>
              ))}
            </select>
          </div>

          <div className="frm">
            <label>หน่วยสินค้า</label>
            <select name="product_unit_id" value={form.product_unit_id ?? ""} onChange={onChange}>
              <option value="">— เลือก —</option>
              {(lookups?.units || []).map((u) => (
                <option key={u.product_unit_id || u.id} value={u.product_unit_id || u.id}>{u.product_unit_name || u.name}</option>
              ))}
            </select>
          </div>

          <div className="frm">
            <label>ขนาด (ตัวเลข)</label>
            <input name="size_value" value={form.size_value} onChange={onChange} placeholder="เช่น 20" />
          </div>

          <div className="frm">
            <label>หน่วยของขนาด</label>
            <select name="size_unit_id" value={form.size_unit_id ?? ""} onChange={onChange}>
              <option value="">— เลือก —</option>
              {(lookups?.size_units || []).map((u) => (
                <option key={u.size_unit_id || u.id} value={u.size_unit_id || u.id}>{u.size_unit_name || u.name}</option>
              ))}
            </select>
          </div>

          <div className="frm">
            <label>สถานะสินค้า</label>
            <select name="product_status_id" value={form.product_status_id ?? ""} onChange={onChange}>
              <option value="">— เลือก —</option>
              {(lookups?.product_statuses || lookups?.statuses || []).map((s) => (
                <option key={s.product_status_id || s.id} value={s.product_status_id || s.id}>{s.status_name || s.name}</option>
              ))}
            </select>
          </div>

          <div className="frm col-span-2">
            <label>คำอธิบายสินค้า</label>
            <textarea name="description" value={form.description} onChange={onChange} placeholder="รายละเอียดสินค้า" />
          </div>

          <div className="frm col-span-2">
            <label>รูปภาพสินค้า</label>
            <input type="file" multiple accept="image/*" onChange={onFiles} />
            {previews.length > 0 && (
              <div className="pm-files-preview">
                {previews.map((p, i) => (
                  <div className="pm-file" key={p.url}>
                    <img src={p.url} alt={p.name} />
                    <div className="pm-meta">
                      <div>{p.name}</div>
                      <button
                        type="button"
                        className="btn-xxs danger"
                        onClick={() => setSelectedFiles((prev) => prev.filter((_, x) => x !== i))}
                      >
                        ลบ
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="pm-actions col-span-2">
            <button className="btn btn-primary btn-lg" type="submit">
              {isEditing ? "อัปเดต" : "บันทึก"}
            </button>
            {isEditing && (
              <button type="button" className="btn btn-ghost btn-lg" onClick={clearForm}>
                ยกเลิก
              </button>
            )}
            {/* ปุ่มเพิ่มตัวเลือก: กดแล้วค่อยเปิด Panel ด้านล่าง */}
            <button
              type="button"
              className="btn btn-primary btn-lg"
              onClick={() => setShowVariantPanel(true)}
            >
              ➕ เพิ่มตัวเลือกสินค้า (สี / ขนาด / วัสดุ)
            </button>
          </div>
        </form>
      </div>

      {/* Variants Panel (ซ่อนจนกว่าจะกดปุ่ม ➕) */}
      {showVariantPanel && (
        <div className="pm-panel">
          <h2 className="section-title">ตัวเลือกสินค้า</h2>

          {/* ตัวเลือก 1-3 */}
          {[1, 2, 3].map((lvl) => {
            const nameState = lvl === 1 ? [opt1Name, setOpt1Name] : lvl === 2 ? [opt2Name, setOpt2Name] : [opt3Name, setOpt3Name];
            const valsState = lvl === 1 ? [opt1Values, setOpt1Values] : lvl === 2 ? [opt2Values, setOpt2Values] : [opt3Values, setOpt3Values];
            const [name, setName] = nameState;
            const [vals, setVals] = valsState;
            return (
              <div key={lvl} className="vm-opt">
                <label>ตัวเลือกที่ {lvl}</label>
                <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
                  <input value={name} onChange={(e) => setName(e.target.value)} placeholder="เช่น สี / ขนาด / วัสดุ" />
                  <button type="button" className="btn-xxs danger" onClick={() => removeOption(lvl)}>🗑️</button>
                </div>
                <div className="vm-chips">
                  {vals.map((v, i) => (
                    <span className="vm-chip" key={i}>
                      {v}
                      <button onClick={() => removeTag(vals, setVals, i)}>×</button>
                    </span>
                  ))}
                  <input
                    placeholder="พิมพ์แล้ว Enter"
                    onKeyDown={(e) => {
                      if (e.key === "Enter") {
                        e.preventDefault();
                        addTag(vals, setVals, e.target.value);
                        e.target.value = "";
                      }
                    }}
                  />
                </div>
              </div>
            );
          })}

          <div className="vm-actions">
            <button className="btn" onClick={resetAllOptions}>🗑️ ลบทั้งหมด</button>
            <button className="btn" onClick={autoSku}>✨ เติม SKU อัตโนมัติ</button>
            <button className="btn-primary" disabled={savingVariants} onClick={saveAllVariants}>
              {savingVariants ? "กำลังบันทึก..." : "💾 บันทึกทั้งหมด"}
            </button>
          </div>

          {/* ตาราง Variants */}
          <table className="vm-table">
            <thead>
              <tr>
                <th>รูปภาพ</th>
                <th>{opt1Name || "ตัวเลือก 1"}</th>
                <th>{opt2Name || "ตัวเลือก 2"}</th>
                <th>{opt3Name || "ตัวเลือก 3"}</th>
                <th>ราคา</th>
                <th>SKU</th>
                <th>ลบ</th>
              </tr>
            </thead>
            <tbody>
              {variantRows.map((r, i) => (
                <tr key={i}>
                  <td>
                    {r.image ? (
                      <img src={r.image} className="vm-thumb" alt="" />
                    ) : (
                      <label className="vm-upload">
                        +
                        <input
                          type="file"
                          accept="image/*"
                          onChange={(e) => onUploadRowImage(e.target.files?.[0], i)}
                        />
                      </label>
                    )}
                  </td>
                  <td>{r.opt1 || "-"}</td>
                  <td>{r.opt2 || "-"}</td>
                  <td>{r.opt3 || "-"}</td>
                  <td>
                    <input
                      type="number"
                      placeholder="ไม่ระบุ"
                      value={r.price}
                      onChange={(e) => {
                        const v = e.target.value;
                        setVariantRows((p) => {
                          const a = [...p];
                          a[i] = { ...a[i], price: v };
                          return a;
                        });
                      }}
                    />
                  </td>
                  <td>
                    <input
                      value={r.sku}
                      onChange={(e) => {
                        const v = e.target.value;
                        setVariantRows((p) => {
                          const a = [...p];
                          a[i] = { ...a[i], sku: v };
                          return a;
                        });
                      }}
                    />
                  </td>
                  <td>
                    <button
                      type="button"
                      className="btn-xxs danger"
                      onClick={() => handleDeleteVariant(r, i)}
                    >
                      🗑️
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* ตารางสินค้าทั้งหมด */}
      <h2 className="section-title">รายการสินค้า</h2>
      <div className="pm-table-wrap">
        <table className="pm-table">
          <thead>
            <tr>
              <th>สินค้า</th>
              <th>ราคา</th>
              <th>คงเหลือ</th>
              <th>หมวดหมู่</th>
              <th>สถานะ</th>
              <th>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p) => {
              const stock = Number(p.stock_qty ?? p.stock ?? 0);
              const published = !!p.is_published;
              return (
                <tr key={p.product_id}>
                  <td><strong>{p.product_name}</strong></td>
                  <td>{Number(p.price ?? 0).toLocaleString()}</td>
                  <td>{stock}</td>
                  <td>{p.category_name || "-"}</td>
                  <td>{p.product_status_name || (published ? "แสดงอยู่" : "ซ่อนอยู่")}</td>
                  <td className="cell-actions">
                    <button
                      className={`btn ${published ? "btn-warn" : "btn-primary"}`}
                      onClick={() => togglePublish(p.product_id, published)}
                    >
                      {published ? "ไม่แสดง" : "แสดง"}
                    </button>
                    <button
                      className="btn btn-ghost"
                      onClick={() => {
                        setIsEditing(true);
                        setEditProductId(p.product_id);
                        setForm({
                          product_name: p.product_name,
                          description: p.description,
                          price: p.price,
                          category_id: p.category_id,
                          subcategory_id: p.subcategory_id,
                          product_unit_id: p.product_unit_id,
                          size_unit_id: p.size_unit_id,
                          size_value: p.size_value,
                          origin: p.origin,
                          product_status_id: p.product_status_id
                        });
                        window.scrollTo({ top: 0, behavior: "smooth" });
                      }}
                    >
                      แก้ไข
                    </button>
                    {!p.is_archived ? (
                      <button className="btn btn-danger" onClick={() => archiveProduct(p.product_id)}>
                        เก็บ
                      </button>
                    ) : (
                      <button className="btn btn-primary" onClick={() => unarchiveProduct(p.product_id)}>
                        กู้คืน
                      </button>
                    )}
                    {/* แทนปุ่ม “ตัวเลือก/ขนาด” เดิม → ใช้ปุ่มนี้เปิด Panel ด้านบน */}
                    <button
                      className="btn btn-primary"
                      onClick={() => {
                        setEditProductId(p.product_id);
                        setShowVariantPanel(true);
                        window.scrollTo({ top: 0, behavior: "smooth" });
                      }}
                    >
                      จัดการสินค้า ⏷
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* ปุ่มหลักด้านล่างสุดของหน้า — ทำงานกับสินค้าที่เลือก (editProductId) */}
      <div className="pm-footer-actions">
        <button className="btn btn-primary btn-lg" disabled={pageActionLoading} onClick={() => footerAction("publish")}>
          ✅ เผยแพร่สินค้า
        </button>
        <button className="btn btn-warn btn-lg" disabled={pageActionLoading} onClick={() => footerAction("unpublish")}>
          🚫 ปิดการขาย
        </button>
        <button className="btn btn-danger btn-lg" disabled={pageActionLoading} onClick={() => footerAction("archive")}>
          🗃️ เก็บเข้าคลัง
        </button>
        <button className="btn btn-primary btn-lg" disabled={pageActionLoading} onClick={() => footerAction("unarchive")}>
          ♻️ คืนจากคลัง
        </button>
      </div>
    </div>
  );
}
