  /* ---------- API helpers ---------- */
  const handleApiError = (err, fallback = '‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î') => {
    console.error(fallback, err?.response?.data || err?.message || err);
