  const fetchCategories = async () => {
    try {
      const { data } = await api.get(path('/categories'));
      setCategories(data || []);
    } catch (err) {
      handleApiError(err, '‚ùå ‡πÇ‡∏´‡∏•‡∏î‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
