  /* ---------- Archive / Unarchive helpers ---------- */
  async function archiveProduct(productId) {
    if (!window.confirm('‡∏¢‡πâ‡∏≤‡∏¢‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ô‡∏µ‡πâ‡πÑ‡∏õ‡∏ñ‡∏±‡∏á‡πÄ‡∏Å‡πá‡∏ö‡πÉ‡∏ä‡πà‡πÑ‡∏´‡∏°?')) return;
    try {
      await api.delete(path(`/admin/products/${productId}`));
      await fetchProducts();
    } catch (err) {
      handleApiError(err, '‚ùå ‡∏¢‡πâ‡∏≤‡∏¢‡πÑ‡∏õ‡∏ñ‡∏±‡∏á‡πÄ‡∏Å‡πá‡∏ö‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    }
  }

  async function unarchiveProduct(productId) {
    try {
      await api.patch(path(`/admin/products/${productId}/unarchive`));
      await fetchProducts();
    } catch (err) {
      handleApiError(err, '‚ùå ‡∏Å‡∏π‡πâ‡∏Ñ‡∏∑‡∏ô‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    }
  }

  /* ---------- init ---------- */
  useEffect(() => {
    (async () => {
      setLoading(true);
      await Promise.all([fetchProducts(), fetchCategories(), fetchSubcategories(), fetchStatuses()]);
      setLoading(false);
