// frontend/src/admin/Dashboard.js
import React, { useEffect, useMemo, useState } from 'react';
import { api } from '../lib/api';
import {
  Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement,
  LineElement, ArcElement, Tooltip, Legend, Title
} from 'chart.js';
import { Bar, Line, Pie } from 'react-chartjs-2';
import './Dashboard.css';

ChartJS.register(
  CategoryScale, LinearScale, BarElement, PointElement,
  LineElement, ArcElement, Tooltip, Legend, Title
);

function StatCard({ title, value, suffix }) {
  return (
    <div className="stat-card">
      <div className="stat-card-title">{title}</div>
      <div className="stat-card-value">
        {value === undefined || value === null ? '-' : value}
        {suffix ? <span className="text-base font-normal ml-1">{suffix}</span> : null}
      </div>
    </div>
  );
}

function DataTable({ columns, rows, emptyText = 'ไม่มีข้อมูล' }) {
  return (
    <div className="table-wrapper">
      <table>
        <thead>
          <tr>{columns.map(c => <th key={c.key}>{c.header}</th>)}</tr>
        </thead>
        <tbody>
          {(!rows || rows.length === 0) ? (
            <tr><td colSpan={columns.length} className="px-3 py-3 text-gray-500">{emptyText}</td></tr>
          ) : rows.map((r, idx) => (
            <tr key={idx}>
              {columns.map(c => (
                <td key={c.key}>{c.render ? c.render(r[c.key], r) : (r[c.key] ?? '')}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

const safe = (arr) => Array.isArray(arr) ? arr : [];

export default function Dashboard() {
  const [summary, setSummary] = useState({});
  const [salesByMonth, setSalesByMonth] = useState([]);
  const [ordersByStatus, setOrdersByStatus] = useState([]);
  const [customersByProvince, setCustomersByProvince] = useState([]);
  const [topCategoriesPurchased, setTopCategoriesPurchased] = useState([]);
  const [productCountByCategory, setProductCountByCategory] = useState([]);
  const [recentOrders, setRecentOrders] = useState([]);
  const [recentProducts, setRecentProducts] = useState([]);
  const [recentAddresses, setRecentAddresses] = useState([]);

  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');

  const q = useMemo(() => {
    const params = new URLSearchParams();
    if (from) params.set('from', from);
    if (to) params.set('to', to);
    const s = params.toString();
    return s ? `?${s}` : '';
  }, [from, to]);

  useEffect(() => {
    api.get(`/api/dashboard/summary${q}`).then(r => setSummary(r.data)).catch(()=>{});
    api.get(`/api/dashboard/sales-by-month${q}`).then(r => setSalesByMonth(r.data)).catch(()=>{});
    api.get(`/api/dashboard/orders-by-status${q}`).then(r => setOrdersByStatus(r.data)).catch(()=>{});
    api.get(`/api/dashboard/customers-by-province`).then(r => setCustomersByProvince(r.data)).catch(()=>{});
    api.get(`/api/dashboard/top-categories-by-purchased${q}`).then(r => setTopCategoriesPurchased(r.data)).catch(()=>{});
    api.get(`/api/dashboard/product-count-by-category`).then(r => setProductCountByCategory(r.data)).catch(()=>{});
    api.get(`/api/dashboard/recent-orders`).then(r => setRecentOrders(r.data)).catch(()=>{});
    api.get(`/api/dashboard/recent-products`).then(r => setRecentProducts(r.data)).catch(()=>{});
    api.get(`/api/dashboard/recent-addresses`).then(r => setRecentAddresses(r.data)).catch(()=>{});
  }, [q]);

  const salesByMonthData = useMemo(() => ({
    labels: safe(salesByMonth).map(x => x.month),
    datasets: [{ label: 'ยอดขายรวม (บาท)', data: safe(salesByMonth).map(x => Number(x.total || 0)) }]
  }), [salesByMonth]);

  const ordersByStatusData = useMemo(() => ({
    labels: safe(ordersByStatus).map(x => x.status_name),
    datasets: [{ label: 'จำนวนคำสั่งซื้อ', data: safe(ordersByStatus).map(x => Number(x.count || 0)) }]
  }), [ordersByStatus]);

  const customersByProvinceData = useMemo(() => {
    const top = safe(customersByProvince).slice(0, 10);
    return {
      labels: top.map(x => x.province || 'ไม่ระบุ'),
      datasets: [{ label: 'จำนวนที่อยู่', data: top.map(x => Number(x.count || 0)) }]
    };
  }, [customersByProvince]);

  const topCategoriesPurchasedData = useMemo(() => ({
    labels: safe(topCategoriesPurchased).map(x => x.category_name),
    datasets: [{ label: 'จำนวนชิ้นที่ขาย', data: safe(topCategoriesPurchased).map(x => Number(x.qty || 0)) }]
  }), [topCategoriesPurchased]);

  const productCountByCategoryData = useMemo(() => ({
    labels: safe(productCountByCategory).map(x => x.category_name),
    datasets: [{ label: 'จำนวนสินค้าในระบบ', data: safe(productCountByCategory).map(x => Number(x.products || 0)) }]
  }), [productCountByCategory]);

  return (
    <div className="dashboard-container">
      <div className="flex items-end justify-between flex-wrap gap-3">
        <h1 className="text-2xl font-bold">📊 Dashboard ภาพรวม</h1>
        <div className="date-filter">
          <div className="text-sm text-gray-600">ช่วงเวลา (ยอดขาย/คำสั่งซื้อ/Top Category)</div>
          <input type="date" value={from} onChange={e=>setFrom(e.target.value)} />
          <span>—</span>
          <input type="date" value={to} onChange={e=>setTo(e.target.value)} />
          <button onClick={()=>{setFrom('');setTo('');}}>ล้าง</button>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
        <StatCard title="สินค้า" value={summary.products}/>
        <StatCard title="ผู้ใช้งาน" value={summary.users}/>
        <StatCard title="คำสั่งซื้อ" value={summary.orders}/>
        <StatCard title="ที่อยู่จัดส่ง" value={summary.addresses}/>
        <StatCard title="ยอดขายรวม" value={summary.sales ? summary.sales.toLocaleString('th-TH') : '-'} suffix="บาท"/>
      </div>

      {/* Charts Row 1 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="chart-box">
          <div className="chart-title">ยอดขายรายเดือน</div>
          <Bar data={salesByMonthData} />
        </div>
        <div className="chart-box">
          <div className="chart-title">คำสั่งซื้อแยกตามสถานะ</div>
          <Pie data={ordersByStatusData} />
        </div>
      </div>

      {/* Charts Row 2 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="chart-box">
          <div className="chart-title">ลูกค้าอยู่จังหวัดไหนเยอะที่สุด (Top 10)</div>
          <Bar data={customersByProvinceData} />
        </div>
        <div className="chart-box">
          <div className="chart-title">ลูกค้าซื้อ “หมวดสินค้า” ไหนเยอะสุด</div>
          <Bar data={topCategoriesPurchasedData} />
        </div>
      </div>

      {/* Charts Row 3 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="chart-box">
          <div className="chart-title">ฝั่งแอดมิน: จำนวนสินค้าแยกตามหมวด</div>
          <Bar data={productCountByCategoryData} />
        </div>
        <div className="chart-box">
          <div className="chart-title">แนวโน้มจำนวนคำสั่งซื้อ (เส้น)</div>
          <Line data={{
            labels: safe(salesByMonth).map(x=>x.month),
            datasets: [{ label: 'จำนวนเดือนที่มีคำสั่งซื้อ', data: safe(salesByMonth).map(()=>1) }]
          }}/>
        </div>
      </div>

      {/* Recents */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="chart-box">
          <div className="chart-title">คำสั่งซื้อล่าสุด</div>
          <DataTable
            columns={[
              { key: 'order_id', header: 'Order' },
              { key: 'email', header: 'ลูกค้า' },
              { key: 'total_amount', header: 'ยอด (฿)', render: v => Number(v||0).toLocaleString('th-TH') },
              { key: 'status_name', header: 'สถานะ' },
              { key: 'order_date', header: 'วันที่', render: v => v ? new Date(v).toLocaleString('th-TH') : '' },
            ]}
            rows={recentOrders}
          />
        </div>
        <div className="chart-box">
          <div className="chart-title">สินค้าล่าสุด</div>
          <DataTable
            columns={[
              { key: 'product_id', header: 'ID' },
              { key: 'product_name', header: 'ชื่อสินค้า' },
              { key: 'category_name', header: 'หมวด' },
              { key: 'created_at', header: 'เพิ่มเมื่อ', render: v => v ? new Date(v).toLocaleString('th-TH') : '' },
            ]}
            rows={recentProducts}
          />
        </div>
        <div className="chart-box">
          <div className="chart-title">ที่อยู่ล่าสุด</div>
          <DataTable
            columns={[
              { key: 'address_id', header: 'ID' },
              { key: 'email', header: 'ของผู้ใช้' },
              { key: 'recipient_name', header: 'ผู้รับ' },
              { key: 'province', header: 'จังหวัด' },
              { key: 'created_at', header: 'เพิ่มเมื่อ', render: v => v ? new Date(v).toLocaleString('th-TH') : '' },
            ]}
            rows={recentAddresses}
          />
        </div>
      </div>
    </div>
  );
}
