          setSaveBack(false);
          setSaveAsNew(true); // ‡πÄ‡∏ú‡∏∑‡πà‡∏≠‡∏≠‡∏¢‡∏≤‡∏Å‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡πÄ‡∏•‡∏¢
        }
      } catch (e) {
        console.warn('Load /api/addresses failed:', e);
        setSelectedId('new');
      } finally {
        setLoadingAddr(false);
      }
    })();
  }, []);

  const onPickAddr = (val) => {
    setSelectedId(val);
