// src/admin/VariantsManager.js
// โหมดใช้งานง่าย:
// - ช่องออปชั่นในฟอร์มใช้ชื่อ "ข้อมูลอื่นๆ N" (toggle สลับชื่อจริงได้)
// - พิมพ์ค่าเองได้ + มี suggestion; ถ้าไม่มีในระบบจะสร้างให้อัตโนมัติ
// - values ไม่บังคับ → บันทึก Variant ได้แม้เว้นว่าง

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

export default function VariantsManager() {
  const { productId } = useParams();

  const [loading, setLoading] = useState(true);
  const [busy, setBusy]       = useState(false);
  const [error, setError]     = useState('');
  const [msg, setMsg]         = useState('');

  const [data, setData] = useState({ product: null, options: [], variants: [] });

  // UI: โชว์ชื่อทั่วไป “ข้อมูลอื่นๆ N” เป็นค่าเริ่มต้น + ซ่อนรายการออปชั่นไว้ก่อน
  const [useGeneric, setUseGeneric] = useState(true);
  const [showOptions, setShowOptions] = useState(false);

  // ฟอร์ม
  const [draft, setDraft] = useState({
    sku: '',
    price_override: '',
    stock: '',
    values: {}, // { [option_id]: value_id }
    texts : {}, // { [option_id]: typed text }
  });

  const boom = (e, fallback = 'เกิดข้อผิดพลาด') => {
    const m = e?.response?.data?.error || e?.message || fallback;
    setError(m);
    console.error(fallback, e);
  };

  async function load() {
    setLoading(true); setError(''); setMsg('');
    try {
      const res = await axios.get(`/api/admin/products/${productId}/variants`);
      const variants = res.data.variants ?? res.data.skus ?? [];
      setData({ product: res.data.product, options: res.data.options || [], variants });
    } catch (e) {
      boom(e, 'โหลดข้อมูลไม่สำเร็จ');
    } finally {
      setLoading(false);
    }
  }
  useEffect(() => { load(); }, [productId]);

  // เลือก/สร้างค่า จากข้อความที่พิมพ์
  async function chooseOrCreateValue(option, typedText) {
    const text = (typedText || '').trim();
    const option_id = option.option_id;

    setDraft(s => ({ ...s, texts: { ...s.texts, [option_id]: text } }));

    if (!text) {
      setDraft(s => ({ ...s, values: { ...s.values, [option_id]: '' } }));
      return;
    }

    // เจอค่าที่มีอยู่แล้ว (ไม่สนตัวพิมพ์)
    const exists = (option.values || []).find(
      v => (v.value_name || '').toLowerCase() === text.toLowerCase()
    );
    if (exists) {
      setDraft(s => ({ ...s, values: { ...s.values, [option_id]: exists.value_id } }));
      return;
    }

    // ไม่เจอ → สร้างใหม่
    try {
      setBusy(true); setError(''); setMsg('');
      const { data } = await axios.post(`/api/admin/options/${option_id}/values`, { value_name: text });
      await load(); // อัปเดต suggestion
      setDraft(s => ({
        ...s,
        values: { ...s.values, [option_id]: data.value_id },
        texts : { ...s.texts , [option_id]: text }
      }));
      setMsg(`เพิ่มค่าใหม่: "${text}"`);
    } catch (e) {
      boom(e, 'เพิ่มค่าใหม่ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  // บันทึก Variant (values ว่างได้)
  async function createVariant() {
    setError(''); setMsg('');
    const values = Object.entries(draft.values)
      .map(([oid, vid]) => ({ option_id: Number(oid), value_id: Number(vid) }))
      .filter(v => !!v.value_id);

    const body = {
      sku: draft.sku || null,
      price_override: draft.price_override === '' ? null : Number(draft.price_override || 0),
      stock: Number(draft.stock || 0),
      values
    };

    setBusy(true);
    try {
      await axios.post(`/api/admin/products/${productId}/variants`, body);
      setDraft({ sku: '', price_override: '', stock: '', values: {}, texts: {} });
      await load();
      setMsg('บันทึก Variant สำเร็จ');
    } catch (e) {
      boom(e, 'บันทึก Variant ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  async function updateVariant(variant_id, patch) {
    setBusy(true); setError(''); setMsg('');
    try {
      await axios.put(`/api/admin/variants/${variant_id}`, patch);
      await load();
      setMsg('อัปเดต Variant สำเร็จ');
    } catch (e) {
      boom(e, 'อัปเดต Variant ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  async function deleteVariant(variant_id) {
    if (!window.confirm('ลบ Variant นี้?')) return;
    setBusy(true); setError(''); setMsg('');
    try {
      await axios.delete(`/api/admin/variants/${variant_id}`);
      await load();
      setMsg('ลบ Variant สำเร็จ');
    } catch (e) {
      boom(e, 'ลบ Variant ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  if (loading) return <div style={{ padding: 16 }}>กำลังโหลด...</div>;

  // ชื่อที่จะแสดง แต่ละ option (generic หรือชื่อจริง)
  const displayName = (opt, idx) => useGeneric ? `ข้อมูลอื่นๆ ${idx + 1}` : (opt.option_name || `ข้อมูลอื่นๆ ${idx + 1}`);

  return (
    <div style={{ padding: 16, display: 'grid', gap: 16 }}>
      {/* Header + toggle */}
      <div style={{ display:'flex', alignItems:'center', gap:10 }}>
        <h2 style={{ margin:0 }}>
          ตัวเลือก/ขนาด — {data.product?.product_name} (#{data.product?.product_id})
        </h2>
        <label style={{marginLeft:12, fontSize:13}}>
          <input
            type="checkbox"
            checked={useGeneric}
            onChange={e=>setUseGeneric(e.target.checked)}
            style={{marginRight:6}}
          />
          แสดงชื่อทั่วไป (ข้อมูลอื่นๆ 1/2/3)
        </label>
        <button
          style={{marginLeft:12}}
          onClick={()=>setShowOptions(s=>!s)}
        >
          {showOptions ? 'ซ่อนรายการออปชั่น' : 'แสดงรายการออปชั่น'}
        </button>
        <Link to="/" style={{ marginLeft:'auto' }}>← กลับหน้าแรก</Link>
      </div>

      {(error || msg) && (
        <div style={{ display:'flex', gap: 12 }}>
          {error && <div style={{ color:'#fff', background:'#d33', padding:'8px 12px', borderRadius:8 }}>{error}</div>}
          {msg   && <div style={{ color:'#fff', background:'green', padding:'8px 12px', borderRadius:8 }}>{msg}</div>}
        </div>
      )}

      {/* ฟอร์มเพิ่ม/อัปเดต Variant — โฟกัสตัวนี้เป็นหลัก */}
      <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
        <h3>เพิ่ม/อัปเดต Variant</h3>

        <div style={{display:'grid', gap:10, maxWidth:560}}>
          <input
            placeholder="SKU (ถ้ามี)"
            value={draft.sku}
            onChange={e=>setDraft(s=>({ ...s, sku: e.target.value }))}
            disabled={busy}
          />
        </div>

        <div style={{ marginTop:12, display:'grid', gap:12 }}>
          {data.options.map((opt, idx) => (
            <div key={opt.option_id} style={{ display:'grid', gap:6 }}>
              <label style={{fontWeight:600}}>
                {displayName(opt, idx)}
                {useGeneric && opt.option_name ? <span style={{color:'#999'}}> ({opt.option_name})</span> : null}
              </label>
              <input
                list={`opt-${opt.option_id}-list`}
                placeholder="พิมพ์ค่าใหม่ หรือเลือกจากลิสต์ แล้วกด Enter"
                value={draft.texts[opt.option_id] ?? ''}
                onChange={e=>setDraft(s=>({ ...s, texts: { ...s.texts, [opt.option_id]: e.target.value } }))}
                onBlur={e=>chooseOrCreateValue(opt, e.target.value)}
                onKeyDown={e=>{ if (e.key === 'Enter') { e.preventDefault(); chooseOrCreateValue(opt, e.currentTarget.value); } }}
                disabled={busy}
              />
              <datalist id={`opt-${opt.option_id}-list`}>
                {(opt.values || []).map(v => <option key={v.value_id} value={v.value_name} />)}
              </datalist>
              <small style={{ color:'#666' }}>
                ปล่อยว่างได้ • ถ้าพิมพ์ค่าใหม่ ระบบจะสร้างให้อัตโนมัติเมื่อกด Enter/คลิกออก
              </small>
            </div>
          ))}
        </div>

        <div style={{ display:'flex', gap:8, marginTop:12 }}>
          <input
            type="number" placeholder="ราคาเฉพาะ Variant (เว้นว่าง = ใช้ราคาหลัก)"
            value={draft.price_override}
            onChange={e=>setDraft(s=>({ ...s, price_override: e.target.value }))}
            disabled={busy}
            style={{ maxWidth:260 }}
          />
          <input
            type="number" placeholder="สต็อก"
            value={draft.stock}
            onChange={e=>setDraft(s=>({ ...s, stock: e.target.value }))}
            disabled={busy}
            style={{ maxWidth:140 }}
          />
          <button onClick={createVariant} disabled={busy}>บันทึก Variant</button>
        </div>
      </section>

      {/* รายการออปชั่นดั้งเดิม (ซ่อนไว้ก่อน) */}
      {showOptions && (
        <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
          <h3>รายการออปชั่น (จัดการค่า/ลบออปชั่นได้)</h3>
          <div style={{ display:'grid', gap:14 }}>
            {data.options.map((opt, idx) => (
              <div key={opt.option_id} style={{ borderTop:'1px dashed #ddd', paddingTop:10 }}>
                <div style={{display:'flex', alignItems:'center', gap:8}}>
                  <strong>{displayName(opt, idx)}</strong>
                  {useGeneric && opt.option_name ? <span style={{color:'#999'}}>({opt.option_name})</span> : null}
                  <button
                    onClick={async ()=>{
                      if (!window.confirm('ลบออปชั่นนี้ทั้งหมด?')) return;
                      try { setBusy(true); await axios.delete(`/api/admin/options/${opt.option_id}`); await load(); }
                      catch(e){ boom(e,'ลบออปชั่นไม่สำเร็จ'); }
                      finally{ setBusy(false); }
                    }}
                    disabled={busy}
                    style={{marginLeft:'auto'}}
                  >
                    ลบออปชั่น
                  </button>
                </div>
                <div style={{ display:'flex', gap:8, flexWrap:'wrap', marginTop:8 }}>
                  {(opt.values || []).length
                    ? opt.values.map(v => (
                        <span key={v.value_id} style={{ background:'#f5f5f5', padding:'6px 10px', borderRadius:8 }}>
                          {v.value_name}
                          <button
                            onClick={async ()=>{
                              try { setBusy(true); await axios.delete(`/api/admin/values/${v.value_id}`); await load(); }
                              catch(e){ boom(e,'ลบค่าไม่สำเร็จ'); }
                              finally{ setBusy(false); }
                            }}
                            disabled={busy}
                            style={{ marginLeft:8 }}
                          >
                            x
                          </button>
                        </span>
                      ))
                    : <span style={{color:'#777'}}>— ยังไม่มีค่า —</span>}
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* รายการ Variant */}
      <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
        <h3>รายการ Variants</h3>
        <table width="100%" cellPadding="8" style={{ borderCollapse:'collapse' }}>
          <thead>
            <tr style={{ background:'#fafafa' }}>
              <th align="left">#</th>
              <th align="left">SKU</th>
              <th align="left">คอมโบ</th>
              <th align="right">ราคาเฉพาะ</th>
              <th align="right">สต็อก</th>
              <th>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {data.variants.map((s, idx) => (
              <tr key={s.variant_id} style={{ borderTop:'1px solid #eee' }}>
                <td>{idx + 1}</td>
                <td>{s.sku || <em>(auto)</em>}</td>
                <td>
                  {(s.combos || []).length
                    ? s.combos.map((c, i) => {
                        const iOpt = data.options.findIndex(o => o.option_id === c.option_id);
                        return `${displayName({option_name:c.option_name}, iOpt)}: ${c.value_name}`;
                      }).join(' ・ ')
                    : <em>-</em>}
                </td>
                <td align="right">
                  <InlineNumber
                    value={s.price_override ?? ''}
                    placeholder="-"
                    onSave={v => updateVariant(s.variant_id, { price_override: v === '' ? '' : Number(v) })}
                    disabled={busy}
                  />
                </td>
                <td align="right">
                  <InlineNumber
                    value={s.stock ?? 0}
                    onSave={v => updateVariant(s.variant_id, { stock: Number(v || 0) })}
                    disabled={busy}
                  />
                </td>
                <td>
                  <button onClick={()=>deleteVariant(s.variant_id)} disabled={busy}>ลบ</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
}

function InlineNumber({ value, onSave, disabled, placeholder }) {
  const [v, setV] = useState(value ?? '');
  useEffect(() => setV(value ?? ''), [value]);
  return (
    <span>
      <input
        type="number"
        value={v}
        placeholder={placeholder}
        onChange={e=>setV(e.target.value)}
        style={{ width:110 }}
        disabled={disabled}
      />
      <button onClick={()=>onSave(v)} style={{ marginLeft:6 }} disabled={disabled}>บันทึก</button>
    </span>
  );
}
