sourcesContent:
// src/admin/VariantsManager.js
// ======================================================================
// จัดการ Variants (โฉมใหม่: โล่งตา ใช้งานง่าย เข้ากับระบบเดิม)
// - ส่วนบน: เพิ่ม Variant รายตัว (อัปโหลด "หลายรูปจากไฟล์" เท่านั้น, ไม่มีช่อง URL)
// - กลาง: สร้างคอมบิเนชันทั้งหมด (SKU prefix/ราคา/สต็อกเริ่มต้น)
// - ล่าง: ตาราง Variants แก้ไขอินไลน์ + อัปโหลด/ลบรูปต่อแถวได้
// - ใช้ป้าย "รายละเอียดที่ 1/2/..." ในการเลือกค่า (ดึงค่าจาก options เดิมของระบบ)
// - ตัดส่วน "จัดการรายละเอียด (Options/Values)" ออกเพื่อความเรียบง่าย
// ======================================================================

import React, { useEffect, useMemo, useRef, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

const MAX_VARIANT_IMAGES = 9; // แนว Shopee

// JWT จาก localStorage
const authHeader = () => {
  const t = localStorage.getItem("token");
  return t ? { Authorization: `Bearer ${t}` } : {};
};

// อัปโหลดไฟล์เดี่ยว -> URL
async function tryUpload(file) {
  const fd = new FormData();
  fd.append("file", file);
  const { data } = await axios.post("/api/uploads", fd, {
    headers: { ...authHeader(), "Content-Type": "multipart/form-data" },
  });
  if (typeof data === "string") return data;
  return data?.url || data?.Location || data?.path || null;
}

// อัปโหลดหลายไฟล์ -> URLs
async function uploadMany(files) {
  const out = [];
  for (const f of files) {
    const url = await tryUpload(f);
    if (url) out.push(url);
  }
  return out;
}

export default function VariantsManager() {
  const params = useParams();
  const productId = Number(params.productId || params.id);

  const [loading, setLoading] = useState(true);
  const [busy, setBusy] = useState(false);
  const [error, setError] = useState("");
  const [msg, setMsg] = useState("");

  // ข้อมูลสินค้า/Options/Variants
  const [product, setProduct] = useState(null);
  const [options, setOptions] = useState([]); // [{option_id, option_name, values:[{value_id,value_name}]}]
  const [variants, setVariants] = useState([]);

  // ฟอร์มเพิ่ม Variant
  const [draft, setDraft] = useState({
    sku: "",
    price: "",
    stock_qty: "",
    images: [], // หลายรูป
    values: {},
    textInputs: {},
  });

  // Generate combos
  const [skuPrefix, setSkuPrefix] = useState(`P${productId}`);
  const [genPrice, setGenPrice] = useState(0);
  const [genStock, setGenStock] = useState(0);

  const addFileRef = useRef(null);

  const boom = (e, fallback = "เกิดข้อผิดพลาด") => {
    setError(
      e?.response?.data?.message || e?.response?.data?.error || e?.message || fallback
    );
    console.error(fallback, e);
  };

  // ---------- โหลดข้อมูล ----------
  async function load() {
    setLoading(true); setError(""); setMsg("");
    try {
      const p = await axios.get(`/api/products/${productId}`).catch(() => ({ data: null }));
      setProduct(p?.data || null);

      const [{ data: opts }, { data: vars }] = await Promise.all([
        axios.get(`/api/variants/products/${productId}/options`),
        axios.get(`/api/variants/product/${productId}?active=1`),
      ]);
      setOptions(opts || []);
      setVariants(vars || []);
    } catch (e) { boom(e, "โหลดข้อมูลไม่สำเร็จ"); }
    finally { setLoading(false); }
  }

  useEffect(() => { load(); /* eslint-disable-next-line */ }, [productId]);

  // ---------- Helpers: ป้าย/คอมโบ ----------
  const optIndexById = useMemo(() => {
    const m = new Map();
    options.forEach((o, i) => m.set(o.option_id, i));
    return m;
  }, [options]);

  const valNameById = useMemo(() => {
    const m = new Map();
    options.forEach((o) => (o.values || []).forEach((v) => m.set(v.value_id, v.value_name)));
    return m;
  }, [options]);

  const formLabel = (_opt, idx) => `รายละเอียดที่ ${idx + 1}`;

  const comboText = (row) => {
    const list = row.combo || row.combos || [];
    if (!list.length) return "—";
    return list
      .map((c) => {
        const idx = optIndexById.get(c.option_id) ?? 0;
        return `รายละเอียดที่ ${idx + 1}: ${valNameById.get(c.value_id) ?? c.value_id}`;
      })
      .join(" ・ ");
  };

  // ---------- Values UX: พิมพ์แล้วสร้างอัตโนมัติ ----------
  async function chooseOrCreateValue(option, typed) {
    const text = (typed || "").trim();
    const oid = option.option_id;
    setDraft((s) => ({ ...s, textInputs: { ...s.textInputs, [oid]: text } }));

    if (!text) { setDraft((s) => ({ ...s, values: { ...s.values, [oid]: "" } })); return; }

    const exists = (option.values || []).find((v) => (v.value_name || "").toLowerCase() === text.toLowerCase());
    if (exists) { setDraft((s) => ({ ...s, values: { ...s.values, [oid]: exists.value_id } })); return; }

    // ไม่มี -> สร้างใหม่ แล้ว map ให้ทันที
    try {
      await axios.post(`/api/variants/options/${oid}/values`, { value_name: text }, { headers: authHeader() });
      await load();
      const found = [...valNameById.entries()].find(([, name]) => (name || "").toLowerCase() === text.toLowerCase());
      if (found) {
        const value_id = found[0];
        setDraft((s) => ({ ...s, values: { ...s.values, [oid]: value_id }, textInputs: { ...s.textInputs, [oid]: text } }));
      }
    } catch (e) { boom(e, "เพิ่มค่าใหม่ไม่สำเร็จ"); }
  }

  // ---------- Actions: Variants ----------
  async function createVariant() {
    setBusy(true); setError(""); setMsg("");
    try {
      const option_values = Object.entries(draft.values)
        .map(([oid, vid]) => ({ option_id: Number(oid), value_id: Number(vid) }))
        .filter((v) => v.value_id);

      await axios.post(`/api/variants/products/${productId}/variants`, {
        sku: (draft.sku || "").trim() || null,
        price: draft.price === "" ? 0 : Math.max(0, Number(draft.price || 0)),
        stock_qty: Math.max(0, Number(draft.stock_qty || 0)),
        image_url: draft.images[0] || null, // รูปหลัก
        images: draft.images,               // แกลเลอรี (หาก backend รองรับ)
        is_active: true,
        option_values,
      }, { headers: authHeader() });

      setDraft({ sku: "", price: "", stock_qty: "", images: [], values: {}, textInputs: {} });
      await load();
      setMsg("บันทึก Variant สำเร็จ");
    } catch (e) { boom(e, "บันทึก Variant ไม่สำเร็จ"); }
    finally { setBusy(false); }
  }

  async function updateVariant(variant_id, patch) {
    setBusy(true); setError(""); setMsg("");
    try {
      const body = {};
      if ("price" in patch) body.price = patch.price === "" ? null : Math.max(0, Number(patch.price || 0));
      if ("stock_qty" in patch) body.stock_qty = Math.max(0, Number(patch.stock_qty || 0));
      if ("sku" in patch) body.sku = patch.sku;
      if ("is_active" in patch) body.is_active = !!patch.is_active;
      if ("images" in patch) { body.image_url = patch.images?.[0] || null; body.images = patch.images; }

      await axios.put(`/api/variants/${variant_id}`, body, { headers: authHeader() });
      await load();
      setMsg("อัปเดต Variant สำเร็จ");
    } catch (e) { boom(e, "อัปเดต Variant ไม่สำเร็จ"); }
    finally { setBusy(false); }
  }

  async function deleteVariant(variant_id) {
    if (!window.confirm("ลบ Variant นี้?")) return;
    setBusy(true);
    try {
      await axios.delete(`/api/variants/${variant_id}`, { headers: authHeader() });
      await load();
      setMsg("ลบ Variant สำเร็จ");
    } catch (e) { boom(e, "ลบ Variant ไม่สำเร็จ"); }
    finally { setBusy(false); }
  }

  async function generateCombos() {
    setBusy(true); setError(""); setMsg("");
    try {
      await axios.post(`/api/variants/products/${productId}/variants/generate`, {
        sku_prefix: (skuPrefix || "").trim() || `P${productId}`,
        price: Math.max(0, Number(genPrice || 0)),
        stock_qty: Math.max(0, Number(genStock || 0)),
      }, { headers: authHeader() });
      await load();
      setMsg("สร้างคอมบิเนชันสำเร็จ");
    } catch (e) { boom(e, "สร้างคอมบิเนชันไม่สำเร็จ"); }
    finally { setBusy(false); }
  }

  if (loading) return <div style={{ padding: 16 }}>กำลังโหลด...</div>;

  return (
    <div style={{ padding: 16, display: "grid", gap: 16 }}>
      {/* Header */}
      <div style={{ display: "flex", alignItems: "center", gap: 10, flexWrap: "wrap" }}>
        <h2 style={{ margin: 0 }}>
          ตัวเลือก/Variants — {product?.product_name || product?.name || product?.title || `สินค้า #${productId}`}
        </h2>
        <Link to="/admin" style={{ marginLeft: "auto" }}>← กลับแอดมิน</Link>
      </div>

      {(error || msg) && (
        <div style={{ display: "flex", gap: 12, flexWrap: "wrap" }}>
          {error && <div style={{ color: "#fff", background: "#d33", padding: "8px 12px", borderRadius: 8 }}>{error}</div>}
          {msg && <div style={{ color: "#fff", background: "green", padding: "8px 12px", borderRadius: 8 }}>{msg}</div>}
        </div>
      )}

      {/* เพิ่ม Variant รายตัว */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>เพิ่มแบบ/ตัวเลือกสินค้า (Variant)</h3>

        <div style={{ display: "grid", gap: 10, maxWidth: 820 }}>
          <input
            placeholder="SKU (เช่น P30-PCH-500G)"
            value={draft.sku}
            onChange={(e) => setDraft((s) => ({ ...s, sku: e.target.value }))}
            disabled={busy}
          />

          {/* อัปโหลดหลายรูปจากไฟล์ */}
          <div style={{ display: "grid", gap: 6 }}>
            <label style={{ fontWeight: 600 }}>รูปภาพของแบบ/ตัวเลือก (สูงสุด {MAX_VARIANT_IMAGES} รูป)</label>
            <div style={{ display: "flex", gap: 8, alignItems: "center", flexWrap: "wrap" }}>
              <button type="button" onClick={() => addFileRef.current?.click()} disabled={busy}>เลือกไฟล์…</button>
              <input type="file" accept="image/*" ref={addFileRef} multiple style={{ display: "none" }}
                onChange={async (e) => {
                  const files = Array.from(e.target.files || []);
                  if (!files.length) return;
                  setBusy(true);
                  try {
                    const remain = Math.max(0, MAX_VARIANT_IMAGES - draft.images.length);
                    const slice = files.slice(0, remain);
                    const urls = await uploadMany(slice);
                    setDraft((s) => ({ ...s, images: [...s.images, ...urls] }));
                    setMsg("อัปโหลดรูปสำเร็จ");
                  } catch (err) { boom(err, "อัปโหลดรูปไม่สำเร็จ"); }
                  finally { setBusy(false); e.target.value = ""; }
                }} />
            </div>

            {draft.images.length > 0 && (
              <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(96px, 1fr))", gap: 8, marginTop: 8 }}>
                {draft.images.map((u, i) => (
                  <div key={u + i} style={{ position: "relative" }}>
                    <img src={u} alt="preview" style={{ width: "100%", aspectRatio: "1/1", objectFit: "cover", borderRadius: 8, border: "1px solid #eee" }} onError={(e) => (e.currentTarget.style.display = "none")} />
                    <button type="button" onClick={() => setDraft((s) => ({ ...s, images: s.images.filter((x) => x !== u) }))} style={{ position: "absolute", top: 4, right: 4 }}>✕</button>
                    {i === 0 && <div style={{ position: "absolute", left: 6, bottom: 6, background: "#0008", color: "#fff", fontSize: 12, padding: "2px 6px", borderRadius: 6 }}>รูปหลัก</div>}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* เลือกค่าตาม "รายละเอียดที่ N" */}
        <div style={{ marginTop: 12, display: "grid", gap: 12 }}>
          {options.map((opt, idx) => (
            <div key={opt.option_id} style={{ display: "grid", gap: 6 }}>
              <label style={{ fontWeight: 600 }}>{formLabel(opt, idx)}</label>
              <input
                list={`opt-${opt.option_id}-list`}
                placeholder="พิมพ์ค่า (เช่น แบบซอง / 500 กรัม / สีแดง) หรือเลือกจากลิสต์ แล้วกด Enter/คลิกออก"
                value={draft.textInputs[opt.option_id] ?? ""}
                onChange={(e) => setDraft((s) => ({ ...s, textInputs: { ...s.textInputs, [opt.option_id]: e.target.value } }))}
                onBlur={(e) => chooseOrCreateValue(opt, e.target.value)}
                onKeyDown={(e) => { if (e.key === "Enter") { e.preventDefault(); chooseOrCreateValue(opt, e.currentTarget.value); } }}
                disabled={busy}
              />
              <datalist id={`opt-${opt.option_id}-list`}>
                {(opt.values || []).map((v) => (<option key={v.value_id} value={v.value_name} />))}
              </datalist>
              <small style={{ color: "#666" }}>ปล่อยว่างได้ • ถ้าพิมพ์ค่าใหม่ ระบบจะสร้างให้อัตโนมัติ</small>
            </div>
          ))}
        </div>

        <div style={{ display: "flex", gap: 8, marginTop: 12, flexWrap: "wrap" }}>
          <input type="number" min={0} placeholder="ราคาเฉพาะแบบ/ตัวเลือก" value={draft.price} onChange={(e) => setDraft((s) => ({ ...s, price: e.target.value }))} disabled={busy} style={{ maxWidth: 220 }} />
          <input type="number" min={0} placeholder="สต็อก" value={draft.stock_qty} onChange={(e) => setDraft((s) => ({ ...s, stock_qty: e.target.value }))} disabled={busy} style={{ maxWidth: 140 }} />
          <button onClick={createVariant} disabled={busy} style={{ fontWeight: 700 }}>บันทึกแบบ/ตัวเลือกนี้</button>
        </div>
      </section>

      {/* Generate ทุกคอมโบ */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>สร้างแบบ/ตัวเลือกทั้งหมด (ทุกคอมบิเนชันที่มี)</h3>
        <div style={{ display: "flex", gap: 8, alignItems: "center", flexWrap: "wrap" }}>
          <label>SKU prefix</label>
          <input value={skuPrefix} onChange={(e) => setSkuPrefix(e.target.value)} disabled={busy} />
          <label>ราคาเริ่มต้น</label>
          <input type="number" min={0} value={genPrice} onChange={(e) => setGenPrice(e.target.value)} disabled={busy} style={{ maxWidth: 160 }} />
          <label>สต็อกเริ่มต้น</label>
          <input type="number" min={0} value={genStock} onChange={(e) => setGenStock(e.target.value)} disabled={busy} style={{ maxWidth: 160 }} />
          <button onClick={generateCombos} disabled={busy}>สร้างคอมบิเนชัน</button>
        </div>
        <div style={{ color: "#6b6b6b", marginTop: 6, fontSize: 14 }}>ระบบจะข้ามรายการที่ซ้ำ (แนะนำให้มี unique อย่างน้อยที่ SKU หรือ (product_id, sku))</div>
      </section>

      {/* ตาราง Variants */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>รายการแบบ/ตัวเลือก (Variants)</h3>
        <VariantsTable rows={variants} comboText={comboText} onSave={(id, patch) => updateVariant(id, patch)} onDelete={(id) => deleteVariant(id)} busy={busy} />
      </section>
    </div>
  );
}

function VariantsTable({ rows, comboText, onSave, onDelete, busy }) {
  return (
    <table width="100%" cellPadding="8" style={{ borderCollapse: "collapse" }}>
      <thead>
        <tr style={{ background: "#fafafa" }}>
          <th align="left">#</th>
          <th align="left">รูป</th>
          <th align="left">SKU</th>
          <th align="left">คอมโบ</th>
          <th align="right">ราคา</th>
          <th align="right">สต็อก</th>
          <th>สถานะ</th>
          <th>จัดการ</th>
        </tr>
      </thead>
      <tbody>
        {rows.length === 0 && (
          <tr><td colSpan={8} align="center"><em>ยังไม่มี Variant</em></td></tr>
        )}
        {rows.map((r, i) => (
          <VariantRow key={r.variant_id} idx={i + 1} row={r} comboText={comboText} onSave={onSave} onDelete={onDelete} busy={busy} />
        ))}
      </tbody>
    </table>
  );
}

function VariantRow({ idx, row, comboText, onSave, onDelete, busy }) {
  const [sku, setSku] = useState(row.sku || "");
  const [price, setPrice] = useState(row.final_price ?? row.price ?? "");
  const [stock, setStock] = useState(row.stock ?? row.stock_qty ?? 0);
  const [active, setActive] = useState(row.is_active ?? true);
  const [images, setImages] = useState(row.images || row.image_urls || (row.image_url ? [row.image_url] : []));
  const fileRef = useRef(null);

  return (
    <tr style={{ borderTop: "1px solid #eee" }}>
      <td>{idx}</td>
      <td>
        <div style={{ display: "flex", gap: 6, alignItems: "center", flexWrap: "wrap" }}>
          {images[0] ? (
            <img src={images[0]} alt="" style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 6, border: "1px solid #eee" }} onError={(e) => (e.currentTarget.style.display = "none")} />
          ) : (
            <div style={{ width: 48, height: 48, border: "1px dashed #ccc", borderRadius: 6 }} />
          )}
          <div style={{ display: "flex", gap: 4, flexWrap: "wrap" }}>
            {images.slice(1, 5).map((u, j) => (
              <img key={u + j} src={u} alt="thumb" style={{ width: 28, height: 28, objectFit: "cover", borderRadius: 4, border: "1px solid #eee" }} onError={(e) => (e.currentTarget.style.display = "none")} />
            ))}
          </div>
          <input type="file" accept="image/*" ref={fileRef} multiple style={{ display: "none" }}
            onChange={async (e) => {
              const files = Array.from(e.target.files || []);
              if (!files.length) return;
              try {
                const remain = Math.max(0, MAX_VARIANT_IMAGES - images.length);
                const slice = files.slice(0, remain);
                const urls = await uploadMany(slice);
                setImages((prev) => [...prev, ...urls]);
              } finally { e.target.value = ""; }
            }}
          />
          <button type="button" onClick={() => fileRef.current?.click()} disabled={busy}>อัปโหลดรูปเพิ่ม…</button>
        </div>
      </td>
      <td>
        <input value={sku} onChange={(e) => setSku(e.target.value)} style={{ width: 180 }} />
      </td>
      <td>{comboText(row)}</td>
      <td align="right"><input type="number" min={0} value={price} onChange={(e) => setPrice(e.target.value)} style={{ width: 120, textAlign: "right" }} /></td>
      <td align="right"><input type="number" min={0} value={stock} onChange={(e) => setStock(e.target.value)} style={{ width: 100, textAlign: "right" }} /></td>
      <td>
        <label style={{ fontSize: 13 }}>
          <input type="checkbox" checked={!!active} onChange={(e) => setActive(e.target.checked)} /> แสดง
        </label>
      </td>
      <td>
        <button onClick={() => onSave(row.variant_id, { sku, price, stock_qty: stock, is_active: active, images })} disabled={busy} style={{ fontWeight: 700 }}>บันทึกแถวนี้</button>
        <button onClick={() => onDelete(row.variant_id)} disabled={busy} style={{ marginLeft: 8 }}>ลบ</button>
      </td>
    </tr>
  );
}
