// FRONTEND: src/pages/SignUp.js
// ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å ‚Üí POST /api/auth/register
// - ‡πÉ‡∏ä‡πâ ThaiAddressPicker (‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏±‡πâ‡∏á‡∏õ‡∏£‡∏∞‡πÄ‡∏ó‡∏®‡πÑ‡∏ó‡∏¢)
// - ‡∏°‡∏µ "‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô" (‡∏û‡∏¥‡∏°‡∏û‡πå 2 ‡∏Ñ‡∏£‡∏±‡πâ‡∏á) ‡πÅ‡∏•‡∏∞‡∏ï‡∏£‡∏ß‡∏à‡πÅ‡∏ö‡∏ö‡πÄ‡∏£‡∏µ‡∏¢‡∏•‡πÑ‡∏ó‡∏°‡πå
// - ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡πÄ‡∏™‡∏£‡πá‡∏à: ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ token -> login ‡∏≠‡∏±‡∏ï‡πÇ‡∏ô‡∏°‡∏±‡∏ï‡∏¥, ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏°‡∏µ -> ‡∏¢‡∏¥‡∏á /api/auth/login ‡∏ï‡πà‡∏≠

import React, { useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ThaiAddressPicker from '../components/ThaiAddressPicker'; // ‚Üê ‡∏õ‡∏£‡∏±‡∏ö path ‡∏ñ‡πâ‡∏≤‡∏´‡∏ô‡πâ‡∏≤‡∏≠‡∏¢‡∏π‡πà‡∏Ñ‡∏ô‡∏•‡∏∞‡πÇ‡∏ü‡∏•‡πÄ‡∏î‡∏≠‡∏£‡πå
import './SignUp.css';

export default function SignUp() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    identifier: '',     // ‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏à‡∏£‡∏¥‡∏á
    password: '',
    confirm: '',        // ‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏û‡∏≤‡∏™
    fullname: '',
    phone: '',
    houseNo: '',
    province: '',
    amphoe: '',
    tambon: '',
    zipcode: '',
    gender: '',
  });

  const [showPw, setShowPw] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError]     = useState('');

  // ===== helpers =====
  const isEmail   = (s) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(String(s).trim());
  const pwMatch   = useMemo(() => form.password && form.password === form.confirm, [form.password, form.confirm]);
  const splitName = (fullname) => {
    const parts = String(fullname || '').trim().split(/\s+/);
    if (!parts.length) return { first_name: '', last_name: '' };
    if (parts.length === 1) return { first_name: parts[0], last_name: '' };
    return { first_name: parts[0], last_name: parts.slice(1).join(' ') };
  };
  const makeUsername = (email) => {
    const raw = String(email).split('@')[0] || '';
    const cleaned = raw.toLowerCase().replace(/[^a-z0-9_.-]/g, '');
    return cleaned || `cust${Date.now().toString().slice(-6)}`;
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm(p => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    // ----- validate ‡∏ù‡∏±‡πà‡∏á‡∏´‡∏ô‡πâ‡∏≤‡πÄ‡∏ß‡πá‡∏ö -----
    const required = [
      'identifier','password','confirm','fullname','phone',
      'houseNo','province','amphoe','tambon','gender'
    ];
    if (required.some(k => !String(form[k]).trim())) {
      setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô'); return;
    }
    if (!isEmail(form.identifier)) {
      setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á'); return;
    }
    if (form.password.length < 8) {
      setError('‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡∏ï‡πâ‡∏≠‡∏á‡∏°‡∏µ‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£'); return;
    }
    if (!pwMatch) {
      setError('‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô'); return;
    }

    const { first_name, last_name } = splitName(form.fullname);
    const address = `${form.houseNo} ‡∏ï.${form.tambon} ‡∏≠.${form.amphoe} ‡∏à.${form.province} ${form.zipcode}`.trim();

    // payload ‡πÉ‡∏´‡πâ‡∏ï‡∏£‡∏á backend (/api/auth/register)
    const payload = {
      first_name,
      last_name,
      email: form.identifier.trim(),
      username: makeUsername(form.identifier),
      password: form.password,
      confirm_password: form.confirm,
      phone_number: form.phone.trim(),
      address,
    };

    try {
      setLoading(true);

      // 1) ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å
      const res = await axios.post('/api/auth/register', payload);

      // 2) ‡πÉ‡∏ä‡πâ token ‡∏ñ‡πâ‡∏≤ backend ‡∏™‡πà‡∏á‡∏°‡∏≤‡πÄ‡∏•‡∏¢
      let token = res?.data?.token || res?.data?.accessToken || res?.data?.jwt;
      let role  = res?.data?.user?.role || 'customer';

      // 3) ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏™‡πà‡∏á token -> login ‡∏î‡πâ‡∏ß‡∏¢ email/pw ‡∏ó‡∏µ‡πà‡πÄ‡∏û‡∏¥‡πà‡∏á‡∏™‡∏°‡∏±‡∏Ñ‡∏£
      if (!token) {
        const loginRes = await axios.post('/api/auth/login', {
          email: form.identifier.trim(),
          password: form.password,
        });
        token = loginRes?.data?.token || loginRes?.data?.accessToken || loginRes?.data?.jwt;
        role  = loginRes?.data?.user?.role || role;
      }

      // 4) ‡πÄ‡∏Å‡πá‡∏ö token/role ‡πÅ‡∏•‡πâ‡∏ß‡πÅ‡∏ô‡∏ö‡πÉ‡∏´‡πâ axios
      if (token) {
        localStorage.setItem('token', token);
        localStorage.setItem('role', role || 'customer');
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      }

      // 5) ‡πÄ‡∏î‡πâ‡∏á‡∏Å‡∏•‡∏±‡∏ö‡∏´‡∏ô‡πâ‡∏≤‡πÅ‡∏£‡∏Å
      navigate('/', { replace: true });
    } catch (err) {
      const msg =
        err?.response?.status === 409 ? (err.response.data?.error || '‡∏≠‡∏µ‡πÄ‡∏°‡∏•/‡∏ä‡∏∑‡πà‡∏≠‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ ‡∏ñ‡∏π‡∏Å‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô‡πÅ‡∏•‡πâ‡∏ß')
      : err?.response?.data?.error || err.message || '‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à';
      setError(msg);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="signup-page">
      {/* ‡πÄ‡∏Æ‡∏î + ‡∏ä‡πà‡∏≠‡∏á‡πÄ‡∏ß‡πâ‡∏≤‡πÇ‡∏•‡πÇ‡∏Å‡πâ */}
      <div className="signup-hero">
        <div className="notch">
          <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="PRACH MAEJO" />
        </div>
      </div>

      {/* Brand + ‡∏´‡∏±‡∏ß‡∏Ç‡πâ‡∏≠ */}
      <div className="brand-head">
        <div className="brand-line">
          <span className="brand">PRACH MAEJO</span>
          <span className="online">ONLINE</span>
        </div>
        <h2>‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏±‡∏ç‡∏ä‡∏µ / ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å</h2>
      </div>

      <form className="signup-form" onSubmit={onSubmit} noValidate>
        {/* ‡∏≠‡∏µ‡πÄ‡∏°‡∏• */}
        <label className="field col">
          <span className="label">‡∏≠‡∏µ‡πÄ‡∏°‡∏•</span>
          <input
            type="email"
            name="identifier"
            placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô‡πÑ‡∏î‡πâ‡∏à‡∏£‡∏¥‡∏á"
            value={form.identifier}
            onChange={onChange}
          />
        </label>

        {/* ‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô */}
        <label className="field col">
          <span className="label">‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô</span>
          <div className="pw-wrap">
            <input
              type={showPw ? 'text' : 'password'}
              name="password"
              placeholder="‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£"
              value={form.password}
              onChange={onChange}
            />
            <button type="button" className="pw-toggle" onClick={() => setShowPw(s => !s)}>
              {showPw ? 'üôà' : 'üëÅÔ∏è'}
            </button>
          </div>
        </label>

        {/* ‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô */}
        <label className="field col">
          <span className="label">‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô</span>
          <input
            type={showPw ? 'text' : 'password'}
            name="confirm"
            placeholder="‡∏û‡∏¥‡∏°‡∏û‡πå‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÄ‡∏î‡∏¥‡∏°‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á"
            value={form.confirm}
            onChange={onChange}
            className={form.confirm && !pwMatch ? 'is-invalid' : ''}
          />
          {!pwMatch && form.confirm ? <small className="hint-error">‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô</small> : null}
        </label>

        {/* ‡∏ä‡∏∑‡πà‡∏≠-‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏• + ‡πÄ‡∏ö‡∏≠‡∏£‡πå */}
        <label className="field col">
          <span className="label">‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•</span>
          <input name="fullname" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•" value={form.fullname} onChange={onChange} />
        </label>
        <label className="field col">
          <span className="label">‡πÄ‡∏ö‡∏≠‡∏£‡πå‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠</span>
          <input type="tel" name="phone" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£‡∏ó‡∏µ‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠‡πÑ‡∏î‡πâ" value={form.phone} onChange={onChange} />
        </label>

        {/* ‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà: ‡πÉ‡∏ä‡πâ ThaiAddressPicker ‡∏Ñ‡∏£‡∏≠‡∏ö‡∏Ñ‡∏•‡∏∏‡∏°‡∏ó‡∏±‡πâ‡∏á‡∏õ‡∏£‡∏∞‡πÄ‡∏ó‡∏® */}
        <div className="col span-2-left">
          <span className="label">‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà</span>
          <div className="addr-grid">
            <input
              type="text"
              name="houseNo"
              placeholder="‡∏ö‡πâ‡∏≤‡∏ô‡πÄ‡∏•‡∏Ç‡∏ó‡∏µ‡πà / ‡∏´‡∏°‡∏π‡πà / ‡∏≠‡∏≤‡∏Ñ‡∏≤‡∏£ (‡∏ñ‡πâ‡∏≤‡∏°‡∏µ)"
              value={form.houseNo}
              onChange={onChange}
            />

            <ThaiAddressPicker
              province={form.province}
              amphoe={form.amphoe}
              tambon={form.tambon}
              zipcode={form.zipcode}
              onChange={(addr) => {
                // ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö‡∏ä‡∏∑‡πà‡∏≠‡∏û‡∏£‡πá‡∏≠‡∏û‡∏´‡∏•‡∏≤‡∏¢‡πÅ‡∏ö‡∏ö‡∏à‡∏≤‡∏Å‡∏Ñ‡∏≠‡∏°‡πÇ‡∏û‡πÄ‡∏ô‡∏ô‡∏ï‡πå‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì
                setForm(p => ({
                  ...p,
                  province: addr.province || '',
                  amphoe:   addr.amphoe || addr.district || '',
                  tambon:   addr.tambon || addr.subdistrict || '',
                  zipcode:  addr.zipcode || ''
                }));
              }}
            />
          </div>
        </div>

        {/* ‡πÄ‡∏û‡∏® */}
        <div className="right-group col">
          <div className="field">
            <span className="label">‡πÄ‡∏û‡∏®</span>
            <div className="radios">
              <label><input type="radio" name="gender" value="‡∏ä‡∏≤‡∏¢"  checked={form.gender==='‡∏ä‡∏≤‡∏¢'}  onChange={onChange}/> ‡∏ä‡∏≤‡∏¢</label>
              <label><input type="radio" name="gender" value="‡∏´‡∏ç‡∏¥‡∏á" checked={form.gender==='‡∏´‡∏ç‡∏¥‡∏á'} onChange={onChange}/> ‡∏´‡∏ç‡∏¥‡∏á</label>
              <label><input type="radio" name="gender" value="‡∏≠‡∏∑‡πà‡∏ô‡πÜ" checked={form.gender==='‡∏≠‡∏∑‡πà‡∏ô‡πÜ'} onChange={onChange}/> ‡∏≠‡∏∑‡πà‡∏ô‡πÜ</label>
            </div>
          </div>
        </div>

        {error && <div className="form-error" role="alert">{error}</div>}

        <div className="actions span-2">
          <button className="btn-primary" type="submit" disabled={loading || (form.confirm && !pwMatch)}>
            {loading ? '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å‚Ä¶' : '‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å'}
          </button>
        </div>
      </form>
    </div>
  );
}
