// - ใช้ ThaiAddressPicker (ข้อมูลทั้งประเทศไทย)
// - มี "ยืนยันรหัสผ่าน" (พิมพ์ 2 ครั้ง) และตรวจแบบเรียลไทม์
// - สมัครเสร็จ: ถ้ามี token -> login อัตโนมัติ, ถ้าไม่มี -> ยิง /api/auth/login ต่อ

import React, { useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ThaiAddressPicker from '../components/ThaiAddressPicker'; // ← ปรับ path ถ้าหน้าอยู่คนละโฟลเดอร์
import './SignUp.css';

export default function SignUp() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    identifier: '',     // อีเมลจริง
    password: '',
    confirm: '',        // ยืนยันพาส
    fullname: '',
    phone: '',
    houseNo: '',
    province: '',
    amphoe: '',
    tambon: '',
    zipcode: '',
    gender: '',
  });

  const [showPw, setShowPw] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError]     = useState('');

  // ===== helpers =====
  const isEmail   = (s) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(String(s).trim());
  const pwMatch   = useMemo(() => form.password && form.password === form.confirm, [form.password, form.confirm]);
  const splitName = (fullname) => {
    const parts = String(fullname || '').trim().split(/\s+/);
    if (!parts.length) return { first_name: '', last_name: '' };
    if (parts.length === 1) return { first_name: parts[0], last_name: '' };
    return { first_name: parts[0], last_name: parts.slice(1).join(' ') };
  };
  const makeUsername = (email) => {
    const raw = String(email).split('@')[0] || '';
    const cleaned = raw.toLowerCase().replace(/[^a-z0-9_.-]/g, '');
    return cleaned || `cust${Date.now().toString().slice(-6)}`;
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm(p => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    // ----- validate ฝั่งหน้าเว็บ -----
    const required = [
      'identifier','password','confirm','fullname','phone',
      'houseNo','province','amphoe','tambon','gender'
    ];
    if (required.some(k => !String(form[k]).trim())) {
      setError('กรุณากรอกข้อมูลให้ครบถ้วน'); return;
    }
    if (!isEmail(form.identifier)) {
      setError('กรุณากรอกอีเมลให้ถูกต้อง'); return;
    }
    if (form.password.length < 8) {
      setError('รหัสผ่านต้องมีอย่างน้อย 8 ตัวอักษร'); return;
    }
    if (!pwMatch) {
      setError('ยืนยันรหัสผ่านไม่ตรงกัน'); return;
    }

    const { first_name, last_name } = splitName(form.fullname);
    const address = `${form.houseNo} ต.${form.tambon} อ.${form.amphoe} จ.${form.province} ${form.zipcode}`.trim();

    // payload ให้ตรง backend (/api/auth/register)
    const payload = {
      first_name,
      last_name,
      email: form.identifier.trim(),
      username: makeUsername(form.identifier),
      password: form.password,
      confirm_password: form.confirm,
      phone_number: form.phone.trim(),
      address,
    };

    try {
      setLoading(true);

      // 1) สมัครสมาชิก
      const res = await axios.post('/api/auth/register', payload);

      // 2) ใช้ token ถ้า backend ส่งมาเลย
      let token = res?.data?.token || res?.data?.accessToken || res?.data?.jwt;
      let role  = res?.data?.user?.role || 'customer';

      // 3) ถ้าไม่ส่ง token -> login ด้วย email/pw ที่เพิ่งสมัคร
      if (!token) {
        const loginRes = await axios.post('/api/auth/login', {
          email: form.identifier.trim(),
          password: form.password,
        });
        token = loginRes?.data?.token || loginRes?.data?.accessToken || loginRes?.data?.jwt;
        role  = loginRes?.data?.user?.role || role;
      }

      // 4) เก็บ token/role แล้วแนบให้ axios
      if (token) {
        localStorage.setItem('token', token);
        localStorage.setItem('role', role || 'customer');
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      }

      // 5) เด้งกลับหน้าแรก
      navigate('/', { replace: true });
    } catch (err) {
      const msg =
        err?.response?.status === 409 ? (err.response.data?.error || 'อีเมล/ชื่อผู้ใช้ ถูกใช้งานแล้ว')
      : err?.response?.data?.error || err.message || 'สมัครสมาชิกไม่สำเร็จ';
      setError(msg);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="signup-page">
      {/* เฮด + ช่องเว้าโลโก้ */}
      <div className="signup-hero">
        <div className="notch">
          <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="PRACH MAEJO" />
        </div>
      </div>

      {/* Brand + หัวข้อ */}
      <div className="brand-head">
        <div className="brand-line">
          <span className="brand">PRACH MAEJO</span>
          <span className="online">ONLINE</span>
        </div>
        <h2>สร้างบัญชี / สมัครสมาชิก</h2>
      </div>

      <form className="signup-form" onSubmit={onSubmit} noValidate>
        {/* อีเมล */}
        <label className="field col">
          <span className="label">อีเมล</span>
          <input
            type="email"
            name="identifier"
            placeholder="กรอกอีเมลที่ใช้งานได้จริง"
            value={form.identifier}
            onChange={onChange}
          />
        </label>

        {/* รหัสผ่าน */}
        <label className="field col">
          <span className="label">รหัสผ่าน</span>
          <div className="pw-wrap">
            <input
              type={showPw ? 'text' : 'password'}
              name="password"
              placeholder="อย่างน้อย 8 ตัวอักษร"
              value={form.password}
              onChange={onChange}
            />
            <button type="button" className="pw-toggle" onClick={() => setShowPw(s => !s)}>
              {showPw ? '🙈' : '👁️'}
            </button>
          </div>
        </label>

        {/* ยืนยันรหัสผ่าน */}
        <label className="field col">
          <span className="label">ยืนยันรหัสผ่าน</span>
          <input
            type={showPw ? 'text' : 'password'}
            name="confirm"
            placeholder="พิมพ์รหัสผ่านเดิมอีกครั้ง"
            value={form.confirm}
            onChange={onChange}
            className={form.confirm && !pwMatch ? 'is-invalid' : ''}
          />
          {!pwMatch && form.confirm ? <small className="hint-error">รหัสผ่านไม่ตรงกัน</small> : null}
        </label>

        {/* ชื่อ-นามสกุล + เบอร์ */}
        <label className="field col">
          <span className="label">ชื่อ - นามสกุล</span>
          <input name="fullname" placeholder="กรอกชื่อ - นามสกุล" value={form.fullname} onChange={onChange} />
        </label>
        <label className="field col">
          <span className="label">เบอร์ติดต่อ</span>
          <input type="tel" name="phone" placeholder="กรอกเบอร์โทรที่สามารถติดต่อได้" value={form.phone} onChange={onChange} />
        </label>

        {/* ที่อยู่: ใช้ ThaiAddressPicker ครอบคลุมทั้งประเทศ */}
        <div className="col span-2-left">
          <span className="label">ที่อยู่</span>
          <div className="addr-grid">
            <input
              type="text"
              name="houseNo"
              placeholder="บ้านเลขที่ / หมู่ / อาคาร (ถ้ามี)"
              value={form.houseNo}
              onChange={onChange}
            />

            <ThaiAddressPicker
              province={form.province}
              amphoe={form.amphoe}
              tambon={form.tambon}
              zipcode={form.zipcode}
              onChange={(addr) => {
                // รองรับชื่อพร็อพหลายแบบจากคอมโพเนนต์ของคุณ
                setForm(p => ({
                  ...p,
                  province: addr.province || '',
                  amphoe:   addr.amphoe || addr.district || '',
                  tambon:   addr.tambon || addr.subdistrict || '',
                  zipcode:  addr.zipcode || ''
                }));
              }}
            />
          </div>
        </div>

        {/* เพศ */}
        <div className="right-group col">
          <div className="field">
            <span className="label">เพศ</span>
            <div className="radios">
              <label><input type="radio" name="gender" value="ชาย"  checked={form.gender==='ชาย'}  onChange={onChange}/> ชาย</label>
              <label><input type="radio" name="gender" value="หญิง" checked={form.gender==='หญิง'} onChange={onChange}/> หญิง</label>
              <label><input type="radio" name="gender" value="อื่นๆ" checked={form.gender==='อื่นๆ'} onChange={onChange}/> อื่นๆ</label>
            </div>
          </div>
        </div>

        {error && <div className="form-error" role="alert">{error}</div>}

        <div className="actions span-2">
          <button className="btn-primary" type="submit" disabled={loading || (form.confirm && !pwMatch)}>
            {loading ? 'กำลังสมัครสมาชิก…' : 'สมัครสมาชิก'}
          </button>
        </div>
      </form>
    </div>
  );
}
