// src/admin/AdminUnits.js
// ✅ หน่วยสินค้า (product_units) — โหลดจากฐานข้อมูลเท่านั้น
// Endpoints ใช้จริง:
//   GET    /api/categories?published=1
//   GET    /api/units               (list)
//   POST   /api/admin/units         (create)
//   PUT    /api/admin/units/:code   (update)
//   DELETE /api/admin/units/:code   (delete)

import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import './admin-units.css';                 // ⬅️ ใช้ไฟล์สไตล์ใหม่
import { api, path } from '../lib/api';

const asStr = (v) => (v === null || v === undefined) ? '' : String(v).trim();
const toArr = (x) => Array.isArray(x) ? x : (x ? [x] : []);
const uniq = (xs) => Array.from(new Set(xs));

/* ---------- Toggle Button Group (multi) ---------- */
function MultiToggle({ options, value = [], onChange }) {
  const set = new Set((value || []).map(String));
  const toggle = (id) => {
    const s = new Set(set);
    const key = String(id);
    if (s.has(key)) s.delete(key); else s.add(key);
    onChange(Array.from(s));
  };
  return (
    <div className="mu-wrap">
      {options.map(opt => {
        const id = String(opt.value);
        const active = set.has(id);
        return (
          <button
            type="button"
            key={id}
            className={`mu-chip ${active ? 'is-active' : ''}`}
            onClick={() => toggle(id)}
            aria-pressed={active}
            title={`${opt.label}`}
          >
            {opt.label}
          </button>
        );
      })}
    </div>
  );
}

export default function AdminUnits() {
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]); // [{value,label}]
  const [units, setUnits] = useState([]);
  const [filter, setFilter] = useState('');
  const [form, setForm] = useState({
    code: '',
    unit_name: '',
    description: '',
    category_ids: [], // array of TEXT ids
  });
  const [editingCode, setEditingCode] = useState(null);

  /* ---------- load categories + units ---------- */
  useEffect(() => {
    (async () => {
      setLoading(true);
      try {
        const catRes = await api.get(path('/api/categories?published=1'));
        const cats = (catRes.data || []).map(c => ({
          value: c.category_id ?? c.id ?? c.code,
          label: `${c.category_id ?? c.id ?? c.code} — ${c.category_name ?? c.name}`,
        }));
        setCategories(cats);

        const unitRes = await api.get(path('/api/units'));
        setUnits(unitRes.data || []);
      } catch (e) {
        console.error(e);
        alert('โหลดข้อมูลไม่สำเร็จ');
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const rows = useMemo(() => {
    const q = asStr(filter).toLowerCase();
    return (units || []).filter(u =>
      !q ||
      asStr(u.code).toLowerCase().includes(q) ||
      asStr(u.unit_name).toLowerCase().includes(q)
    );
  }, [units, filter]);

  const setField = (k, v) => setForm(prev => ({ ...prev, [k]: v }));

  /* ---------- map category ids from record ---------- */
  function extractCategoryIds(u) {
    if (Array.isArray(u?.category_ids) && u.category_ids.length) {
      return u.category_ids.map(String);
    }
    if (Array.isArray(u?.categories) && u.categories.length) {
      return u.categories.map(c => String(c.category_id ?? c.id ?? c.code));
    }
    if (u?.category_id) return [String(u.category_id)];
    return [];
  }

  function startEdit(u) {
    setEditingCode(String(u.code));
    setForm({
      code: String(u.code),
      unit_name: asStr(u.unit_name),
      description: asStr(u.description),
      category_ids: extractCategoryIds(u),
    });
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }

  async function doSubmit(e) {
    e?.preventDefault?.();
    const payload = {
      code: asStr(form.code),
      unit_name: asStr(form.unit_name),
      description: asStr(form.description) || null,
      // ส่งเป็น array ของ TEXT
      category_ids: uniq(toArr(form.category_ids).map(String)),
    };
    if (!payload.code || !payload.unit_name) {
      alert('กรอก code และ unit name ให้ครบ');
      return;
    }
    setLoading(true);
    try {
      if (editingCode) {
        await api.put(path(`/api/admin/units/${encodeURIComponent(editingCode)}`), payload);
      } else {
        await api.post(path('/api/admin/units'), payload);
      }
      const unitRes = await api.get(path('/api/units'));
      setUnits(unitRes.data || []);
      // รีเซ็ตฟอร์ม
      setEditingCode(null);
      setForm({ code: '', unit_name: '', description: '', category_ids: [] });
    } catch (e2) {
      console.error(e2);
      alert('บันทึกไม่สำเร็จ');
    } finally {
      setLoading(false);
    }
  }

  async function onDelete(code) {
    if (!window.confirm(`ลบหน่วย ${code}?`)) return;
    setLoading(true);
    try {
      await api.delete(path(`/api/admin/units/${encodeURIComponent(code)}`));
      const unitRes = await api.get(path('/api/units'));
      setUnits(unitRes.data || []);
    } catch (e) {
      console.error(e);
      alert('ลบไม่สำเร็จ');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="page">
      <p><Link to="/admin">← กลับไปจัดการสินค้า</Link></p>
      <h2>จัดการหน่วยสินค้า (จากฐานข้อมูล)</h2>

      <form className="card" onSubmit={doSubmit} autoComplete="off">
        <div className="form-row">
          <label>code (เช่น: piece, pot) [a-z]</label>
          <input
            value={form.code}
            onChange={e => setField('code', e.target.value)}
            placeholder="เช่น pot, bag, kg"
            disabled={!!editingCode}
          />
        </div>

        <div className="form-row">
          <label>unit name (เช่น: ชิ้น, กระถาง)</label>
          <input
            value={form.unit_name}
            onChange={e => setField('unit_name', e.target.value)}
            placeholder="ชื่อหน่วย"
          />
        </div>

        <div className="form-row">
          <label>หมวด (กดเลือกได้หลายค่า)</label>
          <MultiToggle
            options={categories}
            value={form.category_ids}
            onChange={(ids) => setField('category_ids', ids)}
          />
          <small className="hint">กดปุ่มเพื่อเลือก/ยกเลิก (หลายค่าได้)</small>
        </div>

        <div className="form-row">
          <label>description (ถ้ามี)</label>
          <textarea
            value={form.description}
            onChange={e => setField('description', e.target.value)}
            placeholder="บันทึกเพิ่มเติม"
            rows={2}
          />
        </div>

        <div className="actions">
          <button type="submit" className="btn-primary" disabled={loading}>
            {editingCode ? 'บันทึกการแก้ไข' : 'เพิ่มหน่วยสินค้า'}
          </button>
          {editingCode && (
            <button
              type="button"
              className="btn-secondary"
              onClick={() => { setEditingCode(null); setForm({ code: '', unit_name: '', description: '', category_ids: [] }); }}
              disabled={loading}
            >
              ยกเลิกแก้ไข
            </button>
          )}
        </div>
      </form>

      <div className="toolbar">
        <input
          className="search"
          placeholder="ค้นหา code หรือชื่อหน่วย"
          value={filter}
          onChange={e => setFilter(e.target.value)}
        />
      </div>

      <div className="table card">
        <table>
          <thead>
            <tr>
              <th style={{width:140}}>code</th>
              <th>unit_name</th>
              <th>description</th>
              <th style={{width:220}}>category_id (หลายค่า)</th>
              <th style={{width:120}}>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {rows.map(u => {
              const ids = extractCategoryIds(u);
              return (
                <tr key={u.code}>
                  <td>{u.code}</td>
                  <td>{u.unit_name}</td>
                  <td>{u.description}</td>
                  <td>
                    {ids.length ? ids.join(', ') : <span className="muted">-</span>}
                  </td>
                  <td>
                    <button className="btn-link" onClick={() => startEdit(u)}>แก้ไข</button>
                    <button className="btn-danger" onClick={() => onDelete(u.code)}>ลบ</button>
                  </td>
                </tr>
              );
            })}
            {!rows.length && (
              <tr><td colSpan={5} className="muted">ไม่มีข้อมูล</td></tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
