// FRONTEND: src/pages/SignUp.js
import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css';

export default function SignUp() {
  const nav = useNavigate();

  // ===== ‡∏ü‡∏≠‡∏£‡πå‡∏° =====
  const [form, setForm] = useState({
    email: '',
    password: '',
    confirm: '',
    full_name: '',
    phone: '',
    houseNo: '',
    province: '',
    amphoe: '',
    tambon: '',
    gender: '',
  });
  const [showPw, setShowPw] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // ===== ‡πÇ‡∏´‡∏•‡∏î‡∏ê‡∏≤‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• =====
  const [db, setDb] = useState(null);
  const [addrLoadErr, setAddrLoadErr] = useState('');

  useEffect(() => {
    (async () => {
      try {
        const url = `${process.env.PUBLIC_URL}/data/thai-address.min.json`;
        const res = await fetch(url);
        const json = await res.json();
        setDb(json);
      } catch (e) {
        console.error(e);
        setAddrLoadErr('‡πÇ‡∏´‡∏•‡∏î‡∏ê‡∏≤‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
      }
    })();
  }, []);

  // ===== util: ‡πÅ‡∏õ‡∏•‡∏á‡πÄ‡∏õ‡πá‡∏ô array ‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏≠‡πà‡∏≠‡∏ô‡πÇ‡∏¢‡∏ô =====
  const toList = (v) => {
    if (!v) return [];
    if (Array.isArray(v)) return v;
    if (typeof v === 'object') return Object.keys(v);
    return [];
  };
  const getZip = (entry) => {
    if (!entry) return '';
    if (typeof entry === 'string') return entry;
    if (typeof entry === 'object') return entry.zip || entry.zipcode || entry.postcode || '';
    return '';
  };

  // ===== options ‡∏Ç‡∏≠‡∏á select =====
  const provinces = useMemo(() => {
    if (!db) return [];
    // ‡πÇ‡∏Ñ‡∏£‡∏á‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ô‡∏µ‡πâ provinces ‡∏°‡∏±‡∏Å‡πÄ‡∏õ‡πá‡∏ô array ‡∏Ç‡∏≠‡∏á‡∏ä‡∏∑‡πà‡∏≠‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î
    return toList(db.provinces);
  }, [db]);

  const amphoes = useMemo(() => {
    if (!db || !form.province) return [];
    // districts[‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î] ‚Üí array ‡∏´‡∏£‡∏∑‡∏≠ object ‡∏Å‡πá‡πÑ‡∏î‡πâ
    return toList(db.districts?.[form.province]);
  }, [db, form.province]);

  const tambons = useMemo(() => {
    if (!db || !form.province || !form.amphoe) return [];
    // subdistricts[‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î][‡∏≠‡∏≥‡πÄ‡∏†‡∏≠] ‚Üí array|object
    return toList(db.subdistricts?.[form.province]?.[form.amphoe]);
  }, [db, form.province, form.amphoe]);

  // ZIP ‡∏≠‡∏±‡∏ï‡πÇ‡∏ô‡∏°‡∏±‡∏ï‡∏¥
  const zipcode = useMemo(() => {
    if (!db || !form.province || !form.amphoe || !form.tambon) return '';
    const entry = db.subdistricts?.[form.province]?.[form.amphoe]?.[form.tambon];
    return getZip(entry);
  }, [db, form.province, form.amphoe, form.tambon]);

  // ===== handlers =====
  const isEmail = (v) => /\S+@\S+\.\S+/.test(String(v || '').trim());
  const pwMismatch = form.password && form.confirm && form.password !== form.confirm;

  const splitName = (full) => {
    const p = String(full || '').trim().split(/\s+/);
    if (p.length <= 1) return { first_name: p[0] || '', last_name: '' };
    return { first_name: p[0], last_name: p.slice(1).join(' ') };
  };
  const makeUsername = (email) => {
    const raw = String(email).split('@')[0] || '';
    const cleaned = raw.toLowerCase().replace(/[^a-z0-9_.-]/g, '');
    return cleaned || `cust${Date.now().toString().slice(-6)}`;
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    if (name === 'province') return setForm(p => ({ ...p, province: value, amphoe: '', tambon: '' }));
    if (name === 'amphoe')   return setForm(p => ({ ...p, amphoe: value, tambon: '' }));
    setForm(p => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    if (!isEmail(form.email))      return setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á');
    if (form.password.length < 8)  return setError('‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£');
    if (pwMismatch)                return setError('‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô');

    const required = ['full_name','phone','houseNo','province','amphoe','tambon'];
    if (required.some(k => !String(form[k]).trim())) return setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô');

    const { first_name, last_name } = splitName(form.full_name);
    const address = `${form.houseNo} ‡∏ï.${form.tambon} ‡∏≠.${form.amphoe} ‡∏à.${form.province} ${zipcode || ''}`;

    const payload = {
      first_name,
      last_name,
      email: form.email.trim(),
      username: makeUsername(form.email),
      password: form.password,
      confirm_password: form.confirm,
      phone_number: form.phone.trim(),
      address,
    };

    try {
      setLoading(true);
      const res = await axios.post('/auth/register', payload);

      let token = res?.data?.token || res?.data?.accessToken || res?.data?.jwt;
      if (!token) {
        const login = await axios.post('/auth/login', { email: form.email, password: form.password });
        token = login?.data?.token || login?.data?.accessToken || login?.data?.jwt;
      }
      if (token) {
        localStorage.setItem('token', token);
        localStorage.setItem('role', res?.data?.user?.role || 'customer');
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      }
      nav('/', { replace: true });
    } catch (err) {
      setError(err?.response?.data?.error || '‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="signup-page">
      <div className="signup-hero">
        <div className="notch">
          <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="PRACH MAEJO" />
        </div>
      </div>

      <div className="brand-head">
        <div className="brand-line">
          <span className="brand">PRACH MAEJO</span>
          <span className="online">ONLINE</span>
        </div>
        <h2>‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏±‡∏ç‡∏ä‡∏µ / ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å</h2>
      </div>

      <form className="signup-form" onSubmit={onSubmit} noValidate>
        {/* ‡∏≠‡∏µ‡πÄ‡∏°‡∏• */}
        <label className="field col">
          <span className="label">‡∏≠‡∏µ‡πÄ‡∏°‡∏•</span>
          <input type="email" name="email" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô‡πÑ‡∏î‡πâ‡∏à‡∏£‡∏¥‡∏á" value={form.email} onChange={onChange} />
        </label>

        {/* ‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô */}
        <label className="field col">
          <span className="label">‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô</span>
          <div className="pw-wrap">
            <input
              type={showPw ? 'text' : 'password'}
              name="password"
              placeholder="‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£"
              value={form.password}
              onChange={onChange}
              className={pwMismatch ? 'is-invalid' : ''}
            />
            <button type="button" className="pw-toggle" onClick={() => setShowPw(s => !s)}>
              {showPw ? 'üôà' : 'üëÅÔ∏è'}
            </button>
          </div>
        </label>

        {/* ‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏• */}
        <label className="field col">
          <span className="label">‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•</span>
          <input type="text" name="full_name" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•" value={form.full_name} onChange={onChange} />
        </label>

        {/* ‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô */}
        <label className="field col">
          <span className="label">‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô</span>
          <input
            type={showPw ? 'text' : 'password'}
            name="confirm"
            placeholder="‡∏û‡∏¥‡∏°‡∏û‡πå‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÄ‡∏î‡∏¥‡∏°‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á"
            value={form.confirm}
            onChange={onChange}
            className={pwMismatch ? 'is-invalid' : ''}
          />
          {pwMismatch && <div className="hint-error">‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô</div>}
        </label>

        {/* ‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£ */}
        <label className="field col">
          <span className="label">‡πÄ‡∏ö‡∏≠‡∏£‡πå‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠</span>
          <input type="tel" name="phone" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£‡∏ó‡∏µ‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠‡πÑ‡∏î‡πâ" value={form.phone} onChange={onChange} />
        </label>

        {/* ‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà */}
        <div className="col span-2-left">
          <span className="label">‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà</span>
          {!!addrLoadErr && <div className="hint-error" style={{ marginBottom: 8 }}>{addrLoadErr}</div>}

          <div className="addr-grid">
            <input
              type="text"
              name="houseNo"
              placeholder="‡∏ö‡πâ‡∏≤‡∏ô‡πÄ‡∏•‡∏Ç‡∏ó‡∏µ‡πà / ‡∏´‡∏°‡∏π‡πà / ‡∏≠‡∏≤‡∏Ñ‡∏≤‡∏£ (‡∏ñ‡πâ‡∏≤‡∏°‡∏µ)"
              value={form.houseNo}
              onChange={onChange}
            />

            <select name="province" value={form.province} onChange={onChange} disabled={!db}>
              <option value="">{db ? '‚Äî ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î ‚Äî' : '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î...'}</option>
              {provinces.map(pv => <option key={pv} value={pv}>{pv}</option>)}
            </select>

            <select name="amphoe" value={form.amphoe} onChange={onChange} disabled={!form.province}>
              <option value="">{form.province ? '‚Äî ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏≠‡∏≥‡πÄ‡∏†‡∏≠ ‚Äî' : '‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î‡∏Å‡πà‡∏≠‡∏ô'}</option>
              {amphoes.map(am => <option key={am} value={am}>{am}</option>)}
            </select>

            <select name="tambon" value={form.tambon} onChange={onChange} disabled={!form.amphoe}>
              <option value="">{form.amphoe ? '‚Äî ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏ï‡∏≥‡∏ö‡∏• ‚Äî' : '‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏≠‡∏≥‡πÄ‡∏†‡∏≠‡∏Å‡πà‡∏≠‡∏ô'}</option>
              {tambons.map(tb => <option key={tb} value={tb}>{tb}</option>)}
            </select>

            <input type="text" placeholder="‡∏£‡∏´‡∏±‡∏™‡πÑ‡∏õ‡∏£‡∏©‡∏ì‡∏µ‡∏¢‡πå" value={zipcode} readOnly />
          </div>
        </div>

        {/* ‡πÄ‡∏û‡∏® */}
        <div className="right-group col">
          <div className="field">
            <span className="label">‡πÄ‡∏û‡∏®</span>
            <div className="radios">
              <label><input type="radio" name="gender" value="‡∏ä‡∏≤‡∏¢"  checked={form.gender==='‡∏ä‡∏≤‡∏¢'}  onChange={onChange}/> ‡∏ä‡∏≤‡∏¢</label>
              <label><input type="radio" name="gender" value="‡∏´‡∏ç‡∏¥‡∏á" checked={form.gender==='‡∏´‡∏ç‡∏¥‡∏á'} onChange={onChange}/> ‡∏´‡∏ç‡∏¥‡∏á</label>
              <label><input type="radio" name="gender" value="‡∏≠‡∏∑‡πà‡∏ô‡πÜ" checked={form.gender==='‡∏≠‡∏∑‡πà‡∏ô‡πÜ'} onChange={onChange}/> ‡∏≠‡∏∑‡πà‡∏ô‡πÜ</label>
            </div>
          </div>
        </div>

        {error && <div className="form-error" role="alert">{error}</div>}

        <div className="actions span-2">
          <button className="btn-primary" type="submit" disabled={loading}>
            {loading ? '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å‚Ä¶' : '‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å'}
          </button>
        </div>
      </form>
    </div>
  );
}
