// src/App.js
// หน้าที่: root ของฝั่งเว็บ จัดการเส้นทาง + การ์ดสิทธิ์พื้นฐาน (Auth/RBAC)

import React, { useEffect } from 'react';
import {
  BrowserRouter, Routes, Route, NavLink, Navigate, useNavigate
} from 'react-router-dom';
import axios from 'axios';

import ProductManagement from './admin/ProductManagement'; // หน้าเดิม: จัดการสินค้า
import OrderManagement   from './admin/OrderManagement';   // หน้าเดิม: จัดการคำสั่งซื้อ
import Login             from './auth/Login';               // หน้าเข้าสู่ระบบ
import Addresses         from './account/Addresses';        // ✅ หน้าใหม่: จัดการที่อยู่ผู้ใช้


/* สวิตช์การ์ดสิทธิ์:
   - dev ให้ false → เข้าได้ทุกหน้า
   - หลังทดสอบล็อกอินผ่าน ค่อยตั้ง true เพื่อบังคับต้องมี token */
const ENABLE_AUTH_GUARD = false;

/* อ่าน token จาก localStorage (ใช้ซ้ำหลายที่) */
const getToken = () => localStorage.getItem('token');

/* การ์ดสิทธิ์: ครอบหน้า/เส้นทางที่ต้องล็อกอิน */
function RequireAuth({ children }) {
  if (!ENABLE_AUTH_GUARD) return children;          // โหมด dev: ปล่อยผ่าน
  const token = getToken();
  if (!token) return <Navigate to="/login" replace />; // ไม่มี token → ไปล็อกอิน
  return children;
}

/* แถบนำทางด้านบน + ปุ่ม Login/Logout */
function Navbar() {
  const nav = useNavigate();

  // สไตล์เมนู (NavLink จะส่ง isActive มาให้)
  const linkStyle = ({ isActive }) => ({
    textDecoration: 'none',
    padding: '6px 10px',
    borderRadius: 8,
    background: isActive ? '#f0f0f0' : 'transparent',
  });

  const hasToken = !!getToken();

  // ออกจากระบบ: ลบ token + เคลียร์หัว Authorization ของ axios + เด้งไป /login
  function logout() {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    nav('/login', { replace: true });
  }

  return (
    <nav style={{ padding: 10, display: 'flex', gap: 16, borderBottom: '1px solid #eee' }}>
      {/* เมนูหลักฝั่งซ้าย */}
      <NavLink to="/" style={linkStyle} end>สินค้า</NavLink>
      <NavLink to="/admin/orders" style={linkStyle}>คำสั่งซื้อ</NavLink>
      <NavLink to="/account/addresses" style={linkStyle}>ที่อยู่ของฉัน</NavLink> {/* ✅ เพิ่มเมนู */}

      {/* ปุ่ม Login/Logout ชิดขวา */}
      <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
        {!hasToken ? (
          <NavLink to="/login" style={linkStyle}>เข้าสู่ระบบ</NavLink>
        ) : (
          <button onClick={logout} style={{ padding: '6px 10px', borderRadius: 8 }}>
            ออกจากระบบ
          </button>
        )}
      </div>
    </nav>
  );
}

/* แอปหลัก: ติดหัว Authorization จาก token (ถ้ามี) + กำหนดเส้นทางทั้งหมด */
export default function App() {
  // โหลดครั้งแรก: ถ้ามี token ใน localStorage → ติดหัว Authorization ให้ axios
  useEffect(() => {
    const t = getToken();
    if (t) axios.defaults.headers.common['Authorization'] = `Bearer ${t}`;
  }, []);

  return (
    <BrowserRouter>
      <Navbar />

      <Routes>
        {/* หน้าแรก: ระบบจัดการสินค้า */}
        <Route
          path="/"
          element={
            <div className="App" style={{ padding: 16 }}>
              <h1>🌿 ระบบจัดการสินค้า</h1>
              <ProductManagement />
            </div>
          }
        />

        {/* จัดการคำสั่งซื้อ (เผื่อเปิดการ์ดในอนาคตด้วย RequireAuth) */}
        <Route
          path="/admin/orders"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>📦 จัดการคำสั่งซื้อ</h1>
                <OrderManagement />
              </div>
            </RequireAuth>
          }
        />

        {/* ✅ หน้าใหม่: จัดการที่อยู่ผู้ใช้ (ใช้ร่วมกับ ThaiAddressPicker) */}
        <Route
          path="/account/addresses"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>🏠 ที่อยู่ของฉัน</h1>
                <Addresses />
              </div>
            </RequireAuth>
          }
        />

        {/* หน้าเข้าสู่ระบบ */}
        <Route path="/login" element={<Login />} />



        {/* กันหลงทาง → กลับหน้าแรก */}
        <Route path="*" element={<Navigate to="/" replace />} />
        <Route path="/register" element={<Register />} />

      </Routes>
    </BrowserRouter>
  );
}
