// src/pages/HomeUser.js
import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './HomeUser.css';
import { addItem as addToCart } from '../lib/cart';

/* -------------------- Fallback -------------------- */
const FALLBACK_CATEGORIES = [
  { id: 'PLT', slug: 'plants', name: 'ต้นไม้',            image: process.env.PUBLIC_URL + '/tonmai.jpg' },
  { id: 'TLS', slug: 'tools',  name: 'อุปกรณ์เพาะปลูก', image: process.env.PUBLIC_URL + '/opakorn.jpg' },
];

const FALLBACK_BEST = [
  { id: 1, name: 'ต้นกล้าลำใยคริสตัล', price: 250, img: process.env.PUBLIC_URL + '/lamyai.jpg' },
  { id: 2, name: 'ต้นพญานาคราช',       price: 150, img: process.env.PUBLIC_URL + '/payanaka1.jpg' },
  { id: 3, name: 'กระถางมินิมอล',      price: 300, img: process.env.PUBLIC_URL + '/katar.jpeg' },
  { id: 4, name: 'ดินสูตรทางร้าน',      price:  99, img: process.env.PUBLIC_URL + '/din.jpg' },
  { id: 5, name: 'หินภูเขาไฟปลูกไม้',   price: 129, img: process.env.PUBLIC_URL + '/logo.png' },
];

export default function HomeUser() {
  // กัน baseURL ซ้อน /api
  const apiPath = (p) =>
    (axios.defaults.baseURL || '').replace(/\/+$/, '').endsWith('/api') ? p : '/api' + p;

  const [categories, setCategories] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState({ cat: true, best: true, all: true });
  const [errMsg, setErrMsg] = useState('');

  /* -------------------- Helper: normalize product -------------------- */
  const normalizeProduct = (p, idx) => ({
    id: p.product_id ?? p.id ?? p._id ?? `p-${idx}`,
    name: p.product_name ?? p.name_th ?? p.name ?? p.title ?? 'สินค้า',
    price: Number(
      p.selling_price ?? p.price ?? p.sale_price ?? p.amount ?? p.product_price ?? 0
    ),
    img:
      p.image_url ||
      p.cover_url ||
      p.main_image_url ||
      p.image ||
      p.thumbnail ||
      (Array.isArray(p.images) ? p.images[0] : '') ||
      process.env.PUBLIC_URL + '/logo.png',
  });

  /* -------------------- โหลดหมวดหมู่ (เพื่อใช้ตอน fallback รวมสินค้า) -------------------- */
  useEffect(() => {
    (async () => {
      const normalizeCat = (c, idx) => {
        const cid = c.category_id ?? c.id ?? c.code ?? c.slug ?? `cat-${idx}`;
        const rawName =
          (c.category_name ?? c.name_th ?? c.name ?? c.title ?? '').toString().trim() || 'ไม่ระบุชื่อ';
        const guessSlug =
          c.slug ??
          (rawName.includes('อุปกรณ์') ? 'tools' :
           rawName.includes('ต้นไม้')   ? 'plants' : undefined) ??
          String(cid).toLowerCase();

        return {
          id: String(cid),
          code: (c.code ?? '').toString().toUpperCase(),
          slug: guessSlug,
          name: rawName,
          image:
            c.image_url ||
            c.image ||
            c.cover ||
            c.thumbnail ||
            (guessSlug === 'tools'
              ? process.env.PUBLIC_URL + '/opakorn.jpg'
              : process.env.PUBLIC_URL + '/tonmai.jpg'),
        };
      };

      async function loadFromLookups() {
        try {
          const r = await axios.get(apiPath('/lookups?published=1'));
          const data = r.data || {};
          const list =
            Array.isArray(data.product_categories)
              ? data.product_categories
              : Array.isArray(data.categories)
              ? data.categories
              : [];
          if (!list.length) return null;
          return list.map(normalizeCat);
        } catch {
          return null;
        }
      }

      async function loadFromCategoriesApi() {
        try {
          const r = await axios.get(apiPath('/categories?status=active'));
          const arr = (Array.isArray(r.data?.data) ? r.data.data : r.data) || [];
          if (!arr.length) return null;
          return arr.map(normalizeCat);
        } catch {
          return null;
        }
      }

      try {
        setLoading((s) => ({ ...s, cat: true }));
        let cats = (await loadFromLookups()) || (await loadFromCategoriesApi()) || [];
        const priorityOrder = ['PLT', 'PLANTS', 'plants', 'TLS', 'TOOLS', 'tools'];
        cats.sort((a, b) => {
          const ai = priorityOrder.findIndex((k) =>
            [a.code?.toUpperCase(), a.slug?.toLowerCase()].includes(String(k).toLowerCase())
          );
          const bi = priorityOrder.findIndex((k) =>
            [b.code?.toUpperCase(), b.slug?.toLowerCase()].includes(String(k).toLowerCase())
          );
          return (ai === -1 ? 999 : ai) - (bi === -1 ? 999 : bi);
        });
        if (!cats.length) cats = FALLBACK_CATEGORIES;
        setCategories(cats.slice(0, 2)); // ยังคงโชว์ 2 ปุ่มหมวดด้านบนตามเดิม
      } finally {
        setLoading((s) => ({ ...s, cat: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  /* -------------------- Best Sellers (5 ชิ้น) -------------------- */
  useEffect(() => {
    (async () => {
      async function tryFetch(url) {
        try {
          const r = await axios.get(url);
          const arr = (Array.isArray(r.data?.data) ? r.data.data : r.data) || [];
          const mapped = arr.map((p, i) => normalizeProduct(p, i)).filter((x) => x.id && x.name);
          if (mapped.length) return mapped;
        } catch {}
        return null;
      }

      try {
        setLoading((s) => ({ ...s, best: true }));
        const urls = [
          apiPath('/products/best-sellers?limit=5'),
          apiPath('/products?sort=popular&limit=5'),
          apiPath('/products?featured=1&limit=5'),
        ];
        let products = null;
        for (const u of urls) {
          products = await tryFetch(u);
          if (products) break;
        }
        setBestSellers(products?.slice(0, 5) || FALLBACK_BEST.slice(0, 5));
      } catch {
        setBestSellers(FALLBACK_BEST.slice(0, 5));
        setErrMsg('แสดงข้อมูลจากตัวอย่างชั่วคราว');
      } finally {
        setLoading((s) => ({ ...s, best: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  /* -------------------- สินค้าทั้งหมด (กริดต่อ ๆ กันแบบช้อปปี้) -------------------- */
  const catKeys = useMemo(
    () => categories.map((c) => c.slug || c.code || c.id),
    [categories]
  );

  useEffect(() => {
    (async () => {
      // 1) พยายามดึงทีเดียว
      async function loadAllDirect() {
        const urls = [
          apiPath('/products?limit=60'),         // แนะนำให้ backend รองรับ
          apiPath('/products/all?limit=60'),
        ];
        for (const u of urls) {
          try {
            const r = await axios.get(u);
            const arr = (Array.isArray(r.data?.data) ? r.data.data : r.data) || [];
            if (arr.length) return arr.map((p, i) => normalizeProduct(p, i));
          } catch {}
        }
        return null;
      }

      // 2) ถ้าหมดหนทาง ค่อย “รวมจากแต่ละหมวด”
      async function loadFromEachCategory(keys) {
        let bag = [];
        for (const key of keys) {
          const q = encodeURIComponent(key);
          const urls = [
            apiPath(`/products?category=${q}&limit=30`),
            apiPath(`/products/by-category/${q}?limit=30`),
          ];
          for (const u of urls) {
            try {
              const r = await axios.get(u);
              const arr = (Array.isArray(r.data?.data) ? r.data.data : r.data) || [];
              if (arr.length) {
                bag = bag.concat(arr.map((p, i) => normalizeProduct(p, i)));
                break;
              }
            } catch {}
          }
        }
        // เอาเฉพาะไม่ซ้ำ (ตาม id)
        const seen = new Set();
        return bag.filter((x) => (x.id && !seen.has(x.id) && seen.add(x.id)));
      }

      try {
        setLoading((s) => ({ ...s, all: true }));
        let items = await loadAllDirect();
        if (!items || !items.length) {
          items = await loadFromEachCategory(catKeys);
        }
        // เรียงใหม่เล็กน้อย (ล่าสุดก่อน ถ้ามี id เป็นตัวเลข)
        items.sort((a, b) => {
          const ai = Number(a.id), bi = Number(b.id);
          if (Number.isFinite(ai) && Number.isFinite(bi)) return bi - ai;
          return 0;
        });
        setAllProducts(items || []);
      } finally {
        setLoading((s) => ({ ...s, all: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [catKeys.join('|')]);

  return (
    <div className="home-container">
      {/* Search */}
      <div className="search-box">
        <input type="text" placeholder="ค้นหาสินค้า" />
        <button className="search-btn">🔍</button>
      </div>

      {/* Hero */}
      <section
        className="hero"
        style={{
          backgroundImage: `url(${process.env.PUBLIC_URL + '/p2.png'})`,
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
        }}
      >
        <div className="hero-text">
          <h1>“<span className="highlight">ร้านปราชญ์แม่โจ้</span>”</h1>
          <p>แหล่งผลิตและจัดจำหน่าย ต้นไม้ และวัสดุปลูก มากกว่า 100 รายการ</p>
        </div>
      </section>

      {/* ปุ่มหมวด 2 ใบ */}
      <section className="categories">
        {(loading.cat ? FALLBACK_CATEGORIES : categories).map((cat, idx) => {
          const q = encodeURIComponent(cat.slug || cat.code || cat.id);
          return (
            <Link
              key={cat.id || cat.slug || `cat-${idx}`}
              to={`/products?category=${q}`}
              className="category-card"
              title={cat.name}
            >
              <img
                src={cat.image}
                alt={cat.name}
                onError={(e) => {
                  e.currentTarget.src =
                    (cat.slug || cat.code || '')
                      .toString()
                      .toLowerCase()
                      .includes('tool')
                      ? process.env.PUBLIC_URL + '/opakorn.jpg'
                      : process.env.PUBLIC_URL + '/tonmai.jpg';
                }}
              />
              <p>{cat.name}</p>
            </Link>
          );
        })}
      </section>

      {/* Best Sellers (5 ชิ้น) */}
      <section className="best-sellers">
        <h2>🌟 สินค้าขายดี</h2>
        {errMsg && <div className="info-inline">{errMsg}</div>}
        <div className="product-grid">
          {(loading.best ? FALLBACK_BEST.slice(0, 5) : bestSellers).map((item, idx) => (
            <div className="product-card" key={item.id ?? `best-${idx}`}>
              <img
                src={item.img}
                alt={item.name}
                onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
              />
              <h3>{item.name}</h3>
              <p>{Number(item.price).toLocaleString()} บาท</p>
              <button className="btn-add" onClick={() => addToCart(item)}>
                + เพิ่มลงตะกร้า
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* All products (feed แบบช้อปปี้) */}
      <section className="all-products">
        <h2>🛒 สินค้าทั้งหมด</h2>
        {loading.all ? (
          <div className="info-inline">กำลังโหลดสินค้า…</div>
        ) : (
          <div className="product-grid">
            {(allProducts.length ? allProducts : FALLBACK_BEST).map((p, idx) => (
              <div className="product-card" key={p.id ?? `all-${idx}`}>
                <img
                  src={p.img}
                  alt={p.name}
                  onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
                />
                <h3>{p.name}</h3>
                <p>{Number(p.price).toLocaleString()} บาท</p>
                <button className="btn-add" onClick={() => addToCart(p)}>
                  + เพิ่มลงตะกร้า
                </button>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}
