// src/pages/ProductList.js
import React, { useEffect, useMemo, useState } from "react";
import axios from "axios";
import { useSearchParams, Link } from "react-router-dom";

const API_BASE = import.meta?.env?.VITE_API_BASE || "http://localhost:3000";

export default function ProductList() {
  const [searchParams] = useSearchParams();
  const category = (searchParams.get("category") || "").toLowerCase(); // plants | tools | ""
  const sub = (searchParams.get("sub") || "").toLowerCase();
  const page = parseInt(searchParams.get("page") || "1", 10);

  const [products, setProducts] = useState([]);
  const [loading, setLoading]   = useState(true);
  const [error, setError]       = useState("");

  const axiosClient = useMemo(() => {
    const token = localStorage.getItem("token") || "";
    return axios.create({
      baseURL: API_BASE,
      headers: token ? { Authorization: `Bearer ${token}` } : {},
      timeout: 10000,
    });
  }, []);

  const apiPath = (p) =>
    (axios.defaults.baseURL || "").replace(/\/+$/, "").endsWith("/api")
      ? p
      : "/api" + p;

  useEffect(() => {
    const controller = new AbortController();
    setLoading(true);
    setError("");

    const params = { page, page_size: 24 };
    if (category) params.category = category;
    if (sub) params.sub = sub;

    axiosClient
      .get(apiPath("/products"), { params, signal: controller.signal })
      .then((res) => {
        const payload = res.data;
        const items =
          payload?.items ||
          payload?.data ||
          (Array.isArray(payload) ? payload : []) ||
          [];
        setProducts(items);
      })
      .catch((e) => setError(e?.response?.data?.message || e.message))
      .finally(() => setLoading(false));

    return () => controller.abort();
  }, [axiosClient, category, sub, page]);

  const title =
    category === "plants"
      ? `ต้นไม้${sub ? " • " + subLabel(sub) : ""}`
      : category === "tools"
      ? `อุปกรณ์เพาะปลูก${sub ? " • " + subLabel(sub) : ""}`
      : "รายการสินค้าทั้งหมด";

  return (
    <div style={{ maxWidth: 1120, margin: "0 auto", padding: "16px" }}>
      <div style={{ marginBottom: 8, color: "#6b7280" }}>
        <Link to="/" style={{ color: "#6b7280", textDecoration: "none" }}>
          หน้าแรก
        </Link>{" "}
        ›{" "}
        <span style={{ color: "#111827" }}>
          {category ? (category === "plants" ? "ต้นไม้" : "อุปกรณ์เพาะปลูก") : "สินค้า"}
          {sub ? " › " + subLabel(sub) : ""}
        </span>
      </div>

      <h1 style={{ fontSize: 22, fontWeight: 700, marginBottom: 10 }}>{title}</h1>

      {loading && <div>กำลังโหลดสินค้า…</div>}
      {error && !loading && (
        <div style={{ color: "#b91c1c", marginBottom: 10 }}>เกิดข้อผิดพลาด: {error}</div>
      )}

      {!loading && !error && (
        <>
          {products.length === 0 ? (
            <div style={{ color: "#6b7280" }}>ไม่พบสินค้าในหมวดนี้</div>
          ) : (
            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(2, minmax(0, 1fr))",
                gap: 12,
              }}
            >
              {products.map((p) => (
                <div
                  key={p.id || p.product_id}
                  style={{
                    border: "1px solid #e5e7eb",
                    borderRadius: 12,
                    overflow: "hidden",
                    background: "#fff",
                  }}
                >
                  <img
                    src={
                      p.cover_image_url ||
                      p.image_url ||
                      p.image ||
                      (Array.isArray(p.images) ? p.images[0] : "") ||
                      "/logo.png"
                    }
                    alt={p.name || p.product_name || "สินค้า"}
                    style={{ width: "100%", height: 140, objectFit: "cover", background: "#f9fafb" }}
                    onError={(e) => (e.currentTarget.src = "/logo.png")}
                  />
                  <div style={{ padding: 10 }}>
                    <div
                      title={p.name || p.product_name}
                      style={{
                        fontWeight: 600,
                        color: "#111827",
                        marginBottom: 6,
                        lineHeight: 1.35,
                        display: "-webkit-box",
                        WebkitLineClamp: 2,
                        WebkitBoxOrient: "vertical",
                        overflow: "hidden",
                      }}
                    >
                      {p.name || p.product_name || "สินค้า"}
                    </div>
                    <div style={{ color: "#047857", fontWeight: 700 }}>
                      {new Intl.NumberFormat("th-TH", {
                        style: "currency",
                        currency: "THB",
                      }).format(Number(p.price || p.selling_price || p.sale_price || 0))}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </>
      )}
    </div>
  );
}

function subLabel(s) {
  switch (s) {
    case "fruit": return "ไม้ผล";
    case "ornamental": return "ไม้ดอก/ประดับ";
    case "herb": return "ไม้สมุนไพร";
    case "econ":
    case "economic": return "ไม้เศรษฐกิจ";
    case "pot": return "กระถาง";
    case "bag": return "ถุงเพาะ";
    case "soil": return "วัสดุปลูก/ปรับปรุงดิน";
    default: return s;
  }
}
