// src/App.js
import React, { useEffect } from 'react';
import {
  BrowserRouter, Routes, Route, NavLink, Navigate, useNavigate, Link
} from 'react-router-dom';
import axios from 'axios';

// ===== ตั้งค่า axios ตั้งแต่โหลดไฟล์ (กันยิงไป :3000 ก่อน) =====
axios.defaults.baseURL = process.env.REACT_APP_API_BASEURL || 'http://localhost:3001';
const bootToken = localStorage.getItem('token');
if (bootToken) axios.defaults.headers.common['Authorization'] = `Bearer ${bootToken}`;

// ===== เพจเดิม ๆ ของคุณ =====
import ProductManagement from './admin/ProductManagement';
import OrderManagement   from './admin/OrderManagement';
import Login             from './auth/Login';
import Addresses         from './account/Addresses';
import Register          from './auth/Register';
import VariantsManager   from './admin/VariantsManager';
// (ถ้าคุณยังไม่มีหน้า OptionsPage ให้คอมเมนต์บรรทัด import/Route ทิ้งชั่วคราว)
// import OptionsPage       from './admin/OptionsPage.js';

const ENABLE_AUTH_GUARD = false;
const getToken = () => localStorage.getItem('token');

function RequireAuth({ children }) {
  if (!ENABLE_AUTH_GUARD) return children;
  const token = getToken();
  if (!token) return <Navigate to="/login" replace />;
  return children;
}

// ===== ErrorBoundary: กันจอขาว =====
class ErrorBoundary extends React.Component {
  constructor(props) { super(props); this.state = { error: null }; }
  static getDerivedStateFromError(error) { return { error }; }
  componentDidCatch(error, info) { console.error('💥 UI crash:', error, info); }
  render() {
    if (this.state.error) {
      return (
        <div style={{margin:16,padding:16,border:'1px solid #f00',background:'#ffeaea',borderRadius:12}}>
          <h3 style={{marginTop:0}}>พบข้อผิดพลาดในหน้านี้</h3>
          <pre style={{whiteSpace:'pre-wrap'}}>{String(this.state.error?.message || this.state.error)}</pre>
          <div style={{marginTop:8}}>
            <button onClick={() => this.setState({ error: null })}>ลองโหลดส่วนนี้ใหม่</button>{' '}
            <Link to="/_ui_ok">ไปหน้าเช็คระบบ</Link>
          </div>
        </div>
      );
    }
    return this.props.children;
  }
}

function Navbar() {
  const nav = useNavigate();
  const linkStyle = ({ isActive }) => ({
    textDecoration: 'none', padding: '6px 10px', borderRadius: 8,
    background: isActive ? '#f0f0f0' : 'transparent',
  });
  const hasToken = !!getToken();
  function logout() {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    nav('/login', { replace: true });
  }
  return (
    <nav style={{ padding: 10, display: 'flex', gap: 16, borderBottom: '1px solid #eee' }}>
      <NavLink to="/" style={linkStyle} end>สินค้า</NavLink>
      <NavLink to="/admin/orders" style={linkStyle}>คำสั่งซื้อ</NavLink>
      <NavLink to="/account/addresses" style={linkStyle}>ที่อยู่ของฉัน</NavLink>
      <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
        {!hasToken ? (
          <>
            <NavLink to="/login" style={linkStyle}>เข้าสู่ระบบ</NavLink>
            <NavLink to="/register" style={linkStyle}>สมัครสมาชิก</NavLink>
          </>
        ) : (
          <button onClick={logout} style={{ padding: '6px 10px', borderRadius: 8 }}>
            ออกจากระบบ
          </button>
        )}
      </div>
    </nav>
  );
}

export default function App() {
  // sync header ถ้าล็อกอินใหม่
  useEffect(() => {
    const t = getToken();
    if (t) axios.defaults.headers.common['Authorization'] = `Bearer ${t}`;
  }, []);

  // เพจเช็คเร็ว
  const HealthPage = () => (
    <div style={{ padding: 16 }}>
      <h1>✅ UI OK</h1>
      <p>
        Router ทำงานปกติ • ลองไป{' '}
        <Link to="/admin/products/23/variants">หน้า SKU (ตัวอย่าง #23)</Link>
        {/* {' • '}<Link to="/admin/products/23/options">หน้าออปชั่น</Link> */}
      </p>
    </div>
  );

  return (
    <BrowserRouter>
      <Navbar />

      <Routes>
        {/* หน้าเช็คเร็ว */}
        <Route path="/_ui_ok" element={<HealthPage />} />

        {/* หน้าแรก: ครอบ ErrorBoundary กันพัง */}
        <Route
          path="/"
          element={
            <ErrorBoundary>
              <div className="App" style={{ padding: 16 }}>
                <h1>🌿 ระบบจัดการสินค้า</h1>
                <ProductManagement />
              </div>
            </ErrorBoundary>
          }
        />

        <Route
          path="/admin/orders"
          element={
            <RequireAuth>
              <ErrorBoundary>
                <div style={{ padding: 16 }}>
                  <h1>📦 จัดการคำสั่งซื้อ</h1>
                  <OrderManagement />
                </div>
              </ErrorBoundary>
            </RequireAuth>
          }
        />

        <Route
          path="/account/addresses"
          element={
            <RequireAuth>
              <ErrorBoundary>
                <div style={{ padding: 16 }}>
                  <h1>🏠 ที่อยู่ของฉัน</h1>
                  <Addresses />
                </div>
              </ErrorBoundary>
            </RequireAuth>
          }
        />

        <Route path="/login" element={<ErrorBoundary><Login /></ErrorBoundary>} />
        <Route path="/register" element={<ErrorBoundary><Register /></ErrorBoundary>} />

        {/* ถ้ามีไฟล์ OptionsPage.js แล้วค่อยเปิด 2 บรรทัดล่างนี้
        <Route path="/admin/products/:id/options" element={<ErrorBoundary><OptionsPage /></ErrorBoundary>} />
        */}
        <Route path="/admin/products/:productId/variants" element={<ErrorBoundary><VariantsManager /></ErrorBoundary>} />

        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
}
