// src/pages/Plants.js
import React, { useEffect, useState, useMemo } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./Plants.css";

export default function Plants() {
  const [cats, setCats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  // สร้างลิงก์ไปหน้าสินค้า
  const buildLink = (s) => `/products?category=plants&sub=${encodeURIComponent(s)}`;

  // ปรับให้รองรับหลายแบบ field จาก backend ต่าง ๆ
  const normalize = (item = {}) => {
    const slug =
      item.slug || item.code || item.key || item.id || item._id || "";
    const name =
      item.name || item.title || item.display_name || "ไม่ระบุชื่อ";
    const desc =
      item.description || item.desc || item.subtitle || "";
    const img =
      item.image_url || item.image || item.thumbnail || item.cover || "";

    return { slug: String(slug), name: String(name), desc: String(desc || ""), img: String(img || "") };
  };

  // ดึงหมวดจากฐานข้อมูล
  useEffect(() => {
    let off = false;

    (async () => {
      setLoading(true);
      setErr("");
      try {
        // ลองเรียกหลาย endpoint เผื่อโปรเจกต์คุณตั้งชื่อไม่เหมือนกัน
        const endpoints = [
          "/api/categories?parent=plants",
          "/api/subcategories?category=plants",
          "/api/plants/categories",
          "/api/categories/plants",
        ];

        let data = [];
        for (const url of endpoints) {
          try {
            const r = await axios.get(url);
            const raw = Array.isArray(r.data) ? r.data : (r.data?.items || r.data?.data || []);
            if (raw && raw.length) { data = raw; break; }
          } catch (_) { /* ลองตัวถัดไป */ }
        }

        const mapped = (data || []).map(normalize).filter(x => x.slug);
        if (!off) setCats(mapped);
      } catch (e) {
        if (!off) setErr(e?.response?.data?.message || e.message || "โหลดหมวดหมู่ไม่สำเร็จ");
      } finally {
        if (!off) setLoading(false);
      }
    })();

    return () => { off = true; };
  }, []);

  const placeholders = useMemo(() => Array.from({ length: 4 }), []);

  return (
    <div className="plants-page">
      {/* breadcrumb */}
      <nav className="breadcrumb">
        <Link to="/" className="crumb">หน้าแรก</Link>
        <span className="sep">›</span>
        <span className="crumb current">ต้นไม้</span>
      </nav>

      {/* ปุ่มกลับหน้าแรก */}
      <div className="topbar">
        <Link to="/" className="back-btn" aria-label="กลับหน้าแรก">
          <svg width="18" height="18" viewBox="0 0 24 24" aria-hidden="true" className="back-icon">
            <path d="M15 18l-6-6 6-6" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          กลับหน้าแรก
        </Link>
      </div>

      {/* hero */}
      <header className="plants-hero">
        <h1>เลือกหมวดย่อยของ <span className="hl">ต้นไม้</span></h1>
        <p className="sub">เลือกประเภทที่ต้องการ เพื่อดูรายการสินค้าเฉพาะหมวดนั้น ๆ</p>
      </header>

      {/* error */}
      {err && <div className="note-error">{err}</div>}

      {/* grid */}
      <section className="plants-categories">
        {loading
          ? placeholders.map((_, i) => (
              <div className="plant-card skel" key={`skel-${i}`}>
                <div className="thumb shine" />
                <div className="meta">
                  <div className="line shine" style={{ width: "60%", height: 16 }} />
                  <div className="line shine" style={{ width: "80%", height: 12, marginTop: 6 }} />
                </div>
              </div>
            ))
          : cats.map((c) => (
              <Link to={buildLink(c.slug)} className="plant-card" key={c.slug} aria-label={c.name}>
                <div className="thumb">
                  <img
                    src={c.img || (process.env.PUBLIC_URL + "/placeholder.jpg")}
                    alt={c.name}
                    onError={(e) => (e.currentTarget.src = process.env.PUBLIC_URL + "/logo.png")}
                  />
                </div>
                <div className="meta">
                  <p className="p-name">{c.name}</p>
                  <p className="p-desc">{c.desc}</p>
                </div>
              </Link>
            ))}
      </section>
    </div>
  );
}
