// src/admin/InventoryPage.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";
import {
  listInventory,
  receiveInventory,
  issueInventory,
  listMoves,
} from "../lib/api";
import VariantPicker from "../components/VariantPicker";
import { toast } from "react-hot-toast";
import "./InventoryPage.css";
import { useLookups } from "../lib/lookups";

/* utils */
const fmt = (n) => new Intl.NumberFormat("th-TH").format(Number(n || 0));
const getStock = (it) => Number(it?.stock ?? it?.stock_qty ?? 0);
const currency = (n) =>
  n == null ? "-" :
  new Intl.NumberFormat("th-TH", { style: "currency", currency: "THB" }).format(
    Number(n || 0)
  );
const cls = (...xs) => xs.filter(Boolean).join(" ");
const nowLocalDatetime = () => {
  const d = new Date();
  d.setMinutes(d.getMinutes() - d.getTimezoneOffset());
  return d.toISOString().slice(0, 16);
};
const Help = ({ children }) => <div className="inv-help">{children}</div>;

/* ---------- helpers: print/export ‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏ï‡∏≤‡∏£‡∏≤‡∏á ---------- */
function printTable(ref, title = "‡∏û‡∏¥‡∏°‡∏û‡πå‡∏ï‡∏≤‡∏£‡∏≤‡∏á") {
  const node = ref?.current;
  if (!node) return;
  const win = window.open("", "_blank", "width=1024,height=768");
  const styles = `
    *{box-sizing:border-box;font-family:system-ui,-apple-system,Segoe UI,Roboto,TH Sarabun New,Arial;}
    h2{margin:0 0 8px 0;font-weight:800}
    table{width:100%;border-collapse:separate;border-spacing:0}
    thead th{background:#f6f8fb;text-align:left;padding:10px;border-bottom:1px solid #e2e8f0;font-weight:700}
    tbody td{padding:10px;border-top:1px solid #e2e8f0}
    tbody tr:nth-child(2n) td{background:#fcfdfc}
  `;
  win.document.write(`
    <!doctype html>
    <html>
      <head><meta charset="utf-8"><title>${title}</title>
        <style>${styles}</style>
      </head>
      <body>
        <h2>${title}</h2>
        ${node.outerHTML}
        <script>window.onload = () => { window.print(); window.close(); }</script>
      </body>
    </html>
  `);
  win.document.close();
}

async function exportTablePDF(ref, filename = "table.pdf", landscape = true) {
  const node = ref?.current;
  if (!node) return;
  const { jsPDF } = await import("jspdf");
  const html2canvas = (await import("html2canvas")).default;

  const canvas = await html2canvas(node, { scale: 2, backgroundColor: "#fff" });
  const imgData = canvas.toDataURL("image/png");

  const pdf = new jsPDF({
    orientation: landscape ? "landscape" : "portrait",
    unit: "pt",
    format: "a4",
  });
  const pageW = pdf.internal.pageSize.getWidth();
  const pageH = pdf.internal.pageSize.getHeight();
  const margin = 24;

  const imgW = pageW - margin * 2;
  const imgH = (canvas.height * imgW) / canvas.width;

  let heightLeft = imgH;
  pdf.addImage(imgData, "PNG", margin, margin, imgW, imgH);
  heightLeft -= pageH - margin * 2;

  while (heightLeft > 0) {
    pdf.addPage();
    const position = heightLeft * -1 + margin;
    pdf.addImage(imgData, "PNG", margin, position, imgW, imgH);
    heightLeft -= pageH - margin * 2;
  }

  pdf.save(filename);
}

/* ---------- ‡∏™‡πà‡∏ß‡∏ô‡∏û‡∏±‡∏ö‡∏î‡∏π JSON ---------- */
function JsonDetails({ data, title = "‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î (JSON)" }) {
  const [open, setOpen] = useState(false);
  if (!data) return null;
  return (
    <div className="inv-json">
      <button
        type="button"
        onClick={() => setOpen((v) => !v)}
        className="inv-json__toggle"
      >
        {open ? "‡∏ã‡πà‡∏≠‡∏ô" : "‡∏î‡∏π"} {title}
      </button>
      {open && (
        <pre className="inv-json__pre">{JSON.stringify(data, null, 2)}</pre>
      )}
    </div>
  );
}

/* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Main ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
export default function InventoryPage() {
  const [tab, setTab] = useState("overview"); // overview | receive | issue | moves

  // state ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö ‚Äú‡πÇ‡∏ü‡∏Å‡∏±‡∏™/‡πÄ‡∏î‡πâ‡∏á‡πÑ‡∏õ‡∏î‡∏π‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÉ‡∏´‡∏°‡πà‚Äù ‡∏ö‡∏ô‡∏†‡∏≤‡∏û‡∏£‡∏ß‡∏°
  const [focusSignal, setFocusSignal] = useState(0);
  const [focusQuery, setFocusQuery] = useState(""); // ‡∏ä‡∏∑‡πà‡∏≠‡∏´‡∏£‡∏∑‡∏≠ SKU

  // callback ‡∏´‡∏•‡∏±‡∏á‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤‡πÄ‡∏™‡∏£‡πá‡∏à: ‡πÄ‡∏î‡πâ‡∏á‡πÑ‡∏õ‡∏†‡∏≤‡∏û‡∏£‡∏ß‡∏° + ‡πÇ‡∏ü‡∏Å‡∏±‡∏™‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏ó‡∏µ‡πà‡πÄ‡∏û‡∏¥‡πà‡∏á‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤
  function handleAfterReceive({ name, sku }) {
    setFocusQuery(name || sku || "");
    setFocusSignal((s) => s + 1);
    setTab("overview");
  }

  return (
    <div className="inv">
      <h1 className="inv-title">Inventory Management</h1>

      <div className="inv-tabs">
        {[
          ["overview", "‡∏†‡∏≤‡∏û‡∏£‡∏ß‡∏°‡∏Ñ‡∏á‡∏Ñ‡∏•‡∏±‡∏á"],
          ["receive", "‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤ (IN)"],
          ["issue", "‡∏ï‡∏±‡∏î‡∏≠‡∏≠‡∏Å (OUT) FIFO"],
          ["moves", "‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥ (Moves)"],
        ].map(([k, label]) => (
          <button
            key={k}
            className={cls("inv-tab", tab === k && "is-active")}
            onClick={() => setTab(k)}
          >
            {label}
          </button>
        ))}
      </div>

      <div className="inv-section">
        {tab === "overview" && (
          <InventoryOverview focusSignal={focusSignal} focusQuery={focusQuery} />
        )}
        {tab === "receive" && <ReceiveForm onAfterSuccess={handleAfterReceive} />}
        {tab === "issue" && <IssueForm />}
        {tab === "moves" && <MovesPanel />}
      </div>
    </div>
  );
}

/* ---------- 1) ‡∏†‡∏≤‡∏û‡∏£‡∏ß‡∏° ---------- */
function InventoryOverview({ focusSignal, focusQuery }) {
  const [q, setQ] = useState("");
  const [scope, setScope] = useState("variant");
  const [order, setOrder] = useState("low_stock");
  const [page, setPage] = useState(1);
  const [pageSize] = useState(20);
  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);

  // NEW
  const [mode, setMode] = useState("sku"); // sku | all
  const [categoryId, setCategoryId] = useState("");
  const [subcategoryId, setSubcategoryId] = useState("");

  const { categories, subcategories } = useLookups?.() || { categories: [], subcategories: [] };

  const tableRef = useRef(null);
  const loadLock = useRef(false); // üîí ‡∏Å‡∏±‡∏ô‡∏¢‡∏¥‡∏á‡∏ã‡πâ‡∏≥ dev/StrictMode

  const offset = (page - 1) * pageSize;
  const totalPages = Math.max(1, Math.ceil(total / pageSize));

  async function load() {
    if (loadLock.current) return;
    loadLock.current = true;
    setLoading(true);
    try {
      const data = await listInventory({
        search: q,
        scope,
        order,
        limit: pageSize,
        offset,
        mode,
        category_id: categoryId || undefined,
        subcategory_id: subcategoryId || undefined,
        _t: Date.now(),
      });
      setItems(data.items || []);
      setTotal(Number(data.total || 0));
    } catch (e) {
      console.error(e);
      toast.error(e?.response?.data?.error || "‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à");
    } finally {
      setLoading(false);
      setTimeout(() => {
        loadLock.current = false;
      }, 0);
    }
  }

  useEffect(() => {
    load(); // eslint-disable-next-line
  }, [scope, order, page, mode, categoryId, subcategoryId]);

  // ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏°‡∏µ‡∏™‡∏±‡∏ç‡∏ç‡∏≤‡∏ì‡πÇ‡∏ü‡∏Å‡∏±‡∏™‡∏à‡∏≤‡∏Å‡∏ü‡∏≠‡∏£‡πå‡∏°‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤ ‚Üí ‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤/‡πÄ‡∏£‡∏µ‡∏¢‡∏á ‡πÅ‡∏•‡πâ‡∏ß‡πÇ‡∏´‡∏•‡∏î
  useEffect(() => {
    if (!focusSignal) return;
    setOrder("newest");
    setQ(focusQuery || "");
    setPage(1);
    const t = setTimeout(load, 0);
    return () => clearTimeout(t);
    // eslint-disable-next-line
  }, [focusSignal]);

  // ‡∏Å‡∏•‡∏∏‡πà‡∏°‡∏ï‡∏≤‡∏°‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó/‡∏´‡∏°‡∏ß‡∏î
  const grouped = useMemo(() => {
    const groups = {};
    for (const it of items) {
      const cg = it.category_name || "‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏";
      const sg = it.subcategory_name || "‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏";
      const key = `${cg} / ${sg}`;
      if (!groups[key]) groups[key] = [];
      groups[key].push(it);
    }
    return groups;
  }, [items]);

  return (
    <div>
      <div className="inv-filterRow inv-filterRow-wrap">
        <div className="inv-field inv-field--grow">
          <label className="inv-label">‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤</label>
          <input
            value={q}
            onChange={(e) => setQ(e.target.value)}
            placeholder="‡∏ä‡∏∑‡πà‡∏≠‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ / ‡∏Ñ‡∏≥‡∏≠‡∏ò‡∏¥‡∏ö‡∏≤‡∏¢ / SKU"
            className="inv-input"
          />
          <Help>‡∏û‡∏¥‡∏°‡∏û‡πå‡∏ä‡∏∑‡πà‡∏≠‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ ‡∏Ñ‡∏≥‡∏≠‡∏ò‡∏¥‡∏ö‡∏≤‡∏¢ ‡∏´‡∏£‡∏∑‡∏≠ SKU ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤</Help>
        </div>
        <div className="inv-field">
          <label className="inv-label">Scope</label>
          <select
            value={scope}
            onChange={(e) => {
              setScope(e.target.value);
              setPage(1);
            }}
            className="inv-select"
          >
            <option value="variant">‡∏£‡∏≤‡∏¢‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å (variant)</option>
            <option value="product">‡∏£‡∏≤‡∏¢‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ (product)</option>
          </select>
          <Help>‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏î‡∏π‡∏™‡∏£‡∏∏‡∏õ‡πÄ‡∏õ‡πá‡∏ô‡∏£‡∏≤‡∏¢‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å ‡∏´‡∏£‡∏∑‡∏≠‡∏£‡∏≤‡∏¢‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</Help>
        </div>
        <div className="inv-field">
          <label className="inv-label">‡πÇ‡∏´‡∏°‡∏î‡∏Å‡∏≤‡∏£‡πÅ‡∏™‡∏î‡∏á</label>
          <select
            value={mode}
            onChange={(e) => {
              setMode(e.target.value);
              setPage(1);
            }}
            className="inv-select"
          >
            <option value="sku">‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ó‡∏µ‡πà‡∏°‡∏µ SKU (‡∏™‡∏ï‡πá‡∏≠‡∏Å‡∏à‡∏£‡∏¥‡∏á)</option>
            <option value="all">‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î (‡∏£‡∏ß‡∏°‡∏ó‡∏µ‡πà‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ SKU)</option>
          </select>
          <Help>‡∏™‡∏•‡∏±‡∏ö‡πÇ‡∏´‡∏°‡∏î INNER/LEFT JOIN</Help>
        </div>
        <div className="inv-field">
          <label className="inv-label">‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</label>
          <select
            value={categoryId}
            onChange={(e) => { setCategoryId(e.target.value); setSubcategoryId(""); }}
            className="inv-select"
          >
            <option value="">‚Äî ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î ‚Äî</option>
            {(categories || []).map(c => (
              <option key={c.category_id} value={c.category_id}>{c.category_name}</option>
            ))}
          </select>
        </div>
        <div className="inv-field">
          <label className="inv-label">‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà‡∏¢‡πà‡∏≠‡∏¢</label>
          <select
            value={subcategoryId}
            onChange={(e) => setSubcategoryId(e.target.value)}
            className="inv-select"
          >
            <option value="">‚Äî ‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î ‚Äî</option>
            {(subcategories || [])
              .filter(sc => !categoryId || String(sc.category_id) === String(categoryId))
              .map(sc => (
                <option key={sc.subcategory_id} value={sc.subcategory_id}>{sc.subcategory_name}</option>
              ))}
          </select>
        </div>
        <div className="inv-field">
          <label className="inv-label">‡∏à‡∏±‡∏î‡πÄ‡∏£‡∏µ‡∏¢‡∏á</label>
          <select
            value={order}
            onChange={(e) => setOrder(e.target.value)}
            className="inv-select"
          >
            <option value="low_stock">‡πÄ‡∏´‡∏•‡∏∑‡∏≠‡∏ô‡πâ‡∏≠‡∏¢‡∏Å‡πà‡∏≠‡∏ô</option>
            <option value="newest">‡πÉ‡∏´‡∏°‡πà‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î</option>
            <option value="name_asc">‡∏ä‡∏∑‡πà‡∏≠ A‚ÜíZ</option>
            <option value="name_desc">‡∏ä‡∏∑‡πà‡∏≠ Z‚ÜíA</option>
          </select>
          <Help>‡∏ß‡∏¥‡∏ò‡∏µ‡πÄ‡∏£‡∏µ‡∏¢‡∏á‡∏•‡∏≥‡∏î‡∏±‡∏ö‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏ó‡∏µ‡πà‡πÅ‡∏™‡∏î‡∏á</Help>
        </div>
        <button
          onClick={() => { setPage(1); load(); }}
          className="inv-btn inv-btn--primary"
        >
          ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
        </button>
      </div>

      {/* toolbar ‡∏û‡∏¥‡∏°‡∏û‡πå/‡∏™‡πà‡∏á‡∏≠‡∏≠‡∏Å ‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡∏†‡∏≤‡∏û‡∏£‡∏ß‡∏° */}
      <div className="inv-toolbar">
        <div className="inv-exportBtns">
          <button
            type="button"
            className="inv-btn"
            onClick={() => printTable(tableRef, "‡∏†‡∏≤‡∏û‡∏£‡∏ß‡∏°‡∏Ñ‡∏á‡∏Ñ‡∏•‡∏±‡∏á")}
          >
            ‡∏û‡∏¥‡∏°‡∏û‡πå/‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡πÄ‡∏õ‡πá‡∏ô PDF
          </button>
          <button
            type="button"
            className="inv-btn"
            onClick={() =>
              exportTablePDF(
                tableRef,
                `inventory_overview_${new Date().toISOString().slice(0, 10)}.pdf`,
                false
              )
            }
          >
            ‡∏™‡πà‡∏á‡∏≠‡∏≠‡∏Å PDF
          </button>
        </div>
      </div>

      <div className="inv-tableWrap" ref={tableRef}>
        <table className="inv-table">
          <thead>
            <tr>
              <th className="text-left">‡∏Å‡∏•‡∏∏‡πà‡∏°</th>
              <th className="text-left">‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</th>
              {scope === "variant" && <th className="text-left">SKU</th>}
              <th className="text-right">‡∏™‡∏ï‡πä‡∏≠‡∏Å</th>
              {scope === "variant" && <th className="text-right">‡∏£‡∏≤‡∏Ñ‡∏≤</th>}
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr>
                <td className="p-4" colSpan={scope === "variant" ? 5 : 3}>
