// src/lib/api.js
import axios from 'axios';

export const api = axios.create({
  baseURL: import.meta.env.VITE_API_BASE || 'http://localhost:3001/api',
  // withCredentials: true, // ถ้าใช้คุกกี้/JWT แบบ cookie ให้เปิดบรรทัดนี้
});

// ---- Admin Variants ----
export const getProductMeta = (productId) =>
  api.get(`/admin/products/${productId}/variants`).then(r => r.data);

export const addOption = (productId, option_name) =>
  api.post(`/admin/products/${productId}/options`, { option_name }).then(r => r.data);

export const renameOption = (productId, optionId, option_name) =>
  api.put(`/admin/products/${productId}/options/${optionId}`, { option_name }).then(r => r.data);

export const addValue = (option_id, value_name) =>
  api.post(`/admin/options/${option_id}/values`, { value_name }).then(r => r.data);

export const renameValue = (valueId, value_name) =>
  api.put(`/admin/values/${valueId}`, { value_name }).then(r => r.data);

export const createVariant = (productId, payload) =>
  api.post(`/admin/products/${productId}/variants`, payload).then(r => r.data);

export const generateVariants = (productId, body) =>
  api.post(`/admin/products/${productId}/variants/generate`, body).then(r => r.data);

export const updateVariant = (variantId, patch) =>
  api.put(`/admin/variants/${variantId}`, patch).then(r => r.data);

export const deleteVariant = (variantId) =>
  api.delete(`/admin/variants/${variantId}`).then(r => r.data);

export const deleteOption = (option_id) =>
  api.delete(`/admin/options/${option_id}`).then(r => r.data);

export const deleteValue = (value_id) =>
  api.delete(`/admin/values/${value_id}`).then(r => r.data);
