// FRONTEND: src/pages/SignUp.js
import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css';

export default function SignUp() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    identifier: '',     // ‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏à‡∏£‡∏¥‡∏á
    password: '',
    confirm: '',        // ‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏û‡∏≤‡∏™
    fullname: '',
    phone: '',
    houseNo: '',        // ‡∏ö‡πâ‡∏≤‡∏ô‡πÄ‡∏•‡∏Ç‡∏ó‡∏µ‡πà/‡∏´‡∏°‡∏π‡πà/‡∏≠‡∏≤‡∏Ñ‡∏≤‡∏£ (‡∏ä‡πà‡∏≠‡∏á‡πÉ‡∏´‡∏ç‡πà)
    province: '',
    amphoe: '',
    tambon: '',
    zipcode: '',
    gender: '',
  });

  const [error, setError]     = useState('');
  const [loading, setLoading] = useState(false);
  const [showPw, setShowPw]   = useState(false);

  // ---------- ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡∏ó‡∏±‡πâ‡∏á‡∏õ‡∏£‡∏∞‡πÄ‡∏ó‡∏® ----------
  const [addrRows, setAddrRows] = useState([]);
  useEffect(() => {
    let alive = true;
    (async () => {
      const res = await fetch('/data/thai-address.min.json');
      const data = await res.json();      // ‡∏£‡∏π‡∏õ‡πÅ‡∏ö‡∏ö‡∏ó‡∏±‡πà‡∏ß‡πÑ‡∏õ: [{ province, district, subdistrict, zipcode }, ...]
      if (alive) setAddrRows(data || []);
    })();
    return () => { alive = false; };
  }, []);

  const provinces = useMemo(() => {
    const set = new Set(addrRows.map(r => r.province));
    return [...set].sort();
  }, [addrRows]);

  const amphoes = useMemo(() => {
    if (!form.province) return [];
    const set = new Set(addrRows.filter(r => r.province === form.province).map(r => r.district));
    return [...set].sort();
  }, [addrRows, form.province]);

  const tambons = useMemo(() => {
    if (!form.province || !form.amphoe) return [];
    const set = new Set(
      addrRows
        .filter(r => r.province === form.province && r.district === form.amphoe)
        .map(r => r.subdistrict)
    );
    return [...set].sort();
  }, [addrRows, form.province, form.amphoe]);

  // ‡πÄ‡∏ï‡∏¥‡∏°‡∏£‡∏´‡∏±‡∏™‡πÑ‡∏õ‡∏£‡∏©‡∏ì‡∏µ‡∏¢‡πå‡∏≠‡∏±‡∏ï‡πÇ‡∏ô‡∏°‡∏±‡∏ï‡∏¥
  useEffect(() => {
    if (!form.province || !form.amphoe || !form.tambon) {
      setForm(p => ({ ...p, zipcode: '' }));
      return;
    }
    const hit = addrRows.find(r =>
      r.province === form.province && r.district === form.amphoe && r.subdistrict === form.tambon
    );
    setForm(p => ({ ...p, zipcode: hit?.zipcode || '' }));
  }, [addrRows, form.province, form.amphoe, form.tambon]);

  // ---------- helpers ----------
  const onChange = (e) => {
    const { name, value } = e.target;
    // reset ‡πÅ‡∏ö‡∏ö chain
    if (name === 'province') return setForm(p => ({ ...p, province: value, amphoe: '', tambon: '', zipcode: '' }));
    if (name === 'amphoe')   return setForm(p => ({ ...p, amphoe: value, tambon: '', zipcode: '' }));
    setForm(p => ({ ...p, [name]: value }));
  };

  const isEmail = (s) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(String(s).trim());
  const pwMatch = useMemo(() => form.password && form.password === form.confirm, [form.password, form.confirm]);
  const splitName = (fullname) => {
    const parts = String(fullname || '').trim().split(/\s+/);
    if (!parts.length) return { first_name: '', last_name: '' };
    if (parts.length === 1) return { first_name: parts[0], last_name: '' };
    return { first_name: parts[0], last_name: parts.slice(1).join(' ') };
  };
  const makeUsername = (email) => {
    const raw = String(email).split('@')[0] || '';
    const cleaned = raw.toLowerCase().replace(/[^a-z0-9_.-]/g, '');
    return cleaned || `cust${Date.now().toString().slice(-6)}`;
  };

  // ---------- submit ----------
  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    const required = [
      'identifier', 'password', 'confirm', 'fullname', 'phone',
      'houseNo', 'province', 'amphoe', 'tambon', 'gender'
    ];
    if (required.some(k => !String(form[k]).trim())) return setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô');
    if (!isEmail(form.identifier))               return setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á');
    if (form.password.length < 8)               return setError('‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡∏ï‡πâ‡∏≠‡∏á‡∏°‡∏µ‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£');
    if (!pwMatch)                                return setError('‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô');

    const { first_name, last_name } = splitName(form.fullname);
    const address = `${form.houseNo} ‡∏ï.${form.tambon} ‡∏≠.${form.amphoe} ‡∏à.${form.province} ${form.zipcode}`.trim();

    const payload = {
      first_name,
      last_name,
      email: form.identifier.trim(),
      username: makeUsername(form.identifier),
      password: form.password,
      confirm_password: form.confirm,
      phone_number: form.phone.trim(),
      address,
    };

    try {
      setLoading(true);

      // ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å (backend ‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì‡∏£‡∏±‡∏ö‡∏ó‡∏µ‡πà /api/auth/register)
      const res = await axios.post('/api/auth/register', payload);

      // ‡∏ñ‡πâ‡∏≤ backend ‡∏™‡πà‡∏á token ‡∏°‡∏≤‡πÄ‡∏•‡∏¢
      let token = res?.data?.token || res?.data?.accessToken || res?.data?.jwt;
      let role  = res?.data?.user?.role || 'customer';

      // ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ token ‡πÉ‡∏´‡πâ login ‡∏ï‡πà‡∏≠
      if (!token) {
        const loginRes = await axios.post('/api/auth/login', {
          email: form.identifier.trim(),
          password: form.password,
        });
        token = loginRes?.data?.token || loginRes?.data?.accessToken || loginRes?.data?.jwt;
        role  = loginRes?.data?.user?.role || role;
      }

      if (token) {
        localStorage.setItem('token', token);
        localStorage.setItem('role',  role || 'customer');
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      }
      navigate('/', { replace: true });
    } catch (err) {
      const msg =
        err?.response?.status === 409 ? (err.response.data?.error || '‡∏≠‡∏µ‡πÄ‡∏°‡∏•/‡∏ä‡∏∑‡πà‡∏≠‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ ‡∏ñ‡∏π‡∏Å‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô‡πÅ‡∏•‡πâ‡∏ß')
      : err?.response?.data?.error || err.message || '‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à';
      setError(msg);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="signup-page">
      {/* ‡πÄ‡∏Æ‡∏î + ‡∏ä‡πà‡∏≠‡∏á‡πÄ‡∏ß‡πâ‡∏≤‡πÇ‡∏•‡πÇ‡∏Å‡πâ */}
      <div className="signup-hero">
        <div className="notch">
          <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="PRACH MAEJO" />
        </div>
      </div>

      {/* Brand + ‡∏´‡∏±‡∏ß‡∏Ç‡πâ‡∏≠ */}
      <div className="brand-head">
        <div className="brand-line">
          <span className="brand">PRACH MAEJO</span>
          <span className="online">ONLINE</span>
        </div>
        <h2>‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏±‡∏ç‡∏ä‡∏µ / ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å</h2>
      </div>

      <form className="signup-form" onSubmit={onSubmit} noValidate>
        {/* ‡∏ã‡πâ‡∏≤‡∏¢: ‡∏≠‡∏µ‡πÄ‡∏°‡∏• / ‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™ / ‡πÄ‡∏ö‡∏≠‡∏£‡πå / ‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡∏Å‡∏ß‡πâ‡∏≤‡∏á */}
        <label className="field col">
          <span className="label">‡∏≠‡∏µ‡πÄ‡∏°‡∏•</span>
          <input
            type="email"
            name="identifier"
            placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô‡πÑ‡∏î‡πâ‡∏à‡∏£‡∏¥‡∏á"
            value={form.identifier}
            onChange={onChange}
          />
        </label>

        <label className="field col">
          <span className="label">‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô</span>
          <input
            type={showPw ? 'text' : 'password'}
            name="confirm"
            placeholder="‡∏û‡∏¥‡∏°‡∏û‡πå‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÄ‡∏î‡∏¥‡∏°‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á"
            value={form.confirm}
            onChange={onChange}
            className={form.confirm && !pwMatch ? 'is-invalid' : ''}
          />
          {!pwMatch && form.confirm ? <small className="hint-error">‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô</small> : null}
        </label>

        <label className="field col">
          <span className="label">‡πÄ‡∏ö‡∏≠‡∏£‡πå‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠</span>
          <input
            type="tel"
            name="phone"
            placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£‡∏ó‡∏µ‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠‡πÑ‡∏î‡πâ"
            value={form.phone}
            onChange={onChange}
            autoComplete="tel"
          />
        </label>

        {/* ‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà (‡∏Å‡∏ß‡πâ‡∏≤‡∏á‡∏Ç‡∏∂‡πâ‡∏ô) */}
        <div className="col span-2-left">
          <span className="label">‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà</span>
          <div className="addr-grid">
            <input
              type="text"
              name="houseNo"
              placeholder="‡∏ö‡πâ‡∏≤‡∏ô‡πÄ‡∏•‡∏Ç‡∏ó‡∏µ‡πà / ‡∏´‡∏°‡∏π‡πà / ‡∏≠‡∏≤‡∏Ñ‡∏≤‡∏£ (‡∏ñ‡πâ‡∏≤‡∏°‡∏µ)"
              value={form.houseNo}
              onChange={onChange}
              className="housewide"
            />
            <select name="province" value={form.province} onChange={onChange}>
              <option value="">‚Äî ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î ‚Äî</option>
              {provinces.map(pv => <option key={pv} value={pv}>{pv}</option>)}
            </select>
            <select name="amphoe" value={form.amphoe} onChange={onChange} disabled={!amphoes.length}>
              <option value="">{form.province ? '‚Äî ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏≠‡∏≥‡πÄ‡∏†‡∏≠ ‚Äî' : '‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î‡∏Å‡πà‡∏≠‡∏ô'}</option>
              {amphoes.map(am => <option key={am} value={am}>{am}</option>)}
            </select>
            <select name="tambon" value={form.tambon} onChange={onChange} disabled={!tambons.length}>
              <option value="">{form.amphoe ? '‚Äî ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏ï‡∏≥‡∏ö‡∏• ‚Äî' : '‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏≠‡∏≥‡πÄ‡∏†‡∏≠‡∏Å‡πà‡∏≠‡∏ô'}</option>
              {tambons.map(tb => <option key={tb} value={tb}>{tb}</option>)}
            </select>
            <input
              name="zipcode"
              placeholder="‡∏£‡∏´‡∏±‡∏™‡πÑ‡∏õ‡∏£‡∏©‡∏ì‡∏µ‡∏¢‡πå"
              value={form.zipcode}
              onChange={onChange}
              readOnly
            />
          </div>
        </div>

        {/* ‡∏Ç‡∏ß‡∏≤: ‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô + ‡∏ä‡∏∑‡πà‡∏≠-‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏• + ‡πÄ‡∏û‡∏® */}
        <label className="field col">
          <span className="label">‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô</span>
          <div className="pw-wrap">
            <input
              type={showPw ? 'text' : 'password'}
              name="password"
              placeholder="‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£"
              value={form.password}
              onChange={onChange}
              autoComplete="new-password"
            />
            <button type="button" className="pw-toggle" onClick={() => setShowPw(s => !s)}>
              {showPw ? 'üôà' : 'üëÅÔ∏è'}
            </button>
          </div>
        </label>

        <label className="field col">
          <span className="label">‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•</span>
